mapper.select:
SELECT CTT /* 컨택 */ , BIL_DT /* 이체일자 */ , DP_TP_CD /* 입금유형코드*/ , FNT /* 이체 */ , CST_NO /* 고객번호 */ , CST_NM /* 고객명 */ , DLQ_MCNT /* 연체개월 */ , FNL_CNSL_D /* 최종상담일 */ , OJ_AMT /* 대상금액 */ , OJ_DP /* 대상입금 */ , OJ_BLAM /* 대상금액 - 대상입금 = 대상잔액 */ , TOT_DLQ_AMT /* 연체금액 + 연체가산금 = 총연체금 */ , TOT_DLQ_DP /* 연체입금금액 + 연체가산금입금금액 = 총연체입금 */ , TOT_DLQ_BLAM /* 총연체금 - 총연체입금 = 총연체잔액 */ , DLQ_AMT /* 연체금액 */ , DLQ_DP /* 연체입금 */ , DLQ_BLAM /* 연체금액 - 연체입금 = 연체잔액 */ , MM_CHRAM_AMT /* 월요금액 */ , MM_CHRAM_DP /* 월요금입금 */ , MM_CHRAM_BLAM /* 당월요금금액 - 당월요금입금금액 = 월요금잔액 */ , DLQ_ADD_AMT /* 연체가산금액 */ , DLQ_ADD_DP /* 연체가산입금 */ , DLQ_ADAMT_BLAM /* 연체가산금액 - 연체가산금입금금액 = 연체가산금잔액 */ , UC_AMT /* 미수금액 */ , UC_DP /* 미수입금 */ , UC_BLAM /* 미수금 - 총입금액 = 미수잔액 */ , TOT_DP_AMT /* 연체입금금액 + 연체가산입금금액 + 해지위약금입금금액 + 당월요금입금금액 = 총입금액 */ , SPMT_SL /* 추가매출 */ , CCAM /* 위약금 */ , LSFE /* 분실료 */ , RTLFE1 /* 렌탈료1 */ , RTLFE_ISTM1 /* 렌탈료1할 */ , RTLFE2 /* 렌탈료2 */ , RTLFE_ISTM2 /* 렌탈료2할 */ , CLCTAM_PRTNR_NO /* 집금번호 */ , CLCTAM_ICHR /* 집급담당 */ , CRAL_LOCARA_TNO /* 계약휴대전화번호1 */ , MEXNO_ENCR /* 계약휴대전화번호2 */ , CRAL_IDV_TNO /* 계약휴대전화번호3 */ , IST_CRAL_LOCARA_TNO /* 설치휴대전화번호1 */ , IST_MEXNO_ENCR /* 설치휴대전화번호2 */ , IST_CRAL_IDV_TNO /* 설치휴대전화번호3 */ , VT_AC_BNK /* 가상계좌은행 */ , VT_AC_NO /* 가상계좌번호 */ , SFK /* 세이프키*/ , CLN_PSBL /* 회수가능성*/ , CLN_PRCS /* 회수절차*/ , CST_STAT /* 고객상태*/ , CVCP_INF /* 민원정보*/ , UNUSL_ARTC /* 특이사항*/ , CNTR_TP_CD /* 고객구분*/ , CNTR_NO /* 계약번호 */ , CNTR_SN /* 계약일련번호 */ FROM ( SELECT CASE WHEN T1.BND_ASN_DT > T2.FST_RGST_DTM AND SUBSTR( T2.TEL_CNSL_RS_CD , 1 ,1 ) = 1 THEN '○' WHEN T1.BND_ASN_DT > T2.FST_RGST_DTM AND SUBSTR( T2.TEL_CNSL_RS_CD , 1 ,1 ) = 2 THEN '△' ELSE 'Ｘ' END AS CTT /* 컨택 */ , T3.BIL_DT /* 이체일자 */ , T11.DP_TP_CD /* 입금유형코드*/ , SUBSTR(T3.BIL_DT,7,8) AS FNT /* 이체 */ , T1.CST_NO AS CST_NO /* 고객번호 */ , T4.CST_NM AS CST_NM /* 고객명 */ , T4.DLQ_MCN AS DLQ_MCNT /* 연체개월 */ , SUBSTR(T2.FST_RGST_DTM,0,8) AS FNL_CNSL_D /* 최종상담일 */ , NVL(0, 0) AS OJ_AMT /* 대상금액 */ , NVL(0, 0) AS OJ_DP /* 대상입금 */ , NVL(0, 0) - NVL(0, 0) AS OJ_BLAM /* 대상금액 - 대상입금 = 대상잔액 */ , T4.DLQ_AMT + T4.DLQ_ADD_AMT AS TOT_DLQ_AMT /* 연체금액 + 연체가산금 = 총연체금 */ , T4.DLQ_DP_AMT + T4.DLQ_ADD_DP_AMT AS TOT_DLQ_DP /* 연체입금금액 + 연체가산금입금금액 = 총연체입금 */ , (T4.DLQ_AMT + T4.DLQ_ADD_AMT) - (T4.DLQ_DP_AMT + T4.DLQ_ADD_DP_AMT) AS TOT_DLQ_BLAM /* 총연체금 - 총연체입금 = 총연체잔액 */ , T4.DLQ_AMT AS DLQ_AMT /* 연체금액 */ , T4.DLQ_DP_AMT AS DLQ_DP /* 연체입금 */ , T4.DLQ_AMT - T4.DLQ_DP_AMT AS DLQ_BLAM /* 연체금액 - 연체입금 = 연체잔액 */ , T4.THM_CHRAM_AMT AS MM_CHRAM_AMT /* 월요금액 */ , T4.THM_CHRAM_DP_AMT AS MM_CHRAM_DP /* 월요금입금 */ , T4.THM_CHRAM_AMT - T4.THM_CHRAM_DP_AMT AS MM_CHRAM_BLAM /* 당월요금금액 - 당월요금입금금액 = 월요금잔액 */ , T4.DLQ_ADD_AMT AS DLQ_ADD_AMT /* 연체가산금액 */ , T4.DLQ_ADD_DP_AMT AS DLQ_ADD_DP /* 연체가산입금 */ , T4.DLQ_ADD_AMT - T4.DLQ_ADD_DP_AMT AS DLQ_ADAMT_BLAM /* 연체가산금액 - 연체가산금입금금액 = 연체가산금잔액 */ , T4.UC_AMT AS UC_AMT /* 미수금액 */ , 0 AS UC_DP /* 미수입금 */ , 0 AS UC_BLAM /* 미수금 - 총입금액 = 미수잔액 */ , 0 AS TOT_DP_AMT /* 연체입금금액 + 연체가산입금금액 + 해지위약금입금금액 + 당월요금입금금액 = 총입금액 */ , 0 AS SPMT_SL /* 추가매출 */ , T4.RSG_BOR_AMT AS CCAM /* 위약금 */ , 0 AS LSFE /* 분실료 */ , 0 AS RTLFE1 /* 렌탈료1 */ , 0 AS RTLFE_ISTM1 /* 렌탈료1할 */ , 0 AS RTLFE2 /* 렌탈료2 */ , 0 AS RTLFE_ISTM2 /* 렌탈료2할 */ , T1.CLCTAM_PRTNR_NO /* 집금번호 */ , ( SELECT PRTNR_KNM FROM TB_OGBS_PRTNR_BAS WHERE PRTNR_NO = T1.CLCTAM_PRTNR_NO ) AS CLCTAM_ICHR /* 집급담당 */ , T5.CRAL_LOCARA_TNO /* 계약휴대전화번호1 */ , T5.MEXNO_ENCR /* 계약휴대전화번호2 */ , T5.CRAL_IDV_TNO /* 계약휴대전화번호3 */ , T9.CRAL_LOCARA_TNO AS IST_CRAL_LOCARA_TNO /* 설치휴대전화번호1 */ , T9.MEXNO_ENCR AS IST_MEXNO_ENCR /* 설치휴대전화번호2 */ , T9.CRAL_IDV_TNO AS IST_CRAL_IDV_TNO /* 설치휴대전화번호3 */ , T6.VAC_BNK_CD AS VT_AC_BNK /* 가상계좌은행 */ , T6.VAC_NO AS VT_AC_NO /* 가상계좌번호 */ , T7.SFK_VAL AS SFK /* 세이프키*/ , T2.BND_CLN_PSBL_DV_CD AS CLN_PSBL /* 회수가능성*/ , T2.BND_CLN_PRCS_DV_CD AS CLN_PRCS /* 회수절차*/ , T2.BND_CNSL_CST_STAT_CD AS CST_STAT /* 고객상태*/ , T8.CVCP_CN AS CVCP_INF /* 민원정보*/ , T8.CNSL_UNUITM_CN AS UNUSL_ARTC /* 특이사항*/ , T10.CNTR_TP_CD /* 고객구분*/ , T1.CNTR_NO /* 계약번호 */ , T1.CNTR_SN /* 계약일련번호 */ FROM TB_CBBO_BND_CNSL_BAS_IZ T1 /* 채권상담기본내역 */ LEFT OUTER JOIN ( SELECT CST_NO , BND_CLN_PSBL_DV_CD , BND_CLN_PRCS_DV_CD , BND_CNSL_CST_STAT_CD , TEL_CNSL_RS_CD , FST_RGST_DTM FROM TB_CBBO_BND_TEL_CNSL_IZ /* 채권전화상담내역 */ WHERE ROWNUM = 1 ORDER BY FST_RGST_DTM DESC ) T2 ON T1.CST_NO = T2.CST_NO LEFT OUTER JOIN TB_RVCL_BIL_PLAN_BAS T3 /* 청구계획기본 */ ON T1.CNTR_NO = T3.CNTR_NO AND T1.BASE_YM = T3.BIL_YM LEFT OUTER JOIN TB_CBBO_BND_CNTR_BAS T4 /* 채권계약기본 */ ON T1.CNTR_NO = T4.CNTR_NO AND T1.CNTR_SN = T4.CNTR_SN LEFT OUTER JOIN ( SELECT CR.CNTR_ADRPC_ID /* 계약주소지ID */ , CB.ADR_ID /* 주소ID */ , CR.DTL_CNTR_NO /* 상세계약번호 */ , CR.DTL_CNTR_SN /* 상세계약일련번호 */ , CB.LOCARA_TNO /* 전화번호1 */ , CB.EXNO_ENCR /* 전화번호2 */ , CB.IDV_TNO /* 전화번호3 */ , CB.CRAL_LOCARA_TNO /* 휴대전화번호1 */ , CB.MEXNO_ENCR /* 휴대전화번호2 */ , CB.CRAL_IDV_TNO /* 휴대전화번호3 */ , NVL(AB.NEW_ADR_ZIP, AB.OLD_ADR_ZIP) AS SPP_ZIP /* 우편번호 */ , NVL(AB.RNADR, AB.LTN_ADR) AS SPP_ADR /* 주소 */ , NVL(AB.RDADR, AB.LTN_DTL_ADR) AS SPP_DTL_ADR /* 상세주소 */ FROM TB_SSCT_CNTR_ADR_REL CR /* 계약주소관계 */ LEFT OUTER JOIN TB_SSCT_CNTR_ADRPC_BAS CB /* 계약주소지기본 */ ON CB.CNTR_ADRPC_ID = CR.CNTR_ADRPC_ID LEFT OUTER JOIN TB_GBCO_ADR_BAS AB /* 주소기본 */ ON AB.ADR_ID = CB.ADR_ID WHERE CR.ADRPC_TP_CD = '1' /* 계약주소 */ AND CR.DTA_DL_YN = 'N' ) T5 ON T1.CNTR_NO = T5.DTL_CNTR_NO AND T1.CNTR_SN = T5.DTL_CNTR_SN LEFT OUTER JOIN TB_CBBO_BND_CNTR_BAS T4 ON T4.CNTR_NO = T1.CNTR_NO AND T4.CNTR_SN = T1.CNTR_SN LEFT OUTER JOIN TB_RVDW_VAC_IS_IZ T6 /* 가상계좌발급내역 */ ON T1.CST_NO = T6.ICHR_PRTNR_NO LEFT OUTER JOIN TB_CUBS_CST_BAS T7 /* 고객기본 */ ON T1.CST_NO = T7.CST_NO LEFT OUTER JOIN TB_CBBO_BND_UNUITM_CVCP_DTL T8 /* 채권특이사항민원상세 */ ON T1.CST_NO = T8.CST_NO AND T1.CNTR_NO = T8.CNTR_NO AND T1.CNTR_SN = T8.CNTR_SN LEFT OUTER JOIN ( SELECT CR.CNTR_ADRPC_ID , CR.DTL_CNTR_NO , CR.DTL_CNTR_SN , CB.LOCARA_TNO , CB.EXNO_ENCR , CB.IDV_TNO , CB.CRAL_LOCARA_TNO , CB.MEXNO_ENCR , CB.CRAL_IDV_TNO FROM TB_SSCT_CNTR_ADR_REL CR LEFT OUTER JOIN TB_SSCT_CNTR_ADRPC_BAS CB ON CB.CNTR_ADRPC_ID = CR.CNTR_ADRPC_ID WHERE CR.ADRPC_TP_CD = '3' /* 설치주소 */ AND CR.DTA_DL_YN = 'N' ) T9 ON T1.CNTR_NO = T9.DTL_CNTR_NO AND T1.CNTR_SN = T9.DTL_CNTR_SN INNER JOIN TB_SSCT_CNTR_BAS T10 /* 계약기본 */ ON T4.CNTR_NO = T10.CNTR_NO LEFT OUTER JOIN TB_SSCT_CNTR_STLM_BAS T11 /* 계약결제기본 */ ON T1.CST_NO = T11.CST_NO AND T1.CNTR_NO = T11.CNTR_NO ) WHERE 1=1 AND CLCTAM_PRTNR_NO = #{schClctamNo}
AND CST_NO = #{schCstNo}
AND CST_NM = #{schCstNm}
AND CRAL_LOCARA_TNO = #{schCralLocaraTno} AND MEXNO_ENCR = #{schMexnoEncr} AND CRAL_IDV_TNO = #{schCralIdvTno}
AND SFK = #{schSfK}
AND IST_CRAL_LOCARA_TNO = #{schIstCralLocaraTno} AND IST_MEXNO_ENCR = #{schIstMexnoEncr} AND IST_CRAL_IDV_TNO = #{schIstCralIdvTno}
AND DLQ_MCNT BETWEEN #{schDlqMcntStrt} AND #{schDlqMcntEnd}
AND MPY_MTHD_TP_CD = '140'
AND MPY_MTHD_TP_CD = '160'
AND BIL_DT BETWEEN #{schFntDtStrt} AND #{schFntDtEnd}
AND OJ_BLAM BETWEEN #{schOjBlamStrt} AND #{schOjBlamEnd}
AND DLQ_BLAM <> 0
AND TOT_DP_AMT <> 0
AND OJ_BLAM <> 0
AND DLQ_MCNT <> 0

SELECT CNSL_UNUITM_CN 
  FROM TB_CBBO_BND_UNUITM_CVCP_DTL
 WHERE 1=1 
   AND CST_NO = #{schCstNo}
   AND CNTR_NO = #{schCntrNo} 
   AND CNTR_SN = #{schCntrSn}

mapper.insert:
 MERGE INTO TB_CBBO_BND_UNUITM_CVCP_DTL T1    /* 채권특이사항민원상세 */
 USING ( SELECT #{cstNo} AS CST_NO            /* 고객번호 */
              , #{cntrNo} AS CNTR_NO          /* 계약번호 */
              , #{cntrSn} AS CNTR_SN          /* 계약일련번호 */
           FROM DUAL ) T2
    ON (
                T1.CST_NO = T2.CST_NO         /* 고객번호 */
            AND T1.CNTR_NO = T2.CNTR_NO       /* 계약번호 */
            AND T1.CNTR_SN = T2.CNTR_SN       /* 계약일련번호 */
       )
  WHEN MATCHED THEN
UPDATE SET CNSL_UNUITM_CN = #{cnslUnuitmCn}

  WHEN NOT MATCHED THEN
    INSERT (
           BND_UNUITM_ID
         , BND_CNTR_TP_CD
         , BND_BIZ_DV_CD
         , CST_NO
         , CNTR_NO
         , CNTR_SN
         , CLCTN_OJ_OG_TP_CD
         , CLCTN_OJ_PRTNR_NO
         , CNSL_UNUITM_CN
         , CVCP_CN
         , DTA_DL_YN

    ) VALUES (
           ( SELECT TO_CHAR(SYSTIMESTAMP, 'YYYYMMDDhh24miss') || LPAD((SELECT COUNT(1) FROM TB_CBBO_BND_UNUITM_CVCP_DTL WHERE SUBSTR(FST_RGST_DTM, 0,8) = TO_CHAR(SYSTIMESTAMP, 'YYYYMMDD'))+1, 6, '0')
               FROM DUAL)
         , #{bndCntrTpCd}
         , #{bndBizDvCd}
         , #{cstNo}
         , #{cntrNo}
         , #{cntrSn}
         , #{clctnOjOgTpCd}
         , #{clctnOjPrtnrNo}
         , #{cnslUnuitmCn}
         , #{cvcpCn}
         , 'N'
           )

