mapper.select:
/* 직권해지관리 - 정기배송해지 조회 */ SELECT A.SL_CL_YM AS BASE_YM /*기준년월*/ ,A.CNTR_NO /*계약번호*/ ,A.CNTR_SN /*계약일련번호*/ ,'해지예정' AS AUTH_RSG_STS /*해지상태*/ ,'N' AS AUTH_RSG_CNFM_YN /*직권해지확정여부*/ ,A.CNTR_NO||'-'||A.CNTR_SN AS CNTR_NO_SN /*계약상세번호*/ ,A.CST_NO /*고객번호*/ ,(SELECT S1.CST_KNM FROM TB_CUBS_CST_BAS S1 /*T:고객기본*/ WHERE S1.CST_NO = A.CST_NO) AS CST_KNM /*고객명*/ , C.BASE_PD_CD /*패키지번호*/ , (SELECT S1.PD_NM FROM TB_PDBS_PD_BAS S1 /*상품기본*/ WHERE S1.PD_CD = A.PD_CD) AS PD_NM /*패키지명*/ , F.PRTNR_KNM /*플래너(판매자)명*/ , B.SELL_PRTNR_NO /*플래너(판매자)번호*/ , F.OG_CD /*조직코드*/ ,( SELECT MIN(SL_RCOG_DT) /* 매출(인식)일자 */ FROM TB_CBCL_WELLS_SL_MM_CL_IZ /* WELLS매출월마감내역 */ WHERE 1=1 AND CNTR_NO = A.CNTR_NO AND CNTR_SN = A.CNTR_SN GROUP BY CNTR_NO, CNTR_SN ) AS SL_DT /*매출일자*/ , C.FNL_AMT AS SELL_AMT /*판매금액*/ , D.DLQ_MCN AS DLQ_MCN /*연체개월수*/ , NVL(A.BTD_UC_AMT,0) + NVL(D.BTD_DLQ_ADD_AMT,0) + NVL(D.THM_OC_DLQ_ADD_AMT,0) AS UC_AMT /*미수금액=기초미수금액+기초연체가산금+당월발생연체가산금*/ , NVL(A.BTD_UC_AMT,0) + NVL(D.BTD_DLQ_ADD_AMT,0) AS DLQ_AMT /*연체금액=기초미수금액+기초연체가산금*/ , A.SL_DP_AGG_AMT AS TOT_RVE_AMT /*입금누계-연체시작일자이후부터 입금된 총금액*/ , (SELECT S1.PRTNR_KNM FROM TB_CBBO_CLCTAM_PRTNR_DTL S1 /*T:집금파트너상세*/ WHERE 1 = 1 AND S1.PRTNR_NO = G.CLCTAM_PRTNR_NO AND ROWNUM <= 1 ) AS CLCTAM_PRTNR_NM /*집금담당자명*/ , G.CLCTAM_PRTNR_NO AS CLCTAM_PRTNR_NO , '' AS ERR_CN /*에러내용*/ , G.BND_BIZ_DV_CD AS BND_BIZ_DV_CD /* 채권업무구분코드 */ , G.BND_CLCTN_PRP_DV_CD AS BND_CLCTN_PRP_DV_CD /* 채권추심속성구분코드 */ , G.BND_CLCTN_PRP_RSON_CD AS BND_CLCTN_PRP_RSON_CD /* 채권추심속성사유코드 */ , G.BND_CLCTN_PRP_CHDT AS BND_CLCTN_PRP_CHDT /* 채권추심속성변경일자 */ FROM TB_CBCL_WELLS_SL_MM_CL_IZ A /* WELLS매출월마감내역 */ INNER JOIN TB_SSCT_CNTR_BAS B /*T:계약기본*/ ON 1 = 1 AND A.CNTR_NO = B.CNTR_NO INNER JOIN TB_SSCT_CNTR_DTL C /*T:계약상세*/ ON 1 = 1 AND C.CNTRW_TP_CD = '7' /* 계약상세 7:정기배송 */ AND C.CNTR_NO = A.CNTR_NO AND C.CNTR_SN = A.CNTR_SN INNER JOIN TB_CBCL_DLQ_BAS D ON 1 = 1 AND D.PERF_YM = A.SL_CL_YM AND D.CNTR_NO = A.CNTR_NO AND D.CNTR_SN = A.CNTR_SN LEFT OUTER JOIN TB_CBBO_WELLS_AUTH_RSG_IZ E ON 1 = 1 AND E.BASE_YM = A.SL_CL_YM AND E.CNTR_NO = A.CNTR_NO AND E.CNTR_SN = A.CNTR_SN LEFT JOIN TB_OGBS_PRTNR_BAS F /*T:파트너기본*/ ON 1 = 1 AND F.OG_TP_CD = B.SELL_OG_TP_CD AND F.PRTNR_NO = B.SELL_PRTNR_NO LEFT JOIN TB_CBBO_BND_CNTR_BAS G /*T:채권계약기본*/ ON 1 = 1 AND G.BASE_YM = SUBSTR(#{authRsgDt},1,6) AND G.CNTR_NO = A.CNTR_NO AND G.CNTR_SN = A.CNTR_SN LEFT JOIN TB_FEAM_WELS_NRCTR_MM_CL H /* 웰스순주문계약월마감 */ ON 1 = 1 AND H.BASE_YM = A.SL_CL_YM AND H.CNTR_NO = A.CNTR_NO AND H.CNTR_SN = A.CNTR_SN WHERE 1 = 1 AND A.SL_CL_YM = #{authRsgDt} /* 매출마감년월 */ AND A.SL_RCOG_DT IS NOT NULL /* LCSLEY>0 */ AND A.CAN_DT IS NULL /* 취소일자 */ AND A.CNTR_PTRM > 1 /*계약기간(ASIS 배송회차):1보다 큰 경우만*/ AND A.RENTAL_TN <= A.CNTR_PTRM /*렌탈회사(ASIS 진행차월)가 계약기간(ASIS 배송회차)보다 작거나 같은 경우*/ AND D.BTD_DLQ_AMT > 0 AND D.DLQ_MCN >= 3 /* 연체개월수 */ AND G.BND_CLCTN_PRP_DV_CD IN ('02', '08') /* 채권추심속성구분코드 02연체, 08TF*/ AND G.BND_CLCTN_PRP_RSON_CD = '20' /* 채권추심속성사유코드-직금관리 */ AND NOT EXISTS ( /* -- 렌탈 계약에 묶인 정기배송건이 아닌 순수 정기배송계약건만 대상으로 제한 --> 계약관계 테이블을 이용하여 식별 */ SELECT 1 FROM TB_SSCT_CNTR_REL WHERE BASE_DTL_CNTR_NO = A.CNTR_NO AND BASE_DTL_CNTR_SN = A.CNTR_SN AND CNTR_REL_DTL_CD IN ('214','216') AND VL_END_DTM >= A.SL_CL_YM||'31235959' AND VL_STRT_DTM <= A.SL_CL_YM||'31235959' AND DTA_DL_YN = 'N' ) AND NOT EXISTS ( SELECT 1 FROM TB_SSCT_CNTR_RSG_PROCS_IZ WHERE CNTR_NO = A.CNTR_NO AND CNTR_SN = A.CNTR_SN AND DTA_DL_YN = 'N' )
AND G.CLCTAM_DV_CD = #{clctamDvCd}
AND G.CLCTAM_PRTNR_NO = #{clctamPrtnrNo}

