) SELECT B.CNTR_NO /* 계약번호 */ ,B.CNTR_SN /* 계약일련번호 */ ,B.SELL_TP_CD /* 판매유형코드 */ ,B.SELL_TP_DTL_CD /* 판매유형상세코드 */ ,B.BASE_PD_CD AS PD_CD /* 상품코드 */ ,0 AS PRGS_NMN_N /* 진행차월수 */ ,0 AS CNTR_PASG_DC /* 계약경과일수 */ ,0 AS SPP_NMN_N /* 배송차월수 */ ,0 AS RTNGD_QTY /* 반품수량 */ ,D.SL_DC /* 매출일수 */ ,D.NOM_SL_AMT /* 정상매출금액 */ ,D.SPMT_SL_AMT /* 추가매출금액 */ ,D.NOM_DSC_AMT /* 정상할인금액 */ ,D.SPMT_DSC_AMT /* 추가할인금액 */ ,D.SL_CTR_AMT /* 매출조정금액 */ ,0 AS CAN_CTR_AMT /* CAN_CTR_AMT-취소조정금액 */ ,( D.NOM_SL_AMT + D.SPMT_SL_AMT - D.NOM_DSC_AMT - D.SPMT_DSC_AMT - D.SL_CTR_AMT - 0 ) AS SL_SUM_AMT /* 매출합계금액 */ ,0 AS SL_SUM_VAT /* 매출합계부가가치세 */ ,( D.NOM_SL_AMT + D.SPMT_SL_AMT - D.NOM_DSC_AMT - D.SPMT_DSC_AMT - D.SL_CTR_AMT - 0 ) AS SL_AGG_AMT /* 매출누계금액 */ ,0 AS SL_AGG_AMT_VAT /* SL_AGG_AMT_VAT-매출누계금액부가가치세 */ ,NVL(D.NOM_DSC_AMT + D.SPMT_DSC_AMT, 0) AS DSC_AGG_AMT /* 할인누계금액 */ ,NVL(D.SL_CTR_AMT + 0,0) AS CTR_AGG_AMT /* 조정누계금액 */ ,0 AS NOM_INT_AMT /* 정상이자금액 */ ,0 AS INT_CTR_AMT /* 이자조정금액 */ ,0 AS INT_SUM_AMT /* 이자합계금액 */ ,0 AS INT_VAT /* 이자부가가치세 */ ,0 AS INT_AGG_AMT /* 이자누계금액 */ ,0 AS INT_AGG_VAT /* 이자누계부가가치세 */ ,0 AS INT_DSC_AGG_AMT /* 이자할인누계금액 */ ,0 AS THM_PAIAM /* 당월원리금 */ ,0 AS THM_SV_AMT /* 당월서비스금액 */ ,0 AS SL_BLAM /* 매출잔액 */ ,0 AS ADN_SV_SPMT_SL_AMT /* 부가서비스추가매출금액 TO-BE */ ,0 AS PRM_BTD_AMT /* 선납기초금액 */ ,0 AS PRPD_BTD_AMT /* 선수기초금액 LCAM31-선수잔액 */ ,D.EOT_ATAM /* 기말선수금 */ ,0 AS TOT_PRPD_AMT /* 총선수금액 LCAM36-선수금총액 */ ,CASE WHEN 0 >= F.UC_AMT THEN F.UC_AMT ELSE 0 END AS SL_DP_AMT /* 매출입금금액 */ ,D.SL_DP_AGG_AMT /* 매출입금누계금액 */ ,F.UC_AMT /* 미수금액 */ ,E.BTD_DLQ_AMT AS DLQ_AMT /* (기초)연체금액-기말-EOT_DLQ_AMT */ ,TO_CHAR(LAST_DAY(#{baseYm}||'01'), 'YYYYMMDD') AS RSG_APLC_DT /* 해지신청일자 */ ,TO_CHAR(LAST_DAY(#{baseYm}||'01'), 'YYYYMMDD') AS RSG_FSH_DT /* 해지완료일자 */ ,'05' AS CNTR_STAT_CH_RSON_CD /* 계약상태변경사유코드-'05' */ ,'1' AS CCAM_EXMPT_DV_CD /* 위약금면책구분코드-T1.EXMPT_YN */ ,'1' AS CSMB_CS_EXMPT_DV_CD /* 소모품비용면책구분코드 */ ,'1' AS REQD_CS_EXMPT_DV_CD /* 철거비용면책구분코드 */ ,(LAST_DAY(TO_DATE(#{baseYm} || '01')) - (SELECT TO_DATE(MIN(SLIP_SL_DT)) /* 매출일자 */ FROM TB_CBCL_WELLS_SL_CNFM_BAS WHERE CNTR_NO = B.CNTR_NO AND CNTR_SN = B.CNTR_SN)) AS PD_USE_DC /* 상품사용일수 */ ,B.PD_GD_CD /* 상품등급코드 */ ,D.PRM_RFND_AMT /* 선납환불금액 */ ,0 AS PRPD_RFND_AMT /* 선수환불금액 */ ,0 AS DSC_DDCTAM /* 할인공제금액 */ ,0 AS FILT_DDCTAM /* 필터공제금액 */ ,0 AS RENTAL_RGST_COST_RFND_AMT /* 렌탈등록비환불금액 */ ,0 AS RENTAL_RGST_COST_RFND_AMT_VAT /* 렌탈등록비환불금액부가가치세 */ ,F.RSG_BOR_AMT AS BOR_AMT /* 위약금액 */ , (NVL(A.BTD_UC_AMT,0) + NVL(E.BTD_DLQ_ADD_AMT,0) + NVL(E.THM_OC_DLQ_ADD_AMT,0)) * -1 AS TOT_RFND_AMT /*총환불금액*/ ,G.LS_RNTF /* 분실손료 */ ,D.MLG_BTD_PRPD_AMT /* 마일리지기초선수금액 */ ,D.MLG_SL_AMT /* 마일리지매출금액 */ ,D.MLG_EOT_PRPD_AMT /* 마일리지기말선수금액 */ ,NVL(E.BTD_DLQ_ADD_AMT,0) AS ADAMT_BTD_AMT /* 가산금기초금액 */ ,NVL(E.THM_DLQ_ADD_DP_SUM_AMT,0) AS ADAMT_DP_RPLC_AMT /* 가산금입금대체금액 */ ,NVL(E.EOT_DLQ_ADD_AMT,0) AS ADAMT_EOT_AMT /* 가산금기말금액 */ ,0 AS ADAMT_DDCTAM /* 가산금공제금액 */ ,0 AS ADAMT_THM_AMT /* 가산금당월금액 TO-BE */ ,0 AS CCAM_RES_AMT1 /* 위약금잔여금액1 */ ,0 AS RENTAL_RGST_COST_BOR_AMT1 /* 렌탈등록비위약금액1 */ ,0 AS DSC_CS_BOR_AMT1 /* 할인비용위약금액1 */ ,0 AS CSMB_COST_BOR_AMT1 /* 소모품비위약금액1 */ ,0 AS RSTL_BOR_AMT1 /* 재약정위약금액1 */ ,0 AS P_BOR_AMT1 /* 포인트위약금액1 */ ,0 AS REQD_CS_BOR_AMT1 /* 철거비용위약금액1 */ ,0 AS CCAM_RES_AMT2 /* 위약금잔여금액2 */ ,0 AS RENTAL_RGST_COST_BOR_AMT2 /* 렌탈등록비위약금액2 */ ,0 AS DSC_CS_BOR_AMT2 /* 할인비용위약금액2 */ ,0 AS CSMB_COST_BOR_AMT2 /* 소모품비위약금액2 */ ,0 AS RSTL_BOR_AMT2 /* 재약정위약금액2 */ ,0 AS P_BOR_AMT2 /* 포인트위약금액2 */ ,0 AS REQD_CS_BOR_AMT2 /* 철거비용위약금액2 TO-BE */ ,'KF' AS RSG_CRT_DV_CD /* 해지생성구분코드 */ ,'U' AS BLK_AUTO_CRT_YN /* 일괄자동생성여부 */ ,'' AS SL_CTR_RMK_CN /* 매출조정비고내용 */ ,'' AS CNTR_CH_RCP_ID /* 계약변경접수ID */ ,'HR1' AS ICHR_OG_TP_CD /* 담당조직유형코드 */ ,#{session.employeeIDNumber} AS ICHR_PRTNR_NO /* 담당파트너번호 */ ,'N' AS DTA_DL_YN /* DTA_DL_YN-데이터삭제여부 */
FROM TB_SSCT_CNTR_BAS A /* 계약기본 */ LEFT JOIN TB_SSCT_CNTR_DTL B /*T:계약상세*/ ON A.CNTR_NO = B.CNTR_NO INNER JOIN TB_CUBS_CST_BAS C /*T:고객기본*/ ON C.CST_NO = A.CNTR_CST_NO LEFT JOIN TB_CBCL_WELLS_SL_MM_CL_IZ D /*T:WELLS매출월마감내역*/ ON D.SL_CL_YM = #{baseYm} /* --TO_CHAR(SYSDATE, 'YYYYMM') */ AND D.CNTR_NO = B.CNTR_NO AND D.CNTR_SN = B.CNTR_SN INNER JOIN TB_CBCL_DLQ_BAS E /*T:연체기본*/ ON E.CNTR_NO = B.CNTR_NO AND E.CNTR_SN = B.CNTR_SN AND E.PERF_YM = D.SL_CL_YM AND E.DTA_DL_YN = 'N' AND E.EOT_DLQ_AMT > 0 /*연체금액이 존재하는 경우*/ AND E.DLQ_MCN >= 3 /*연체개월 : 3개월 이상인 건*/ INNER JOIN TB_CBBO_BND_CNTR_BAS F /*T:채권계약기본*/ ON F.BASE_YM = #{baseYm} AND F.CNTR_NO = B.CNTR_NO AND F.CNTR_SN = B.CNTR_SN LEFT JOIN TB_CBCL_WELLS_BOR_AMT_BAS G /*T:WELLS위약금액기본*/ ON G.PERF_YM = D.SL_CL_YM AND G.CNTR_NO = B.CNTR_NO AND G.CNTR_SN = B.CNTR_SN AND G.DTA_DL_YN = 'N' WHERE 1 = 1 AND B.CNTR_NO = #{cntrNo} AND B.CNTR_SN = #{cntrSn} AND NOT EXISTS ( SELECT 1 FROM TB_SSCT_CNTR_RSG_PROCS_IZ Z1 /* T:계약해지처리내역 */ WHERE 1 = 1 AND DTA_DL_YN = 'N' AND Z1.CNTR_NO = B.CNTR_NO AND Z1.CNTR_SN = B.CNTR_SN ) AND E.DTA_DL_YN = 'N'

mapper.update:
/* 직권해지관리 - 정기배송해지 월매출마감 취소일자 업데이트 */
UPDATE TB_CBCL_WELLS_SL_MM_CL_IZ /*T:WELLS매출월마감내역*/
SET CAN_DT = LAST_DAY(TO_DATE(#{baseYm} || '01'))

WHERE 1 = 1
    AND SL_CL_YM = #{baseYm}
    AND CNTR_NO = #{cntrNo}
    AND CNTR_SN = #{cntrSn}
    AND DTA_DL_YN = 'N'
