mapper.update:
/* 렌탈CB 연체이력 조회 - 저장(NICE발송제외 여부) */
UPDATE TB_CBBO_WELLS_RENTAL_CB_DLQ_IZ
   SET NICE_FW_EXCD_YN = #{niceFwExcdYn}

 WHERE 1 = 1
   AND CST_NO = #{cstNo}
   AND OC_BSDT LIKE #{baseYm}||'%'

mapper.insert:
/* 렌탈CB 연체정보 조회 - 저장(이력) */ INSERT INTO TB_CBBO_RENTAL_CB_DLQ_HIST ( /*T:렌탈CB연체이력*/ CNTR_NO /*계약번호*/ , HIST_CH_DTM /*이력변경일시*/ , CST_NO /*고객번호*/ , RENTAL_CB_RGST_DV_CD /*렌탈CB등록구분코드*/ , DLQ_AMT /*연체금액*/ , DLQ_BLAM /*연체잔액*/ , FST_OC_DT /*최초발생일자*/ , OC_BSDT /*발생기준일자*/ , OC_CRT_DT /*발생생성일자*/ , OC_FILE_CRT_DT /*발생파일생성일자*/ , CHG_FILE_CRT_DT /*변동파일생성일자*/ , RLS_FILE_CRT_DT /*해제파일생성일자*/ , BF_RLS_DT /*이전해제일자*/ , RLS_DT /*해제일자*/ , DL_DT /*삭제일자*/ , CLCTAM_OG_TP_CD /*집금조직유형코드*/ , CLCTAM_PRTNR_NO /*집금파트너번호*/ , NICE_FW_EXCD_YN /*나이스발송제외여부*/ , NOTY_FW_OJ_YN /*알림발송대상여부*/ , NOTY_TRS_DT /*알림전송일자*/ , NOTY_RCV_DT /*알림수신일자*/ , NOTY_RCV_YN /*알림수신여부*/ , NOTAK_RS_CD /*알림톡결과코드*/ , SMS_FW_RCV_STAT_CD /*SMS발송수신상태코드*/ , DTA_DL_YN /*데이터삭제여부*/
) SELECT CNTR_NO /*계약번호*/ , TO_CHAR(SYSDATE,'YYMMDDHH24MMSS')||LPAD(ROWNUM,2,0) AS HIST_CH_DTM , CST_NO /*고객번호*/ , RENTAL_CB_RGST_DV_CD /*렌탈CB등록구분코드*/ , DLQ_AMT /*연체금액*/ , DLQ_BLAM /*연체잔액*/ , FST_OC_DT /*최초발생일자*/ , OC_BSDT /*발생기준일자*/ , OC_CRT_DT /*발생생성일자*/ , OC_FILE_CRT_DT /*발생파일생성일자*/ , CHG_FILE_CRT_DT /*변동파일생성일자*/ , RLS_FILE_CRT_DT /*해제파일생성일자*/ , BF_RLS_DT /*이전해제일자*/ , RLS_DT /*해제일자*/ , DL_DT /*삭제일자*/ , CLCTAM_OG_TP_CD /*집금조직유형코드*/ , CLCTAM_PRTNR_NO /*집금파트너번호*/ , NICE_FW_EXCD_YN /*나이스발송제외여부*/ , NOTY_FW_OJ_YN /*알림발송대상여부*/ , NOTY_TRS_DT /*알림전송일자*/ , NOTY_RCV_DT /*알림수신일자*/ , NOTY_RCV_YN /*알림수신여부*/ , NOTAK_RS_CD /*알림톡결과코드*/ , SMS_FW_RCV_STAT_CD /*SMS발송수신상태코드*/ , DTA_DL_YN /*데이터삭제여부*/
FROM TB_CBBO_WELLS_RENTAL_CB_DLQ_IZ /*T:WELLS렌탈CB연체내역*/ WHERE 1 = 1 AND CST_NO = #{cstNo} AND OC_BSDT LIKE #{baseYm}||'%'
