mapper.update:
/* 렌탈CB 연체정보 조회 - 저장(알림톡 대상 선정) */ MERGE INTO TB_CBBO_WELLS_RENTAL_CB_DLQ_IZ MRG_CB /*WELLS렌탈CB연체내역*/ USING (WITH RENTAL_CB AS ( SELECT Y1.LCDAMT - Y1.BTD_DLQ_ADD_AMT + Y1.THM_DLQ_ADD_DP_SUM_AMT AS DLQ_AMT , Y1.LCDAMT - Y1.BTD_DLQ_ADD_AMT - Y1.NET_DP_AMT AS DLQ_BLAM , Y1.* FROM ( SELECT C2.CNTR_NO , C2.CNTR_SN , C1.CNTR_CST_NO , C1.CNTR_CST_NM , C1.COPN_DV_CD , C1.CNTR_CNFM_DTM , C2.SELL_TP_CD , C2.SELL_TP_DTL_CD , C2.CNTR_DTL_STAT_CD , C2.CNTR_PD_STRTDT , C2.CNTR_PD_ENDDT , NVL(Q1.EOT_DLQ_AMT, 0) AS EOT_DLQ_AMT , NVL(Q1.EOT_DLQ_AMT, 0) + (NVL(Q1.BTD_DLQ_ADD_AMT, 0) - LEAST(NVL(Q1.THM_CTR_DLQ_ADD_AMT, 0) + NVL(Q1.THM_DLQ_ADD_DP_SUM_AMT, 0) - NVL(Q1.THM_DLQ_ADD_RFND_SUM_AMT, 0), NVL(Q1.BTD_DLQ_ADD_AMT, 0))) + NVL(Q2.EOT_BOR_AMT, 0)AS LCDAMT -- 연체금액(LCDAMT) , NVL(Q1.BTD_DLQ_ADD_AMT, 0) AS BTD_DLQ_ADD_AMT-- 기초금액(LCAM81) , S1.MLG_DP_AMT , S1.MLG_RFND_AMT , S1.THM_ATAM_DP_AMT , S1.THM_ATAM_RFND_AMT , NVL(Q1.THM_DLQ_ADD_DP_SUM_AMT, 0) AS THM_DLQ_ADD_DP_SUM_AMT -- 당월연체가산입금합계금액 , S1.MLG_DP_AMT - S1.MLG_RFND_AMT + S1.THM_ATAM_DP_AMT - S1.THM_ATAM_RFND_AMT AS NET_DP_AMT , S1.SL_RCOG_DT , C2.BASE_PD_CD , C4.DP_TP_CD , C4.MPY_BSDT , (CASE WHEN TO_CHAR(ADD_MONTHS(TO_DATE(Q1.PERF_YM, 'YYYYMM'), 1), 'YYYYMM') = #{baseYm} THEN #{baseYm} || '01' ELSE Q1.PERF_YM || C4.MPY_BSDT END ) AS OC_BSDT /*발생일자-MPY_BSDT 납부기준일자*/ , ROW_NUMBER() OVER (PARTITION BY C2.CNTR_NO, C2.CNTR_SN ORDER BY C4.FNL_MDFC_DTM DESC) AS RN FROM TB_SSCT_CNTR_BAS C1 INNER JOIN TB_SSCT_CNTR_DTL C2 ON C1.CNTR_NO = C2.CNTR_NO LEFT JOIN TB_CBCL_DLQ_BAS Q1 ON C2.CNTR_NO = Q1.CNTR_NO AND C2.CNTR_SN = Q1.CNTR_SN AND Q1.PERF_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm},'YYYYMM'),-1),'YYYYMM') /*전월기준*/ AND Q1.DTA_DL_YN = 'N' LEFT JOIN TB_CBCL_WELLS_BOR_AMT_BAS Q2 ON Q1.CNTR_NO = Q2.CNTR_NO AND Q1.CNTR_SN = Q2.CNTR_SN AND Q1.PERF_YM = Q2.PERF_YM AND Q2.DTA_DL_YN = 'N' INNER JOIN TB_CBCL_WELLS_SL_MM_CL_IZ S1 ON C2.CNTR_NO = S1.CNTR_NO AND C2.CNTR_SN = S1.CNTR_SN AND S1.SL_CL_YM = #{baseYm} /*당월기준*/ INNER JOIN TB_SSCT_CNTR_STLM_REL C3 ON C2.CNTR_NO = C3.DTL_CNTR_NO AND C2.CNTR_SN = C3.DTL_CNTR_SN AND #{baseYm} BETWEEN SUBSTR(C3.VL_STRT_DTM, 1, 6) AND SUBSTR(C3.VL_END_DTM, 1, 6) INNER JOIN TB_SSCT_CNTR_STLM_BAS C4 ON C3.CNTR_STLM_ID = C4.CNTR_STLM_ID AND C4.DP_TP_CD IN ('0102', '0203') WHERE 1 = 1 AND C1.DTA_DL_YN = 'N' AND C2.DTA_DL_YN = 'N' AND C2.SELL_TP_CD = '2' AND C2.SELL_TP_DTL_CD IN ('22', '24', '25') /*판매유형상세코드-리스*/ AND C1.CNTR_CNFM_DTM >= '20200901' AND NOT EXISTS (SELECT '1' FROM TB_CBBO_WELLS_RENTAL_CB_DLQ_IZ X1 WHERE C1.CNTR_CST_NO = X1.CST_NO AND X1.RENTAL_CB_RGST_DV_CD IN ('1','3') AND X1.NOTY_RCV_YN = 'Y' AND X1.DTA_DL_YN = 'N') ) Y1 WHERE 1 = 1 AND Y1.RN = 1 ) SELECT B1.CST_NO , T1.CNTR_CST_NM AS CST_KNM , T1.CNTR_NO , B1.CRAL_LOCARA_TNO , B1.MEXNO_ENCR , B1.CRAL_IDV_TNO , T1.DLQ_AMT , T1.DLQ_BLAM , N1.CLCTAM_PRTNR_NO , T1.OC_BSDT , (SELECT RENTAL_CB_RGST_DV_CD FROM TB_CBBO_WELLS_RENTAL_CB_DLQ_IZ X1 WHERE B1.CST_NO = X1.CST_NO AND T1.CNTR_NO = X1.CNTR_NO AND X1.DTA_DL_YN = 'N') AS RENTAL_CB_RGST_DV_CD FROM RENTAL_CB T1 INNER JOIN TB_CUBS_CST_BAS B1 ON T1.CNTR_CST_NO = B1.CST_NO INNER JOIN TB_PDBS_PD_BAS P1 ON T1.BASE_PD_CD = P1.PD_CD INNER JOIN TB_PDBS_PD_CLSF_BAS P2 ON P1.PD_CLSF_ID = P2.PD_CLSF_ID INNER JOIN LATERAL ( SELECT X1.* FROM TB_CBBO_BND_CNTR_BAS X1 WHERE T1.CNTR_NO = X1.CNTR_NO AND T1.CNTR_SN = X1.CNTR_SN AND X1.BASE_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm},'YYYYMM'), -1),'YYYYMM') AND X1.BND_CLCTN_PRP_RSON_CD IN ('20', '3C', '80') ORDER BY X1.BASE_YM ASC FETCH FIRST 1 ROW ONLY ) N1 ON 1 = 1 LEFT JOIN TB_CBBO_CLCTAM_PRTNR_DTL N2 ON N1.CLCTAM_PRTNR_NO = N2.PRTNR_NO WHERE 1 = 1 AND T1.DLQ_AMT > 0 AND T1.DLQ_BLAM > 0 AND T1.CNTR_CST_NO IN (SELECT X1.CNTR_CST_NO FROM RENTAL_CB X1 GROUP BY X1.CNTR_CST_NO HAVING SUM(X1.DLQ_BLAM) >= 100000) /* 연체잔액합계가 10만이 넘는 고객들을 계약별로 조회하여 저장 */ AND NOT EXISTS (SELECT 1 FROM TB_CBBO_BND_CNTC_EXCD_OJ_IZ X1 WHERE T1.CNTR_CST_NO = X1.CST_NO AND X1.CTNT_EXCD_BND_BIZ_CD = '02' -- 렌탈CB AND X1.DTA_DL_YN = 'N' AND #{baseYm} BETWEEN SUBSTR(X1.APY_STRTDT, 1, 6) AND SUBSTR(X1.APY_ENDDT, 1, 6)) ) SRC2 ON (MRG_CB.CNTR_NO = SRC2.CNTR_NO) WHEN NOT MATCHED THEN INSERT ( MRG_CB.CNTR_NO /*계약번호*/ , MRG_CB.CST_NO /*고객번호*/ , MRG_CB.RENTAL_CB_RGST_DV_CD /*렌탈DB등록구분코드*/ , MRG_CB.DLQ_AMT /*연체금액*/ , MRG_CB.DLQ_BLAM /*연체잔액*/ , MRG_CB.FST_OC_DT /*최초발생일자*/ , MRG_CB.OC_BSDT /*발생기준일자*/ , MRG_CB.OC_CRT_DT /*발생생성일자*/ , MRG_CB.OC_FILE_CRT_DT /*발생파일생성일자*/ , MRG_CB.CHG_FILE_CRT_DT /*변동파일생성일자*/ , MRG_CB.RLS_FILE_CRT_DT /*해제파일생성일자*/ , MRG_CB.BF_RLS_DT /*이전해제일자*/ , MRG_CB.RLS_DT /*해제일자*/ , MRG_CB.DL_DT /*삭제일자*/ , MRG_CB.CLCTAM_OG_TP_CD /*집금조직유형코드*/ , MRG_CB.CLCTAM_PRTNR_NO /*집금파트너번호*/ , MRG_CB.NOTY_FW_OJ_YN /*알림톡발송대상여부*/
) VALUES ( SRC2.CNTR_NO /*계약번호*/ , SRC2.CST_NO /*고객번호*/ , '1' /*렌탈DB등록구분코드*/ , SRC2.DLQ_AMT /*연체금액*/ , SRC2.DLQ_BLAM /*연체잔액*/ , SRC2.OC_BSDT /*최초발생일자*/ , SRC2.OC_BSDT /*발생기준일자*/ , TO_CHAR(SYSDATE,'YYYYMMDD') /*발생생성일자*/ , '' /*발생파일생성일자*/ , '' /*변동파일생성일자*/ , '' /*해제파일생성일자*/ , '' /*이전해제일자*/ , '' /*해제일자*/ , '' /*삭제일자*/ , '' /*집금조직유형코드*/ , SRC2.CLCTAM_PRTNR_NO /*집금파트너번호*/ , 'Y' /*알림톡발송대상여부*/
)

mapper.insert:
/* 렌탈CB 연체정보 조회 - 저장(이력) */ INSERT INTO TB_CBBO_RENTAL_CB_DLQ_HIST ( /*T:렌탈CB연체이력*/ CNTR_NO /*계약번호*/ , HIST_CH_DTM /*이력변경일시*/ , CST_NO /*고객번호*/ , RENTAL_CB_RGST_DV_CD /*렌탈CB등록구분코드*/ , DLQ_AMT /*연체금액*/ , DLQ_BLAM /*연체잔액*/ , FST_OC_DT /*최초발생일자*/ , OC_BSDT /*발생기준일자*/ , OC_CRT_DT /*발생생성일자*/ , OC_FILE_CRT_DT /*발생파일생성일자*/ , CHG_FILE_CRT_DT /*변동파일생성일자*/ , RLS_FILE_CRT_DT /*해제파일생성일자*/ , BF_RLS_DT /*이전해제일자*/ , RLS_DT /*해제일자*/ , DL_DT /*삭제일자*/ , CLCTAM_OG_TP_CD /*집금조직유형코드*/ , CLCTAM_PRTNR_NO /*집금파트너번호*/ , NICE_FW_EXCD_YN /*나이스발송제외여부*/ , NOTY_FW_OJ_YN /*알림발송대상여부*/ , NOTY_TRS_DT /*알림전송일자*/ , NOTY_RCV_DT /*알림수신일자*/ , NOTY_RCV_YN /*알림수신여부*/ , NOTAK_RS_CD /*알림톡결과코드*/ , SMS_FW_RCV_STAT_CD /*SMS발송수신상태코드*/ , DTA_DL_YN /*데이터삭제여부*/
) SELECT CNTR_NO /*계약번호*/ , TO_CHAR(SYSDATE,'YYYYMMDDHH24MMSS') , CST_NO /*고객번호*/ , RENTAL_CB_RGST_DV_CD /*렌탈CB등록구분코드*/ , DLQ_AMT /*연체금액*/ , DLQ_BLAM /*연체잔액*/ , FST_OC_DT /*최초발생일자*/ , OC_BSDT /*발생기준일자*/ , OC_CRT_DT /*발생생성일자*/ , OC_FILE_CRT_DT /*발생파일생성일자*/ , CHG_FILE_CRT_DT /*변동파일생성일자*/ , RLS_FILE_CRT_DT /*해제파일생성일자*/ , BF_RLS_DT /*이전해제일자*/ , RLS_DT /*해제일자*/ , DL_DT /*삭제일자*/ , CLCTAM_OG_TP_CD /*집금조직유형코드*/ , CLCTAM_PRTNR_NO /*집금파트너번호*/ , NICE_FW_EXCD_YN /*나이스발송제외여부*/ , NOTY_FW_OJ_YN /*알림발송대상여부*/ , NOTY_TRS_DT /*알림전송일자*/ , NOTY_RCV_DT /*알림수신일자*/ , NOTY_RCV_YN /*알림수신여부*/ , NOTAK_RS_CD /*알림톡결과코드*/ , SMS_FW_RCV_STAT_CD /*SMS발송수신상태코드*/ , DTA_DL_YN /*데이터삭제여부*/
FROM TB_CBBO_WELLS_RENTAL_CB_DLQ_IZ /*T:WELLS렌탈CB연체내역*/ WHERE 1 = 1 AND OC_BSDT LIKE #{baseYm}||'%' AND NOTY_FW_OJ_YN = 'Y'
