mapper.sql:
SELECT CLCTAM_PA_CD
  FROM TB_CBBO_CLCTAM_PA_MNGT_BASE_BAS
 WHERE BZ_HDQ_DV_CD = ${BZ_HDQ_DV_CD}
   AND CLCTAM_DV_CD = ${CLCTAM_DV_CD}

mapper.insert:
INSERT INTO TB_CBBO_BND_ASN_IZ ( /* 채권배정내역 Table */ BND_CNTR_ID , BASE_YM /* 기준년월 */ , CNTR_NO /* 계약번호 */ , CNTR_SN /* 계약일련번호 */ , CO_CD /* 회사코드 */ , BZ_HDQ_DV_CD /* 사업본부구분코드 */ , BND_BIZ_DV_CD /* 채권업무구분코드 */ , BND_TF_DT /* 채권이관일자 */ , CLCTAM_PA_CD /* 집금파트코드 */ , BND_ASN_DT /* 채권배정일자 */ , CLCTAM_PRTNR_NO /* 집금파트너번호 */ , ACTCS_DT /* 수임일자 */ , CST_NO /* 고객번호 */ , CST_NM /* 고객명 */ , CLCTAM_DV_CD /* 집금구분코드 */ , DLQ_RCK_DT /* 연체기산일자 */ , DLQ_MCN /* 연체개월수 */ , DLQ_AMT /* 연체금액 */ , DLQ_ADD_AMT /* 연체가산금액 */ , RSG_BOR_AMT /* 해지위약금액 */ , THM_CHRAM_AMT /* 당월요금금액 */ , UC_AMT /* 미수금액 */ , BND_NW_DV_CD /* 신규여부 */ , BND_ASN_STAT_CD /* 채권배정상태코드 : 파트이관 실행 시 02 부여.. */ , BND_CLN_RS_CD /* 채권회수결과코드 */ , BND_ASN_MTH_CD /* 채권배정형태코드 */ , BND_CLCTN_PRP_DV_CD
) SELECT BND_CNTR_ID , BASE_YM /* 기준년월 */ , CNTR_NO /* 계약번호 */ , CNTR_SN /* 계약일련번호 */ , CO_CD /* 회사코드 */ , BZ_HDQ_DV_CD /* 사업본부구분코드 */ , BND_BIZ_DV_CD /* 채권업무구분코드 */ , BND_TF_DT /* 채권이관일자 */ , CASE WHEN CLCTAM_DV_CD = '01' THEN '11' WHEN CLCTAM_DV_CD = '02' THEN '11' WHEN CLCTAM_DV_CD = '03' THEN '21' WHEN CLCTAM_DV_CD = '04' THEN '30' WHEN CLCTAM_DV_CD = '10' THEN '30' ELSE '' END AS CLCTAM_PA_CD /* 집금파트코드 */ , BND_ASN_DT /* 채권배정일자 */ , CLCTAM_PRTNR_NO /* 집금파트너번호 */ , ACTCS_DT /* 수임일자 */ , CST_NO /* 고객번호 */ , CST_NM /* 고객명 */ , CLCTAM_DV_CD /* 집금구분코드 */ , DLQ_RCK_DT /* 연체기산일자 */ , DLQ_MCN /* 연체개월수 */ , DLQ_AMT /* 연체금액 */ , DLQ_ADD_AMT /* 연체가산금액 */ , RSG_BOR_AMT /* 해지위약금액 */ , THM_CHRAM_AMT /* 당월요금금액 */ , UC_AMT /* 미수금액 */ , BND_NW_DV_CD /* 신규여부 */ , '02' AS BND_ASN_STAT_CD /* 채권배정상태코드 : 파트이관 실행 시 02 부여.. */ , BND_CLN_RS_CD /* 채권회수결과코드 */ , BND_ASN_MTH_CD /* 채권배정형태코드 */ , BND_CLCTN_PRP_DV_CD
FROM () T1

mapper.update:
UPDATE TB_CBBO_BND_CNTR_BAS
   SET CLCTAM_DV_CD = #{clctamDvCd}

 WHERE 1 = 1
   AND BASE_YM = #{baseYm}
   AND BZ_HDQ_DV_CD = #{bzHdqDvCd}
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND CST_NO = #{cstNo}

UPDATE TB_CBBO_BND_CNTR_BAS T1
   SET CLCTAM_PA_CD = (SELECT CLCTAM_PA_CD FROM TB_CBBO_BND_ASN_IZ WHERE BND_CNTR_ID = T1.BND_CNTR_ID AND BASE_YM = T1.BASE_YM)

 WHERE 1 = 1
   AND BASE_YM = #{baseYm}
   AND BZ_HDQ_DV_CD = #{bzHdqDvCd}

UPDATE TB_CBBO_BND_ASN_IZ
   SET CLCTAM_DV_CD = #{clctamDvCd}

 WHERE 1 = 1
   AND BASE_YM = #{baseYm}
   AND BZ_HDQ_DV_CD = #{bzHdqDvCd}
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND CST_NO = #{cstNo}
