mapper.select:
SELECT (CASE WHEN COUNT(*) >= 1 THEN 1
            ELSE 0
         END) AS hasPartTransfer
 FROM TB_CBBO_BND_TF_ASN_EXCN_IZ     /* 채권이관배정수행내역 */
WHERE BASE_YM = #{baseYm}
  AND BZ_HDQ_DV_CD = #{bzHdqDvCd}
  AND TF_BIZ_DV_CD = '03' /* 집금자배정 = 03 */
  AND CLCTAM_DV_CD = #{clctamDvCd}

mapper.update:
UPDATE TB_CBBO_BND_CNTR_BAS
   SET CLCTAM_PRTNR_NO = #{prtnrNo}

 WHERE 1 = 1
   AND BASE_YM = #{baseYm}
   AND BZ_HDQ_DV_CD = #{bzHdqDvCd}
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND CST_NO = #{cstNo}

UPDATE TB_CBBO_BND_ASN_IZ
   SET PRTNR_NO = #{prtnrNo}

 WHERE 1 = 1
   AND BASE_YM = #{baseYm}
   AND BZ_HDQ_DV_CD = #{bzHdqDvCd}
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND CST_NO = #{cstNo}
