mapper.update:
MERGE INTO TB_CBBO_BND_CNSL_BAS_IZ T1 /* 채권상담기본내역 */ USING ( SELECT (TO_CHAR(SYSTIMESTAMP, 'YYYYMMDDhh24miss')||LPAD((ROWNUM), 6, '0')) AS BND_CNSL_BAS_ID , BASE_YM , CASE WHEN CLCTAM_DV_CD = '09' THEN '03' /* 위탁 */ WHEN CLCTAM_DV_CD = '11' THEN '04' /* TF */ ELSE '01' /* 연체 */ END AS BND_CNTR_TP_CD , BND_BIZ_DV_CD , BZ_HDQ_DV_CD , CASE WHEN #{bzHdqDvCd} = '10' THEN '1200' WHEN #{bzHdqDvCd} = '20' THEN '2000' END AS KW_GRP_CO_CD /*교원그룹회사코드(교원-EDU,교원프라퍼티-WELLS)*/ , BND_TF_DT , BND_ASN_DT , CLCTAM_DV_CD , (SELECT OG_TP_CD FROM TB_CBBO_CLCTAM_PRTNR_DTL WHERE PRTNR_NO = CLCTAM_PRTNR_NO AND DTA_DL_YN = 'N' ) AS CLCTAM_OG_TP_CD , CLCTAM_PRTNR_NO , CST_NO , CNTR_NO , CNTR_SN , (SELECT OG_TP_CD FROM TB_CBBO_CLCTAM_PRTNR_DTL WHERE PRTNR_NO = CLCTN_OJ_PRTNR_NO AND DTA_DL_YN = 'N' ) AS CLCTN_OJ_OG_TP_CD , CLCTN_OJ_PRTNR_NO , '01' AS CLCO_CD , #{trfDt} AS CLCO_TF_DT , 'N' AS CINF_AK_STAT_YN /*신용정보요청상태여부(초기값 N)*/ , 'N' AS DTA_DL_YN FROM TB_CBBO_BND_CNTR_BAS WHERE 1 = 1 AND BASE_YM = #{baseYm} AND BZ_HDQ_DV_CD = #{bzHdqDvCd} AND CLCTAM_DV_CD = '11' /* 집금구분코드 : 09.위탁, 11.TF, 99.특수 */ AND BND_CLCTN_PRP_DV_CD = '08' /* 채권추심속성구분코드 : 08.TF */ AND CLCTAM_PRTNR_NO IN (SELECT MIN(PRTNR_NO) FROM TB_CBBO_CLCTAM_PRTNR_DTL WHERE 1 = 1 AND BZ_HDQ_DV_CD = #{bzHdqDvCd} --사업본부구분코드 '10'.EDU, '20'.WELLS AND CLCTAM_DV_CD = '11' --집금구분코드 '11'.tf AND CLCTAM_MNGT_YN = 'Y' AND DTA_DL_YN = 'N') AND DTA_DL_YN = 'N' ) T2 ON (T1.BASE_YM = T2.BASE_YM AND T1.CNTR_NO = T2.CNTR_NO AND T1.CNTR_SN = T2.CNTR_SN ) WHEN MATCHED THEN UPDATE SET T1.BND_CNTR_TP_CD = T2.BND_CNTR_TP_CD , T1.BND_BIZ_DV_CD = T2.BND_BIZ_DV_CD , T1.BZ_HDQ_DV_CD = T2.BZ_HDQ_DV_CD , T1.KW_GRP_CO_CD = T2.KW_GRP_CO_CD , T1.BND_TF_DT = T2.BND_TF_DT , T1.BND_ASN_DT = T2.BND_ASN_DT , T1.CLCTAM_DV_CD = T2.CLCTAM_DV_CD , T1.CLCTAM_OG_TP_CD = T2.CLCTAM_OG_TP_CD , T1.CLCTAM_PRTNR_NO = T2.CLCTAM_PRTNR_NO , T1.CST_NO = T2.CST_NO , T1.CLCTN_OJ_OG_TP_CD = T2.CLCTN_OJ_OG_TP_CD , T1.CLCTN_OJ_PRTNR_NO = T2.CLCTN_OJ_PRTNR_NO , T1.CLCO_CD = T2.CLCO_CD , T1.CLCO_TF_DT = T2.CLCO_TF_DT , T1.CINF_AK_STAT_YN = T2.CINF_AK_STAT_YN , T1.DTA_DL_YN = T2.DTA_DL_YN
WHEN NOT MATCHED THEN INSERT ( T1.BND_CNSL_BAS_ID , T1.BASE_YM , T1.BND_CNTR_TP_CD , T1.BND_BIZ_DV_CD , T1.BZ_HDQ_DV_CD , T1.KW_GRP_CO_CD , T1.BND_TF_DT , T1.BND_ASN_DT , T1.CLCTAM_DV_CD , T1.CLCTAM_OG_TP_CD , T1.CLCTAM_PRTNR_NO , T1.CST_NO , T1.CNTR_NO , T1.CNTR_SN , T1.CLCTN_OJ_OG_TP_CD , T1.CLCTN_OJ_PRTNR_NO , T1.CLCO_CD , T1.CLCO_TF_DT , T1.CINF_AK_STAT_YN , T1.DTA_DL_YN
) VALUES ( T2.BND_CNSL_BAS_ID , T2.BASE_YM , T2.BND_CNTR_TP_CD , T2.BND_BIZ_DV_CD , T2.BZ_HDQ_DV_CD , T2.KW_GRP_CO_CD , T2.BND_TF_DT , T2.BND_ASN_DT , T2.CLCTAM_DV_CD , T2.CLCTAM_OG_TP_CD , T2.CLCTAM_PRTNR_NO , T2.CST_NO , T2.CNTR_NO , T2.CNTR_SN , T2.CLCTN_OJ_OG_TP_CD , T2.CLCTN_OJ_PRTNR_NO , T2.CLCO_CD , T2.CLCO_TF_DT , T2.CINF_AK_STAT_YN , T2.DTA_DL_YN )

mapper.insert:
INSERT INTO TB_CBBO_BND_CNTR_HIST ( BND_CNTR_ID , HIST_CH_DTM , BASE_YM , CNTR_NO , CNTR_SN , CO_CD , BZ_HDQ_DV_CD , BND_BIZ_DV_CD , BND_CLCTN_PRP_DV_CD , BND_CLCTN_PRP_RSON_CD , BND_CLCTN_PRP_CHDT , CLCTAM_MNGT_YN , FNT_BIL_YN , CHAR_FW_YN , BU_DDTN_YN , DLQ_ADAMT_OC_YN , BND_TF_RMK_CN , BND_TF_DT , CLCTAM_PA_CD , BND_ASN_DT , CLCTAM_PRTNR_NO , BF_CLCTAM_PRTNR_NO , ACTCS_DT , CST_NO , CLCTAM_DV_CD , DLQ_RCK_DT , DLQ_MCN , DLQ_AMT , DLQ_ADD_AMT , RSG_BOR_AMT , THM_CHRAM_AMT , UC_AMT , DLQ_DP_AMT , DLQ_ADD_DP_AMT , RSG_BOR_DP_AMT , THM_CHRAM_DP_AMT , FULPY_EXP_AMT , BND_NW_DV_CD , BND_ASN_STAT_CD , BND_CLN_RS_CD , BND_ASN_MTH_CD , CLCO_CD , CLCO_TF_DT , CLCTN_OJ_PRTNR_NO , APR_RQR_NO , APR_AK_STAT_CD , APR_AK_DTM , APR_PSIC_NO , APR_DTM , DTA_DL_YN
) SELECT BND_CNTR_ID , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') AS HIST_CH_DTM , BASE_YM , CNTR_NO , CNTR_SN , CO_CD , BZ_HDQ_DV_CD , BND_BIZ_DV_CD , BND_CLCTN_PRP_DV_CD , BND_CLCTN_PRP_RSON_CD , BND_CLCTN_PRP_CHDT , CLCTAM_MNGT_YN , FNT_BIL_YN , CHAR_FW_YN , BU_DDTN_YN , DLQ_ADAMT_OC_YN , BND_TF_RMK_CN , BND_TF_DT , CLCTAM_PA_CD , BND_ASN_DT , CLCTAM_PRTNR_NO , BF_CLCTAM_PRTNR_NO , ACTCS_DT , CST_NO , CLCTAM_DV_CD , DLQ_RCK_DT , DLQ_MCN , DLQ_AMT , DLQ_ADD_AMT , RSG_BOR_AMT , THM_CHRAM_AMT , UC_AMT , DLQ_DP_AMT , DLQ_ADD_DP_AMT , RSG_BOR_DP_AMT , THM_CHRAM_DP_AMT , FULPY_EXP_AMT , BND_NW_DV_CD , BND_ASN_STAT_CD , BND_CLN_RS_CD , BND_ASN_MTH_CD , CLCO_CD , CLCO_TF_DT , CLCTN_OJ_PRTNR_NO , APR_RQR_NO , APR_AK_STAT_CD , APR_AK_DTM , APR_PSIC_NO , APR_DTM , DTA_DL_YN
FROM TB_CBBO_BND_CNTR_BAS T1 WHERE 1 = 1 AND T1.BASE_YM = #{baseYm} AND T1.CNTR_NO = #{cntrNo} AND T1.CNTR_SN = #{cntrSn} AND T1.DTA_DL_YN = 'N'

mapper.update:
UPDATE TB_CBBO_BND_CNTR_BAS T1

   SET T1.CLCTAM_PRTNR_NO   = #{clctamPrtnrNo}
     , T1.CLCTN_OJ_PRTNR_NO = #{clctamPrtnrNo}
     , T1.BND_ASN_DT        = TO_CHAR(SYSDATE, 'YYYYMMDD')


   SET T1.CLCTAM_PRTNR_NO   = #{clctamPrtnrNo}
     , T1.BND_ASN_DT        = TO_CHAR(SYSDATE, 'YYYYMMDD')


 WHERE 1 = 1
   AND T1.BASE_YM = #{baseYm}
   AND T1.CNTR_NO = #{cntrNo}
   AND T1.CNTR_SN = #{cntrSn}
   AND T1.DTA_DL_YN = 'N'

mapper.select:
SELECT PRTNR_NO
  FROM TB_CBBO_CLCTAM_PRTNR_DTL
 WHERE DTA_DL_YN = 'N'
   AND (BIZ_STRTDT <= #{baseYm} AND BIZ_ENDDT >= #{baseYm})
   AND BZ_HDQ_DV_CD = #{bzHdqDvCd}
   AND CLCTAM_DV_CD = #{clctamDvCd}
