mapper.insert:
INSERT INTO TB_CBBO_BND_CNTR_HIST ( BND_CNTR_ID , HIST_CH_DTM , BASE_YM , CNTR_NO , CNTR_SN , CO_CD , BZ_HDQ_DV_CD , BND_BIZ_DV_CD , BND_CLCTN_PRP_DV_CD , BND_CLCTN_PRP_RSON_CD , BND_CLCTN_PRP_CHDT , CLCTAM_MNGT_YN , FNT_BIL_YN , CHAR_FW_YN , BU_DDTN_YN , DLQ_ADAMT_OC_YN , BND_TF_RMK_CN , BND_TF_DT , CLCTAM_PA_CD , BND_ASN_DT , CLCTAM_PRTNR_NO , BF_CLCTAM_PRTNR_NO , ACTCS_DT , CST_NO , CLCTAM_DV_CD , DLQ_RCK_DT , DLQ_MCN , DLQ_AMT , DLQ_ADD_AMT , RSG_BOR_AMT , THM_CHRAM_AMT , UC_AMT , DLQ_DP_AMT , DLQ_ADD_DP_AMT , RSG_BOR_DP_AMT , THM_CHRAM_DP_AMT , FULPY_EXP_AMT , BND_NW_DV_CD , BND_ASN_STAT_CD , BND_CLN_RS_CD , BND_ASN_MTH_CD , CLCO_CD , CLCO_TF_DT , CLCTN_OJ_PRTNR_NO , APR_RQR_NO , APR_AK_STAT_CD , APR_AK_DTM , APR_PSIC_NO , APR_DTM , DTA_DL_YN
) SELECT BND_CNTR_ID , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') AS HIST_CH_DTM , BASE_YM , CNTR_NO , CNTR_SN , CO_CD , BZ_HDQ_DV_CD , BND_BIZ_DV_CD , BND_CLCTN_PRP_DV_CD , BND_CLCTN_PRP_RSON_CD , BND_CLCTN_PRP_CHDT , CLCTAM_MNGT_YN , FNT_BIL_YN , CHAR_FW_YN , BU_DDTN_YN , DLQ_ADAMT_OC_YN , BND_TF_RMK_CN , BND_TF_DT , CLCTAM_PA_CD , BND_ASN_DT , CLCTAM_PRTNR_NO , BF_CLCTAM_PRTNR_NO , ACTCS_DT , CST_NO , CLCTAM_DV_CD , DLQ_RCK_DT , DLQ_MCN , DLQ_AMT , DLQ_ADD_AMT , RSG_BOR_AMT , THM_CHRAM_AMT , UC_AMT , DLQ_DP_AMT , DLQ_ADD_DP_AMT , RSG_BOR_DP_AMT , THM_CHRAM_DP_AMT , FULPY_EXP_AMT , BND_NW_DV_CD , BND_ASN_STAT_CD , BND_CLN_RS_CD , BND_ASN_MTH_CD , CLCO_CD , CLCO_TF_DT , CLCTN_OJ_PRTNR_NO , APR_RQR_NO , APR_AK_STAT_CD , APR_AK_DTM , APR_PSIC_NO , APR_DTM , DTA_DL_YN
FROM TB_CBBO_BND_CNTR_BAS T1 WHERE 1 = 1 AND T1.BASE_YM = #{baseYm} AND T1.CNTR_NO = #{cntrNo} AND T1.CNTR_SN = #{cntrSn} AND T1.DTA_DL_YN = 'N'

mapper.update:
UPDATE TB_CBBO_BND_CNTR_BAS T1

   SET T1.CLCTAM_PRTNR_NO   = #{clctamPrtnrNo}
     , T1.CLCTN_OJ_PRTNR_NO = #{clctamPrtnrNo}
     , T1.BND_ASN_DT        = TO_CHAR(SYSDATE, 'YYYYMMDD')


   SET T1.CLCTAM_PRTNR_NO   = #{clctamPrtnrNo}
     , T1.BND_ASN_DT        = TO_CHAR(SYSDATE, 'YYYYMMDD')


 WHERE 1 = 1
   AND T1.BASE_YM = #{baseYm}
   AND T1.CNTR_NO = #{cntrNo}
   AND T1.CNTR_SN = #{cntrSn}
   AND T1.DTA_DL_YN = 'N'

mapper.select:
SELECT PRTNR_NO
  FROM TB_CBBO_CLCTAM_PRTNR_DTL
 WHERE DTA_DL_YN = 'N'
   AND (BIZ_STRTDT <= #{baseYm} AND BIZ_ENDDT >= #{baseYm})
   AND BZ_HDQ_DV_CD = #{bzHdqDvCd}
   AND CLCTAM_DV_CD = #{clctamDvCd}
