) VALUES ( #{clinrRgno} /* 청소원등록번호 */ , SUBSTR(#{aplcDt},1,6) /* 접수년월*/ , #{bldCd} /* 빌딩코드*/ , #{session.employeeIDNumber} /*신청자사번*/ , #{aplcDt} /* 신청일자*/ , #{aplcnsNm} /* 신청자 명 */ , #{clinrFxnAmt} /* 청소원고정금액 */ , #{taxDdctam} /* 세금공제금액 */ , #{clinrNm} /* 청소원 명 */ , #{wrkStrtdt} /* 근무시작일자 */ , #{wrkEnddt} /* 근무종료일자 */ , #{rrnoEncr} /* 주민등록번호 */ , #{locaraTno} /* 지역번호 */ , #{exnoEncr} /* 전화국별 */ , #{idvTno} /* 개별전화번호 */ , #{zip} /* 우편번호 */ , #{basAdr} /* 기본주소 */ , #{dtlAdr} /* 상세주소 */ , #{bnkCd} /* 은행코드 */ , #{acnoEncr} /* 계좌번호 */ , #{idfApnFileId} /* 신분증 첨부파일 */ , #{bnkbApnFileId} /* 통장사본 첨부파일 */ , #{cntrwApnFileId} /* 계약서 첨부파일 */ , #{cntrLroreApnFileId} /* 계약해지 첨부파일*/ , #{dtaDlYn} /* 삭제여부*/
)

