mapper.insert:
INSERT INTO TB_CBCL_OPCS_ADJ_DTL( /*T:운영비정산상세*/ OPCS_ADJ_NO /*운영비정산번호*/ , OPCS_ADJ_SN /*운영비정산일련번호*/ , DST_OJ_OG_TP_CD /*배분대상조직유형코드*/ , DST_OJ_PRTNR_NO /*배분대상파트너번호*/ , DST_OJPS_NM /*배분대상자명*/ , DST_OJPS_PERF_AMT /*배분대상자실적금액*/ , DST_AMT /*배분금액*/ , DST_WHTX /*배분원천세*/ , ERNTX /*소득세*/ , RSDNTX /*주민세*/ , PNPYAM_TRS_YN /*가지급금전송여부*/ , SIGN_DTM /*서명일시*/ , SIGNR_OG_TP_CD /*서명자조직유형코드*/ , SIGNR_PRTNR_NO /*서명자파트너번호*/ , SIGN_IMG_APN_FILE_ID /*서명이미지첨부파일ID*/ , DTA_DL_YN /*데이터삭제여부*/
) VALUES ( #{opcsAdjNo} /*운영비정산번호-3번 조회 결과에서 리턴된 OPCS_ADJ_NO*/ , (SELECT NVL(MAX(S1.OPCS_ADJ_SN)+1,1) FROM TB_CBCL_OPCS_ADJ_DTL S1 WHERE S1.OPCS_ADJ_NO = #{opcsAdjNo}) /*운영비정산일련번호*/ , #{dstOjOgTpCd} /*하단 grid의 항목 : DST_OJ_OG_TP_CD*/ , #{dstOjPrtnrNo} /*하단 grid의 항목 : DST_OJ_PRTNR_NO*/ , #{dstOjpsNm} /*하단 grid의 항목 : DST_OJPS_NM*/ , #{dstOjpsPerfAmt} /*하단 grid의 항목 : DST_OJPS_PERF_AMT*/ , #{dstAmt} /*하단 grid의 항목 : DST_AMT*/ , #{dstWhtx} /*하단 grid의 항목 : DST_WHTX -- null이면 0 값으로 입력*/ , #{erntx} /*하단 grid의 항목 : ERNTX -- null이면 0 값으로 입력*/ , #{rsdntx} /*하단 grid의 항목 : RSDNTX -- null이면 0 값으로 입력*/ , 'N' /*가지급금전송여부*/ , '' /*서명일시*/ , '' /*서명자조직유형코드*/ , '' /*서명자파트너번호*/ , '' /*서명이미지첨부파일ID*/ , 'N' /*데이터삭제여부*/
)

INSERT INTO TB_CBCL_OPCS_ADJ_BAS ( /*T:운영비정산기본*/ OPCS_ADJ_NO /*운영비정산번호*/ , BASE_YM /*기준년월*/ , PSTN_DV_CD /*직급구분코드*/ , MSCR_YN /*유가증권여부*/ , ADJ_OG_ID /*정산조직ID*/ , OG_TP_CD /*조직유형코드*/ , ADJ_PRTNR_NO /*정산파트너번호*/ , OPCS_DST_OPT_CD /*운영비배분옵션코드*/ , USE_AMT /*사용금액*/ , DTA_DL_YN /*데이터삭제여부*/
) VALUES ( #{opcsAdjNo} /*운영비정산번호-5.1.에서 리턴 받은 값*/ , #{baseYm} /*기준년월-부모창에서 리턴 받은 값*/ , #{pstnDvCd} /*직급구분코드-하단 grid의 PSTN_DV_CD*/ , 'N' /*유가증권여부-하단 grid의 MSCR_YN*/ , #{adjOgId} /*정산조직ID-하단 grid의 ADJ_OG_ID*/ , #{ogTpCd} /*조직유형코드-하단 grid의 OG_TP_CD*/ , #{adjPrtnrNo} /*정산파트너번호-하단 grid의 ADJ_PRTNR_NO*/ , #{pdstOpt} /*배분옵션-위에서 설명한 부분 찾아볼것*/ , #{dstAmt} /*운영비 정산금액-하단 grid의 DST_AMT*/ , 'N' /*데이터삭제여부*/
)

MERGE INTO TB_CBCL_OPCS_ADJ_USE_REL A /*T:운영비정산사용관계*/ USING DUAL ON (A.OPCS_ADJ_NO = #{opcsAdjNo}) WHEN NOT MATCHED THEN INSERT ( OPCS_ADJ_NO /*운영비정산번호*/ , BASE_YM /*기준년월*/ , OPCS_CARD_USE_IZ_ID /*운영비카드사용내역ID*/ , DTA_DL_YN /*데이터삭제여부*/
) VALUES ( #{opcsAdjNo} , #{baseYm} , #{opcsCardUseIzId} /*운영비카드사용내역ID->부모화면에서 리턴 받아야 함*/ , 'N'
)
