mapper.select:
SELECT T3.DGR1_LEVL_OG_NM , T3.OG_NM , T4.BLD_NM , DST_OJ_OG_TP_CD /*배분대상조직유형코드*/ , DST_OJPS_NM /*성명*/ , SIGNR_PRTNR_NO /*번호*/ , RSB_DV_CD /*직책*/ , F_CMZ_CD_NM(#{session.tenantId}, 'RSB_DV_CD', T5.RSB_DV_CD) AS RSB_DV_NM /*직책*/ , USE_AMT /*금액*/ FROM WSMDBS.TB_CBCL_OPCS_ADJ_BAS T1 INNER JOIN TB_CBCL_OPCS_CNFM_BAS T4 ON T1.BASE_YM = T4.BASE_YM AND T1.OG_TP_CD = T4.OG_TP_CD AND T1.MSCR_YN = T4.MSCR_YN AND T1.PSTN_DV_CD = T4.PSTN_DV_CD INNER JOIN WSMDBS.TB_CBCL_OPCS_ADJ_DTL T2 ON T1.OPCS_ADJ_NO = T2.OPCS_ADJ_NO INNER JOIN TB_OGBS_OG_BAS T3 /*조직기본*/ ON T1.ADJ_DEPT_OG_ID = T3.OG_ID AND T1.PSTN_DV_CD = T3.OG_CD INNER JOIN TB_OGBS_PRTNR_DTL T5 /*파트너상세*/ ON T1.PSTN_DV_CD = T5.PSTN_DV_CD AND T3.OG_TP_CD = T5.OG_TP_CD INNER JOIN TB_OGBS_BLD_BAS T5 /*빌딩기본*/ ON T3.BLD_CD = T5.BLD_CD INNER JOIN TB_OGBS_PRTNR_DTL T6 /*파트너 상세*/ ON T1.PSTN_DV_CD = T6.PSTN_DV_CD AND T3.OG_TP_CD = T6.OG_TP_CD WHERE T1.DTA_DL_YN = 'N' AND T1.MSCR_YN = 'Y'
AND T5.RSB_DV_CD = #{rsbDvCd}
AND T3.DGR2_LEVL_OG_NM = #{ogLevlDvCd2}
AND DST_OJPS_NM LIKE '%'|| #{dstOjpsNm} ||'%'
AND BLD_CD LIKE '%'|| #{bldCd} ||'%'

SELECT T3.DGR2_LEVL_OG_NM , T3.OG_NM , T5.BLD_NM , DST_OJ_OG_TP_CD /*배분대상조직유형코드*/ , DST_OJPS_NM /*성명*/ , SIGNR_PRTNR_NO /*번호*/ , T6.RSB_DV_CD /*직책*/ , F_CMZ_CD_NM(#{session.tenantId}, 'RSB_DV_CD', T6.RSB_DV_CD) AS RSB_DV_NM /*직책*/ , T4.ADJ_CNFM_AMT /*운영비 확정금액*/ , DST_WHTX /*원천세*/ FROM TB_CBCL_OPCS_ADJ_BAS T1 INNER JOIN TB_CBCL_OPCS_CNFM_BAS T4 ON T1.BASE_YM = T4.BASE_YM AND T1.OG_TP_CD = T4.OG_TP_CD AND T1.MSCR_YN = T4.MSCR_YN AND T1.PSTN_DV_CD = T4.PSTN_DV_CD INNER JOIN TB_CBCL_OPCS_ADJ_DTL T2 ON T1.OPCS_ADJ_NO = T2.OPCS_ADJ_NO INNER JOIN TB_OGBS_OG_BAS T3 /*조직기본*/ ON T1.ADJ_DEPT_OG_ID = T3.OG_ID AND T1.PSTN_DV_CD = T3.OG_CD INNER JOIN TB_OGBS_BLD_BAS T5 /*빌딩기본*/ ON T3.BLD_CD = T5.BLD_CD INNER JOIN TB_OGBS_PRTNR_DTL T6 /*파트너 상세*/ ON T1.PSTN_DV_CD = T6.PSTN_DV_CD AND T3.OG_TP_CD = T6.OG_TP_CD WHERE T1.DTA_DL_YN = 'N' AND T1.MSCR_YN = 'Y'
AND PSTN_DV_CD = #{pstnDvCd}
AND T2.DGR2_LEVL_OG_ID = #{dgr2LevlOgId}
AND T2.DGR3_LEVL_OG_ID = #{dgr3LevlOgId}
AND T2.DGR4_LEVL_OG_ID = #{dgr4LevlOgId}
AND DST_OJPS_NM LIKE '%'|| #{dstOjpsNm} ||'%'
AND BLD_CD LIKE '%'|| #{bldCd} ||'%'
