mapper.select:
/* [WDCD] 운영비 등록 관리 - newCgcdInjSpptAplcId - 유가증권 제외*/ SELECT A.OPCS_CARD_USE_IZ_ID /*운영비카드사용내역ID*/ , TO_CHAR(TO_DATE(A.APR_DT||A.APVTM_MS,'YYYYMMDDHH24MISS'),'YYYY-MM-DD AM HH24:MI') AS USE_DTM /*사용일시*/ , C.DGR1_LEVL_OG_ID /*(hidden)총괄단조직ID*/ , C.DGR2_LEVL_OG_ID /*(hidden)지역단ID*/ , C.DGR1_LEVL_OG_NM /*총괄단명*/ , A.CRCDNO_ENCR /*카드번호*/ , A.MRC_NM /*가맹점명*/ , A.MRC_TOBZ_NM /*가맹점업종명*/ , A.MRC_ADR_CN /*가맹점주소내용*/ , A.CARD_APRNO /*카드승인번호*/ , SUM(CASE WHEN A.CARD_APR_STAT_CD ='0' THEN DOM_TRD_AMT*-1 ELSE A.DOM_TRD_AMT END) OVER() AS DOM_TRD_SUM_AMT /*(hidden)사용합계금액*/ , (CASE WHEN A.CARD_APR_STAT_CD ='0' THEN DOM_TRD_AMT*-1 ELSE A.DOM_TRD_AMT END) AS DOM_TRD_AMT /*사용금액*/ , A.OPCS_ADJ_EXCD_YN /*운영비정산제외여부*/ , A.OPCS_ADJ_SMRY_DV_CD /*운영비정산적요구분코드*/ , (SELECT CD_CNTN FROM T_CMZ_CD_D S1 WHERE S1.TENANT_ID = 'TNT_WELLS' AND S1.CD_ID = 'OPCS_ADJ_SMRY_DV_CD' AND S1.CD_VLD_VAL = A.OPCS_ADJ_SMRY_DV_CD) AS OPCS_ADJ_SMRY_DV_NM /*운영비정산적요구분명*/ , A.OJ_APY_CN /*대상적용내용*/ , A.USR_SMRY_CN /*사용자적요내용*/ , A.MRC_TOBZ_CD /*(hidden)가맹점업종코드*/ , A.CDCO_CD /*(hidden)카드사코드*/ , '원천세 정산' AS OPCS_ADJ_BTN /*원천세정산버튼*/ , (CASE (SELECT COUNT(1) FROM TB_CBCL_OPCS_ADJ_USE_REL S1 WHERE S1.OPCS_CARD_USE_IZ_ID = A.OPCS_CARD_USE_IZ_ID) WHEN 0 THEN '' ELSE '완료' END) AS ADJ_CLS /*정산여부*/ , B.ADJ_OG_ID , B.OPCS_ADJ_NO /*운영비정산번호*/ FROM TB_CBCL_OPCS_CARD_USE_IZ A /*T:운영비카드사용내역*/ INNER JOIN TB_CBCL_OPCS_ADJ_CARD_IZ B /*T:운영비정산카드내역*/ ON B.CRCDNO_ENCR = A.CRCDNO_ENCR AND B.BASE_YM = A.BASE_YM INNER JOIN TB_OGBS_MM_OG_IZ C /*T:월조직내역*/ ON C.BASE_YM = B.BASE_YM AND C.OG_ID = B.ADJ_OG_ID WHERE 1=1 AND A.DTA_DL_YN = 'N' AND B.USE_YN = 'Y' AND B.BASE_YM = #{baseYm}
AND B.ADJ_OG_ID = #{dgr1LevlOgId} /*1레벨 조직ID*/
AND B.ADJ_OG_ID = #{dgr2LevlOgId} /*2레벨 조직ID*/
AND B.ADJ_OG_ID = #{dgr3LevlOgId} /*3레벨 조직ID*/
AND A.MRC_TOBZ_NM NOT LIKE '%'||'상품권'||'%' AND A.MRC_NM NOT LIKE '%'||'상품권'||'%' AND A.MRC_NM NOT LIKE '%'||'기프트'||'%' AND A.MRC_NM NOT LIKE '%'||'기프티'||'%' AND A.MRC_NM NOT LIKE '%'||'카카오'||'%'||'선물'||'%' ORDER BY A.CRCDNO_ENCR , A.APR_DT , A.APVTM_MS
