mapper.select:
-- 웰스서비스실적내역 Table 조회
SELECT COUNT(*) /*처리건수*/
  FROM TB_FEAM_WELS_SV_PERF_IZ /*웰스서비스실적내역*/
 WHERE DTA_DL_YN = 'N' /*데이터삭제여부*/
   AND CL_DV_CD  = '01' /*마감구분코드*/
   AND BASE_YM   = #{baseYm}
   AND CNTR_NO   = #{cntrNo}
   AND CNTR_SN   = #{cntrSn}

mapper.insert:
