) VALUES ( TO_CHAR(SYSDATE, 'YYYYMM') /*실적년월*/ , #{cntrNo} /*계약번호*/ , #{cntrSn} /*계약일련번호*/ , #{ccamCmptRqdt} /*위약금산출요청일자*/ , #{ccamCmptCltnDt} /*위약금산출해약일자*/ , #{resRtlfeBorAmt} /*잔여렌탈료위약금액*/ , #{rgstCostDscBorAmt} /*등록비할인위약금액*/ , #{rentalDscBorAmt} /*렌탈할인위약금액*/ , #{csmbCostBorAmt} /*소모품비위약금액*/ , #{rstlBorAmt} /*재약정위약금액*/ , #{pBorAmt} /*포인트위약금액*/ , #{reqdCsBorAmt} /*철거비용위약금액*/ , #{lsRntf} /*분실손료*/ , #{borAmt} /*위약금액*/ , 0 /*기초위약금액*/ , #{borAmt} + #{lsRntf} + #{reqdCsBorAmt} /*발생위약금액*/ , 0 /*입금위약금합계금액*/ , 0 /*환불위약금합계금액*/ , 0 /*조정위약금합계금액*/ , 0 /*추가위약금합계금액*/ , #{borAmt} + #{lsRntf} + #{reqdCsBorAmt} /*기말위약금액*/ , 'N' /*데이터삭제여부*/
