mapper.select:
SELECT T1.PERF_YM /*실적년월*/
       /*판매채널*/
     , T2.CNTR_NO	/*계약상세번호*/
       /*,CST_KNM	고객명한글명*/
     , BTD_DLQ_ADD_AMT /*전기이월*/
     , THM_OC_DLQ_ADD_AMT	/*당월발생*/
       	/*당월공제*/
        /*달월입금*/
     , THM_DLQ_RFND_SUM_AMT	/*당월환불*/
     , EOT_DLQ_ADD_AMT	/*기말잔액*/
   FROM TB_CBCL_DLQ_BAS	T1
  INNER JOIN TB_SSCT_CNTR_DTL T2
     ON T1.CNTR_NO = T2.CNTR_NO
  /*INNER JOIN TB_CUBS_CST_BAS CBAS*/
         /*TB_SSCT_CNTR_BAS*/
   WHERE T1.PERF_YM = #{perfYm}

     AND SELL_TP_CD	= #{sellTpCd}
