AND T1.SELL_INFLW_CHNL_DTL_CD IN ('3010', '4010') /*3010:아웃바운드(SalesTM), 4010:여행특판*/
/*화면 조직선택 - 고객센터인 경우*/ --------------------------------------------------- -- 2022.11.25 조상희 수정 : 3. 5. 번 쿼리 병합 -- 3. 일반일시불(국고보조금,웰스팜 등) + 즉시멤버십유상 가입건 --------------------------------------------------- UNION ALL SELECT T2.CNTR_NO , T2.CNTR_SN , T.CST_KNM --고객명 , T2.SELL_TP_CD --판매유형 , T2.CNTRW_TP_CD --계약서유형코드 , T1.SELL_OG_TP_CD --판매자조직유형코드 , T2.BASE_PD_CD --기준상품코드 --, B.LCIUSE , T2.SV_PRD --서비스주기 , T2.FRISU_YN --무상여부 , T2.STPL_PTRM --약정기간 , T3.HCR_DV_CD --홈케어구분코드 --, B.LCPRT2 --상품구분2 --, B.LCCHK1 , T1.CNTR_CST_NO --계약고객번호 , T1.CNTR_RCP_FSH_DTM --계약접수완료일시 , T2.CNTR_PD_STRTDT --계약상품시작일자 , T3.IST_DT --설치일자 , T2.SELL_AMT --판매금액 , T2.STLM_TP_CD --결제유형코드 , T2.DSC_AMT --할인금액 , T2.CTT_RS_CD --컨택결과코드 , T2.CTT_PSIC_ID --컨택담당자ID , T1.FST_RGST_DTM --최초등록일시 , T1.FST_RGST_USR_ID --최초등록사용자ID , T1.FST_RGST_PRG_ID --최초등록프로그램ID , T1.FNL_MDFC_DTM --최종수정일자 , T1.FNL_MDFC_USR_ID --최종수정사용자ID , T1.FNL_MDFC_PRG_ID --최종수정프로그램ID , T2.CNTR_PD_ENDDT --계약상품종료일자 , T1.CNTR_CNFM_DTM --계약확정일시 --, T2.CNTR_PD_STRTDT --계약상품시작일자 , T2.SELL_TP_DTL_CD --판매유형상세코드 --, KA11.KAETC2 FROM TB_SSCT_CNTR_BAS T1 --계약기본 INNER JOIN TB_SSCT_CNTR_DTL T2 --계약상세 ON T1.CNTR_NO = T2.CNTR_NO INNER JOIN TB_SSCT_CNTR_WELLS_DTL T3 --계약WELLS상세 ON T2.CNTR_NO = T3.CNTR_NO AND T2.CNTR_SN = T3.CNTR_SN AND T3.IST_DT > 0 LEFT OUTER JOIN TB_CUBS_CST_BAS T --고객기본 ON T1.CNTR_CST_NO = T.CST_NO AND T.NUSD_RSON_CD IS NULL AND T.DTA_DL_YN = 'N' WHERE 1 = 1 AND T2.CNTRW_TP_CD != '5' --홈케어멤버십 재외 AND T1.CNTR_CST_NO != '1018139767000' /*계약고객번호(공문교육건제외)*/ AND SUBSTR(T1.CNTR_RCP_FSH_DTM,1,6) >= #{fromCntrRcpFshDtm} --계약접수완료일자 AND SUBSTR(T1.CNTR_RCP_FSH_DTM,1,6) <= #{toCntrRcpFshDtm} --계약접수완료일자 AND T2.CNTR_PD_STRTDT IS NULL --멤버십 가입일자 미존재 --계약상품시작일자 미존재 (AS-IS 멤버십 가입일자) AND T2.CNTR_PD_ENDDT = '99991231' --멤버십 탈퇴일자 미존재 --계약상품종료일자 미존재 (AS-IS 멤버십 탈퇴일자, 멤버십 취소일자) AND T1.CNTR_CNFM_DTM IS NULL --멤버십 확정일자 미존재 --계약확정일자 미존재 (AS-IS 멤버십 확정일자) AND T2.CNTR_PD_STRTDT > 0
AND T2.CNTR_NO = #{cntrNo} AND T2.CNTR_SN = #{cntrSn}
AND T2.SELL_TP_CD = #{sellTpCd}
AND T2.SELL_TP_DTL_CD = #{sellTpDtlCd}
