/*화면 조직선택 - 고객센터인 경우*/ --------------------------------------------------- -- 4.회사설치 건 확정대상 조회 조건 추가 --------------------------------------------------- UNION ALL SELECT T2.CNTR_NO , T2.CNTR_SN , T.CST_KNM --고객명 , T2.SELL_TP_CD --판매유형 , T2.CNTRW_TP_CD --계약서유형코드 , T1.SELL_OG_TP_CD --판매자조직유형코드 , T2.BASE_PD_CD --기준상품코드 --, B.LCIUSE , T2.SV_PRD --서비스주기 , T2.FRISU_YN --무상여부 , T2.STPL_PTRM --약정기간 , F.HCR_DV_CD --홈케어구분코드 --, B.LCPRT2 --상품구분2 --, B.LCCHK1 , T1.CNTR_CST_NO --계약고객번호 , T1.CNTR_RCP_FSH_DTM --계약접수완료일시 , T2.CNTR_PD_STRTDT --계약상품시작일자 , F.IST_DT --설치일자 , T2.SELL_AMT --판매금액 , T2.STLM_TP_CD --결제유형코드 , T2.DSC_AMT --할인금액 , T2.CTT_RS_CD --컨택결과코드 , T2.CTT_PSIC_ID --컨택담당자ID , T1.FST_RGST_DTM --최초등록일시 , T1.FST_RGST_USR_ID --최초등록사용자ID , T1.FST_RGST_PRG_ID --최초등록프로그램ID , T1.FNL_MDFC_DTM --최종수정일자 , T1.FNL_MDFC_USR_ID --최종수정사용자ID , T1.FNL_MDFC_PRG_ID --최종수정프로그램ID , T2.CNTR_PD_ENDDT --계약상품종료일자 , T1.CNTR_CNFM_DTM --계약확정일시 --, T2.CNTR_PD_STRTDT --계약상품시작일자 , E.SELL_TP_DTL_CD --판매유형상세코드 --, KA11.KAETC2 FROM TB_SSCT_CNTR_DTL T2 /*계약기본상세 [AS-IS:LCLIB.LC3500P] -- 멤버십계약만*/ INNER JOIN TB_SSCT_CNTR_BAS T1 /*계약기본 [AS-IS:LCLIB.LC3500P]*/ ON T1.DTA_DL_YN = 'N' AND T1.CNTR_NO = T1.CNTR_NO --AND SUBSTR(T1.CNTR_RCP_FSH_DTM, 1, 6) >= TO_CHAR(ADD_MONTHS(SYSDATE, -4), 'YYYYMM') AND T1.SELL_INFLW_CHNL_DTL_CD NOT IN ('3010', '4010') /*3010:아웃바운드(SalesTM), 4010:여행특판*/ INNER JOIN TB_SSCT_CNTR_REL D /*계약관계 (렌탈 -> 멤버십) [AS-IS:LCLIB.LC3100P+LCLIB.LC3500P]*/ ON D.DTA_DL_YN = 'N' AND D.BASE_DTL_CNTR_NO = T2.CNTR_NO AND D.BASE_DTL_CNTR_SN = T2.CNTR_SN AND D.CNTR_UNIT_TP_CD = '020' /*계약단위유형코드 [010:계약, 020:계약상세]*/ AND D.CNTR_REL_TP_CD = '20' /*계약관계유형코드 [10:계약전환관계, 20:연계상품계약관계, 30:대체회원계약관계]*/ AND D.CNTR_REL_DTL_CD = '212' /*계약관계상세코드 [212:원주문-멤버십]*/ INNER JOIN TB_SSCT_CNTR_DTL E /*계약기본상세 [AS-IS:LCLIB.LC3100P]*/ ON E.DTA_DL_YN = 'N' AND E.CNTR_NO = D.OJ_DTL_CNTR_NO AND E.CNTR_SN = D.OJ_DTL_CNTR_SN AND E.SELL_TP_CD = '3' /*판매유형코드 [1:할부, 2:렌탈/리스, 3:멤버십, 4:회사설치, 5:유지관리, 6:정기배송, 9:필터]*/ AND E.SELL_TP_DTL_CD = '34' /*판매유형상세코드 [34:회사설치]*/ INNER JOIN TB_SSCT_CNTR_WELLS_DTL F /*계약WELLS상세 [AS-IS:LCLIB.LCAK10P]*/ ON F.DTA_DL_YN = 'N' AND F.CNTR_NO = E.CNTR_NO AND F.CNTR_SN = E.CNTR_SN LEFT OUTER JOIN TB_CUBS_CST_BAS T --고객기본 ON T1.CNTR_CST_NO = T.CST_NO AND T.NUSD_RSON_CD IS NULL AND T.DTA_DL_YN = 'N' WHERE T2.DTA_DL_YN = 'N' AND T2.SELL_TP_CD = '3' /*판매유형코드 [1:할부, 2:렌탈/리스, 3:멤버십, 4:회사설치, 5:유지관리, 6:정기배송, 9:필터]*/ AND T2.SELL_TP_DTL_CD != '33' /*판매유형상세코드 [33:홈케어]*/ AND SUBSTR(T1.CNTR_RCP_FSH_DTM,1,6) >= #{fromCntrRcpFshDtm} --계약접수완료일자 AND SUBSTR(T1.CNTR_RCP_FSH_DTM,1,6) <= #{toCntrRcpFshDtm} --계약접수완료일자 AND T2.CNTR_PD_STRTDT IS NULL --멤버십 가입일자 미존재 --계약상품시작일자 미존재 (AS-IS 멤버십 가입일자) AND T2.CNTR_PD_ENDDT = '99991231' --멤버십 탈퇴일자 미존재 --계약상품종료일자 미존재 (AS-IS 멤버십 탈퇴일자, 멤버십 취소일자) AND T1.CNTR_CNFM_DTM IS NULL --멤버십 확정일자 미존재 --계약확정일자 미존재 (AS-IS 멤버십 확정일자)
AND T2.CNTR_NO = #{cntrNo} AND T2.CNTR_SN = #{cntrSn}
AND T2.SELL_TP_CD = #{sellTpCd}
AND E.SELL_TP_DTL_CD = #{sellTpDtlCd}
/*화면 조직선택 - 영업부인 경우*/
AND T1.SELL_INFLW_CHNL_DTL_CD NOT IN ('3010', '4010') /*3010:아웃바운드(SalesTM), 4010:여행특판*/
/*화면 조직선택 - 영업부인 경우*/
/*화면 조직선택 - 고객센터인 경우*/
AND T1.SELL_INFLW_CHNL_DTL_CD IN ('3010', '4010') /*3010:아웃바운드(SalesTM), 4010:여행특판*/ ) M /*화면 조직선택 - 고객센터인 경우*/ ORDER BY M.CNTR_NO
