SELECT M.CNTR_NO --계약번호 , M.CNTR_SN --계약일련번호 , M.CNTR_NO || M.CNTR_SN AS CNTR_NO_SN --계약일련번호 , M.CST_KNM --고객명 , F_CMZ_CD_NM('TNT_WELLS', 'SELL_TP_CD', M.SELL_TP_CD) AS SELL_TP_NM --판매유형 , F_CMZ_CD_NM('TNT_WELLS', 'SELL_TP_DTL_CD', M.SELL_TP_DTL_CD) AS SELL_TP_DTL_NM --판매유형상세코드 , M.CNTRW_TP_CD --계약서유형코드 , M.SELL_OG_TP_CD --판매자조직유형코드 , M.BASE_PD_CD --기준상품코드 , (SELECT PD_NM FROM TB_PDBS_PD_BAS WHERE PD_CD = M.BASE_PD_CD) AS PD_NM--상품명 , F_CMZ_CD_NM('TNT_WELLS', 'SV_PD_TP_CD', T13.SV_PD_TP_CD) AS SV_PD_TP_NM -- 용도구분 , M.SV_PRD --서비스주기 , M.FRISU_YN --무상여부 , M.STPL_PTRM --약정기간 , M.HCR_DV_CD --구분1(홈케어구분코드) , M.HCR_DV_CD AS DV --상품구분2 구분1과 동일한 값이 들어감 --, B.LCPRT2 --상품구분2 구분1과 동일한 값이 들어감 , F_CMZ_CD_NM('TNT_WELLS', 'DP_TP_CD', (SELECT DP_TP_CD FROM TB_SSCT_CNTR_STLM_REL T14 --계약결제관계 WHERE T14.DTL_CNTR_NO = M.CNTR_NO AND T14.DTL_CNTR_SN = M.CNTR_SN ORDER BY CNTR_STLM_REL_ID DESC FETCH FIRST 1 ROWS ONLY)) AS DP_TP_NM --이체구분 , (SELECT DP_TP_CD FROM TB_SSCT_CNTR_STLM_REL T14 --계약결제관계 WHERE T14.DTL_CNTR_NO = M.CNTR_NO AND T14.DTL_CNTR_SN = M.CNTR_SN ORDER BY CNTR_STLM_REL_ID DESC FETCH FIRST 1 ROWS ONLY) AS DP_TP_CD --이체구분코드 , M.CNTR_CST_NO --계약고객번호 , M.CNTR_RCP_FSH_DTM --계약접수완료일시 , M.CNTR_PD_STRTDT --계약상품시작일자 , M.IST_DT --설치일자 , M.SELL_AMT --판매금액 , F_CMZ_CD_NM('TNT_WELLS', 'STLM_TP_CD', M.STLM_TP_CD) AS STLM_TP_NM --결제유형코드 , M.DSC_AMT --할인금액 , M.CTT_RS_CD --컨택결과코드 , F_CMZ_CD_NM('TNT_WELLS', 'LC_CTT_RS_CD', M.CTT_RS_CD) AS CTT_RS_NM --컨택결과코드명 , M.CTT_PSIC_ID --컨택담당자ID , (SELECT PRTNR_KNM FROM TB_OGBS_PRTNR_BAS /*파트너기본*/ WHERE 1 = 1 AND PRTNR_NO = M.CTT_PSIC_ID AND ROWNUM <= 1) AS CTT_PSIC_NM --컨택담당자 , M.FST_RGST_DTM --최초등록일시 , M.FST_RGST_USR_ID --최초등록사용자ID , (SELECT PRTNR_KNM FROM TB_OGBS_PRTNR_BAS /*파트너기본*/ WHERE 1 = 1 AND PRTNR_NO = M.FST_RGST_USR_ID AND ROWNUM <= 1) AS FST_RGST_USR_NM --등록자 , M.FST_RGST_PRG_ID --최초등록프로그램ID , M.FNL_MDFC_DTM --최종수정일자 , M.FNL_MDFC_USR_ID --최종수정사용자ID , (SELECT PRTNR_KNM FROM TB_OGBS_PRTNR_BAS /*파트너기본*/ WHERE 1 = 1 AND PRTNR_NO = M.FNL_MDFC_USR_ID AND ROWNUM <= 1) AS FNL_MDFC_USR_NM --수정자 , M.FNL_MDFC_PRG_ID --최종수정프로그램ID , M.CNTR_PD_ENDDT --계약상품종료일자 , M.CNTR_CNFM_DTM --계약확정일시 --,M.CNTR_PD_STRTDT --계약상품시작일자 --, KA11.KAETC2 FROM (SELECT A.CNTR_NO , A.CNTR_SN , T.CST_KNM , A.SELL_TP_CD --판매유형 , A.CNTRW_TP_CD --계약서유형코드 , B.SELL_OG_TP_CD --판매자조직유형코드 , A.BASE_PD_CD --기준상품코드 , A.SV_PRD --서비스주기 , A.FRISU_YN --무상여부 , A.STPL_PTRM --약정기간 , S.HCR_DV_CD --홈케어구분코드 , B.CNTR_CST_NO --계약고객번호 , B.CNTR_RCP_FSH_DTM --계약접수완료일자 , A.CNTR_PD_STRTDT --계약상품시작일자 , S.IST_DT --설치일자 , A.SELL_AMT --판매금액 , A.STLM_TP_CD --결제유형코드 , A.DSC_AMT --할인금액 , A.CTT_RS_CD --컨택결과코드 , A.CTT_PSIC_ID --컨택담당자ID , B.FST_RGST_DTM --최초등록일시 , B.FST_RGST_USR_ID --최초등록사용자ID , B.FST_RGST_PRG_ID --최초등록프로그램ID , B.FNL_MDFC_DTM --최종수정일자 , B.FNL_MDFC_USR_ID --최종수정사용자ID , B.FNL_MDFC_PRG_ID --최종수정프로그램ID , A.CNTR_PD_ENDDT --계약상품종료일자 , B.CNTR_CNFM_DTM --계약확정일시 , A.SELL_TP_DTL_CD --판매유형상세코드 FROM TB_SSCT_CNTR_DTL A /*계약상세 - 멤버십*/ , TB_SSCT_CNTR_BAS B /*계약기본 - 멤버십*/ LEFT OUTER JOIN TB_CUBS_CST_BAS T --고객기본 ON B.CNTR_CST_NO = T.CST_NO AND T.NUSD_RSON_CD IS NULL AND T.DTA_DL_YN = 'N' , TB_SSCT_CNTR_REL C /*계약관계*/ , TB_SSCT_CNTR_DTL D /*계약상세 - 렌탈/리스*/ , TB_SSCT_CNTR_BAS E /*계약기본 - 렌탈/리스*/ , TB_CBCL_WELLS_SL_MM_CL_IZ F /*WELLS매출월마감내역*/ LEFT OUTER JOIN TB_CBCL_DLQ_BAS G /*연체기본*/ ON G.CNTR_NO = F.CNTR_NO AND G.CNTR_SN = F.CNTR_SN AND G.PERF_YM = TO_CHAR(SYSDATE, 'YYYYMM') , TB_SSCT_CNTR_WELLS_DTL S /*계약WELLS상세 [AS-IS:LCLIB.LCAK10P]*/ WHERE 1 = 1 AND A.CNTR_NO = B.CNTR_NO AND C.BASE_DTL_CNTR_NO = A.CNTR_NO AND C.BASE_DTL_CNTR_SN = A.CNTR_SN AND D.CNTR_NO = C.OJ_DTL_CNTR_NO AND D.CNTR_SN = C.OJ_DTL_CNTR_SN AND E.CNTR_NO = C.OJ_DTL_CNTR_NO AND F.CNTR_NO = C.OJ_DTL_CNTR_NO AND F.CNTR_SN = C.OJ_DTL_CNTR_SN AND F.SL_CL_YM = TO_CHAR(ADD_MONTHS(SYSDATE, -1), 'YYYYMM') AND A.SELL_TP_CD = '3' AND A.SELL_TP_DTL_CD <> '33' --홈케어 맴버십 제외 AND B.CNTR_CNFM_DTM IS NULL --멤버십 확정일시 미존재 AND A.CNTR_PD_ENDDT = '99991231' --멤버십 탈퇴일자 미존재 AND B.CNTR_CAN_DTM IS NULL --멤버십 취소일시 미존재 AND A.CNTR_PD_STRTDT IS NULL --멤버십 가입일자 미존재 AND SUBSTR(D.CNTR_PD_ENDDT, 1, 6) = TO_CHAR(SYSDATE, 'YYYYMM') AND ((D.SELL_TP_DTL_CD IN ('21', '23') AND F.RENTAL_TN >= D.CNTR_PTRM) --사용차월, 렌탈차월 비교 OR (D.SELL_TP_DTL_CD NOT IN ('21', '23') AND F.RENTAL_TN >= D.CNTR_PTRM AND TO_CHAR(ADD_MONTHS(D.CNTR_PD_STRTDT, D.CNTR_PTRM), 'YYYYMMDD') < TO_CHAR(SYSDATE, 'YYYYMMDD'))) AND NVL(G.DLQ_MCN, 0) <= 2 --연체건수 2개월 미만 AND S.CNTR_NO = A.CNTR_NO AND S.CNTR_SN = A.CNTR_SN AND S.REQD_DT IS NULL --철거일자 /*조회조건 시작*/ AND SUBSTR(B.CNTR_RCP_FSH_DTM,1,6) BETWEEN #{fromCntrRcpFshDtm} AND #{toCntrRcpFshDtm} --계약접수완료일자
