mapper.select:
SELECT NOM_ACC_EXCD_ID /*정상계정제외ID - 히든*/ ,SELL_TP_CD /*판매유형코드*/ ,SELL_TP_DTL_CD /*판매유형상세코드*/ ,PD_HCLSF_ID /*상품대분류*/ ,PD_MCLSF_ID /*상품중분류*/ ,PD_CD /*상품코드*/ ,(SELECT PD_NM FROM TB_PDBS_PD_BAS WHERE PD_CD = T1.PD_CD) AS PD_NM /*상품명*/ ,PMOT_CD /*프로모션코드*/ ,(SELECT PMOT_NM FROM TB_PDBS_PMOT_BAS WHERE PMOT_CD = #{pmotCd}) AS PMOT_NM ,CNTR_STRTDT /*계약시작일자*/ ,CNTR_ENDDT /*계약종료일자*/ ,NOM_ACC_EXCD_RSON_CN /*정상계정제외사유*/ ,SUBSTR(FST_RGST_DTM,1,8) AS FST_RGST_DTM /*등록일자*/ ,FST_RGST_USR_ID /*등록담당번호*/ ,(SELECT USR_NM FROM T_CMP_USR_ACO_M WHERE USR_ID = T1.FST_RGST_USR_ID) AS FST_USER_NM /*등록자명*/ ,SUBSTR(FNL_MDFC_DTM,1,8) AS FNL_MDFC_DTM /*변경일자*/ ,FNL_MDFC_USR_ID /*변경담당번호*/ ,(SELECT USR_NM FROM T_CMP_USR_ACO_M WHERE USR_ID = T1.FNL_MDFC_USR_ID) AS FNL_USER_NM /*등록자명*/ FROM TB_CBCL_WELLS_NOM_ACC_EXCD_BAS T1 WHERE DTA_DL_YN = 'N'
AND SUBSTR(FST_RGST_DTM,1,8) BETWEEN #{regDtFrom} AND #{regDtTo}
AND PD_CD = #{pdCd}

SELECT NOM_ACC_EXCD_ID /*정상계정제외ID - 히든*/ ,CNTR_NO ,CNTR_SN ,CNTR_NO || CNTR_SN AS CNTR_DTL /*계약상세번호*/ ,CNTR_EXCD_RSON_CN /*내용*/ ,NOM_ACC_EXCD_RSON_CN /*정상계정제외사유*/ ,SUBSTR(FST_RGST_DTM,1,8) AS FST_RGST_DTM /*등록일자*/ ,FST_RGST_USR_ID /*등록담당번호*/ ,(SELECT USR_NM FROM T_CMP_USR_ACO_M WHERE USR_ID = T1.FST_RGST_USR_ID) AS FST_USER_NM /*등록자명*/ ,SUBSTR(FNL_MDFC_DTM,1,8) AS FNL_MDFC_DTM /*변경일자*/ ,FNL_MDFC_USR_ID /*변경담당번호*/ ,(SELECT USR_NM FROM T_CMP_USR_ACO_M WHERE USR_ID = T1.FNL_MDFC_USR_ID) AS FNL_USER_NM /*등록자명*/ FROM TB_CBCL_WELLS_NOM_ACC_EXCD_BAS T1 WHERE DTA_DL_YN = 'N'
AND CNTR_NO = #{cntrNo}
AND CNTR_SN = #{cntrSn}
AND SUBSTR(FST_RGST_DTM,1,8) BETWEEN #{regDtFrom} AND #{regDtTo}

mapper.insert:
INSERT INTO TB_CBCL_WELLS_NOM_ACC_EXCD_BAS
VALUES(
       (SELECT TO_NUMBER(MAX(NOM_ACC_EXCD_ID))+1 FROM TB_CBCL_WELLS_NOM_ACC_EXCD_BAS)
      ,#{sellTpCd}
      ,#{sellTpDtlCd}
      ,(SELECT MAX(PD_HCLSF_ID) FROM TB_PDBS_PD_BAS WHERE PD_CD = #{pdCd})
      ,(SELECT MAX(PD_MCLSF_ID) FROM TB_PDBS_PD_BAS WHERE PD_CD = #{pdCd})
      ,#{pdCd}
      ,#{pmotCd}
      ,#{cntrStrtdt}
      ,#{cntrEnddt}
      ,#{cntrNo}
      ,#{cntrSn}
      ,#{cntrExcdRsonCn}
      ,#{nomAccExcdRsonCn}
      ,'N'

)

mapper.update:
UPDATE TB_CBCL_WELLS_NOM_ACC_EXCD_BAS
   SET SELL_TP_CD = #{sellTpCd}
      ,SELL_TP_DTL_CD = #{sellTpDtlCd}
      ,PD_HCLSF_ID = (SELECT MAX(PD_HCLSF_ID) FROM TB_PDBS_PD_BAS WHERE PD_CD = #{pdCd})
      ,PD_MCLSF_ID = (SELECT MAX(PD_MCLSF_ID) FROM TB_PDBS_PD_BAS WHERE PD_CD = #{pdCd})
      ,PD_CD = #{pdCd}
      ,PMOT_CD = #{pmotCd}
      ,CNTR_STRTDT = #{cntrStrtdt}
      ,CNTR_ENDDT = #{cntrEnddt}
      ,CNTR_NO = #{cntrNo}
      ,CNTR_SN = #{cntrSn}
      ,CNTR_EXCD_RSON_CN = #{cntrExcdRsonCn}
      ,NOM_ACC_EXCD_RSON_CN = #{nomAccExcdRsonCn}

 WHERE NOM_ACC_EXCD_ID = #{nomAccExcdId}

UPDATE TB_CBCL_WELLS_NOM_ACC_EXCD_BAS
   SET DTA_DL_YN = 'Y'

 WHERE NOM_ACC_EXCD_ID = #{nomAccExcdId}
