mapper.select:
/* 10.가압펌프조회 */ SELECT C1.CNFM_YN /* 확정여부 */ , C1.ERR_MSG /* 오류상세 */ , C1.CNTR_NO /* 계약번호 */ , C1.CNTR_SN /* 계약일련번호 */ , (SELECT Z2.RCGVP_KNM FROM TB_SSCT_CNTR_ADR_REL Z1 /* 계약주소관계 */ INNER JOIN TB_SSCT_CNTR_ADRPC_BAS Z2 /* 계약주소지기본 */ ON Z2.CNTR_ADRPC_ID = Z1.CNTR_ADRPC_ID AND Z2.DTA_DL_YN = 'N' WHERE Z1.DTA_DL_YN = 'N' AND Z1.ADRPC_TP_CD = '3' AND Z1.DTL_CNTR_NO = C1.CNTR_NO AND Z1.DTL_CNTR_SN = C1.CNTR_SN AND C1.FNL_VST_FSH_DT BETWEEN Z1.VL_STRT_DTM AND Z1.VL_END_DTM AND ROWNUM = 1) AS CST_KNM /* 고객명 */ , F_CMZ_CD_NM(#{session.tenantId}, 'SELL_TP_CD', C1.FNL_SELL_TP_CD) AS FILT_SELL_TP_CD /* 판매유형 */ , C1.ADN_SV_SN /* 일련번호 */ , C1.ITM_PD_CD /* 제품코드 */ , C1.BASE_PD_CD /* 기준상품코드 */ , C1.PD_ABBR_NM /* 제품명 */ , C1.PD_NM /* 가압펌프자재 */ , (SELECT X1.OG_NM FROM TB_OGBS_PRTNR_BAS X1 /* 파트너기본 */ WHERE X1.PRTNR_NO = C1.ICHR_PRTNR_NO AND ROWNUM = 1) AS OG_NM /* 센터 */ , F_CMZ_CD_NM(#{session.tenantId}, 'SV_BIZ_DCLSF_CD', C1.SV_BIZ_DCLSF_CD) AS WRK_TYP_NM /* 업무유형 */ , C1.GUBN /* 사용/회수 */ , CASE WHEN C1.ADN_SV_CS_AMT IS NOT NULL THEN C1.ADN_SV_CS_AMT WHEN C1.ITM_PD_CD IN ('WM07100081', 'WM07100080') THEN 2000 ELSE 0 END AS LCTAMT /* 제품가격 */ , C1.SV_MCN /* 서비스기간 */ , C1.DUTY_USE_MCN /* 의무기간 */ , C1.FNL_VST_FSH_DT /* 작업일자 */ , C1.RCPDT /* 접수일자 */ , C1.IST_DT /* 설치일자 */ , C1.REQD_DT /* 철거일자 */ , C1.RSG_DT /* 해약일자 */ , C1.FNL_ITM_GD_CD /* 등급 */ , C1.USE_QTY /* 수량 */ , CASE WHEN C1.REFRI_DV_CD = '2' THEN '유상' ELSE '무상' END AS CHAG_GB_NM /* 구분 */ , (SELECT X1.PRTNR_KNM FROM TB_OGBS_PRTNR_BAS X1 /* 파트너기본 */ WHERE X1.PRTNR_NO = C1.ICHR_PRTNR_NO AND ROWNUM = 1) AS PRTNR_KNM /* 엔지니어 */ , (SELECT SUM(CASE WHEN X2.SV_BIZ_HCLSF_CD = 6 THEN -1 ELSE 1 END) FROM TB_SVST_SV_WK_OSTR_IZ X2 /* 서비스작업출고내역 */ WHERE X2.DTA_DL_YN = 'N' AND X2.ITM_PD_CD IN ('WM07100081', 'WM07100080') AND X2.FNL_VST_FSH_DT >= '20160901' AND X2.CNTR_NO = C1.CNTR_NO) AS T_CNT /* 누적재고 */ , C1.FST_RGST_DTM /* 등록일자 */ , C1.FST_RGST_USR_ID /* 등록자 */ , C1.FNL_MDFC_DTM /* 수정일자 */ , C1.FNL_MDFC_USR_ID /* 수정자 */ , C1.ADN_SV_STRT_YM /* 부가서비스시작년월 */ , C1.IST_DUEDT /* 설치예정일자 */ , C1.SV_BIZ_HCLSF_CD /* 사용/회수여부 */ , C1.SV_BIZ_DCLSF_CD /* 서비스업무세분류코드 */ , (SELECT MAX(X3.BASE_PD_CD) FROM TB_PDBS_PD_REL X3 /* 상품관계 */ WHERE X3.OJ_PD_CD = C1.ITM_PD_CD AND X3.PD_REL_TP_CD = '05') AS PDCT_PD_CD /* 제품상품코드 */ FROM (SELECT CASE WHEN B1.CNFM_TMP_YN = '확정' THEN '확정' WHEN B1.SV_BIZ_HCLSF_CD = 6 AND B1.UPDATE_CNFM_YN = 'Y' AND B1.SV_BIZ_DCLSF_CD != '1121' THEN '오류' WHEN B1.SV_BIZ_HCLSF_CD = 6 AND B1.UPDATE_YN = 'N' AND B1.SV_BIZ_DCLSF_CD != '1121' THEN '오류' ELSE B1.CNFM_TMP_YN END AS CNFM_YN , CASE WHEN B1.CNFM_TMP_YN = '확정' THEN '' WHEN B1.SV_BIZ_HCLSF_CD = 6 AND B1.UPDATE_CNFM_YN = 'Y' AND B1.SV_BIZ_DCLSF_CD != '1121' THEN '회수 하지않은 상태로 데이타가 등록되었습니다.' WHEN B1.SV_BIZ_HCLSF_CD = 6 AND B1.UPDATE_YN = 'N' AND B1.SV_BIZ_DCLSF_CD != '1121' THEN '회수할 수 없습니다. 이전 설치 데이타가 존재하지 않습니다.' END AS ERR_MSG --오류상세 , CASE WHEN B1.SV_BIZ_HCLSF_CD != 6 THEN '사용' WHEN B1.SV_BIZ_HCLSF_CD = 6 THEN '회수' END AS GUBN --사용/회수 , B1.* FROM (SELECT A1.CNTR_NO , A1.CNTR_SN , A1.FNL_SELL_TP_CD , A1.ITM_PD_CD , A1.SV_BIZ_HCLSF_CD , A1.SV_BIZ_DCLSF_CD , A1.FNL_VST_FSH_DT , A1.REFRI_DV_CD , A1.BRCH_OG_ID , A1.ICHR_PRTNR_NO , A1.FNL_ITM_GD_CD , A1.USE_QTY , A2.PD_CD , A2.PD_ABBR_NM AS PD_NM , A3.PD_ABBR_NM , A4.CNTR_CST_NO , SUBSTR(A4.CNTR_CNFM_DTM, 1, 8) AS RCPDT , A5.CNTR_PD_STRTDT AS IST_DT , A5.BASE_PD_CD , A6.ADN_SV_SN , A6.SV_MCN , A6.DUTY_USE_MCN , A6.REQD_DT , A6.RSG_DT , A6.ADN_SV_STRT_YM , A6.IST_DUEDT , A6.ADN_SV_CS_AMT , NVL(A6.FST_RGST_DTM, A1.FST_RGST_DTM) AS FST_RGST_DTM , NVL(A6.FST_RGST_USR_ID, A1.FST_RGST_USR_ID) AS FST_RGST_USR_ID , NVL(A6.FNL_MDFC_DTM, A1.FNL_MDFC_DTM) AS FNL_MDFC_DTM , NVL(A6.FNL_MDFC_USR_ID, A1.FNL_MDFC_USR_ID) AS FNL_MDFC_USR_ID , CASE WHEN A6.RCPDT = A7.RCPDT AND A1.SV_BIZ_HCLSF_CD = '6' THEN 'Y' ELSE 'N' END UPDATE_CNFM_YN , CASE WHEN A7.REQD_DT IS NULL THEN 'Y' ELSE 'N' END AS UPDATE_YN , CASE WHEN A1.SV_BIZ_HCLSF_CD = 6 AND A1.FNL_VST_FSH_DT = A6.REQD_DT THEN '확정' WHEN A1.SV_BIZ_HCLSF_CD = 6 THEN '미확정' WHEN A1.FNL_VST_FSH_DT = A6.RCPDT THEN '확정' ELSE '미확정' END AS CNFM_TMP_YN --확정여부 FROM TB_SVST_SV_WK_OSTR_IZ A1 /* 서비스작업출고내역 */ INNER JOIN TB_PDBS_PD_BAS A2 /* 상품기본 */ ON A2.PD_CD = A1.ITM_PD_CD INNER JOIN TB_PDBS_PD_BAS A3 /* 상품기본 */ ON A3.PD_CD = A1.FST_PD_CD INNER JOIN TB_SSCT_CNTR_BAS A4 /* 계약기본 */ ON A4.CNTR_NO = A1.CNTR_NO INNER JOIN TB_SSCT_CNTR_DTL A5 /* 계약상세 */ ON A5.CNTR_NO = A1.CNTR_NO AND A5.CNTR_SN = A1.CNTR_SN LEFT OUTER JOIN (SELECT Z1.* , Z2.OJ_PD_CD FROM TB_SSCT_RENTAL_ADN_SV_IZ Z1 /* 렌탈부가서비스내역 */ INNER JOIN TB_PDBS_PD_REL Z2 /* 상품관계 */ ON Z2.BASE_PD_CD = Z1.PDCT_PD_CD AND Z2.PD_REL_TP_CD = '05' AND Z2.DTA_DL_YN = 'N' WHERE Z1.ADN_SV_DV_CD = '01' AND Z1.ADN_SV_SN = (SELECT MAX(Y1.ADN_SV_SN) FROM TB_SSCT_RENTAL_ADN_SV_IZ Y1 /* 렌탈부가서비스내역 */ WHERE Y1.CNTR_NO = Z1.CNTR_NO AND Y1.CNTR_SN = Z1.CNTR_SN AND Y1.PDCT_PD_CD = Z1.PDCT_PD_CD AND Y1.ADN_SV_DV_CD = Z1.ADN_SV_DV_CD AND Y1.DTA_DL_YN = 'N') AND Z1.DTA_DL_YN = 'N' ) A6 ON A6.CNTR_NO = A1.CNTR_NO AND A6.CNTR_SN = A1.CNTR_SN AND A6.OJ_PD_CD = A1.ITM_PD_CD LEFT OUTER JOIN TB_SSCT_RENTAL_ADN_SV_IZ A7 ON A7.CNTR_NO = A1.CNTR_NO AND A7.PDCT_PD_CD = A6.PDCT_PD_CD AND A7.ADN_SV_DV_CD = '01' AND A7.ADN_SV_SN = (SELECT MAX(Y2.ADN_SV_SN) FROM TB_SSCT_RENTAL_ADN_SV_IZ Y2 /* 렌탈부가서비스내역 */ WHERE Y2.CNTR_NO = A7.CNTR_NO AND Y2.PDCT_PD_CD = A7.PDCT_PD_CD AND Y2.ADN_SV_DV_CD = A7.ADN_SV_DV_CD AND Y2.DTA_DL_YN = 'N') AND A7.DTA_DL_YN = 'N' WHERE A1.DTA_DL_YN = 'N' AND NVL(A1.REFRI_DV_CD, '1') != '2' AND A1.FST_VST_FSH_DT BETWEEN #{fstVstFshDtFrom} AND #{fstVstFshDtTo}
AND A1.ITM_PD_CD IN ('WM07100081', 'WM07100080')
AND A1.ITM_PD_CD = #{itmPdCd}
AND A1.SV_BIZ_DCLSF_CD NOT IN ('1110','1121','1122','1123','1124','3110','3112','3112','3310','3320','3420','9999')
AND A1.SV_BIZ_DCLSF_CD = #{svBizDclsfCd}
AND A1.SV_BIZ_HCLSF_CD = '6'
AND A1.SV_BIZ_HCLSF_CD != '6'
) B1 ) C1
WHERE C1.CNFM_YN = #{cnfmYn}
ORDER BY C1.CNTR_NO , C1.FNL_VST_FSH_DT , C1.GUBN DESC

mapper.delete:
DELETE FROM TB_SSCT_RENTAL_ADN_SV_IZ
 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND ADN_SV_SN = #{adnSvSn}
   AND ADN_SV_DV_CD = '01'
