mapper.select:
SELECT '' AS SL_RCOG_DT --매출인식일자 ,'' AS SELL_TP_CD --판매유형 ,'' AS SELL_TP_DTL_CD --판매유형상세코드 ,'' AS SAP_PD_DV_CD ,'' AS SAP_PD_DV_NM --SAP구분코드명 ,TO_CHAR(SUM(DECODE(T1.SELL_TP_CD,'3',T1.THM_SL_SUM_AMT,0)), 'FM999,999,999,999,990') AS SELL_AMT --매출금액-회비 ,TO_CHAR(SUM(DECODE(T1.SELL_TP_CD,'3',T1.THM_SL_SUM_AMT-T1.SL_SUM_VAT,0)), 'FM999,999,999,999,990') AS SELL_SPL_AMT --공급가액-회비 ,TO_CHAR(SUM(DECODE(T1.SELL_TP_CD,'3',T1.SL_SUM_VAT,0)), 'FM999,999,999,999,990') AS SELL_AMT_VAT --부가세-회비 ,TO_CHAR(SUM(DECODE(T1.SELL_TP_CD,'9',T1.THM_SL_SUM_AMT,0)), 'FM999,999,999,999,990') AS FIL_SELL_AMT --매출금액-필터 ,TO_CHAR(SUM(DECODE(T1.SELL_TP_CD,'9',T1.SELL_AMT-T1.SL_SUM_VAT,0)), 'FM999,999,999,999,990') AS FIL_SELL_SPL_AMT --공급가액-필터 ,TO_CHAR(SUM(DECODE(T1.SELL_TP_CD,'9',T1.SL_SUM_VAT,0)), 'FM999,999,999,999,990') AS FIL_SELL_AMT_VAT --부가세-필터 ,TO_CHAR(SUM(T1.THM_SL_SUM_AMT), 'FM999,999,999,999,990') AS TOT_SELL_AMT --매출금액-매출합계 ,TO_CHAR(SUM(T1.THM_SL_SUM_AMT-T1.SL_SUM_VAT), 'FM999,999,999,999,990') AS TOT_SELL_SPL_AMT --공급가액-매출합계 ,TO_CHAR(SUM(T1.SL_SUM_VAT), 'FM999,999,999,999,990') AS TOT_SELL_AMT_VAT --부가세-매출합계 FROM TB_CBCL_WELLS_SL_MM_CL_IZ T1 INNER JOIN TB_PDBS_PD_BAS T2 ON T1.PD_CD = T2.PD_CD WHERE T1.SL_RCOG_DT BETWEEN #{baseDtFrom} AND #{baseDtTo} AND SUBSTR(SL_RCOG_DT,1,6) = T1.SL_CL_YM AND T1.SELL_TP_CD = #{sellTpCd}
AND T1.SELL_TP_DTL_CD = #{sellTpDtlCd}
AND T1.SELL_INFLW_CHNL_DTL_CD = #{sellChnlDvCd} /*판매채널*/
AND T1.SAP_PD_DV_CD = #{sapPdDvCd} --SAP상품구분코드

SELECT T1.SL_RCOG_DT AS SL_RCOG_DT --매출인식일자 ,F_CMZ_CD_NM('TNT_WELLS', 'SELL_TP_CD', T1.SELL_TP_CD) AS SELL_TP_CD --판매유형 ,F_CMZ_CD_NM('TNT_WELLS', 'SELL_TP_DTL_CD', T1.SELL_TP_DTL_CD) AS SELL_TP_DTL_CD --판매유형상세코드 ,T1.SAP_PD_DV_CD ,(SELECT SAP_PD_DV_NM AS CODE_NAME /*SAP상품항목코드*/ FROM TB_CBCL_SAP_PD_DV_CD WHERE SAP_BZ_TERY_CD = '1210' /*에듀는 1110, 웰스는 1210*/ AND SAP_PD_ATC_CD = '00' /*상품항목 00 - SAP상품코드인건, 01부터는 SAP항목코드*/ AND SAP_PD_DV_CD = T1.SAP_PD_DV_CD GROUP BY SAP_PD_DV_CD, SAP_PD_DV_NM) AS SAP_PD_DV_NM --SAP구분코드명 ,SUM(DECODE(T1.SELL_TP_CD,'3',T1.THM_SL_SUM_AMT,0)) AS SELL_AMT --매출금액-회비 ,SUM(DECODE(T1.SELL_TP_CD,'3',T1.THM_SL_SUM_AMT-T1.SL_SUM_VAT,0)) AS SELL_SPL_AMT --공급가액-회비 ,SUM(DECODE(T1.SELL_TP_CD,'3',T1.SL_SUM_VAT,0)) AS SELL_AMT_VAT --부가세-회비 ,SUM(DECODE(T1.SELL_TP_CD,'9',T1.THM_SL_SUM_AMT,0)) AS FIL_SELL_AMT --매출금액-필터 ,SUM(DECODE(T1.SELL_TP_CD,'9',T1.SELL_AMT-T1.SL_SUM_VAT,0)) AS FIL_SELL_SPL_AMT --공급가액-필터 ,SUM(DECODE(T1.SELL_TP_CD,'9',T1.SL_SUM_VAT,0)) AS FIL_SELL_AMT_VAT --부가세-필터 ,SUM(T1.THM_SL_SUM_AMT) AS TOT_SELL_AMT --매출금액-매출합계 ,SUM(T1.THM_SL_SUM_AMT-T1.SL_SUM_VAT) AS TOT_SELL_SPL_AMT --공급가액-매출합계 ,SUM(T1.SL_SUM_VAT) AS TOT_SELL_AMT_VAT --부가세-매출합계 FROM TB_CBCL_WELLS_SL_MM_CL_IZ T1 INNER JOIN TB_PDBS_PD_BAS T2 ON T1.PD_CD = T2.PD_CD WHERE T1.SL_RCOG_DT BETWEEN #{baseDtFrom} AND #{baseDtTo} AND SUBSTR(SL_RCOG_DT,1,6) = T1.SL_CL_YM AND T1.SELL_TP_CD = #{sellTpCd}
AND T1.SELL_TP_DTL_CD = #{sellTpDtlCd}
AND T1.SELL_INFLW_CHNL_DTL_CD = #{sellChnlDvCd} /*판매채널*/
AND T1.SAP_PD_DV_CD = #{sapPdDvCd} --SAP상품구분코드
GROUP BY T1.SL_RCOG_DT, T1.SELL_TP_CD, T1.SELL_TP_DTL_CD, T1.SAP_PD_DV_CD
