mapper.select:
WITH W1 AS (SELECT SUBSTR(SL_CL_DT, 1, 6) AS SL_CL_YM , SL_CL_DT AS SL_CL_DT , ROWNUM AS ROW_NUM , RANK() OVER (ORDER BY SL_CL_DT DESC) AS RANK_NUM FROM (SELECT TO_CHAR(TO_DATE(#{slClYm}||'02','YYYYMMDD') + (LEVEL-1), 'YYYYMMDD') AS SL_CL_DT FROM DUAL CONNECT BY LEVEL <= (CASE WHEN #{slClYm} < TO_CHAR(SYSDATE, 'YYYYMM') THEN (SELECT LAST_DAY((SELECT TO_DATE(#{slClYm}||'01', 'YYYYMMDD') FROM DUAL)) FROM DUAL) - TO_DATE(#{slClYm}||'02','YYYYMMDD') ELSE (SELECT TO_DATE(SYSDATE-1) FROM DUAL) - TO_DATE(#{slClYm}||'02','YYYYMMDD') END)) WHERE SL_CL_DT < TO_CHAR(SYSDATE, 'YYYYMMDD') AND SL_CL_DT <= (SELECT LAST_DAY((SELECT TO_DATE(#{slClYm}||'02', 'YYYYMMDD') FROM DUAL)) FROM DUAL) ORDER BY SL_CL_DT ) , W2 AS (SELECT SL_CL_YM AS SL_CL_YM /* 실적년월 */ , SL_CL_DT AS SL_CL_DT /* 실적일자 */ , SELL_TP_CD AS SELL_TP_CD /* 판매유형코드 */ , SELL_TP_DTL_CD AS SELL_TP_DTL_CD /* 판매유형상세코드 */ , SAP_PD_DV_CD AS SAP_PD_DV_CD /* SAP상품구분코드 */ ------------------------------------ -- 매출 영역 ------------------------------------ , SUM(NOM_SL_AMT) AS NOM_SL_AMT /* 정상매출 */ , SUM(CAN_SL_AMT) AS CAN_SL_AMT /* 취소매출 */ , SUM(FEE_SL_AMT) AS FEE_SL_AMT /* 수수료매출 */ , SUM(BOUT_NOR_SL_AMT) AS BOUT_NOR_SL_AMT /* 상품권정상 */ , SUM(BOUT_CAN_SL_AMT) AS BOUT_CAN_SL_AMT /* 상품권취소 */ , SUM(ORI_SL_AMT) AS ORI_SL_AMT /* 원금매출 */ , SUM(ITR_SL_AMT) AS ITR_SL_AMT /* 이자매출 */ , SUM(SVC_SL_AMT) AS SVC_SL_AMT /* 서비스매출 */ ------------------------------------ -- 매출대사 영역(미조회) ------------------------------------ , SUM(CNTR_DP_AMT) AS CNTR_DP_AMT /* 계약금 */ , SUM(INST_DP_AMT) AS INST_DP_AMT /* 할부금 */ , SUM(TOT_DP_AMT) AS TOT_DP_AMT /* 매출대사합계 */ , SUM(SL_ADJ_AMT) AS SL_ADJ_AMT /* 매출조정 */ ------------------------------------ -- 기타 영역 ------------------------------------ , SUM(DFA_PROCS_AMT) AS DFA_PROCS_AMT /* 대손 */ , SUM(CRP_UC_AMT) AS CRP_UC_AMT /* 법인미수 */ , SUM(BOUT_DP_AMT) AS BOUT_DP_AMT /* 상품권입금 */ , SUM(ETC_DP_AMT) AS ETC_DP_AMT /* 기타선수대체 */ , SUM(INTER_CONT_NOM_SL_AMT) AS INTER_CONT_NOM_SL_AMT /* 사간거래정상 */ , SUM(INTER_CONT_CAN_SL_AMT) AS INTER_CONT_CAN_SL_AMT /* 사간거래취소 */ , SUM(INTER_CONT_DP_AMT) AS INTER_CONT_DP_AMT /* 사간거래입금 */ FROM (SELECT SUBSTR(A.SL_RCOG_DT,1,6) AS SL_CL_YM /* 실적년월 */ , A.SL_RCOG_DT AS SL_CL_DT /* 실적일자 */ , A.SELL_TP_CD AS SELL_TP_CD /* 판매유형코드 */ , A.SELL_TP_DTL_CD AS SELL_TP_DTL_CD /* 판매유형상세코드 */ , A.SAP_PD_DV_CD AS SAP_PD_DV_CD /* SAP상품구분코드 */ ------------------------------------ -- 미수금 ------------------------------------ , 0 AS PRE_TOT_UC_AMT /* 기초미수 */ , 0 AS PRE_TOT_UC_AMT /* 기말미수 */ ------------------------------------ -- 매출 영역 ------------------------------------ , CASE WHEN (A.SELL_TP_CD = '1' AND C.ALNCMP_CD <> '51') OR (A.SELL_TP_CD = '2' AND A.SELL_TP_DTL_CD IN ('21', '23')) OR A.SELL_TP_CD = '3' OR A.SELL_TP_CD = '6' THEN NVL(A.NOM_SL_AMT, 0) ELSE 0 END AS NOM_SL_AMT /* 정상매출 */ , CASE WHEN (A.SELL_TP_CD = '1' AND C.ALNCMP_CD <> '51') OR (A.SELL_TP_CD = '2' AND A.SELL_TP_DTL_CD IN ('21', '23')) OR A.SELL_TP_CD = '3' THEN NVL(A.SL_CAN_AMT, 0) ELSE 0 END AS CAN_SL_AMT /* 취소매출 */ , CASE WHEN (A.SELL_TP_CD = '1' AND C.ALNCMP_CD <> '51' AND SUBSTR(A.IST_DTM,1,8) = A.SL_RCOG_DT) THEN NVL(C.SELL_FEE, 0) ELSE 0 END AS FEE_SL_AMT /* 수수료매출 */ , 0 AS BOUT_NOR_SL_AMT /* 상품권정상 */ , NVL((SELECT SUM(NVL(RVE_AMT, 0)) FROM TB_CBCL_BZNS_ATAM_BAS /* 영업선수기본 */ WHERE DP_CL_DT = A.SL_RCOG_DT AND CNTR_NO = A.CNTR_NO AND CNTR_SN = A.CNTR_SN AND DP_TP_CD = '0802' /* K머니 */ AND DP_DV_CD = '2' /* 환불 */), 0) AS BOUT_CAN_SL_AMT /* 상품권_취소/만료매출 ※ 통합입금취소사유코드(ITG_DP_CAN_RSON_CD)가 21이면 만료건, 아니면 취소건 (DP30.DPCCAM+DP30.DPCCAM) */ , CASE WHEN (A.SELL_TP_CD = '2' AND A.SELL_TP_DTL_CD NOT IN ('21', '23')) THEN NVL(A.SL_AMT, 0) ELSE 0 END AS ORI_SL_AMT /* 원금매출 */ , CASE WHEN (A.SELL_TP_CD = '2' AND A.SELL_TP_DTL_CD NOT IN ('21', '23')) THEN NVL(A.NOM_INT_AMT, 0) ELSE 0 END AS ITR_SL_AMT /* 이자매출 */ , CASE WHEN (A.SELL_TP_CD = '2' AND A.SELL_TP_DTL_CD NOT IN ('21', '23')) THEN NVL(A.SV_AMT, 0) ELSE 0 END AS SVC_SL_AMT /* 서비스매출 */ ------------------------------------ -- 매출대사 영역(미조회) ------------------------------------ , 0 AS CNTR_DP_AMT /* 계약금 */ , 0 AS INST_DP_AMT /* 할부금 */ , 0 AS TOT_DP_AMT /* 매출대사합계 */ , 0 AS SL_ADJ_AMT /* 매출조정 */ ------------------------------------ -- 기타 영역 ------------------------------------ , 0 AS DFA_PROCS_AMT /* 대손 */ , 0 AS CRP_UC_AMT /* 법인미수 */ , 0 AS BOUT_DP_AMT /* 상품권입금 */ , 0 AS ETC_DP_AMT /* 기타선수대체 */ , CASE WHEN (A.SELL_TP_CD = '1' AND C.ALNCMP_CD = '51') THEN NVL(A.NOM_SL_AMT, 0) ELSE 0 END AS INTER_CONT_NOM_SL_AMT /* 사간거래정상 */ , CASE WHEN (A.SELL_TP_CD = '1' AND C.ALNCMP_CD = '51') THEN NVL(A.SL_CAN_AMT, 0) ELSE 0 END AS INTER_CONT_CAN_SL_AMT /* 사간거래취소 */ , 0 AS INTER_CONT_DP_AMT /* 사간거래입금 */ FROM TB_CBCL_WELLS_SL_CNFM_BAS A /* 매출확정기본 */ INNER JOIN TB_SSCT_CNTR_BAS B /* 계약기본 */ ON A.CNTR_NO = B.CNTR_NO AND B.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_DTL C /* 계약상세 */ ON C.CNTR_NO = A.CNTR_NO AND C.CNTR_SN = A.CNTR_SN AND C.DTA_DL_YN = 'N' WHERE SUBSTR(A.SL_RCOG_DT, 1, 6) = #{slClYm} AND A.SELL_TP_CD = #{sellTpCd}
AND A.SELL_TP_DTL_CD = #{sellTpDtlCd}
AND A.SAP_PD_DV_CD = #{sapPdDvCd}
AND A.CNTR_NO = #{cntrNo}
AND A.CNTR_SN = #{cntrSn}
AND B.SELL_INFLW_CHNL_DTL_CD = #{sellChnlDtl}
AND NOT EXISTS (SELECT /*+ UNNEST */ 1 FROM TB_SSCT_CNTR_DTL AA INNER JOIN TB_PDBS_PD_CLSF_BAS BB /* 상품분류기본-소분류[AS-IS:KA1100P] */ ON BB.PD_CLSF_ID = AA.PD_LCLSF_ID /* 상품분류ID = 상품세분류ID */ AND BB.DTA_DL_YN = 'N' AND BB.USE_YN = 'Y' AND BB.REF_PD_CLSF_VAL = '05003002' /* [Cooking+커피머신+원두] */ WHERE AA.CNTR_NO = A.CNTR_NO AND AA.CNTR_SN = A.CNTR_SN )
UNION ALL SELECT SUBSTR(A.SL_RCOG_DT,1,6) AS SL_CL_YM /* 실적년월 */ , A.SL_RCOG_DT AS SL_CL_DT /* 실적일자 */ , A.SELL_TP_CD AS SELL_TP_CD /* 판매유형코드 */ , A.SELL_TP_DTL_CD AS SELL_TP_DTL_CD /* 판매유형상세코드 */ , A.SAP_PD_DV_CD AS SAP_PD_DV_CD /* SAP상품구분코드 */ ------------------------------------ -- 미수금 → 이건 아래에서 처리 ------------------------------------ , 0 AS PRE_TOT_UC_AMT /* 기초미수 */ , 0 AS PRE_TOT_UC_AMT /* 기말미수 */ ------------------------------------ -- 매출 영역 ------------------------------------ , A.SL_AMT AS NOM_SL_AMT /* 정상매출 */ , 0 AS CAN_SL_AMT /* 취소매출 */ , 0 AS FEE_SL_AMT /* 수수료매출 */ , 0 AS BOUT_NOR_SL_AMT /* 상품권정상 */ , 0 AS BOUT_CAN_SL_AMT /* 상품권취소 */ , 0 AS ORI_SL_AMT /* 원금매출 */ , 0 AS ITR_SL_AMT /* 이자매출 */ , 0 AS SVC_SL_AMT /* 서비스매출 */ ------------------------------------ -- 매출대사 영역(미조회) ------------------------------------ , 0 AS CNTR_DP_AMT /* 계약금 */ , 0 AS INST_DP_AMT /* 할부금 */ , 0 AS TOT_DP_AMT /* 매출대사합계 */ , 0 AS SL_ADJ_AMT /* 매출조정 */ ------------------------------------ -- 기타 영역 ------------------------------------ , 0 AS DFA_PROCS_AMT /* 대손 */ , 0 AS CRP_UC_AMT /* 법인미수 */ , 0 AS BOUT_DP_AMT /* 상품권입금 */ , 0 AS ETC_DP_AMT /* 기타선수대체 */ , 0 AS INTER_CONT_NOM_SL_AMT /* 사간거래정상 */ , 0 AS INTER_CONT_CAN_SL_AMT /* 사간거래취소 */ , 0 AS INTER_CONT_DP_AMT /* 사간거래입금 */ FROM TB_CBCL_WELLS_SL_CNFM_BAS A /* 매출확정기본 */ INNER JOIN TB_SSCT_CNTR_BAS B /* 계약기본 */ ON A.CNTR_NO = B.CNTR_NO AND B.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_DTL C /* 계약상세 */ ON C.CNTR_NO = A.CNTR_NO AND C.CNTR_SN = A.CNTR_SN AND C.DTA_DL_YN = 'N' WHERE SUBSTR(A.SL_RCOG_DT, 1, 6) = #{slClYm} AND A.SELL_TP_CD = '2' AND A.SELL_TP_DTL_CD IN ('21', '23') AND EXISTS (SELECT /*+ UNNEST */ 1 FROM TB_SSCT_CNTR_DTL AA INNER JOIN TB_PDBS_PD_CLSF_BAS BB /* 상품분류기본-소분류[AS-IS:KA1100P] */ ON BB.PD_CLSF_ID = AA.PD_LCLSF_ID /* 상품분류ID = 상품세분류ID */ AND BB.DTA_DL_YN = 'N' AND BB.USE_YN = 'Y' AND BB.REF_PD_CLSF_VAL = '05003002' /* [Cooking+커피머신+원두] */ WHERE AA.CNTR_NO = A.CNTR_NO AND AA.CNTR_SN = A.CNTR_SN )
AND A.SAP_PD_DV_CD = #{sapPdDvCd}
AND A.CNTR_NO = #{cntrNo}
AND A.CNTR_SN = #{cntrSn}
AND B.SELL_INFLW_CHNL_DTL_CD = #{sellChnlDtl}
) GROUP BY SL_CL_YM, SL_CL_DT, SELL_TP_CD, SELL_TP_DTL_CD, SAP_PD_DV_CD ORDER BY SL_CL_YM, SL_CL_DT, SELL_TP_CD, SELL_TP_DTL_CD, SAP_PD_DV_CD ) , W3 AS (
) /*--------------------------------------------------------------*/ /* 1일자 조회 */ /*--------------------------------------------------------------*/ SELECT TO_CHAR(TO_DATE(#{slClYm}, 'YYYYMM'), 'YYYY-MM') AS SL_CL_YM , TO_CHAR(TO_DATE(#{slClYm}||'01', 'YYYYMMDD'), 'YYYY-MM-DD') AS SL_CL_DT , W3.SELL_TP_CD AS SELL_TP_CD , (SELECT CD_CNTN FROM T_CMZ_CD_D WHERE TENANT_ID = 'TNT_WELLS' AND CD_ID = 'SELL_TP_CD' AND CD_VLD_VAL = W3.SELL_TP_CD) AS SELL_TP_CD_NM /* 판매유형코드명 */ , W3.SELL_TP_DTL_CD AS SELL_TP_DTL_CD , (SELECT CD_CNTN FROM T_CMZ_CD_D WHERE TENANT_ID = 'TNT_WELLS' AND CD_ID = 'SELL_TP_DTL_CD' AND CD_VLD_VAL = W3.SELL_TP_DTL_CD) AS SELL_TP_DTL_CD_NM /* 판매유형상세코드명 */ , W3.SAP_PD_DV_CD AS SAP_PD_DV_CD , (SELECT MAX(SAP_PD_ATC_NM) FROM TB_CBCL_SAP_PD_DV_CD WHERE SAP_BZ_TERY_CD = '1210' /* SAP_BZ_TERY_CD : WELLS면 1210, EDU면 1110 */ AND SAP_PD_DV_CD = W3.SAP_PD_DV_CD) AS SAP_PD_ATC_NM /* SAP상품코드명 */ , W3.PRE_TOT_UC_AMT AS PRE_TOT_UC_AMT /* 전기이월 */ ------------------------------------ -- 매출 영역 ------------------------------------ , W2.NOM_SL_AMT AS NOM_SL_AMT /* 정상매출 */ , W2.CAN_SL_AMT AS CAN_SL_AMT /* 취소매출 */ , W2.FEE_SL_AMT AS FEE_SL_AMT /* 수수료매출 */ , W2.BOUT_NOR_SL_AMT AS BOUT_NOR_SL_AMT /* 상품권정상 */ , W2.BOUT_CAN_SL_AMT AS BOUT_CAN_SL_AMT /* 상품권취소 */ , W2.ORI_SL_AMT AS ORI_SL_AMT /* 원금매출 */ , W2.ITR_SL_AMT AS ITR_SL_AMT /* 이자매출 */ , W2.SVC_SL_AMT AS SVC_SL_AMT /* 서비스매출 */ , CASE WHEN W2.SELL_TP_CD = '1' THEN W2.NOM_SL_AMT + W2.CAN_SL_AMT + W2.FEE_SL_AMT WHEN W2.SELL_TP_CD = '2' AND W2.SELL_TP_DTL_CD IN ('21', '23') THEN W2.NOM_SL_AMT + W2.CAN_SL_AMT WHEN W2.SELL_TP_CD = '2' AND W2.SELL_TP_DTL_CD NOT IN ('21', '23') THEN W2.ORI_SL_AMT + W2.ITR_SL_AMT + W2.SVC_SL_AMT WHEN W2.SELL_TP_CD = '3' THEN W2.NOM_SL_AMT + W2.CAN_SL_AMT WHEN W2.SELL_TP_CD = '6' THEN W2.NOM_SL_AMT ELSE 0 END AS TOT_SL_AMT /* 매출합계 */ ------------------------------------ -- 매출대사(미조회) ------------------------------------ , W2.CNTR_DP_AMT AS CNTR_DP_AMT /* 계약금 */ , W2.INST_DP_AMT AS INST_DP_AMT /* 할부금 */ , W2.TOT_DP_AMT AS TOT_DP_AMT /* 매출대사합계 */ , W2.SL_ADJ_AMT AS SL_ADJ_AMT /* 매출조정 ------------------------------------ */ -- 기타 영역 ------------------------------------ , W2.DFA_PROCS_AMT AS DFA_PROCS_AMT /* 대손 */ , W2.CRP_UC_AMT AS CRP_UC_AMT /* 법인미수 */ , W2.BOUT_DP_AMT AS BOUT_DP_AMT /* 상품권입금 */ , W2.ETC_DP_AMT AS ETC_DP_AMT /* 기타선수대체 */ , W2.INTER_CONT_NOM_SL_AMT AS INTER_CONT_NOM_SL_AMT /* 사간거래정상 */ , W2.INTER_CONT_CAN_SL_AMT AS INTER_CONT_CAN_SL_AMT /* 사간거래취소 */ , W2.INTER_CONT_DP_AMT AS INTER_CONT_DP_AMT /* 사간거래입금 */ , 0 AS TOT_UC_BLAM /* 기말미수 */ FROM W3 LEFT OUTER JOIN W2 ON 1=1 AND W2.SL_CL_DT = #{slClYm}||'01' UNION ALL /*--------------------------------------------------------------*/ /* 일자별 조회 */ /*--------------------------------------------------------------*/ SELECT TO_CHAR(TO_DATE(#{slClYm}, 'YYYYMM'), 'YYYY-MM') AS SL_CL_YM , TO_CHAR(TO_DATE(W1.SL_CL_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS SL_CL_DT , W2.SELL_TP_CD AS SELL_TP_CD , (SELECT CD_CNTN FROM T_CMZ_CD_D WHERE TENANT_ID = 'TNT_WELLS' AND CD_ID = 'SELL_TP_CD' AND CD_VLD_VAL = W2.SELL_TP_CD) AS SELL_TP_CD_NM /* 판매유형코드명 */ , W2.SELL_TP_DTL_CD AS SELL_TP_DTL_CD , (SELECT CD_CNTN FROM T_CMZ_CD_D WHERE TENANT_ID = 'TNT_WELLS' AND CD_ID = 'SELL_TP_DTL_CD' AND CD_VLD_VAL = W2.SELL_TP_DTL_CD) AS SELL_TP_DTL_CD_NM /* 판매유형상세코드명 */ , W2.SAP_PD_DV_CD AS SAP_PD_DV_CD , (SELECT MAX(SAP_PD_ATC_NM) FROM TB_CBCL_SAP_PD_DV_CD WHERE SAP_BZ_TERY_CD = '1210' /* SAP_BZ_TERY_CD : WELLS면 1210, EDU면 1110 */ AND SAP_PD_DV_CD = W2.SAP_PD_DV_CD) AS SAP_PD_ATC_NM /* SAP상품코드명 */ , 0 AS PRE_TOT_UC_AMT /* 전기이월 */ ------------------------------------ -- 매출 영역 ------------------------------------ , W2.NOM_SL_AMT AS NOM_SL_AMT /* 정상매출 */ , W2.CAN_SL_AMT AS CAN_SL_AMT /* 취소매출 */ , W2.FEE_SL_AMT AS FEE_SL_AMT /* 수수료매출 */ , W2.BOUT_NOR_SL_AMT AS BOUT_NOR_SL_AMT /* 상품권정상 */ , W2.BOUT_CAN_SL_AMT AS BOUT_CAN_SL_AMT /* 상품권취소 */ , W2.ORI_SL_AMT AS ORI_SL_AMT /* 원금매출 */ , W2.ITR_SL_AMT AS ITR_SL_AMT /* 이자매출 */ , W2.SVC_SL_AMT AS SVC_SL_AMT /* 서비스매출 */ , CASE WHEN W2.SELL_TP_CD = '1' THEN W2.NOM_SL_AMT + W2.CAN_SL_AMT + W2.FEE_SL_AMT WHEN W2.SELL_TP_CD = '2' AND W2.SELL_TP_DTL_CD IN ('21', '23') THEN W2.NOM_SL_AMT + W2.CAN_SL_AMT WHEN W2.SELL_TP_CD = '2' AND W2.SELL_TP_DTL_CD NOT IN ('21', '23') THEN W2.ORI_SL_AMT + W2.ITR_SL_AMT + W2.SVC_SL_AMT WHEN W2.SELL_TP_CD = '3' THEN W2.NOM_SL_AMT + W2.CAN_SL_AMT WHEN W2.SELL_TP_CD = '6' THEN W2.NOM_SL_AMT ELSE 0 END AS TOT_SL_AMT /* 매출합계 */ ------------------------------------ -- 매출대사(미조회) ------------------------------------ , W2.CNTR_DP_AMT AS CNTR_DP_AMT /* 계약금 */ , W2.INST_DP_AMT AS INST_DP_AMT /* 할부금 */ , W2.TOT_DP_AMT AS TOT_DP_AMT /* 매출대사합계 */ , W2.SL_ADJ_AMT AS SL_ADJ_AMT /* 매출조정 ------------------------------------ */ -- 기타 영역 ------------------------------------ , W2.DFA_PROCS_AMT AS DFA_PROCS_AMT /* 대손 */ , W2.CRP_UC_AMT AS CRP_UC_AMT /* 법인미수 */ , W2.BOUT_DP_AMT AS BOUT_DP_AMT /* 상품권입금 */ , W2.ETC_DP_AMT AS ETC_DP_AMT /* 기타선수대체 */ , W2.INTER_CONT_NOM_SL_AMT AS INTER_CONT_NOM_SL_AMT /* 사간거래정상 */ , W2.INTER_CONT_CAN_SL_AMT AS INTER_CONT_CAN_SL_AMT /* 사간거래취소 */ , W2.INTER_CONT_DP_AMT AS INTER_CONT_DP_AMT /* 사간거래입금 */ , 0 AS TOT_UC_BLAM /* 기말미수 */ FROM W1 LEFT OUTER JOIN W2 ON W2.SL_CL_DT = W1.SL_CL_DT UNION ALL /*--------------------------------------------------------------*/ /* 마지막일자 조회(전일자) */ /*--------------------------------------------------------------*/ SELECT TO_CHAR(TO_DATE(#{slClYm}, 'YYYYMM'), 'YYYY-MM') AS SL_CL_YM , (SELECT TO_CHAR(TO_DATE(SL_CL_DT, 'YYYYMMDD')+1, 'YYYY-MM-DD') FROM W1 WHERE RANK_NUM = 1) AS SL_CL_DT , W3.SELL_TP_CD AS SELL_TP_CD , (SELECT CD_CNTN FROM T_CMZ_CD_D WHERE TENANT_ID = 'TNT_WELLS' AND CD_ID = 'SELL_TP_CD' AND CD_VLD_VAL = W3.SELL_TP_CD) AS SELL_TP_CD_NM /* 판매유형코드명 */ , W3.SELL_TP_DTL_CD AS SELL_TP_DTL_CD , (SELECT CD_CNTN FROM T_CMZ_CD_D WHERE TENANT_ID = 'TNT_WELLS' AND CD_ID = 'SELL_TP_DTL_CD' AND CD_VLD_VAL = W3.SELL_TP_DTL_CD) AS SELL_TP_DTL_CD_NM /* 판매유형상세코드명 */ , W3.SAP_PD_DV_CD AS SAP_PD_DV_CD , (SELECT MAX(SAP_PD_ATC_NM) FROM TB_CBCL_SAP_PD_DV_CD WHERE SAP_BZ_TERY_CD = '1210' /* SAP_BZ_TERY_CD : WELLS면 1210, EDU면 1110 */ AND SAP_PD_DV_CD = W3.SAP_PD_DV_CD) AS SAP_PD_ATC_NM /* SAP상품코드명 */ , 0 AS PRE_TOT_UC_AMT /* 전기이월 */ ------------------------------------ -- 매출 영역 ------------------------------------ , W2.NOM_SL_AMT AS NOM_SL_AMT /* 정상매출 */ , W2.CAN_SL_AMT AS CAN_SL_AMT /* 취소매출 */ , W2.FEE_SL_AMT AS FEE_SL_AMT /* 수수료매출 */ , W2.BOUT_NOR_SL_AMT AS BOUT_NOR_SL_AMT /* 상품권정상 */ , W2.BOUT_CAN_SL_AMT AS BOUT_CAN_SL_AMT /* 상품권취소 */ , W2.ORI_SL_AMT AS ORI_SL_AMT /* 원금매출 */ , W2.ITR_SL_AMT AS ITR_SL_AMT /* 이자매출 */ , W2.SVC_SL_AMT AS SVC_SL_AMT /* 서비스매출 */ , CASE WHEN W2.SELL_TP_CD = '1' THEN W2.NOM_SL_AMT + W2.CAN_SL_AMT + W2.FEE_SL_AMT WHEN W2.SELL_TP_CD = '2' AND W2.SELL_TP_DTL_CD IN ('21', '23') THEN W2.NOM_SL_AMT + W2.CAN_SL_AMT WHEN W2.SELL_TP_CD = '2' AND W2.SELL_TP_DTL_CD NOT IN ('21', '23') THEN W2.ORI_SL_AMT + W2.ITR_SL_AMT + W2.SVC_SL_AMT WHEN W2.SELL_TP_CD = '3' THEN W2.NOM_SL_AMT + W2.CAN_SL_AMT WHEN W2.SELL_TP_CD = '6' THEN W2.NOM_SL_AMT ELSE 0 END AS TOT_SL_AMT /* 매출합계 */ ------------------------------------ -- 매출대사(미조회) ------------------------------------ , W2.CNTR_DP_AMT AS CNTR_DP_AMT /* 계약금 */ , W2.INST_DP_AMT AS INST_DP_AMT /* 할부금 */ , W2.TOT_DP_AMT AS TOT_DP_AMT /* 매출대사합계 */ , W2.SL_ADJ_AMT AS SL_ADJ_AMT /* 매출조정 ------------------------------------ */ -- 기타 영역 ------------------------------------ , W2.DFA_PROCS_AMT AS DFA_PROCS_AMT /* 대손 */ , W2.CRP_UC_AMT AS CRP_UC_AMT /* 법인미수 */ , W2.BOUT_DP_AMT AS BOUT_DP_AMT /* 상품권입금 */ , W2.ETC_DP_AMT AS ETC_DP_AMT /* 기타선수대체 */ , W2.INTER_CONT_NOM_SL_AMT AS INTER_CONT_NOM_SL_AMT /* 사간거래정상 */ , W2.INTER_CONT_CAN_SL_AMT AS INTER_CONT_CAN_SL_AMT /* 사간거래취소 */ , W2.INTER_CONT_DP_AMT AS INTER_CONT_DP_AMT /* 사간거래입금 */ , W3.TOT_UC_BLAM AS TOT_UC_BLAM /* 기말미수 */ FROM W3 LEFT OUTER JOIN W2 ON 1=1 AND W2.SL_CL_DT = (SELECT TO_CHAR(TO_DATE(SL_CL_DT, 'YYYYMMDD')+1, 'YYYY-MM-DD') FROM W1 WHERE RANK_NUM = 1) ORDER BY SL_CL_DT, 