mapper.select:
SELECT A.CNTR_NO AS CNTR_NO /* 계약번호 */ , A.CNTR_SN AS CNTR_SN /* 계약일련번호 */ , A.SL_CL_YM AS SL_CL_YM /* 매출년월 */ , B.SELL_TP_CD AS SELL_TP_CD , CASE WHEN B.SELL_TP_DTL_CD IN ('21', '23') THEN 'N' ELSE 'Y' END AS ISLEASE , CASE WHEN A.SL_STP_YN='Y' THEN '매출중지' ELSE '' END AS SL_STP_YN /* 매출중지(LC50.LCSTOP) */ , A.RENTAL_TN AS RENTAL_TN /* 렌탈차월(LC50.LCRCNT) */ , '' AS SL_CTR_DV_CD /* 매출조정구분코드(미검색) */ , A.PRM_MCN AS PRM_MCN /* 선납개월(LC50.LCPMON) */ , (CASE WHEN A.SELL_TP_DTL_CD NOT IN ('21', '23') /* 리스의 경우 청구금액((LCMAM2 - LCMAM4 + LCMAM5 + LCCAM2 - LCCAM4 + LCCAM5 + LCSAM2 - LCSAM4 + LCSAM5), 렌탈은 매출금액(LC50.LCAM16) */ THEN NVL(A.THM_BIL_OC_AMT, 0) - NVL(A.THM_BIL_CTR_AMT, 0) + NVL(A.THM_BIL_SPMT_AMT, 0) ELSE NVL(A.THM_SL_SUM_AMT, 0) END) AS THM_SL_SUM_AMT /* 매출합계(LC50.LCAM16) */ , (CASE WHEN A.SL_CL_YM = SUBSTR(D.CCAM_CMPT_RQDT, 1, 6) THEN NVL(D.BOR_AMT, 0) ELSE 0 END) AS BOR_AMT /* 위약금액(LC50.LCAM44 + LC50.LCAM46으로 누적 이행) 위약금산출월에만 해당 금액 출력 */ , NVL(D.LS_RNTF, 0) AS LS_RNTF /* 분실손료(LC50.LCAM46) */ , NVL(A.THM_ATAM_DP_AMT, 0) /* 선수입금(LC50.LCAM32) */ - NVL(A.THM_ATAM_RFND_AMT, 0) /* 선수환불(LC50.LCAM33) */ + NVL(A.MLG_DP_AMT, 0) /* 포인트_입금(LC50.LCAM72) */ - NVL(A.MLG_RFND_AMT, 0) /* 포인트_환불(LC50.LCAM73) */ + CASE WHEN A.RENTAL_TN = 0 AND A.SL_CL_YM >= SUBSTR(B.CNTR_PD_STRTDT, 1, 6) /* LC50.LCRCNT = 0 AND LC50.LCSLEY > 0 */ THEN NVL(A.RENTAL_RGST_COST, 0)*-1 + NVL(A.RENTAL_RGST_COST, 0) - NVL(A.CNTR_AMT, 0) /* 렌탈등록비(LC50.LCTAMT) + 할인금액(LC50.LCRAMT) */ WHEN A.SL_CL_YM < '201605' THEN NVL(C.THM_DLQ_ADD_DP_SUM_AMT, 0) - NVL(C.THM_DLQ_ADD_RFND_SUM_AMT, 0) /* 가산금입금(LC50.LCAM83) - 가산금환불(LC50.LCAM86) */ ELSE 0 END AS DP_AMT /* 입금금액 */ , CASE WHEN A.SELL_TP_DTL_CD NOT IN ('21', '23') THEN NVL(A.EOT_ATAM, 0) + NVL(A.MLG_EOT_PRPD_AMT, 0) /* 선수기말(LC50.LCAM36) + 포인트기말(LC50.LCAM75) */ ELSE NVL(A.EOT_ATAM, 0) END AS EOT_ATAM /* 선수금액 */ , (CASE WHEN A.SELL_TP_DTL_CD NOT IN ('21', '23') THEN NVL(A.EOT_BIL_UC_AMT, 0) ELSE NVL(A.EOT_UC_AMT, 0) END) + NVL(C.EOT_DLQ_ADD_AMT, 0) + NVL(D.EOT_BOR_AMT, 0) AS EOT_UC_AMT /* 미수금액(LC50.LCMAMT) 리스는 청구 미수*/ , NVL(C.EOT_DLQ_AMT, 0) + NVL(D.EOT_BOR_AMT, 0) + /* 원금연체 + 위약금 */ (CASE WHEN F.CL_FSH_YN = 'Y' THEN NVL(C.BTD_DLQ_ADD_AMT, 0) - (NVL(C.THM_DLQ_ADD_DP_SUM_AMT, 0) - NVL(C.THM_DLQ_ADD_RFND_SUM_AMT, 0)) - NVL(C.THM_CTR_DLQ_ADD_AMT, 0) /* 기초 - (입금 - 환불) - 공제 */ ELSE GREATEST(NVL(E.EOT_DLQ_ADD_AMT, 0) - NVL(E.THM_OC_DLQ_ADD_AMT, 0) - (NVL(C.THM_DLQ_ADD_DP_SUM_AMT, 0) - NVL(C.THM_DLQ_ADD_RFND_SUM_AMT, 0)) - NVL(C.THM_CTR_DLQ_ADD_AMT, 0), 0) /* 당월실제 기초연체가산금 - (입금 - 환불) - 조정 */ END) AS EOT_DLQ_AMT /* 연체금액(마감 전인 경우, 전월 발생 가산금은 제외) */ , NVL(C.DLQ_MCN, 0) AS DLQ_MCN /* 연체개월수(LC50.LCDCNT) */ , NVL(C.BTD_DLQ_ADD_AMT, 0) AS BTD_DLQ_ADD_AMT /* 가산금기초(LC50.LCAM81) */ , NVL(C.THM_OC_DLQ_ADD_AMT, 0) AS THM_OC_DLQ_ADD_AMT /* 가산금발생(LC50.LCAM82) */ , NVL(C.THM_CTR_DLQ_ADD_AMT, 0) AS THM_CTR_DLQ_ADD_AMT /* 가산금공제(LC50.LCAM85) */ , NVL(C.THM_DLQ_ADD_DP_SUM_AMT, 0) AS THM_DLQ_ADD_DP_SUM_AMT /* 가산금입금(LC50.LCAM83) */ , NVL(C.THM_DLQ_ADD_RFND_SUM_AMT, 0) AS THM_DLQ_ADD_RFND_SUM_AMT /* 가산금환불(LC50.LCAM86) */ , NVL(C.EOT_DLQ_ADD_AMT, 0) AS EOT_DLQ_ADD_AMT /* 가산금기말(LC50.LCAM84) */ , TRUNC(NVL(A.SL_STP_AMT, 0) * 0.3,-1) AS SL_STP_AMT /* 매출중지금액(LC50.LCAM96) */ FROM TB_CBCL_WELLS_SL_MM_CL_IZ A /* WELLS매출월마감내역 */ INNER JOIN TB_SSCT_CNTR_DTL B /* 계약상세 */ ON A.CNTR_NO = B.CNTR_NO AND A.CNTR_SN = B.CNTR_SN AND B.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_DLQ_BAS C /* 연체기본 */ ON A.SL_CL_YM = C.PERF_YM AND A.CNTR_NO = C.CNTR_NO AND A.CNTR_SN = C.CNTR_SN AND C.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_DLQ_BAS E /* 연체기본 */ ON E.PERF_YM = TO_CHAR(ADD_MONTHS(TO_DATE(A.SL_CL_YM||'01'), -1), 'YYYYMM') AND E.CNTR_NO = A.CNTR_NO AND E.CNTR_SN = A.CNTR_SN AND E.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_WELLS_BOR_AMT_BAS D /* WELLS위약금액기본 */ ON A.SL_CL_YM = D.PERF_YM AND A.CNTR_NO = D.CNTR_NO AND A.CNTR_SN = D.CNTR_SN AND D.DTA_DL_YN = 'N' LEFT OUTER JOIN LATERAL (SELECT (CASE WHEN COUNT(1) > 0 THEN 'N' ELSE 'Y' END) AS CL_FSH_YN FROM TB_CBCL_CL_PSIC_BAS WHERE CL_BIZ_DV_CD = 'W06' AND CL_STAT_CD IN ('1','2') /*1.대기중 , 2.진행중 . 3.완료 */ AND BASE_YM = A.SL_CL_YM AND DTA_DL_YN = 'N') F /* 마감담당자기본 */ ON 1=1 WHERE A.CNTR_NO = #{cntrNo} AND A.CNTR_SN = #{cntrSn} AND A.DTA_DL_YN = 'N'
AND SUBSTR(A.SL_CL_YM,1,4) >= #{fromSlClYy}
AND SUBSTR(A.SL_CL_YM,1,4) <= #{toSlClYy}
ORDER BY A.SL_CL_YM

SELECT A.CNTR_NO AS CNTR_NO /* 계약번호 */ , A.CNTR_SN AS CNTR_SN /* 계약일련번호 */ , A.SL_CL_YM AS SL_CL_YM /* 매출년월 */ , B.SELL_TP_CD AS SELL_TP_CD , CASE WHEN A.SL_STP_YN='Y' THEN '매출중지' ELSE '' END AS SL_STP_YN /* 매출중지 */ , A.RENTAL_TN AS RENTAL_TN /* 렌탈차월 */ , '' AS SL_CTR_DV_CD /* 매출조정구분코드 LC55.LCMGUB */ , A.PRM_MCN AS PRM_MCN /* 선납개월 */ , NVL(A.THM_SL_SUM_AMT, 0) AS THM_SL_SUM_AMT /* 매출합계(LC55.LCAM16) */ , NVL(D.BOR_AMT, 0) AS BOR_AMT /* 위약금액 */ , NVL(A.THM_ATAM_DP_AMT, 0) /* 선수입금(LC55.LCAM32) */ - NVL(A.THM_ATAM_RFND_AMT, 0) /* 선수환불(LC55.LCAM33) */ + CASE WHEN A.SL_CL_YM < '201605' THEN NVL(C.THM_DLQ_ADD_DP_SUM_AMT, 0) /* 가산금입금(LC55.LCAM83) - 판매금액(LC55.LCIAMT) ※ ASIS LCIAMT데이터 미존재하여 제외처리함 */ ELSE 0 END AS DP_AMT /* 입금금액 */ , NVL(A.EOT_ATAM, 0) AS EOT_ATAM /* 선수잔액(LC55.LCAM36) */ , NVL(A.EOT_UC_AMT, 0) + NVL(C.EOT_DLQ_ADD_AMT, 0) + NVL(D.EOT_BOR_AMT, 0) AS EOT_UC_AMT /* 미수금액*/ , NVL(C.EOT_DLQ_AMT, 0) + NVL(D.EOT_BOR_AMT, 0) + /* 원금연체 + 위약금 */ (CASE WHEN F.CL_FSH_YN = 'Y' THEN NVL(C.BTD_DLQ_ADD_AMT, 0) - (NVL(C.THM_DLQ_ADD_DP_SUM_AMT, 0) - NVL(C.THM_DLQ_ADD_RFND_SUM_AMT, 0)) - NVL(C.THM_CTR_DLQ_ADD_AMT, 0) /* 기초 - (입금 - 환불) - 공제 */ ELSE GREATEST(NVL(E.EOT_DLQ_ADD_AMT, 0) - NVL(E.THM_OC_DLQ_ADD_AMT, 0) - (NVL(C.THM_DLQ_ADD_DP_SUM_AMT, 0) - NVL(C.THM_DLQ_ADD_RFND_SUM_AMT, 0)) - NVL(C.THM_CTR_DLQ_ADD_AMT, 0), 0) /* 당월실제 기초연체가산금 - (입금 - 환불) - 조정 */ END) AS EOT_DLQ_AMT /* 연체금액(마감 전인 경우, 전월 발생 가산금은 제외) */ , NVL(C.DLQ_MCN, 0) AS DLQ_MCN /* 연체개월수 */ , NVL(C.BTD_DLQ_ADD_AMT, 0) AS BTD_DLQ_ADD_AMT /* 가산금기초(LC55.LCAM81) */ , NVL(C.THM_OC_DLQ_ADD_AMT, 0) AS THM_OC_DLQ_ADD_AMT /* 가산금발생(LC50.LCAM82) */ , NVL(C.THM_CTR_DLQ_ADD_AMT, 0) AS THM_CTR_DLQ_ADD_AMT /* 가산금공제(LC55.LCAM85) */ , NVL(C.THM_DLQ_ADD_DP_SUM_AMT, 0) AS THM_DLQ_ADD_DP_SUM_AMT /* 가산금입금(LC55.LCAM83) */ , NVL(C.THM_DLQ_ADD_RFND_SUM_AMT, 0) AS THM_DLQ_ADD_RFND_SUM_AMT /* 가산금환불(LC55.LCAM86) */ , NVL(C.EOT_DLQ_ADD_AMT, 0) AS EOT_DLQ_ADD_AMT /* 가산금기말(LC50.LCAM84) */ , TRUNC(NVL(A.SL_STP_AMT, 0) * 0.3,-1) AS SL_STP_AMT /* 매출중지금액 */ FROM TB_CBCL_WELLS_SL_MM_CL_IZ A /* WELLS매출월마감내역 */ INNER JOIN TB_SSCT_CNTR_DTL B /* 계약상세 */ ON A.CNTR_NO = B.CNTR_NO AND A.CNTR_SN = B.CNTR_SN AND B.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_DLQ_BAS C /* 연체기본 */ ON A.SL_CL_YM = C.PERF_YM AND A.CNTR_NO = C.CNTR_NO AND A.CNTR_SN = C.CNTR_SN AND C.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_DLQ_BAS E /* 연체기본 */ ON E.PERF_YM = TO_CHAR(ADD_MONTHS(TO_DATE(A.SL_CL_YM||'01'), -1), 'YYYYMM') AND E.CNTR_NO = A.CNTR_NO AND E.CNTR_SN = A.CNTR_SN AND E.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_WELLS_BOR_AMT_BAS D /* WELLS위약금액기본 */ ON A.SL_CL_YM = D.PERF_YM AND A.CNTR_NO = D.CNTR_NO AND A.CNTR_SN = D.CNTR_SN AND D.DTA_DL_YN = 'N' LEFT OUTER JOIN LATERAL (SELECT (CASE WHEN COUNT(1) > 0 THEN 'N' ELSE 'Y' END) AS CL_FSH_YN FROM TB_CBCL_CL_PSIC_BAS WHERE CL_BIZ_DV_CD = 'W06' AND CL_STAT_CD IN ('1','2') /*1.대기중 , 2.진행중 . 3.완료 */ AND BASE_YM = A.SL_CL_YM AND DTA_DL_YN = 'N') F /* 마감담당자기본 */ ON 1=1 WHERE A.CNTR_NO = #{cntrNo} AND A.CNTR_SN = #{cntrSn} AND A.DTA_DL_YN = 'N'
AND SUBSTR(A.SL_CL_YM,1,4) >= #{fromSlClYy}
AND SUBSTR(A.SL_CL_YM,1,4) <= #{toSlClYy}
ORDER BY A.SL_CL_YM

SELECT A.CNTR_NO AS CNTR_NO /* 계약번호 */ , A.CNTR_SN AS CNTR_SN /* 계약일련번호 */ , A.SL_CL_YM AS SL_CL_YM /* 매출년월 */ , B.SELL_TP_CD AS SELL_TP_CD , CASE WHEN A.SL_STP_YN='Y' THEN '매출중지' ELSE '' END AS SL_STP_YN /* 매출중지(LD50.LCSTOP) */ , A.RENTAL_TN AS RENTAL_TN /* 렌탈차월(LD50.LCRCNT) */ , '' AS SL_CTR_DV_CD /* 매출조정구분코드(미검색) */ , A.PRM_MCN AS PRM_MCN /* 선납개월 */ , NVL(A.THM_SL_SUM_AMT, 0) AS THM_SL_OC_AMT /* 매출금액(LC55.LCAM16) */ , NVL(A.THM_BIL_OC_AMT, 0) - NVL(A.THM_BIL_CTR_AMT, 0) + NVL(A.THM_BIL_SPMT_AMT, 0) AS THM_SL_SUM_AMT /* 발생금액 */ , NVL(D.BOR_AMT, 0) AS BOR_AMT /* 위약금액 */ , NVL(A.THM_ATAM_DP_AMT, 0) - NVL(A.THM_ATAM_RFND_AMT, 0) AS DP_AMT /* 입금금액 : 선수입금(LD55.LCAM32) - 선수환불(LD50.LCAM33) */ , NVL(A.EOT_ATAM, 0) AS EOT_ATAM /* 선수잔액(LD50.LCAM36) */ , NVL(A.EOT_BIL_UC_AMT, 0) + NVL(C.EOT_DLQ_ADD_AMT, 0) + NVL(D.EOT_BOR_AMT, 0) AS EOT_UC_AMT /* 미수금액 */ , NVL(C.EOT_DLQ_AMT, 0) + NVL(D.EOT_BOR_AMT, 0) + /* 원금연체 + 위약금 */ (CASE WHEN F.CL_FSH_YN = 'Y' THEN NVL(C.BTD_DLQ_ADD_AMT, 0) - (NVL(C.THM_DLQ_ADD_DP_SUM_AMT, 0) - NVL(C.THM_DLQ_ADD_RFND_SUM_AMT, 0)) - NVL(C.THM_CTR_DLQ_ADD_AMT, 0) /* 기초 - (입금 - 환불) - 공제 */ ELSE GREATEST(NVL(E.EOT_DLQ_ADD_AMT, 0) - NVL(E.THM_OC_DLQ_ADD_AMT, 0) - (NVL(C.THM_DLQ_ADD_DP_SUM_AMT, 0) - NVL(C.THM_DLQ_ADD_RFND_SUM_AMT, 0)) - NVL(C.THM_CTR_DLQ_ADD_AMT, 0), 0) /* 당월실제 기초연체가산금 - (입금 - 환불) - 조정 */ END) AS EOT_DLQ_AMT /* 연체금액(마감 전인 경우, 전월 발생 가산금은 제외) */ , NVL(C.DLQ_MCN, 0) AS DLQ_MCN /* 연체개월수 */ , NVL(C.BTD_DLQ_ADD_AMT, 0) AS BTD_DLQ_ADD_AMT /* 가산금기초 */ , NVL(C.THM_OC_DLQ_ADD_AMT, 0) AS THM_OC_DLQ_ADD_AMT /* 가산금발생 */ , NVL(C.THM_CTR_DLQ_ADD_AMT, 0) AS THM_CTR_DLQ_ADD_AMT /* 가산금공제 */ , NVL(C.THM_DLQ_ADD_DP_SUM_AMT, 0) AS THM_DLQ_ADD_DP_SUM_AMT /* 가산금입금 */ , NVL(C.THM_DLQ_ADD_RFND_SUM_AMT, 0) AS THM_DLQ_ADD_RFND_SUM_AMT /* 가산금환불 */ , NVL(C.EOT_DLQ_ADD_AMT, 0) AS EOT_DLQ_ADD_AMT /* 가산금기말 */ , TRUNC(NVL(A.SL_STP_AMT, 0) * 0.3,-1) AS SL_STP_AMT /* 매출중지금액 */ FROM TB_CBCL_WELLS_SL_MM_CL_IZ A /* WELLS매출월마감내역 */ INNER JOIN TB_SSCT_CNTR_DTL B /* 계약상세 */ ON A.CNTR_NO = B.CNTR_NO AND A.CNTR_SN = B.CNTR_SN AND B.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_DLQ_BAS C /* 연체기본 */ ON A.SL_CL_YM = C.PERF_YM AND A.CNTR_NO = C.CNTR_NO AND A.CNTR_SN = C.CNTR_SN AND C.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_DLQ_BAS E /* 연체기본 */ ON E.PERF_YM = TO_CHAR(ADD_MONTHS(TO_DATE(A.SL_CL_YM||'01'), -1), 'YYYYMM') AND E.CNTR_NO = A.CNTR_NO AND E.CNTR_SN = A.CNTR_SN AND E.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_WELLS_BOR_AMT_BAS D /* WELLS위약금액기본 */ ON A.SL_CL_YM = D.PERF_YM AND A.CNTR_NO = D.CNTR_NO AND A.CNTR_SN = D.CNTR_SN AND D.DTA_DL_YN = 'N' LEFT OUTER JOIN LATERAL (SELECT (CASE WHEN COUNT(1) > 0 THEN 'N' ELSE 'Y' END) AS CL_FSH_YN FROM TB_CBCL_CL_PSIC_BAS WHERE CL_BIZ_DV_CD = 'W06' AND CL_STAT_CD IN ('1','2') /*1.대기중 , 2.진행중 . 3.완료 */ AND BASE_YM = A.SL_CL_YM AND DTA_DL_YN = 'N') F /* 마감담당자기본 */ ON 1=1 WHERE A.CNTR_NO = #{cntrNo} AND A.CNTR_SN = #{cntrSn} AND A.DTA_DL_YN = 'N'
AND SUBSTR(A.SL_CL_YM,1,4) >= #{fromSlClYy}
AND SUBSTR(A.SL_CL_YM,1,4) <= #{toSlClYy}
ORDER BY A.SL_CL_YM
