mapper.select:
/*WELLS 집계구분 상세 */ SELECT SELL_TP_CD , (SELECT CD_CNTN FROM T_CMZ_CD_D WHERE TENANT_ID = 'TNT_WELLS' AND CD_ID = 'SELL_TP_CD' AND CD_VLD_VAL = T1.SELL_TP_CD) AS SELL_TP_CD_NM /* 판매유형명 */ , SELL_TP_DTL_CD , (SELECT CD_CNTN FROM T_CMZ_CD_D WHERE TENANT_ID = 'TNT_WELLS' AND CD_ID = 'SELL_TP_DTL_CD' AND CD_VLD_VAL = T1.SELL_TP_DTL_CD) AS SELL_TP_DTL_CD_NM /* 판매유형상세명 */ , SAP_PD_DV_CD , (SELECT MAX(SAP_PD_ATC_NM) FROM TB_CBCL_SAP_PD_DV_CD WHERE SAP_BZ_TERY_CD = '1210' AND SAP_PD_ATC_CD = '00' AND SAP_PD_DV_CD = T1.SAP_PD_DV_CD) AS SAP_PD_DV_NM /* SAP상품구분코드명 */ , DG_CST_ID , (SELECT MAX(DG_CST_NM) FROM TB_CBCL_DG_CST_ID_BAS WHERE SAP_BZ_TERY_CD = '1210' AND DG_CST_ID = T1.DG_CST_ID) AS DG_CST_NM /* 대표고객ID명 */ , BASE_YM , CNTR_NO AS CNTR_NO , CNTR_SN AS CNTR_SN , CST_NO AS CST_NO , (SELECT CST_KNM FROM TB_CUBS_CST_BAS WHERE CST_NO = T1.CST_NO AND DTA_DL_YN = 'N') AS CST_KNM /* 고객명 */ , MAX(SL_BND_ALRPY_DT) AS SL_BND_ALRPY_DT /* 채권반제일자 */ , MAX(SL_BND_ALRPY_SLIP_TRS_NO) AS SL_BND_ALRPY_SLIP_TRS_NO /* 전표전송번호 */ , MAX(SAP_ALRPY_SLPNO) AS SAP_ALRPY_SLPNO /* 반제전표번호 */ , MAX(RVE_NO) AS RVE_NO /* 수납번호 */ , MAX(RVE_SN) AS RVE_SN /* 수납일련번호 */ , MAX(DP_CL_DT) AS DP_CL_DT /* 수납일자 */ , MAX(RVE_AMT) AS RVE_AMT /* 수납금액 */ , MAX(SAP_DP_SLPNO) AS SAP_DP_SLPNO /* 입금전표번호 */ , MAX(SL_RCOG_DT) AS SL_RCOG_DT /* 채권발생일자 */ , MAX(SL_BND_OC_AMT) AS SL_BND_OC_AMT /* 채권발생금액 */ , MAX(SAP_SL_SLPNO) AS SAP_SL_SLPNO /* 매출전표번호 */ , SUM(SL_BND_ALRPY_AMT) AS SL_BND_ALRPY_AMT , SUM(DP_BLAM) AS DP_BLAM , SUM(UC_AMT) AS UC_AMT FROM (SELECT (CASE WHEN A.SELL_TP_CD = '2' AND A.SELL_TP_DTL_CD IN ('21', '23') AND E.REF_PD_CLSF_VAL IS NOT NULL THEN '6' ELSE A.SELL_TP_CD END) AS SELL_TP_CD /* 판매유형 */ , A.SELL_TP_DTL_CD AS SELL_TP_DTL_CD /* 판매유형상세 */ , A.SAP_PD_DV_CD AS SAP_PD_DV_CD /* SAP상품구분코드 */ , A.DG_CST_ID AS DG_CST_ID /* 대표고객ID */ , A.BASE_YM AS BASE_YM /* 기준년월 */ , A.CNTR_NO AS CNTR_NO /* 계약번호 */ , A.CNTR_SN AS CNTR_SN /* 일련번호 */ , A.CST_NO AS CST_NO /* 고객번호 */ , A.SL_BND_ALRPY_DT AS SL_BND_ALRPY_DT /* 채권반제일자 */ , A.SL_BND_ALRPY_AMT + (CASE WHEN A.SELL_TP_CD = '1' THEN (CASE WHEN B.ALNCMP_CD = '51' THEN 0 ELSE NVL(C.CNTRAM_DP_AMT, 0) END) /* 계약금입금액 (일시불은 반제금액에 할부금만 포함) */ ELSE 0 END) AS SL_BND_ALRPY_AMT /* 반제금액 */ , A.SL_BND_ALRPY_SLIP_TRS_NO AS SL_BND_ALRPY_SLIP_TRS_NO /* 전표전송번호 */ , A.SAP_ALRPY_SLPNO AS SAP_ALRPY_SLPNO /* 반제전표번호 */ , A.RVE_NO AS RVE_NO /* 수납번호 */ , A.RVE_SN AS RVE_SN /* 수납일련번호 */ , A.DP_CL_DT AS DP_CL_DT /* 수납일자 */ , A.RVE_AMT AS RVE_AMT /* 수납금액 */ , A.SAP_DP_SLPNO AS SAP_DP_SLPNO /* 입금전표번호 */ , A.DP_BLAM AS DP_BLAM /* 선수잔액 */ , A.SL_RCOG_DT AS SL_RCOG_DT /* 채권발생일자 */ , A.SL_BND_OC_AMT AS SL_BND_OC_AMT /* 채권발생금액 */ , A.SAP_SL_SLPNO AS SAP_SL_SLPNO /* 매출전표번호 */ , (CASE WHEN A.SELL_TP_CD = '1' THEN (CASE WHEN B.ALNCMP_CD = '51' THEN 0 ELSE A.UC_AMT END) WHEN A.SELL_TP_CD = '2' AND A.SELL_TP_DTL_CD IN ('21', '23') THEN A.UC_AMT + NVL(D.EOT_BOR_AMT, 0) ELSE A.UC_AMT END) AS UC_AMT /* 미수채권잔액 */ , NVL(D.EOT_BOR_AMT, 0) AS EOT_BOR_AMT FROM TB_CBCL_SL_BND_ALRPY_BAS A /* 매출채권반제기본 */ INNER JOIN TB_SSCT_CNTR_DTL B /* 계약상세 */ ON B.CNTR_NO = A.CNTR_NO AND B.CNTR_SN = A.CNTR_SN AND B.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_WELLS_SL_MM_CL_IZ C /* 매출월마감(일시불 계약금입금액 필요) */ ON C.SL_CL_YM = A.BASE_YM AND C.CNTR_NO = A.CNTR_NO AND C.CNTR_SN = A.CNTR_SN AND C.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_WELLS_BOR_AMT_BAS D /* 위약기본 */ ON D.PERF_YM = A.BASE_YM AND D.CNTR_NO = A.CNTR_NO AND D.CNTR_SN = A.CNTR_SN AND D.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_PDBS_PD_CLSF_BAS E ON E.PD_CLSF_ID = B.PD_LCLSF_ID AND E.DTA_DL_YN = 'N' AND E.USE_YN = 'Y' AND E.REF_PD_CLSF_VAL = '05003002' /* Cooking+커피머신+원두 (렌탈 원두는 정기배송 실적으로 집계 필요 asis 동일) */ WHERE A.BASE_YM = #{baseYm} AND A.DTA_DL_YN = 'N' AND ((#{dpKndCd} = '1' AND A.SELL_TP_CD = '1' ) /*일시불*/ OR (#{dpKndCd} = '5' AND A.RVE_DV_CD = '02') /*연체가산금*/ OR (#{dpKndCd} = '2' AND A.SELL_TP_CD <> '1' ) ) /*일시불 아닌거 ★여기 */
AND A.CNTR_NO = #{cntrNo} /*계약번호*/
AND A.CNTR_SN = #{cntrSn} /*계약번호*/
AND A.SAP_ALRPY_SLPNO = #{sapAlrpySlpno} /*SAP반제전표번호*/
AND A.SAP_PD_DV_CD = #{sapPdDvCd}
UNION ALL /* 포인트 선수금 별도 추가 */ SELECT A.SELL_TP_CD AS SELL_TP_CD /* 판매유형 */ , A.SELL_TP_DTL_CD AS SELL_TP_DTL_CD /* 판매유형상세 */ , A.SAP_PD_DV_CD AS SAP_PD_DV_CD /* SAP상품구분코드 */ , '포인트' AS DG_CST_ID /* 대표고객ID */ , A.SL_CL_YM AS BASE_YM /* 기준년월 */ , A.CNTR_NO AS CNTR_NO /* 계약번호 */ , A.CNTR_SN AS CNTR_SN /* 일련번호 */ , A.CST_NO AS CST_NO /* 고객번호 */ , '' AS SL_BND_ALRPY_DT /* 채권반제일자 */ , 0 AS SL_BND_ALRPY_AMT /* 반제금액 */ , '' AS SL_BND_ALRPY_SLIP_TRS_NO /* 전표전송번호 */ , '' AS SAP_ALRPY_SLPNO /* 반제전표번호 */ , '' AS RVE_NO /* 수납번호 */ , 0 AS RVE_SN /* 수납일련번호 */ , '' AS DP_CL_DT /* 수납일자 */ , 0 AS RVE_AMT /* 수납금액 */ , '' AS SAP_DP_SLPNO /* 입금전표번호 */ , (CASE WHEN A.SELL_TP_CD = '2' AND A.SELL_TP_DTL_CD IN ('21', '23') AND E.REF_PD_CLSF_VAL IS NOT NULL THEN 0 ELSE NVL(A.MLG_EOT_PRPD_AMT, 0) END) AS DP_BLAM /* 선수잔액 */ , '' AS SL_RCOG_DT /* 채권발생일자 */ , 0 AS SL_BND_OC_AMT /* 채권발생금액 */ , '' AS SAP_SL_SLPNO /* 매출전표번호 */ , 0 AS UC_AMT /* 미수채권잔액 */ , 0 AS EOT_BOR_AMT FROM TB_CBCL_WELLS_SL_MM_CL_IZ A /* 매출월마감(포인트잔액 필요) */ INNER JOIN TB_SSCT_CNTR_DTL B /* 계약상세 */ ON B.CNTR_NO = A.CNTR_NO AND B.CNTR_SN = A.CNTR_SN AND B.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_PDBS_PD_CLSF_BAS E ON E.PD_CLSF_ID = B.PD_LCLSF_ID AND E.DTA_DL_YN = 'N' AND E.USE_YN = 'Y' AND E.REF_PD_CLSF_VAL = '05003002' /* Cooking+커피머신+원두 (렌탈 원두는 정기배송 실적으로 집계 필요 asis 동일) */ WHERE A.SL_CL_YM = #{baseYm} AND A.DTA_DL_YN = 'N' AND A.SELL_TP_CD = '2'
AND A.CNTR_NO = #{cntrNo} /*계약번호*/
AND A.CNTR_SN = #{cntrSn} /*계약번호*/
AND A.SAP_ALRPY_SLPNO = #{sapAlrpySlpno} /*SAP반제전표번호*/
AND A.SAP_PD_DV_CD = #{sapPdDvCd}
) T1 GROUP BY T1.SELL_TP_CD, T1.SELL_TP_DTL_CD, T1.DG_CST_ID, T1.SAP_PD_DV_CD, T1.BASE_YM, T1.CNTR_NO, T1.CNTR_SN, T1.CST_NO ORDER BY T1.SELL_TP_CD, T1.SELL_TP_DTL_CD, T1.DG_CST_ID, T1.SAP_PD_DV_CD, T1.BASE_YM, T1.CNTR_NO, T1.CNTR_SN, T1.CST_NO
