mapper.select:
-- WELLS위약금액기본 Table의 분실손료 존재여부 확인
SELECT LS_RNTF /*분실손료*/
  FROM TB_CBCL_WELLS_BOR_AMT_BAS /*WELLS위약금액기본*/
 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

mapper.update:
-- WELLS위약금액기본 Table 수정
UPDATE TB_CBCL_WELLS_BOR_AMT_BAS                           /*WELLS위약금액기본*/
   SET LS_RNTF = 0                                         /*분실손료*/
     , CCAM_RES_AMT = CCAM_RES_AMT - LS_RNTF               /*위약금잔여금액    */

 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
