----------------------------------WELLS 상품별매출현황 상세 (일시불,금융리스,정기구매) ------------------------------------------------------------------------ SELECT F_CMZ_CD_NM(#{session.tenantId}, 'SELL_TP_CD', T1.SELL_TP_CD) AS SELL_TP_CD --판매유형 , F_CMZ_CD_NM(#{session.tenantId}, 'SELL_TP_DTL_CD', T1.SELL_TP_DTL_CD) AS SELL_TP_DTL_CD --판매유형상세코드 , T1.PD_CD --상품코드 , T2.PD_NM --상품명 , F_CMZ_CD_NM(#{session.tenantId}, 'SELL_CHNL_DTL_CD', T1.SELL_INFLW_CHNL_DTL_CD) AS SELL_INFLW_CHNL_DTL_CD --판매유입채널상세 , T1.SL_RCOG_DT --매출인식일자 , T1.CNTR_NO || '-' || T1.CNTR_SN AS CNTR_DTL_NO /*계약번호*/ , T1.CNTR_NO /*계약번호*/ , T1.CNTR_SN , T3.CST_KNM --고객명 , (SELECT SAP_PD_DV_NM FROM TB_CBCL_SAP_PD_DV_CD WHERE SAP_PD_DV_CD = T1.SAP_PD_DV_CD AND SAP_PD_ATC_CD = '00' AND SAP_BZ_TERY_CD = '1210' GROUP BY SAP_PD_DV_NM) AS SAP_PD_DV_CD --SAP상품구분코드 , SUM(NVL(T1.SELL_QTY,0)) AS SELL_QTY --판매수량-정상매출 , DECODE(T1.SELL_TP_CD ,'2',SUM(NVL(T1.THM_SL_SUM_AMT,0))+SUM(NVL(T1.PVDA_AMT,0)), SUM(NVL(T1.THM_SL_SUM_AMT,0))) AS SELL_AMT --매출금액-정상매출 , DECODE(T1.SELL_TP_CD ,'2',SUM(NVL(T1.THM_SL_SUM_AMT,0))+SUM(NVL(T1.PVDA_AMT,0)), SUM(NVL(T1.THM_SL_SUM_AMT,0)))-SUM(NVL(T1.SELL_AMT_VAT,0)) AS SELL_SPL_AMT --공급가액-정상매출 , SUM(NVL(T1.SELL_AMT_VAT,0)) AS SELL_AMT_VAT --부가세-정상매출 , SUM(NVL(T1.PVDA_AMT,0)) AS PVDA_AMT --현할차-정상매출 , SUM(NVL(T1.CH_QTY,0)) AS CH_QTY --판매수량-매출변경 , SUM(NVL(T1.SL_CH_AMT,0)) AS SL_CH_AMT --매출금액-매출변경 , SUM(NVL(T1.SL_CH_AMT,0)) - SUM(NVL(T1.CH_VAT,0)) AS CH_SPL_AMT --공급가액-매출변경 , SUM(NVL(T1.CH_VAT,0)) AS CH_VAT --부가세-매출변경 , SUM(DECODE(NVL(T1.SL_CH_AMT,0),0,0,T1.PVDA_AMT)) AS CH_PVDA_AMT --현할차-매출변경 , SUM(NVL(T1.CAN_QTY,0)) AS CAN_QTY --판매수량-매출취소 , SUM(NVL(T1.SL_CAN_AMT,0)) AS SL_CAN_AMT --매출금액-매출취소 , SUM(NVL(T1.SL_CAN_AMT,0)) - SUM(NVL(T1.CAN_VAT,0)) AS CAN_SPL_AMT --공급가액-매출취소 , SUM(NVL(T1.CAN_VAT,0)) AS CAN_VAT --부가세-매출취소 , SUM(DECODE(NVL(T1.SL_CAN_AMT,0),0,0,T1.PVDA_AMT)) AS CAN_PVDA_AMT --현할차-매출취소 , SUM(NVL(T1.SELL_QTY,0)) + SUM(NVL(T1.CH_QTY,0)) - SUM(NVL(T1.CAN_QTY,0)) AS TOT_QTY--판매수량-매출합계 , DECODE(T1.SELL_TP_CD ,'2',SUM(NVL(T1.THM_SL_SUM_AMT,0))+SUM(NVL(T1.PVDA_AMT,0)), SUM(NVL(T1.THM_SL_SUM_AMT,0))) + SUM(NVL(T1.SL_CH_AMT,0)) - SUM(NVL(T1.SL_CAN_AMT,0)) AS TOT_AMT--매출금액-매출합계 , (DECODE(T1.SELL_TP_CD ,'2',SUM(NVL(T1.THM_SL_SUM_AMT,0))+SUM(NVL(T1.PVDA_AMT,0)), SUM(NVL(T1.THM_SL_SUM_AMT,0))) + SUM(NVL(T1.SL_CH_AMT,0)) - SUM(NVL(T1.SL_CAN_AMT,0))) - (SUM(NVL(T1.SELL_AMT_VAT,0)) + SUM(NVL(T1.CAN_VAT,0)) - SUM(NVL(T1.CAN_VAT,0))) AS TOT_SPL_AMT--공급가액-매출합계 , SUM(NVL(T1.SELL_AMT_VAT,0)) + SUM(NVL(T1.CAN_VAT,0)) - SUM(NVL(T1.CAN_VAT,0)) AS TOT_VAT --부가세-매출합계 , SUM(NVL(T1.PVDA_AMT,0)) + SUM(DECODE(NVL(T1.SL_CH_AMT,0),0,0,T1.PVDA_AMT)) - SUM(DECODE(NVL(T1.SL_CAN_AMT,0),0,0,T1.PVDA_AMT)) AS TOT_PVDA_AMT --현할차-매출합계 FROM TB_CBCL_WELLS_SL_MM_CL_IZ T1 INNER JOIN TB_PDBS_PD_BAS T2 ON T1.PD_CD = T2.PD_CD LEFT OUTER JOIN TB_CUBS_CST_BAS T3 --고객기본 ON T1.CST_NO = T3.CST_NO AND T3.NUSD_RSON_CD IS NULL AND T3.DTA_DL_YN = 'N' /*원두제외*/ LEFT JOIN (SELECT SA.CNTR_NO, SA.CNTR_SN, B.PD_CD, B.PD_NM, C.REF_PD_CLSF_VAL FROM TB_SSCT_CNTR_PD_REL SA /*계약상품관계*/ , TB_PDBS_PD_BAS B /*상품기본*/ , TB_PDBS_PD_CLSF_BAS C /*상품분류기본*/ WHERE SA.BASE_PD_CD = B.PD_CD AND B.PD_CLSF_ID = C.PD_CLSF_ID AND C.REF_PD_CLSF_VAL IN ('05003002','0A003002') /*참조상품분류값 = 상품대분류2자리||상품중분류3자리||상품소분류3자리||상품세분류3자리*/ )T10 ON T1.CNTR_NO = T10.CNTR_NO AND T1.CNTR_SN = T10.CNTR_SN WHERE T1.SL_RCOG_DT BETWEEN #{baseDtmnFrom} AND #{baseDtmnTo} AND SUBSTR(T1.SL_RCOG_DT,1,6) = T1.SL_CL_YM AND T1.SELL_TP_CD = DECODE(#{sellTpCd}, '10', '2', #{sellTpCd})
AND T1.SELL_TP_DTL_CD = #{sellTpDtlCd}
AND T1.SELL_TP_DTL_CD IN ('22', '24','25','26')
AND SUBSTR(T1.SELL_INFLW_CHNL_DTL_CD,1,2) = #{sellChnlDtl}
AND T1.CNTR_NO = #{cntrNo}
AND T1.CNTR_SN = #{cntrSn}
AND T1.CST_NO = #{cstNo}
AND T1.SAP_PD_DV_CD = #{sapPdDvCd}
AND T10.CNTR_NO IS NULL AND T10.CNTR_SN IS NULL GROUP BY T1.SL_RCOG_DT, T1.SELL_TP_CD, T1.SELL_TP_DTL_CD, T1.PD_CD, T2.PD_NM, T1.SAP_PD_DV_CD, T1.SELL_INFLW_CHNL_DTL_CD,T1.CNTR_NO,T1.CNTR_SN,T3.CST_KNM
UNION ALL /*판매유형이 정기배송인 경우에만 추가 (SELL_TP_CD = '6') START */ SELECT F_CMZ_CD_NM('TNT_WELLS', 'SELL_TP_CD', T1.SELL_TP_CD) AS SELL_TP_CD --판매유형 , F_CMZ_CD_NM('TNT_WELLS', 'SELL_TP_DTL_CD', T1.SELL_TP_DTL_CD) AS SELL_TP_DTL_CD --판매유형상세코드 , T1.PD_CD --상품코드 , T2.PD_NM --상품명 , F_CMZ_CD_NM('TNT_WELLS', 'SELL_CHNL_DTL_CD', T1.SELL_INFLW_CHNL_DTL_CD) AS SELL_INFLW_CHNL_DTL_CD --판매유입채널상세 , T1.SL_RCOG_DT --매출인식일자 , T1.CNTR_NO || '-' || T1.CNTR_SN AS CNTR_DTL_NO /*계약번호*/ , T1.CNTR_NO /*계약번호*/ , T1.CNTR_SN /*계약일련번호*/ , T3.CST_KNM --고객명 , (SELECT SAP_PD_DV_NM FROM TB_CBCL_SAP_PD_DV_CD WHERE SAP_PD_DV_CD = T1.SAP_PD_DV_CD AND SAP_BZ_TERY_CD = '1210' GROUP BY SAP_PD_DV_NM) AS SAP_PD_DV_NM --SAP상품구분코드명 , SUM(NVL(T1.SELL_QTY,0)) AS SELL_QTY --판매수량-정상매출 , SUM(NVL(T1.THM_SL_SUM_AMT,0)) AS SELL_AMT --매출금액-정상매출 , SUM(NVL(T1.THM_SL_SUM_AMT,0))-SUM(NVL(T1.SELL_AMT_VAT,0)) AS SELL_SPL_AMT --공급가액-정상매출 , SUM(NVL(T1.SELL_AMT_VAT,0)) AS SELL_AMT_VAT --부가세-정상매출 , SUM(NVL(T1.PVDA_AMT,0)) AS PVDA_AMT --현할차-정상매출 , SUM(NVL(T1.CH_QTY,0)) AS CH_QTY --판매수량-매출변경 , SUM(NVL(T1.SL_CH_AMT,0)) AS SL_CH_AMT --매출금액-매출변경 , SUM(NVL(T1.SL_CH_AMT,0)) - SUM(NVL(T1.CH_VAT,0)) AS CH_SPL_AMT --공급가액-매출변경 , SUM(NVL(T1.CH_VAT,0)) AS CH_VAT --부가세-매출변경 , SUM(DECODE(NVL(T1.SL_CH_AMT,0),0,0,T1.PVDA_AMT)) AS CH_PVDA_AMT --현할차-매출변경 , SUM(NVL(T1.CAN_QTY,0)) AS CAN_QTY --판매수량-매출취소 , SUM(NVL(T1.SL_CAN_AMT,0)) AS SL_CAN_AMT --매출금액-매출취소 , SUM(NVL(T1.SL_CAN_AMT,0)) - SUM(NVL(T1.CAN_VAT,0)) AS CAN_SPL_AMT --공급가액-매출취소 , SUM(NVL(T1.CAN_VAT,0)) AS CAN_VAT --부가세-매출취소 , SUM(DECODE(NVL(T1.SL_CAN_AMT,0),0,0,T1.PVDA_AMT)) AS CAN_PVDA_AMT --현할차-매출취소 , SUM(NVL(T1.SELL_QTY,0)) + SUM(NVL(T1.CH_QTY,0)) - SUM(NVL(T1.CAN_QTY,0)) AS TOT_QTY--판매수량-매출합계 , DECODE(T1.SELL_TP_CD ,'2',SUM(NVL(T1.THM_SL_SUM_AMT,0))+SUM(NVL(T1.PVDA_AMT,0)), SUM(NVL(T1.THM_SL_SUM_AMT,0))) + SUM(NVL(T1.SL_CH_AMT,0)) - SUM(NVL(T1.SL_CAN_AMT,0)) AS TOT_AMT--매출금액-매출합계 , (DECODE(T1.SELL_TP_CD ,'2',SUM(NVL(T1.THM_SL_SUM_AMT,0))+SUM(NVL(T1.PVDA_AMT,0)), SUM(NVL(T1.THM_SL_SUM_AMT,0))) + SUM(NVL(T1.SL_CH_AMT,0)) - SUM(NVL(T1.SL_CAN_AMT,0))) - (SUM(NVL(T1.SELL_AMT_VAT,0)) + SUM(NVL(T1.CAN_VAT,0)) - SUM(NVL(T1.CAN_VAT,0))) AS TOT_SPL_AMT--공급가액-매출합계 , SUM(NVL(T1.SELL_AMT_VAT,0)) + SUM(NVL(T1.CAN_VAT,0)) - SUM(NVL(T1.CAN_VAT,0)) AS TOT_VAT --부가세-매출합계 , SUM(NVL(T1.PVDA_AMT,0)) + SUM(DECODE(NVL(T1.SL_CH_AMT,0),0,0,T1.PVDA_AMT)) - SUM(DECODE(NVL(T1.SL_CAN_AMT,0),0,0,T1.PVDA_AMT)) AS TOT_PVDA_AMT --현할차-매출합계 FROM TB_CBCL_WELLS_SL_MM_CL_IZ T1 INNER JOIN TB_PDBS_PD_BAS T2 ON T1.PD_CD = T2.PD_CD LEFT OUTER JOIN TB_CUBS_CST_BAS T3 --고객기본 ON T1.CST_NO = T3.CST_NO AND T3.NUSD_RSON_CD IS NULL AND T3.DTA_DL_YN = 'N' LEFT JOIN (SELECT SA.CNTR_NO, SA.CNTR_SN FROM TB_SSCT_CNTR_PD_REL SA /*계약상품관계*/ , TB_PDBS_PD_BAS B /*상품기본*/ , TB_PDBS_PD_CLSF_BAS C /*상품분류기본*/ WHERE SA.BASE_PD_CD = B.PD_CD AND B.PD_CLSF_ID = C.PD_CLSF_ID AND C.REF_PD_CLSF_VAL IN ('05003002','0A003002') /*참조상품분류값 = 상품대분류2자리||상품중분류3자리||상품소분류3자리||상품세분류3자리*/ GROUP BY SA.CNTR_NO, SA.CNTR_SN )T10 ON T1.CNTR_NO = T10.CNTR_NO AND T1.CNTR_SN = T10.CNTR_SN WHERE T1.SL_RCOG_DT BETWEEN #{baseDtmnFrom} AND #{baseDtmnTo} AND SUBSTR(T1.SL_RCOG_DT,1,6) = T1.SL_CL_YM AND T1.SELL_TP_CD = '2'
AND SUBSTR(T1.SELL_INFLW_CHNL_DTL_CD,1,2) = #{sellChnlDtl}
AND T1.CNTR_NO = #{cntrNo}
AND T1.CNTR_SN = #{cntrSn}
AND T1.SAP_PD_DV_CD = #{sapPdDvCd}
AND T1.CST_NO = #{cstNo}
AND T10.CNTR_NO = T1.CNTR_NO AND T10.CNTR_SN = T1.CNTR_SN GROUP BY T1.SL_RCOG_DT, T1.SELL_TP_CD, T1.SELL_TP_DTL_CD, T1.PD_CD, T2.PD_NM, T1.SAP_PD_DV_CD, T1.SELL_INFLW_CHNL_DTL_CD,T1.CNTR_NO,T1.CNTR_SN,T3.CST_KNM /*판매유형이 정기배송인 경우에만 추가 (SELL_TP_CD = '6') END */

/* WELLS 상품별매출현황 상세 (렌탈) */ SELECT F_CMZ_CD_NM(#{session.tenantId}, 'SELL_TP_CD', B1.SELL_TP_CD) AS SELL_TP_CD /* 판매유형명 */ , F_CMZ_CD_NM(#{session.tenantId}, 'SELL_TP_DTL_CD', B1.SELL_TP_DTL_CD) AS SELL_TP_DTL_CD /* 판매유형상세명 */ , B1.PD_CD /* 상품코드 */ , B1.PD_NM /* 상품명 */ , F_CMZ_CD_NM(#{session.tenantId}, 'SELL_CHNL_DTL_CD', B1.SELL_INFLW_CHNL_DTL_CD) AS SELL_INFLW_CHNL_DTL_CD /* 판매유입채널상세명 */ , B1.SL_RCOG_DT /* 매출인식일자 */ , B1.CNTR_NO || '-' || B1.CNTR_SN AS CNTR_DTL_NO /* 계약상세번호 */ , B1.CNTR_NO /* 계약번호 */ , B1.CNTR_SN /* 계약일련번호 */ , B2.CST_KNM /* 고객명 */ , (SELECT Z1.SAP_PD_DV_NM FROM TB_CBCL_SAP_PD_DV_CD Z1 /* SAP상품구분코드 */ WHERE Z1.DTA_DL_YN = 'N' AND Z1.SAP_PD_DV_CD = B1.SAP_PD_DV_CD AND Z1.SAP_BZ_TERY_CD = '1210' AND ROWNUM = 1) AS SAP_PD_DV_CD /* SAP상품구분코드명 */ , B1.RENTAL_RGST_COST /* 매출금액-등록비 */ , B1.RENTAL_RGST_COST - B1.RENTAL_RGST_COST_VAT AS RENTAL_RGST_COST_SPL /* 공급가액-등록비 */ , B1.RENTAL_RGST_COST_VAT /* 부가세-등록비 */ , B1.NOM_SL_AMT /* 매출금액-렌탈료 */ , B1.NOM_SL_AMT - B1.VAT AS RENTAL_RGST_COST_SPL /* 공급가액-렌탈료 */ , B1.VAT /* 부가세-렌탈료 */ , B1.RENTAL_RGST_COST + B1.NOM_SL_AMT AS TOT_SL_AMT /* 매출금액-매출합계 */ , B1.RENTAL_RGST_COST - B1.RENTAL_RGST_COST_VAT + B1.NOM_SL_AMT - B1.VAT AS TOT_SPL_AMT /* 공급가액-매출합계 */ , B1.RENTAL_RGST_COST_VAT + B1.VAT AS TOT_VAT /* 부가세-매출합계 */ FROM (SELECT A1.CNTR_NO , A1.CNTR_SN , A1.CST_NO , A1.PD_CD , A2.PD_NM , A1.SELL_TP_CD , A1.SELL_TP_DTL_CD , A1.SELL_INFLW_CHNL_DTL_CD , A1.SL_RCOG_DT , A1.SAP_PD_DV_CD , NVL(SUM(CASE WHEN A1.RENTAL_TN = 0 THEN A1.CNTR_AMT END), 0) AS RENTAL_RGST_COST --매출금액-등록비 , NVL(SUM(CASE WHEN A1.RENTAL_TN = 0 THEN A1.RENTAL_RGST_COST_VAT END), 0) AS RENTAL_RGST_COST_VAT --부가세-등록비 , NVL(SUM(A1.THM_SL_SUM_AMT), 0) AS NOM_SL_AMT --매출금액-렌탈 , NVL(SUM(A1.SL_SUM_VAT), 0) AS VAT --부가세-렌탈료 FROM TB_CBCL_WELLS_SL_MM_CL_IZ A1 /* WELLS매출춸마감내역 */ INNER JOIN TB_PDBS_PD_BAS A2 /* 상품기본 */ ON A2.PD_CD = A1.PD_CD WHERE A1.SL_RCOG_DT BETWEEN #{baseDtmnFrom} AND #{baseDtmnTo} AND A1.SELL_TP_CD = '2' AND A1.SL_RCOG_DT LIKE A1.SL_CL_YM || '%'
AND A1.SELL_TP_DTL_CD = #{sellTpDtlCd}
AND A1.SELL_TP_DTL_CD IN ('21', '23')
AND SUBSTR(A1.SELL_INFLW_CHNL_DTL_CD,1,2) = #{sellChnlDtl}
AND A1.CNTR_NO = #{cntrNo}
AND A1.CNTR_SN = #{cntrSn}
AND A1.CST_NO = #{cstNo}
AND A1.SAP_PD_DV_CD = #{sapPdDvCd}
GROUP BY A1.CNTR_NO , A1.CNTR_SN , A1.CST_NO , A1.PD_CD , A2.PD_NM , A1.SELL_TP_CD , A1.SELL_TP_DTL_CD , A1.SELL_INFLW_CHNL_DTL_CD , A1.SL_RCOG_DT , A1.SL_RCOG_DT , A1.SAP_PD_DV_CD ) B1 LEFT OUTER JOIN TB_CUBS_CST_BAS B2 /* 고객기본 */ ON B2.CST_NO = B1.CST_NO AND B2.NUSD_RSON_CD IS NULL AND B2.DTA_DL_YN = 'N' WHERE B1.RENTAL_RGST_COST + B1.NOM_SL_AMT != 0 AND EXISTS (SELECT 1 FROM TB_SSCT_CNTR_PD_REL Z1 /* 계약상품관계 */ INNER JOIN TB_PDBS_PD_BAS Z2 /* 상품기본 */ ON Z2.PD_CD = Z1.BASE_PD_CD AND Z2.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_CLSF_BAS Z3 /* 상품분류기본 */ ON Z3.PD_CLSF_ID = Z2.PD_CLSF_ID AND NVL(Z3.REF_PD_CLSF_VAL, 'X') NOT IN ('05003002','0A003002') /*원두제외*/ AND Z3.DTA_DL_YN = 'N' WHERE Z1.DTA_DL_YN = 'N' AND Z1.CNTR_NO = B1.CNTR_NO AND Z1.CNTR_SN = B1.CNTR_SN)

----------------------------------WELLS 상품별매출현황 상세 (멤버십) ------------------------------------------------------------------------ SELECT F_CMZ_CD_NM(#{session.tenantId}, 'SELL_TP_CD', T1.SELL_TP_CD) AS SELL_TP_CD --판매유형 , F_CMZ_CD_NM(#{session.tenantId}, 'SELL_TP_DTL_CD', T1.SELL_TP_DTL_CD) AS SELL_TP_DTL_CD --판매유형상세코드 , T1.PD_CD --상품코드 , T2.PD_NM --상품명 , F_CMZ_CD_NM(#{session.tenantId}, 'SELL_CHNL_DTL_CD', T1.SELL_INFLW_CHNL_DTL_CD) AS SELL_INFLW_CHNL_DTL_CD --판매유입채널상세 , T1.SL_RCOG_DT --매출인식일자 , T1.CNTR_NO || '-' || T1.CNTR_SN AS CNTR_DTL_NO /*계약번호*/ , T1.CNTR_NO /*계약번호*/ , T1.CNTR_SN , T3.CST_KNM --고객명 , (SELECT SAP_PD_DV_NM FROM TB_CBCL_SAP_PD_DV_CD WHERE SAP_PD_DV_CD = T1.SAP_PD_DV_CD AND SAP_BZ_TERY_CD = '1210' GROUP BY SAP_PD_DV_NM) AS SAP_PD_DV_CD --SAP상품구분코드 , SUM(DECODE(T1.SELL_TP_CD,'3',T1.THM_SL_SUM_AMT,0)) AS SELL_AMT --매출금액-회비 , SUM(DECODE(T1.SELL_TP_CD,'3',T1.THM_SL_SUM_AMT-T1.SELL_AMT_VAT,0)) AS SELL_SPL_AMT --공급가액-회비 , SUM(DECODE(T1.SELL_TP_CD,'3',T1.SELL_AMT_VAT,0)) AS SELL_AMT_VAT --부가세-회비 , SUM(DECODE(T1.SELL_TP_CD,'9',T1.THM_SL_SUM_AMT,0)) AS FIL_SELL_AMT --매출금액-필터 , SUM(DECODE(T1.SELL_TP_CD,'9',T1.SELL_AMT-T1.SELL_AMT_VAT,0)) AS FIL_SELL_SPL_AMT --공급가액-필터 , SUM(DECODE(T1.SELL_TP_CD,'9',T1.SELL_AMT_VAT,0)) AS FIL_SELL_AMT_VAT --부가세-필터 , SUM(T1.THM_SL_SUM_AMT) AS TOT_SELL_AMT --매출금액-매출합계 , SUM(T1.THM_SL_SUM_AMT-T1.SELL_AMT_VAT) AS TOT_SELL_SPL_AMT --공급가액-매출합계 , SUM(T1.SELL_AMT_VAT) AS TOT_SELL_AMT_VAT --부가세-매출합계 FROM TB_CBCL_WELLS_SL_MM_CL_IZ T1 INNER JOIN TB_PDBS_PD_BAS T2 ON T1.PD_CD = T2.PD_CD LEFT OUTER JOIN TB_CUBS_CST_BAS T3 --고객기본 ON T1.CST_NO = T3.CST_NO AND T3.NUSD_RSON_CD IS NULL AND T3.DTA_DL_YN = 'N' WHERE T1.SL_RCOG_DT BETWEEN #{baseDtmnFrom} AND #{baseDtmnTo} AND T1.SELL_TP_CD = '3' --멤버십 AND SUBSTR(T1.SL_RCOG_DT,1,6) = T1.SL_CL_YM
AND T1.SELL_TP_DTL_CD = #{sellTpDtlCd}
AND SUBSTR(T1.SELL_INFLW_CHNL_DTL_CD,1,2) = #{sellChnlDtl}
AND T1.CNTR_NO = #{cntrNo}
AND T1.CNTR_SN = #{cntrSn}
AND T1.CST_NO = #{cstNo}
AND T1.SAP_PD_DV_CD = #{sapPdDvCd}
GROUP BY T1.SL_RCOG_DT, T1.SELL_TP_CD, T1.SELL_TP_DTL_CD, T1.PD_CD, T2.PD_NM, T1.SAP_PD_DV_CD, T1.SELL_INFLW_CHNL_DTL_CD,T1.CNTR_NO,T1.CNTR_SN,T3.CST_KNM
