mapper.update:
MERGE INTO TB_PSGA_ACTI_GDS_APLC_BASE_BAS T1 /* 활동물품신청기준기본 */ USING ( SELECT #{ogTpCd} AS OG_TP_CD /* 조직유형코드 */ , #{aplcPsbStrtD} AS APLC_PSB_STRT_D /* 신청가능시작일 */ , #{aplcPsbEndD} AS APLC_PSB_END_D /* 신청가능종료일 */ , #{rtngdPsbStrtD} AS RTNGD_PSB_STRT_D /* 반품가능시작일 */ , #{rtngdPsbEndD} AS RTNGD_PSB_END_D /* 반품가능종료일 */ , #{rtngdShrnEmadr} AS RTNGD_SHRN_EMADR /* 반품공유이메일주소 */ , #{confArtcCn} AS CONF_ARTC_CN /* 확인사항내용 */ , #{dtaDlYn} AS DTA_DL_YN /* 데이터삭제여부 */ FROM DUAL ) T2 ON (T1.OG_TP_CD = T2.OG_TP_CD /* 조직유형코드 */ ) WHEN MATCHED THEN UPDATE SET APLC_PSB_STRT_D = T2.APLC_PSB_STRT_D , APLC_PSB_END_D = T2.APLC_PSB_END_D , RTNGD_PSB_STRT_D = T2.RTNGD_PSB_STRT_D , RTNGD_PSB_END_D = T2.RTNGD_PSB_END_D , RTNGD_SHRN_EMADR = T2.RTNGD_SHRN_EMADR , CONF_ARTC_CN = T2.CONF_ARTC_CN , DTA_DL_YN = T2.DTA_DL_YN
WHEN NOT MATCHED THEN INSERT ( OG_TP_CD , APLC_PSB_STRT_D , APLC_PSB_END_D , RTNGD_PSB_STRT_D , RTNGD_PSB_END_D , RTNGD_SHRN_EMADR , CONF_ARTC_CN , DTA_DL_YN
) VALUES ( T2.OG_TP_CD , T2.APLC_PSB_STRT_D , T2.APLC_PSB_END_D , T2.RTNGD_PSB_STRT_D , T2.RTNGD_PSB_END_D , T2.RTNGD_SHRN_EMADR , T2.CONF_ARTC_CN , T2.DTA_DL_YN )

mapper.insert:
