mapper.insert:
INSERT INTO TB_PSEV_AWD_EVL_PD_BASE_BAS (  /* 시상평가상품기준기본 */
       BASE_YM          /* 기준년월 */
     , EVL_OG_TP_CD     /* 평가조직유형코드 */
     , EVL_PD_DV_CD     /* 평가상품구분코드 */
     , PD_CD            /* 상품코드 */
     , CVT_PC           /* 환산점수 */
     , DTA_DL_YN        /* 데이터삭제여부 */
     )
VALUES (
       #{baseYm}
     , #{evlOgTpCd}
     , #{evlPdDvCd}
     , #{pdCd}
     , #{cvtPc}
     , 'N'
      )

INSERT INTO TB_PSEV_AWD_EVL_BASE_BAS (  /* 시상평가기준기본 */
       BASE_YM          /* 기준년월 */
     , EVL_OG_TP_CD     /* 평가조직유형코드 */
     , EVL_DV_CD        /* 평가구분코드 */
     , EVL_RSB_REL_ID   /* 평가직책관계ID */
     , CTST_GRP_USE_YN  /* 경진그룹사용여부 */
     , EVL_ATC_CN       /* 평가항목내용 */
     , DTA_DL_YN        /* 데이터삭제여부 */
     )
VALUES (
       #{baseYm}
     , #{evlOgTpCd}
     , #{evlDvCd}
     , #{evlOgTpCd}||
     , #{ctstGrpUseYn}
     , #{evlAtcCn}
     , #{dtaDlYn}
      )

INSERT INTO TB_PSEV_AWD_EVL_RSB_REL (  /* 시상평가직책관계 */
       EVL_OG_TP_CD     /* 평가조직유형코드 */
     , RGST_SN          /* 등록일련번호 */
     , EVL_RSB_DV_CD    /* 평가직책구분코드 */
     , QLF_DV_CD        /* 자격구분코드 */
     , EVL_RSB_REL_ID   /* 평가직책관계ID */
     , CALF_CN          /* 계산식내용 */
     , CEXP_CN          /* 조건식내용 */
     , DTA_DL_YN        /* 데이터삭제여부 */
     )

 VALUES (
       #{evlOgTpCd}
     , (SELECT NVL(MAX(RGST_SN)+1,1)
          FROM TB_PSEV_AWD_EVL_RSB_REL
       )
     , #{item}
     , #{qlfDvCd}
     , (SELECT #{evlOgTpCd}||#{evlDvCd}||LPAD('1', 5, '0')
          FROM DUAL )
     , #{calfCn}
     , #{cexpCn}
     , 'N'
      )

mapper.update:
MERGE INTO TB_PSEV_AWD_EVL_BASE_DTL T1 /* 시상평가기준상세 */ USING ( SELECT #{baseYm} AS BASE_YM /* 기준년월 */ , #{evlOgTpCd} AS EVL_OG_TP_CD /* 평가조직유형코드 */ , #{evlDvCd} AS EVL_DV_CD /* 평가구분코드 */ , #{evlAtcDvCd} AS EVL_ATC_DV_CD /* 평가항목구분코드 */ , #{evlBaseUnitN} AS EVL_BASE_UNIT_N /* 평가기준단위수 */ , #{cvtBasePc} AS CVT_BASE_PC /* 환산기준점수 */ , #{dtaDlYn} AS DTA_DL_YN /* 데이터삭제여부 */ FROM DUAL ) T2 ON (T1.BASE_YM = T2.BASE_YM /* 기준년월 */ AND T1.EVL_OG_TP_CD = T2.EVL_OG_TP_CD /* 평가조직유형코드 */ AND T1.EVL_DV_CD = T2.EVL_DV_CD /* 평가구분코드 */ AND T1.EVL_ATC_DV_CD = T2.EVL_ATC_DV_CD /* 평가항목구분코드 */ ) WHEN MATCHED THEN UPDATE SET EVL_BASE_UNIT_N = T2.EVL_BASE_UNIT_N , CVT_BASE_PC = T2.CVT_BASE_PC , DTA_DL_YN = T2.DTA_DL_YN
WHEN NOT MATCHED THEN INSERT ( BASE_YM , EVL_OG_TP_CD , EVL_DV_CD , EVL_ATC_DV_CD , EVL_BASE_UNIT_N , CVT_BASE_PC , DTA_DL_YN
) VALUES ( T2.BASE_YM , T2.EVL_OG_TP_CD , T2.EVL_DV_CD , T2.EVL_ATC_DV_CD , T2.EVL_BASE_UNIT_N , T2.CVT_BASE_PC , T2.DTA_DL_YN )

UPDATE TB_PSEV_AWD_EVL_BASE_DTL
   SET TRG_BASE_PC = #{trgBasePc}
     , CTST_GRP_CD = #{ctstGrpCd}

 WHERE BASE_YM = #{baseYm}
   AND EVL_OG_TP_CD = #{evlOgTpCd}
   AND EVL_DV_CD = #{evlDvCd}
   AND EVL_ATC_DV_CD = #{evlAtcDvCd}
