mapper.insert:
INSERT INTO TB_PSEV_AWD_EVL_PD_BASE_BAS (  /* 시상평가상품기준기본 */
       BASE_YM          /* 기준년월 */
     , EVL_OG_TP_CD     /* 평가조직유형코드 */
     , EVL_PD_DV_CD     /* 평가상품구분코드 */
     , PD_CD            /* 상품코드 */
     , CVT_PC           /* 환산점수 */
     , DTA_DL_YN        /* 데이터삭제여부 */
     )
VALUES (
       #{baseYm}
     , #{evlOgTpCd}
     , #{evlPdDvCd}
     , #{pdCd}
     , #{cvtPc}
     , 'N'
      )

INSERT INTO TB_PSEV_AWD_EVL_BASE_BAS (  /* 시상평가기준기본 */
       BASE_YM          /* 기준년월 */
     , EVL_OG_TP_CD     /* 평가조직유형코드 */
     , EVL_DV_CD        /* 평가구분코드 */
     , EVL_RSB_REL_ID   /* 평가직책관계ID */
     , CTST_GRP_USE_YN  /* 경진그룹사용여부 */
     , EVL_ATC_CN
     , DTA_DL_YN        /* 데이터삭제여부 */
     )
VALUES (
       #{baseYm}
     , #{evlOgTpCd}
     , #{evlDvCd}
     , #{evlOgTpCd}||#{evlDvCd}||LPAD('1', 5, '0')
     , NVL(#{ctstGrpUseYn}, 'N')
     , #{evlAtcCn}
     , 'N'
      )

insert.foreach:
  INTO TB_PSEV_AWD_EVL_RSB_REL (  /* 시상평가직책관계 */
       EVL_OG_TP_CD     /* 평가조직유형코드 */
     , RGST_SN          /* 등록일련번호 */
     , EVL_RSB_DV_CD    /* 평가직책구분코드 */
     , QLF_DV_CD        /* 자격구분코드 */
     , EVL_RSB_REL_ID   /* 평가직책관계ID */
     , CALF_CN          /* 계산식내용 */
     , CEXP_CN          /* 조건식내용 */
     , DTA_DL_YN        /* 데이터삭제여부 */

     )
VALUES
     ( #{evlOgTpCd}
     , (SELECT NVL(MAX(RGST_SN)+(1+#{idx}),1)
          FROM TB_PSEV_AWD_EVL_RSB_REL
       )
     , #{item}
     , #{qlfDvCd}
     , (#{evlOgTpCd}||#{evlDvCd}||LPAD('1', 5, '0'))
     , #{calfCn}
     , #{cexpCn}
     , 'N'

     )

mapper.update:
UPDATE TB_PSEV_AWD_EVL_BASE_DTL
   SET TRG_BASE_PC = #{trgBasePc}

 WHERE BASE_YM = #{baseYm}
   AND EVL_OG_TP_CD = #{evlOgTpCd}
   AND EVL_DV_CD = #{evlDvCd}
   AND EVL_ATC_DV_CD = #{evlAtcDvCd}

mapper.insert:
INSERT INTO TB_PSEV_AWD_EVL_BASE_DTL ( BASE_YM , EVL_OG_TP_CD , OG_ID , EVL_DV_CD , EVL_ATC_DV_CD , EVL_BASE_UNIT_N , CVT_BASE_PC , DTA_DL_YN , SORT_ODR
)
WITH TARGET_LIST AS ( SELECT OG_ID FROM ( SELECT CASE #{param.evlDvCd} WHEN 'M01' THEN T3.DGR1_LEVL_OG_ID WHEN 'M02' THEN T3.DGR2_LEVL_OG_ID WHEN 'M03' THEN T3.DGR3_LEVL_OG_ID ELSE T3.DGR1_LEVL_OG_ID END AS OG_ID FROM ( SELECT PRTNR_NO FROM (
${item.calfCn}
${item.cexpCn}
AND QLF_DV_CD = #{item.qlfDvCd}
) GROUP BY PRTNR_NO ) T1 INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 ON T2.PRTNR_NO = T1.PRTNR_NO INNER JOIN TB_OGBS_MM_OG_IZ T3 ON T3.BASE_YM = T2.BASE_YM /* 기준년월 */ AND T3.OG_TP_CD = T2.OG_TP_CD /* 조직유형코드 */ AND T3.OG_ID = T2.OG_ID /* 조직ID */ AND T3.DTA_DL_YN = 'N' WHERE T3.BASE_YM = #{param.baseYm} ) GROUP BY OG_ID )
SELECT #{param.baseYm} AS BASE_YM /* 기준년월 */ , #{param.evlOgTpCd} AS EVL_OG_TP_CD /* 평가조직유형코드 */ , OG_ID , #{param.evlDvCd} AS EVL_DV_CD /* 평가구분코드 */ , #{param.evlAtcDvCd} AS EVL_ATC_DV_CD /* 평가항목구분코드 */ , #{param.evlBaseUnitN} AS EVL_BASE_UNIT_N /* 평가기준단위수 */ , #{param.cvtBasePc} AS CVT_BASE_PC /* 환산기준점수 */ , #{param.dtaDlYn} AS DTA_DL_YN /* 데이터삭제여부 */ , ( SELECT NVL(MAX(SORT_ODR), 0) + 1 FROM TB_PSEV_AWD_EVL_BASE_DTL WHERE EVL_OG_TP_CD = #{param.evlOgTpCd} AND EVL_DV_CD = #{param.evlDvCd} ) AS SORT_ODR
FROM TARGET_LIST T1

mapper.delete:
DELETE FROM TB_PSEV_AWD_EVL_BASE_DTL
 WHERE BASE_YM = #{baseYm}
   AND EVL_OG_TP_CD = #{evlOgTpCd}
   AND EVL_DV_CD = #{evlDvCd}
   AND EVL_ATC_DV_CD = #{evlAtcDvCd}

mapper.update:
UPDATE T_CMS_DDLN_D
   SET START_DT = #{startDt}
     , START_HM = #{startHm}
     , FINS_DT = #{finsDt}
     , FINS_HM = #{finsHm}

 WHERE DDLN_ID = #{ddlnId}
   AND BAS_YRMN = #{basYrmn}
   AND DDLN_DV_ID = 'DLD_'||#{ogTpCd}||'_EVL_PERF_CL'
   AND TENANT_ID = #{session.tenantId}

mapper.insert:
INSERT INTO T_CMS_DDLN_D
     ( TENANT_ID
     , DDLN_ID
     , DDLN_DV_ID
     , BAS_YRMN
     , START_DT
     , START_HM
     , FINS_DT
     , FINS_HM
     , DEL_YN
      )
VALUES (
     #{session.tenantId}
     , (SELECT 'DLN_'||LPAD(ROWNUM + (SELECT MAX(SUBSTR(DDLN_ID, 5, 10)) FROM T_CMS_DDLN_D WHERE TENANT_ID = #{session.tenantId}), 5, '0') FROM DUAL)
     , 'DLD_'||#{ogTpCd}||'_EVL_PERF_CL'
     , #{basYrmn}
     , #{startDt}
     , #{startHm}
     , #{finsDt}
     , #{finsHm}
     , 'N'
      )
