mapper.select:
SELECT EVL_OG_TP_CD /* 조직유형코드 */ , EVL_DV_CD , F_CMZ_CD_NM(#{session.tenantId}, 'EVL_DV_CD', EVL_DV_CD) AS EVL_DV_NM , CTST_GRP_CD /* 경진그룹코드 */ , VL_STRTDT /* 유효시작일자 */ , VL_ENDDT /* 유효종료일자 */ , CTST_GRP_NM /* 경진그룹명 */ , UNUITM_CN /* 특이사항내용 */ , DTA_DL_YN /* 데이터삭제여부 */ , FST_RGST_DTM /* 최초등록일시 */ , FST_RGST_USR_ID /* 최초등록사용자ID */ , FST_RGST_PRG_ID /* 최초등록프로그램ID */ , FST_RGST_DEPT_ID /* 최초등록부서ID */ , FNL_MDFC_DTM /* 최종수정일시 */ , FNL_MDFC_USR_ID /* 최종수정사용자ID */ , FNL_MDFC_PRG_ID /* 최종수정프로그램ID */ , FNL_MDFC_DEPT_ID /* 최종수정부서ID */ FROM TB_PSEV_CTST_GRP_CD_MNGT_BAS /* 경진그룹코드관리기본 */ WHERE 1 = 1
AND EVL_OG_TP_CD = #{evlOgTpCd} /* 조직유형코드 */
AND EVL_DV_CD = #{evlDvCd}
AND CTST_GRP_CD = #{ctstGrpCd}
AND TO_CHAR(SYSDATE,'YYYYMMDD') BETWEEN VL_STRTDT AND VL_ENDDT

WITH TARGET_LIST AS ( SELECT PRTNR_NO FROM (
${item.calfCn}
${item.cexpCn}
) GROUP BY PRTNR_NO )
SELECT T2.BASE_YM , T2.OG_TP_CD AS EVL_OG_TP_CD , T2.PRTNR_NO , T2.PRTNR_KNM , T2.OG_CD , T2.OG_NM , CASE #{param.evlDvCd} WHEN 'M01' THEN T3.DGR1_LEVL_OG_ID WHEN 'M02' THEN T3.DGR2_LEVL_OG_ID WHEN 'M03' THEN T3.DGR3_LEVL_OG_ID ELSE T3.DGR1_LEVL_OG_ID END AS OG_ID , #{param.evlDvCd} AS EVL_DV_CD , ( SELECT MAX(EVL_RFLT_DTM) FROM TB_PSEV_AWD_EVL_IZ S1 WHERE S1.PRTNR_NO = T1.PRTNR_NO AND S1.EVL_OG_TP_CD = T2.OG_TP_CD AND S1.EVL_DV_CD = #{param.evlDvCd} AND S1.BASE_YM = T2.BASE_YM AND S1.DTA_DL_YN = 'N' ) AS EVL_RFLT_DTM
, NVL(( SELECT S1.AWD_BASE_CALC_PC FROM TB_PSEV_AWD_EVL_IZ S1 WHERE S1.PRTNR_NO = T1.PRTNR_NO AND S1.EVL_OG_TP_CD = T2.OG_TP_CD AND S1.BASE_YM = T2.BASE_YM AND S1.EVL_DV_CD = #{param.evlDvCd} AND S1.EVL_ATC_DV_CD = #{item.evlAtcDvCd} AND S1.DTA_DL_YN = 'N'
AND S1.CNTR_PERF_DV_CD = #{param.cntrPerfDvCd}
), 0) AS ${item.fieldName}
, NVL(( SELECT TRG_BASE_PC FROM TB_PSEV_AWD_EVL_IZ S1 WHERE S1.PRTNR_NO = T1.PRTNR_NO AND S1.EVL_OG_TP_CD = T2.OG_TP_CD AND S1.BASE_YM = T2.BASE_YM AND S1.EVL_DV_CD = #{param.evlDvCd} AND S1.EVL_ATC_DV_CD = #{item.evlAtcDvCd} AND S1.DTA_DL_YN = 'N'
AND S1.CNTR_PERF_DV_CD = #{param.cntrPerfDvCd}
), 0) AS ${item.fieldName}
, NVL(( SELECT (S1.AWD_BASE_CALC_PC / TRG_BASE_PC) * 100 FROM TB_PSEV_AWD_EVL_IZ S1 WHERE S1.PRTNR_NO = T1.PRTNR_NO AND S1.EVL_OG_TP_CD = T2.OG_TP_CD AND S1.BASE_YM = T2.BASE_YM AND S1.EVL_DV_CD = #{param.evlDvCd} AND S1.EVL_ATC_DV_CD = #{item.evlAtcDvCd} AND S1.DTA_DL_YN = 'N'
AND S1.CNTR_PERF_DV_CD = #{param.cntrPerfDvCd}
), 0) AS ${item.fieldName}
, ${item.dataType} AS ${item.fieldName}
FROM TARGET_LIST T1 INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 ON T2.PRTNR_NO = T1.PRTNR_NO AND T2.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_OG_IZ T3 ON T3.BASE_YM = T2.BASE_YM /* 기준년월 */ AND T3.OG_TP_CD = T2.OG_TP_CD /* 조직유형코드 */ AND T3.OG_ID = T2.OG_ID /* 조직ID */ AND T3.DTA_DL_YN = 'N' WHERE 1=1 AND T2.BASE_YM = #{param.baseYm} ORDER BY T2.OG_CD, T1.PRTNR_NO
SELECT T2.BASE_YM , T2.OG_TP_CD AS EVL_OG_TP_CD , T2.PRTNR_NO , T2.PRTNR_KNM , T2.OG_CD , T2.OG_NM , CASE #{param.evlDvCd} WHEN 'M01' THEN T3.DGR1_LEVL_OG_ID WHEN 'M02' THEN T3.DGR2_LEVL_OG_ID WHEN 'M03' THEN T3.DGR3_LEVL_OG_ID ELSE T3.DGR1_LEVL_OG_ID END AS OG_ID , #{param.evlDvCd} AS EVL_DV_CD , ( SELECT MAX(EVL_RFLT_DTM) FROM TB_PSEV_AWD_EVL_IZ S1 WHERE S1.PRTNR_NO = T1.PRTNR_NO AND S1.EVL_OG_TP_CD = T2.OG_TP_CD AND S1.BASE_YM = T2.BASE_YM AND S1.DTA_DL_YN = 'N' ) AS EVL_RFLT_DTM
, NVL(( SELECT S1.AWD_BASE_CALC_PC FROM TB_PSEV_AWD_EVL_IZ S1 WHERE S1.PRTNR_NO = T1.PRTNR_NO AND S1.EVL_OG_TP_CD = T2.OG_TP_CD AND S1.BASE_YM = T2.BASE_YM AND S1.EVL_ATC_DV_CD = #{item.evlAtcDvCd} AND S1.DTA_DL_YN = 'N'
AND S1.CNTR_PERF_DV_CD = #{param.cntrPerfDvCd}
), 0) AS ${item.fieldName}
, NVL(( SELECT TRG_BASE_PC FROM TB_PSEV_AWD_EVL_IZ S1 WHERE S1.PRTNR_NO = T1.PRTNR_NO AND S1.EVL_OG_TP_CD = T2.OG_TP_CD AND S1.BASE_YM = T2.BASE_YM AND S1.EVL_ATC_DV_CD = #{item.evlAtcDvCd} AND S1.DTA_DL_YN = 'N'
AND S1.CNTR_PERF_DV_CD = #{param.cntrPerfDvCd}
), 0) AS ${item.fieldName}
, NVL(( SELECT (S1.AWD_BASE_CALC_PC / TRG_BASE_PC) * 100 FROM TB_PSEV_AWD_EVL_IZ S1 WHERE S1.PRTNR_NO = T1.PRTNR_NO AND S1.EVL_OG_TP_CD = T2.OG_TP_CD AND S1.BASE_YM = T2.BASE_YM AND S1.EVL_ATC_DV_CD = #{item.evlAtcDvCd} AND S1.DTA_DL_YN = 'N'
AND S1.CNTR_PERF_DV_CD = #{param.cntrPerfDvCd}
), 0) AS ${item.fieldName}
, ${item.dataType} AS ${item.fieldName}
FROM TARGET_LIST T1 INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 ON T2.PRTNR_NO = T1.PRTNR_NO AND T2.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_OG_IZ T3 ON T3.BASE_YM = T2.BASE_YM /* 기준년월 */ AND T3.OG_TP_CD = T2.OG_TP_CD /* 조직유형코드 */ AND T3.OG_ID = T2.OG_ID /* 조직ID */ AND T3.DTA_DL_YN = 'N' WHERE 1=1 AND T2.BASE_YM = #{param.baseYm} AND T2.OG_TP_CD = #{param.evlOgTpCd}
AND EXISTS ( SELECT 1 FROM TB_PSEV_AWD_EVL_BASE_DTL WHERE CTST_GRP_CD = #{param.ctstGrpCd} AND DTA_DL_YN = 'N' AND OG_ID = CASE 'M04' WHEN 'M01' THEN T3.DGR1_LEVL_OG_ID WHEN 'M02' THEN T3.DGR2_LEVL_OG_ID WHEN 'M03' THEN T3.DGR3_LEVL_OG_ID ELSE T3.DGR1_LEVL_OG_ID END )
ORDER BY T2.OG_CD, T1.PRTNR_NO

mapper.update:
UPDATE TB_PSEV_AWD_EVL_IZ
   SET TRG_BASE_PC = #{trgBasePc}

 WHERE BASE_YM = #{baseYm}
   AND EVL_OG_TP_CD = #{evlOgTpCd}
   AND EVL_DV_CD = #{evlDvCd}
   AND EVL_ATC_DV_CD = UPPER(#{evlAtcDvCd})
   AND OG_ID = #{ogId}
   AND PRTNR_NO = #{prtnrNo}

mapper.select:
SELECT BASE_YM , EVL_OG_TP_CD , EVL_DV_CD , EVL_DV_NM , OG_ID , OG_NM , CTST_GRP_CD , EVL_RSB_DV_CD , EVL_RSB_DV_NM FROM ( SELECT T1.BASE_YM , T1.EVL_OG_TP_CD , T1.EVL_DV_CD , F_CMZ_CD_NM(#{session.tenantId}, 'EVL_DV_CD', T1.EVL_DV_CD) AS EVL_DV_NM , T1.EVL_ATC_DV_CD , F_CMZ_CD_NM(#{session.tenantId}, 'EVL_ATC_DV_CD', T1.EVL_ATC_DV_CD) AS EVL_ATC_DV_NM , T1.OG_ID , T3.OG_NM , T1.CTST_GRP_CD , T2.EVL_RSB_DV_CD , F_CMZ_CD_NM(#{session.tenantId}, 'EVL_RSB_DV_CD', T2.EVL_RSB_DV_CD) AS EVL_RSB_DV_NM FROM TB_PSEV_AWD_EVL_BASE_DTL T1 INNER JOIN TB_PSEV_AWD_EVL_RSB_REL T2 ON T2.EVL_OG_TP_CD = T1.EVL_OG_TP_CD AND T2.EVL_RSB_REL_ID = (T1.EVL_OG_TP_CD||EVL_DV_CD||LPAD('1', 5, '0')) AND T2.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_OG_IZ T3 ON T3.OG_ID = T1.OG_ID AND T3.BASE_YM = T1.BASE_YM WHERE T1.DTA_DL_YN = 'N'
AND T1.EVL_OG_TP_CD = #{evlOgTpCd}
AND T1.EVL_DV_CD = #{evlDvCd}
AND T2.EVL_RSB_DV_CD = #{evlRsbDvCd}
AND T1.CTST_GRP_CD = #{ctstGrpCd}
AND T1.BASE_YM = #{baseYm}
) GROUP BY BASE_YM , EVL_OG_TP_CD , EVL_DV_CD , EVL_DV_NM , OG_ID , OG_NM , CTST_GRP_CD , EVL_RSB_DV_CD , EVL_RSB_DV_NM

mapper.update:
UPDATE TB_PSEV_AWD_EVL_BASE_DTL
   SET CTST_GRP_CD = #{ctstGrpCd}

 WHERE BASE_YM = #{baseYm}
   AND EVL_OG_TP_CD = #{evlOgTpCd}
   AND EVL_DV_CD = #{evlDvCd}
   AND OG_ID = #{ogId}
