INSERT INTO TB_PSEV_AWD_ERN_WHTX_IZ (  /* 시상소득원천세내역 */
       PERF_YM          /* 실적년월 */
     , OG_TP_CD         /* 조직유형코드 */
     , PRTNR_NO         /* 파트너번호 */
     , AWD_DV_CD        /* 시상구분코드 */
     , AWD_PERF_SN      /* 시상실적일련번호 */
     , AWD_NM           /* 시상명 */
     , AWD_INTBS_AMT    /* 시상소득과표금액 */
     , AWD_ERN_WHTX     /* 시상소득원천세 */
     , DTA_DL_YN        /* 데이터삭제여부 */
     )
VALUES (
       #{perfYm}
     , #{ogTpCd}
     , #{prtnrNo}
     , #{awdDvCd}
     , (
        SELECT NVL(MAX(AWD_PERF_SN), 0) + 1
          FROM TB_PSEV_AWD_ERN_WHTX_IZ
         WHERE PERF_YM = #{perfYm}
           AND OG_TP_CD = #{ogTpCd}
           AND PRTNR_NO = #{prtnrNo}
           AND AWD_DV_CD = #{awdDvCd}
         )
     , #{awdNm}
     , #{awdIntbsAmt}
     , #{awdErnWhtx}
     , 'N'
      )
