mapper.select:
SELECT T1.LECTR_SPPT_APLC_ID /* 강의지원신청ID */ , T1.LECTR_SPPT_OG_TP_CD /* 강의지원조직유형코드 */ , T1.LECTR_YM /* 강의년월 */ , T1.LECTR_TCNT /* 강의차수 */ , T1.LECTR_SPPT_LECT_CD /* 강의지원강사코드 */ , T1.LECTR_SPPT_LECTR_CD /* 강의지원강의코드 */ , T1.LECTR_DT /* 강의일자 */ , T1.OG_ID /* 조직ID */ , T1.BLD_CD /* 빌딩코드 */ , T1.LECTR_APLC_USR_ID /* 강의신청사용자ID */ , T1.DTA_DL_YN /* 데이터삭제여부 */ , T1.FST_RGST_DTM /* 최초등록일시 */ , T1.FST_RGST_USR_ID /* 최초등록사용자ID */ , T1.FST_RGST_PRG_ID /* 최초등록프로그램ID */ , T1.FST_RGST_DEPT_ID /* 최초등록부서ID */ , T1.FNL_MDFC_DTM /* 최종수정일시 */ , T1.FNL_MDFC_USR_ID /* 최종수정사용자ID */ , T1.FNL_MDFC_PRG_ID /* 최종수정프로그램ID */ , T1.FNL_MDFC_DEPT_ID /* 최종수정부서ID */ FROM TB_PSCA_LECTR_APLC_IZ T1 /* 강의신청내역 */ INNER JOIN ( SELECT S1.DGR1_LEVL_OG_ID , S1.DGR2_LEVL_OG_ID , S1.BASE_YM FROM TB_OGBS_MM_OG_IZ S1 WHERE 1=1
AND S1.BASE_YM = #{lectrYm}
AND S1.OG_TP_CD = #{lectrSpptOgTpCd}
AND S1.DGR1_LEVL_OG_ID = #{ogLevlDvCd1}
AND S1.DGR2_LEVL_OG_ID = #{ogLevlDvCd2}
GROUP BY S1.DGR1_LEVL_OG_ID, S1.DGR2_LEVL_OG_ID, S1.BASE_YM )T2 ON T1.OG_ID = T2.DGR2_LEVL_OG_ID AND T1.LECTR_YM = T2.BASE_YM WHERE 1 = 1
AND LECTR_SPPT_OG_TP_CD = #{lectrSpptOgTpCd} /* 강의지원조직유형코드 */
AND LECTR_YM = #{lectrYm} /* 강의년월 */
AND T1.DTA_DL_YN = 'N' ORDER BY LECTR_SPPT_LECTR_CD ASC

SELECT A.BLD_CD, A.BLD_NM FROM TB_OGBS_MM_OG_IZ A INNER JOIN TB_OGBS_MM_PRTNR_IZ B ON B.BASE_YM = A.BASE_YM AND B.OG_TP_CD = A.OG_TP_CD AND B.PRTNR_NO = #{session.employeeIDNumber} WHERE 1=1
AND A.OG_TP_CD = #{ogTpCd}
AND A.BASE_YM = #{baseYm}
START WITH (A.OG_ID = B.OG_ID OR A.HGR_OG_ID = B.OG_ID) CONNECT BY PRIOR A.BASE_YM = B.BASE_YM AND A.HGR_OG_ID = A.OG_ID GROUP BY A.BLD_CD, A.BLD_NM ORDER BY BLD_CD ASC

mapper.insert:

    SELECT 'PSC'||TO_CHAR(SYSDATE, 'YYYY')||LPAD(NVL(SUBSTR(MAX(LECTR_SPPT_APLC_ID), 8, 15), 0) + 1, 7, '0') AS LECTR_SPPT_APLC_ID
      FROM TB_PSCA_LECTR_APLC_IZ
     WHERE 1=1
       AND LECTR_SPPT_APLC_ID LIKE 'PSC'||TO_CHAR(SYSDATE, 'YYYY')||'%'


INSERT INTO TB_PSCA_LECTR_APLC_IZ (  /* 강의신청내역 */
       LECTR_SPPT_APLC_ID  /* 강의지원신청ID */
     , LECTR_SPPT_OG_TP_CD /* 강의지원조직유형코드 */
     , LECTR_YM            /* 강의년월 */
     , LECTR_TCNT          /* 강의차수 */
     , LECTR_SPPT_LECT_CD  /* 강의지원강사코드 */
     , LECTR_SPPT_LECTR_CD /* 강의지원강의코드 */
     , LECTR_DT            /* 강의일자 */
     , OG_ID               /* 조직ID */
     , BLD_CD              /* 빌딩코드 */
     , LECTR_APLC_USR_ID   /* 강의신청사용자ID */
     , DTA_DL_YN           /* 데이터삭제여부 */
     )
VALUES (
       (
         SELECT 'PSC'||TO_CHAR(SYSDATE, 'YYYY')||LPAD(NVL(SUBSTR(MAX(LECTR_SPPT_APLC_ID), 8, 15), 0) + 1, 7, '0') AS LECTR_SPPT_APLC_ID
          FROM TB_PSCA_LECTR_APLC_IZ
         WHERE 1=1
           AND LECTR_SPPT_APLC_ID LIKE 'PSC'||TO_CHAR(SYSDATE, 'YYYY')||'%'
        )
     , #{lectrSpptOgTpCd}
     , #{lectrYm}
     , #{lectrTCnt}
     , #{lectrSpptLectCd}
     , #{lectrSpptLectrCd}
     , #{lectrDt}
     , ( SELECT DGR2_LEVL_OG_ID
           FROM TB_OGBS_MM_OG_IZ
          WHERE BASE_YM = #{lectrYm}
            AND BLD_CD = #{bldCd}
          GROUP BY DGR2_LEVL_OG_ID
       )
     , #{bldCd}
     , #{session.employeeIDNumber}
     , 'N'
      )
