mapper.select:
SELECT PRBAI.RPOT_BIZ_APLC_ID /* 보고서업무신청ID */ , PRBAI.RPOT_BIZ_TP_ID /* 보고서업무유형ID */ , PRBAI.APLCNS_PRTNR_NO /* 신청자파트너번호 */ , TO_CHAR(TO_DATE(PRBAI.APLC_DTM, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') AS APLC_DTM /* 신청일시 */ , PRBAI.BIZ_AK_CN /* 업무요청내용 */ , PRBPI.PROCS_SN /* 처리일련번호 */ , PRBPI.RPOT_BIZ_PROCS_STAT_CD /* 보고서업무처리상태코드 */ , PRBPI.PROCS_CN /* 처리내용 */ , PCTCAI.BIZ_CRAL_TEL_CH_TP_CD /* 업무휴대전화변경유형코드 */ , TO_CHAR(TO_DATE(PCTCAI.CH_RQDT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') AS CH_RQDT /* 변경요청일자 */ , PCTCAI.SELL_PRTNR_NO /* 판매파트너번호 */ , PCTCAI.BFCH_CRAL_LOCARA_TNO /* 변경전휴대지역전화번호 */ , PCTCAI.BFCH_MEXNO_ENCR /* 변경전휴대전화국번호암호화 */ , PCTCAI.BFCH_CRAL_IDV_TNO /* 변경전휴대개별전화번호 */ , PCTCAI.AFCH_CRAL_LOCARA_TNO /* 변경후휴대지역전화번호 */ , PCTCAI.AFCH_MEXNO_ENCR /* 변경후휴대전화국번호암호화 */ , PCTCAI.AFCH_CRAL_IDV_TNO /* 변경후휴대개별전화번호 */ , OPB4.PRTNR_KNM AS SELL_PRTNR_NM , OMPI.RSB_DV_CD , F_CMZ_CD_NM('TNT_WELLS', 'RSB_DV_CD',OMPI.RSB_DV_CD, 'ko') AS RSB_DV_NM , PRBPB.PRTNR_NO , OPB.PRTNR_KNM /* 파트너 한글명 */ , OPB.CRAL_LOCARA_TNO /* 휴대지역전화번호 */ , OPB.MEXNO_ENCR /* 휴대전화국번호암호화 */ , OPB.CRAL_IDV_TNO /* 휴대개별전화번호 */ , DECODE(PRBPI.RPOT_BIZ_PROCS_STAT_CD, '30', TO_CHAR(TO_DATE(PRBPI.FNL_MDFC_DTM, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD'), '40', TO_CHAR(TO_DATE(PRBPI.FNL_MDFC_DTM, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD'), '') AS FNL_MDFC_DTM , DECODE(PRBPI.RPOT_BIZ_PROCS_STAT_CD, '30', CUAM.LOGIN_ID, '40', CUAM.LOGIN_ID, '') AS FNL_MDFC_USR_ID , DECODE(PRBPI.RPOT_BIZ_PROCS_STAT_CD, '30', CUAM.USR_NM, '40', CUAM.USR_NM, '') AS FNL_MDFC_USR_NM , F_CMZ_CD_NM(#{session.tenantId}, 'RPOT_BIZ_PROCS_STAT_CD', PRBPI.RPOT_BIZ_PROCS_STAT_CD) AS RPOT_BIZ_PROCS_STAT_NM , F_CMZ_CD_NM(#{session.tenantId}, 'BIZ_CRAL_TEL_CH_TP_CD', PCTCAI.BIZ_CRAL_TEL_CH_TP_CD) AS BIZ_CRAL_TEL_CH_TP_NM , CUAM.USR_NM AS PROCS_PRTNR_NM /* 파트너 한글명 */ , CUAM.CRAL_LOCARA_TNO AS PROCS_CRAL_LOCARA_TNO /* 휴대지역전화번호 */ , CUAM.MEXNO_GBENCR AS PROCS_MEXNO_ENCR /* 휴대전화국번호암호화 */ , CUAM.CRAL_IDV_TNO AS PROCS_CRAL_IDV_TNO /* 휴대개별전화번호 */ , PRBAI.APLCNS_PRTNR_NO AS RCST_PRTNR_NO , OPB3.PRTNR_KNM AS RCST_PRTNR_NM /* 파트너 한글명 */ , OPB3.CRAL_LOCARA_TNO AS RCST_CRAL_LOCARA_TNO /* 휴대지역전화번호 */ , OPB3.MEXNO_ENCR AS RCST_MEXNO_ENCR /* 휴대전화국번호암호화 */ , OPB3.CRAL_IDV_TNO AS RCST_CRAL_IDV_TNO /* 휴대개별전화번호 */ , OMOI.OG_CD , OMOI.OG_NM FROM TB_PSGA_RPOT_BIZ_APLC_IZ PRBAI /* 보고서업무신청내역 */ JOIN TB_PSGA_RPOT_BIZ_PROCS_IZ PRBPI /* 보고서업무처리내역 */ ON PRBAI.RPOT_BIZ_APLC_ID = PRBPI.RPOT_BIZ_APLC_ID AND PRBPI.DTA_DL_YN = 'N' JOIN TB_PSGA_CRAL_TEL_CH_APLC_IZ PCTCAI /* 휴대전화변경신청내역 */ ON PRBAI.RPOT_BIZ_APLC_ID = PCTCAI.RPOT_BIZ_APLC_ID AND PCTCAI.DTA_DL_YN = 'N' JOIN TB_PSGA_RPOT_BIZ_PSIC_BAS PRBPB /* 보고서업무담당자기본 */ ON PRBAI.RPOT_BIZ_TP_ID = PRBPB.RPOT_BIZ_TP_ID AND PRBPB.DG_YN = 'Y' JOIN TB_OGBS_PRTNR_BAS OPB ON PRBPB.OG_TP_CD = OPB.OG_TP_CD AND PRBPB.PRTNR_NO = OPB.PRTNR_NO LEFT JOIN T_CMP_USR_ACO_M CUAM ON CUAM.USR_ID = PRBPI.FNL_MDFC_USR_ID AND CUAM.DEL_YN = 'N' LEFT OUTER JOIN TB_OGBS_PRTNR_BAS OPB3 ON PRBAI.APLCNS_PRTNR_NO = OPB3.PRTNR_NO LEFT OUTER JOIN TB_OGBS_PRTNR_BAS OPB4 ON PCTCAI.SELL_PRTNR_NO = OPB4.PRTNR_NO LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ OMPI /* 월파트너내역 */ ON OPB4.PRTNR_NO = OMPI.PRTNR_NO AND OMPI.BASE_YM = TO_CHAR(SYSDATE, 'YYYYMM') LEFT OUTER JOIN TB_OGBS_MM_OG_IZ OMOI ON OPB4.OG_ID = OMOI.OG_ID AND OPB4.OG_TP_CD = OMOI.OG_TP_CD AND OMOI.BASE_YM = TO_CHAR(SYSDATE,'YYYYMM') WHERE 1 = 1 AND PRBAI.DTA_DL_YN = 'N'
AND PRBAI.APLC_DTM BETWEEN #{rcpStartDtm} || '000000' AND #{rcpEndDtm} || '235959'
AND PRBAI.RPOT_BIZ_TP_ID = #{rpotBizTpId}
AND PRBAI.RPOT_BIZ_APLC_ID = #{rpotBizAplcId}
AND PRBAI.APLCNS_PRTNR_NO = #{prtnrNo}
AND PRBAI.RPOT_BIZ_PROCS_STAT_CD = #{rpotBizProcsStatCd}
ORDER BY PRBAI.APLC_DTM DESC

mapper.insert:

    SELECT 'M' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(RPOT_BIZ_APLC_ID, 2))) + 1, 1), 14, '0')
      FROM TB_PSGA_RPOT_BIZ_APLC_IZ

INSERT INTO TB_PSGA_RPOT_BIZ_APLC_IZ (  /* 보고서업무신청내역 */
       RPOT_BIZ_APLC_ID /* 보고서업무신청ID */
     , RPOT_BIZ_TP_ID   /* 보고서업무유형ID */
     , APLCNS_PRTNR_NO  /* 신청자파트너번호 */
     , APLC_DTM         /* 신청일시 */
     , BIZ_AK_CN        /* 업무요청내용 */
     , APN_FILE_DOC_ID  /* 첨부파일문서ID */

     , BIZ_AK_BLD_CD    /* 업무요청빌딩코드 */
     , BIZ_AK_BLD_NM    /* 업무요청빌딩명 */

     )
VALUES (
       #{dvo.rpotBizAplcId}
     , #{dvo.rpotBizTpId}
     , #{dvo.rcstPrtnrNo}
     , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
     , #{dvo.bizAkCn}
     , #{dvo.rpotBizAplcId}

     , #{dvo.bizAkBldCd}
     , #{dvo.bizAkBldNm}

 )

INSERT INTO TB_PSGA_RPOT_BIZ_PROCS_IZ (  /* 보고서업무처리내역 */
       RPOT_BIZ_APLC_ID       /* 보고서업무신청ID */
     , PROCS_SN               /* 처리일련번호 */
     , RPOT_BIZ_PROCS_STAT_CD /* 보고서업무처리상태코드 */
     )
VALUES (
       #{rpotBizAplcId}
     , '1'
     , '10'  /*처리중*/
      )

INSERT INTO TB_PSGA_CRAL_TEL_CH_APLC_IZ ( /* 휴대전화변경신청내역 */
       RPOT_BIZ_APLC_ID      /* 보고서업무신청ID */
     , BIZ_CRAL_TEL_CH_TP_CD /* 업무휴대전화변경유형코드 */
     , CH_RQDT               /* 변경요청일자 */
     , SELL_PRTNR_NO         /* 판매파트너번호 */
     , BFCH_CRAL_LOCARA_TNO  /* 변경전휴대지역전화번호 */
     , BFCH_MEXNO_ENCR       /* 변경전휴대전화국번호암호화 */
     , BFCH_CRAL_IDV_TNO     /* 변경전휴대개별전화번호 */
     , AFCH_CRAL_LOCARA_TNO  /* 변경후휴대지역전화번호 */
     , AFCH_MEXNO_ENCR       /* 변경후휴대전화국번호암호화 */
     , AFCH_CRAL_IDV_TNO     /* 변경후휴대개별전화번호 */
     )
VALUES (
       #{rpotBizAplcId}
     , #{bizCralTelChTpCd}
     , #{chRqdt}
     , #{sellPrtnrNo}
     , #{bfchCralLocaraTno}
     , #{bfchMexnoEncr}
     , #{bfchCralIdvTno}
     , #{afchCralLocaraTno}
     , #{afchMexnoEncr}
     , #{afchCralIdvTno}
      )

mapper.update:
UPDATE TB_PSGA_RPOT_BIZ_PROCS_IZ   /* 보고서업무처리내역 */
   SET RPOT_BIZ_PROCS_STAT_CD = #{rpotBizProcsStatCd} /* 보고서업무처리상태코드 */
     , PROCS_CN = #{procsCn}               /* 처리내용 */

 WHERE RPOT_BIZ_APLC_ID = #{rpotBizAplcId}
   AND PROCS_SN = #{procsSn}
