mapper.select:
SELECT PRBPB.RPOT_BIZ_ASN_ID /* 보고서업무배정ID */ , PRBPB.RPOT_BIZ_TP_ID /* 보고서업무유형ID */ , PRBPB.DG_YN /* 대표여부 */ , PRBPB.OG_TP_CD /* 조직유형코드 */ , PRBPB.PRTNR_NO /* 파트너번호 */ , PRBPB.DTA_DL_YN /* 데이터삭제여부 */ , OPB.CRAL_LOCARA_TNO /* 휴대지역전화번호 */ , OPB.MEXNO_ENCR /* 휴대전화국번호암호화 */ , OPB.CRAL_IDV_TNO /* 휴대개별전화번호 */ , OMPI.PRTNR_KNM /* 파트너 한글명 */ , OMPI.OG_CD , OMPI.OG_NM , F_CMZ_CD_NM(#{session.tenantId}, 'OG_TP_CD', PRBPB.OG_TP_CD, 'ko') AS OG_TP_NM FROM TB_PSGA_RPOT_BIZ_PSIC_BAS PRBPB /* 보고서업무담당자기본 */ JOIN TB_PSGA_RPOT_BIZ_TP_BAS PRBTB/* 보고서업무유형기본 */ ON PRBPB.RPOT_BIZ_TP_ID = PRBTB.RPOT_BIZ_TP_ID AND PRBTB.RPOT_BIZ_TP_DV_CD = #{rpotBizTpDvCd} JOIN TB_OGBS_PRTNR_BAS OPB ON PRBPB.OG_TP_CD = OPB.OG_TP_CD AND PRBPB.PRTNR_NO = OPB.PRTNR_NO LEFT JOIN TB_OGBS_MM_PRTNR_IZ OMPI ON OMPI.BASE_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND PRBPB.OG_TP_CD = OMPI.OG_TP_CD AND PRBPB.PRTNR_NO = OMPI.PRTNR_NO WHERE 1 = 1 AND PRBPB.DTA_DL_YN = 'N'
AND OMPI.OG_CD = #{ogCd}
AND PRBPB.RPOT_BIZ_TP_ID = #{rpotBizTpId}
AND PRBPB.DG_YN = #{dgYn}

mapper.update:
MERGE INTO TB_PSGA_RPOT_BIZ_PSIC_BAS T1 /* 보고서업무담당자기본 */ USING ( SELECT #{rpotBizAsnId} AS RPOT_BIZ_ASN_ID /* 보고서업무배정ID */ , #{rpotBizTpId} AS RPOT_BIZ_TP_ID /* 보고서업무유형ID */ , #{dgYn} AS DG_YN /* 대표여부 */ , #{ogTpCd} AS OG_TP_CD /* 조직유형코드 */ , #{prtnrNo} AS PRTNR_NO /* 파트너번호 */ FROM DUAL ) T2 ON (T1.RPOT_BIZ_ASN_ID = T2.RPOT_BIZ_ASN_ID /* 보고서업무배정ID */ ) WHEN MATCHED THEN UPDATE SET RPOT_BIZ_TP_ID = T2.RPOT_BIZ_TP_ID , DG_YN = T2.DG_YN , OG_TP_CD = T2.OG_TP_CD , PRTNR_NO = T2.PRTNR_NO
WHEN NOT MATCHED THEN INSERT ( RPOT_BIZ_ASN_ID , RPOT_BIZ_TP_ID , DG_YN , OG_TP_CD , PRTNR_NO , DTA_DL_YN
) VALUES ( (SELECT 'M' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(RPOT_BIZ_ASN_ID,2))) + 1, 1), 14, '0') FROM TB_PSGA_RPOT_BIZ_PSIC_BAS) , T2.RPOT_BIZ_TP_ID , T2.DG_YN , T2.OG_TP_CD , T2.PRTNR_NO , 'N' )
