mapper.select:
SELECT PRBAI.RPOT_BIZ_APLC_ID /* 보고서업무신청ID */ , PRBAI.RPOT_BIZ_TP_ID /* 보고서업무유형ID */ , PRBAI.APLCNS_PRTNR_NO /* 신청자파트너번호 */ , TO_CHAR(TO_DATE(PRBAI.APLC_DTM, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') AS APLC_DTM /* 신청일시 */ , PRBAI.BIZ_AK_CN /* 업무요청내용 */ , PRBAI.APN_FILE_DOC_ID /* 첨부파일문서ID */ , PRBAI.BIZ_AK_BLD_CD /* 업무요청빌딩코드 */ , PRBAI.BIZ_AK_BLD_NM /* 업무요청빌딩명 */ , PRBPI.PROCS_SN /* 처리일련번호 */ , PRBPI.RPOT_BIZ_PROCS_STAT_CD /* 보고서업무처리상태코드 */ , PRBPI.PROCS_CN /* 처리내용 */ , PRAI.BLD_CD /* 빌딩코드 */ , PRAI.BLD_NM /* 빌딩명 */ , PRAI.RNT_APLC_TP_CD /* 임차신청유형코드 */ , PRBPB.PRTNR_NO , OPB.PRTNR_KNM /* 파트너 한글명 */ , OPB.CRAL_LOCARA_TNO /* 휴대지역전화번호 */ , OPB.MEXNO_ENCR /* 휴대전화국번호암호화 */ , OPB.CRAL_IDV_TNO /* 휴대개별전화번호 */ , DECODE(PRBPI.RPOT_BIZ_PROCS_STAT_CD, '30', TO_CHAR(TO_DATE(PRBPI.FNL_MDFC_DTM, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD'), '40', TO_CHAR(TO_DATE(PRBPI.FNL_MDFC_DTM, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD'), '') AS FNL_MDFC_DTM , DECODE(PRBPI.RPOT_BIZ_PROCS_STAT_CD, '30', CUAM.LOGIN_ID, '40', CUAM.LOGIN_ID, '') AS FNL_MDFC_USR_ID , DECODE(PRBPI.RPOT_BIZ_PROCS_STAT_CD, '30', CUAM.USR_NM, '40', CUAM.USR_NM, '') AS FNL_MDFC_USR_NM , F_CMZ_CD_NM(#{session.tenantId}, 'RPOT_BIZ_PROCS_STAT_CD', PRBPI.RPOT_BIZ_PROCS_STAT_CD) AS RPOT_BIZ_PROCS_STAT_NM , F_CMZ_CD_NM(#{session.tenantId}, 'RNT_APLC_TP_CD', PRAI.RNT_APLC_TP_CD) AS RNT_APLC_TP_NM , CUAM.USR_NM AS PROCS_PRTNR_KNM /* 파트너 한글명 */ , CUAM.CRAL_LOCARA_TNO AS PROCS_CRAL_LOCARA_TNO /* 휴대지역전화번호 */ , CUAM.MEXNO_GBENCR AS PROCS_MEXNO_ENCR /* 휴대전화국번호암호화 */ , CUAM.CRAL_IDV_TNO AS PROCS_CRAL_IDV_TNO /* 휴대개별전화번호 */ , PRBAI.APLCNS_PRTNR_NO AS RCST_PRTNR_NO /* 접수자 번호 */ , OPB3.PRTNR_KNM AS RCST_PRTNR_NM /* 파트너 한글명 */ , OPB3.CRAL_LOCARA_TNO AS RCST_CRAL_LOCARA_TNO /* 휴대지역전화번호 */ , OPB3.MEXNO_ENCR AS RCST_MEXNO_ENCR /* 휴대전화국번호암호화 */ , OPB3.CRAL_IDV_TNO AS RCST_CRAL_IDV_TNO /* 휴대개별전화번호 */ FROM TB_PSGA_RPOT_BIZ_APLC_IZ PRBAI /* 보고서업무신청내역 */ LEFT JOIN TB_PSGA_RPOT_BIZ_PROCS_IZ PRBPI /* 보고서업무처리내역 */ ON PRBAI.RPOT_BIZ_APLC_ID = PRBPI.RPOT_BIZ_APLC_ID AND PRBPI.DTA_DL_YN = 'N' JOIN TB_PSGA_RNT_APLC_IZ PRAI /* 임차신청내역 */ ON PRBAI.RPOT_BIZ_APLC_ID = PRAI.RPOT_BIZ_APLC_ID AND PRAI.DTA_DL_YN = 'N' JOIN TB_PSGA_RPOT_BIZ_PSIC_BAS PRBPB /* 보고서업무담당자기본 */ ON PRBAI.RPOT_BIZ_TP_ID = PRBPB.RPOT_BIZ_TP_ID AND PRBPB.DG_YN = 'Y' JOIN TB_OGBS_PRTNR_BAS OPB ON PRBPB.OG_TP_CD = OPB.OG_TP_CD AND PRBPB.PRTNR_NO = OPB.PRTNR_NO LEFT JOIN T_CMP_USR_ACO_M CUAM ON CUAM.USR_ID = PRBPI.FNL_MDFC_USR_ID AND CUAM.DEL_YN = 'N' LEFT OUTER JOIN TB_OGBS_PRTNR_BAS OPB3 ON PRBAI.APLCNS_PRTNR_NO = OPB3.PRTNR_NO WHERE 1 = 1 AND PRBAI.DTA_DL_YN = 'N'
AND PRBAI.APLC_DTM BETWEEN #{rcpStartDtm} || '000000' AND #{rcpEndDtm} || '235959'
AND PRBAI.RPOT_BIZ_TP_ID = #{rpotBizTpId}
AND PRBAI.RPOT_BIZ_APLC_ID = #{rpotBizAplcId}
AND PRBAI.RNT_APLC_TP_CD = #{rntAplcTpCd}
AND PRBAI.RPOT_BIZ_PROCS_STAT_CD = #{rpotBizProcsStatCd}
ORDER BY PRBAI.APLC_DTM DESC

mapper.insert:

    SELECT 'M' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(RPOT_BIZ_APLC_ID, 2))) + 1, 1), 14, '0')
      FROM TB_PSGA_RPOT_BIZ_APLC_IZ

INSERT INTO TB_PSGA_RPOT_BIZ_APLC_IZ (  /* 보고서업무신청내역 */
       RPOT_BIZ_APLC_ID /* 보고서업무신청ID */
     , RPOT_BIZ_TP_ID   /* 보고서업무유형ID */
     , APLCNS_PRTNR_NO  /* 신청자파트너번호 */
     , APLC_DTM         /* 신청일시 */
     , BIZ_AK_CN        /* 업무요청내용 */
     , APN_FILE_DOC_ID  /* 첨부파일문서ID */
     , BIZ_AK_BLD_CD    /* 업무요청빌딩코드 */
     , BIZ_AK_BLD_NM    /* 업무요청빌딩명 */
     )
VALUES (
       #{dvo.rpotBizAplcId}
     , #{dvo.rpotBizTpId}
     , #{dvo.rcstPrtnrNo}
     , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
     , #{dvo.bizAkCn}
     , #{dvo.rpotBizAplcId}
     , #{dvo.bizAkBldCd}
     , #{dvo.bizAkBldNm}
 )

INSERT INTO TB_PSGA_RPOT_BIZ_PROCS_IZ (  /* 보고서업무처리내역 */
       RPOT_BIZ_APLC_ID       /* 보고서업무신청ID */
     , PROCS_SN               /* 처리일련번호 */
     , RPOT_BIZ_PROCS_STAT_CD /* 보고서업무처리상태코드 */
     )
VALUES (
       #{rpotBizAplcId}
     , '1'
     , '10'  /*처리중*/
      )

INSERT INTO TB_PSGA_RNT_APLC_IZ (  /* 임차신청내역 */
       RPOT_BIZ_APLC_ID /* 보고서업무신청ID */
     , BLD_CD           /* 빌딩코드 */
     , BLD_NM           /* 빌딩명 */
     , RNT_APLC_TP_CD   /* 임차신청유형코드 */
     )
VALUES (
       #{rpotBizAplcId}
     , #{bizAkBldCd}
     , #{bizAkBldNm}
     , #{rntAplcTpCd}
      )

mapper.update:
UPDATE TB_PSGA_RPOT_BIZ_PROCS_IZ   /* 보고서업무처리내역 */
   SET RPOT_BIZ_PROCS_STAT_CD = #{rpotBizProcsStatCd} /* 보고서업무처리상태코드 */
     , PROCS_CN = #{procsCn}               /* 처리내용 */

 WHERE RPOT_BIZ_APLC_ID = #{rpotBizAplcId}
   AND PROCS_SN = #{procsSn}
