mapper.select:
SELECT T1.VOC_BIZ_ASN_ID /* VOC업무배정ID */ , T1.VOC_BIZ_TP_DTL_CD /* VOC업무유형상세코드 */ , T1.DG_YN /* 대표여부 */ , T1.OG_TP_CD /* 조직유형코드 */ , T1.PRTNR_NO /* 파트너번호 */ , T2.PRTNR_KNM /* 파트너한글명 */ , T2.OG_NM /* 조직명 */ , T2.CRAL_LOCARA_TNO /* 휴대지역전화번호 */ , T2.MEXNO_ENCR /* 휴대전화국번호암호화 */ , T2.CRAL_IDV_TNO /* 휴대개별전화번호 */ FROM TB_PSGA_VOC_BIZ_PSIC_BAS T1 /* VOC업무담당자기본 */ INNER JOIN TB_OGBS_PRTNR_BAS T2 /* 파트너기본 */ ON T1.PRTNR_NO = T2.PRTNR_NO WHERE 1 = 1 AND T1.DTA_DL_YN = 'N'
AND T1.VOC_BIZ_TP_DTL_CD LIKE SUBSTR(#{vocBizTpCd}, 0, 1) || '%'
AND T1.VOC_BIZ_TP_DTL_CD LIKE '2' || #{ipvmProsTpCd}
AND T1.VOC_BIZ_TP_DTL_CD LIKE '4' || #{mattVocDvTpCd}
AND T2.OG_CD = #{ichrDeptId}
ORDER BY T1.VOC_BIZ_ASN_ID DESC

mapper.insert:
INSERT INTO TB_PSGA_VOC_BIZ_PSIC_BAS (  /* VOC업무담당자기본 */
       VOC_BIZ_ASN_ID    /* VOC업무배정ID */
     , VOC_BIZ_TP_DTL_CD /* VOC업무유형상세코드 */
     , DG_YN             /* 대표여부 */
     , OG_TP_CD          /* 조직유형코드 */
     , PRTNR_NO          /* 파트너번호 */
     , DTA_DL_YN         /* 데이터삭제여부 */
     )
    VALUES (
      (SELECT 'M' || LPAD(NVL(MAX(SUBSTR(VOC_BIZ_ASN_ID, 2, 14)), 0) + 1, 14, 0) FROM TB_PSGA_VOC_BIZ_PSIC_BAS)
    , #{vocBizTpDtlCd}
    , #{dgYn}
    , #{ogTpCd}
    , #{prtnrNo}
    , 'N'
     )
