mapper.select:
SELECT '1' AS CST_DV
     , CST_NO
     , MAX(ITG_CST_NO) AS ITG_CST_NO
     , MAX(CST_KNM) AS CST_KNM
     , MAX(CRAL_LOCARA_TNO) AS CRAL_LOCARA_TNO
     , MAX(MEXNO_ENCR) AS MEXNO
     , MAX(CRAL_IDV_TNO) AS CRAL_IDV_TNO
     , MAX(LOCARA_TNO) AS LOCARA_TNO
     , MAX(EXNO_ENCR) AS EXNO
     , MAX(IDV_TNO) AS IDV_TNO
     , MAX(EMADR) AS EMADR
     , MAX(BRYY_MMDD) AS BRYY_MMDD
     , MAX(SEX_DV_CD) AS SEX_DV_CD
  FROM (SELECT CST_NO, CST_KNM, ITG_CST_NO, EMADR, BRYY_MMDD, SEX_DV_CD
             , CRAL_LOCARA_TNO, MEXNO_ENCR, CRAL_IDV_TNO
             , '' AS LOCARA_TNO, '' AS EXNO_ENCR, '' AS IDV_TNO
          FROM TB_CUBS_CST_BAS
         WHERE DTA_DL_YN = 'N'
           AND CST_NO = #{CST_NO}
           AND CST_KNM = #{CST_NM}

               AND ( 1=1
               AND CRAL_LOCARA_TNO = #{CRAL_LOCARA_TNO}
               AND MEXNO_ENCR = #{MEXNO}
               AND CRAL_IDV_TNO = #{CRAL_IDV_TNO}
               )

         UNION ALL
        SELECT AB.CNTR_CST_NO, CB.CST_KNM, CB.ITG_CST_NO, CB.EMADR, CB.BRYY_MMDD, CB.SEX_DV_CD
             , '' AS CRAL_LOCARA_TNO, '' AS MEXNO_ENCR, '' AS CRAL_IDV_TNO
             , AB.LOCARA_TNO, AB.EXNO_ENCR, AB.IDV_TNO
          FROM TB_SSCT_CNTR_ADRPC_BAS AB
         INNER JOIN TB_CUBS_CST_BAS CB
            ON CB.CST_NO = AB.CNTR_CST_NO
           AND CB.DTA_DL_YN = 'N'
         WHERE AB.CNTR_ADRPC_ID IN (SELECT CNTR_ADRPC_ID
                                   FROM TB_SSCT_CNTR_ADR_REL
                                  WHERE ADRPC_TP_CD = '2'
                                    AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN VL_STRT_DTM AND VL_END_DTM
                                    AND DTA_DL_YN = 'N')
           AND AB.DTA_DL_YN = 'N'
           AND AB.CNTR_CST_NO = #{CST_NO}
           AND AB.RCGVP_KNM = #{CST_NM}

               AND ( 1=1
               AND AB.CRAL_LOCARA_TNO = #{CRAL_LOCARA_TNO}
               AND AB.MEXNO_ENCR = #{MEXNO}
               AND AB.CRAL_IDV_TNO = #{CRAL_IDV_TNO}
               )


               AND ( 1=1
               AND AB.LOCARA_TNO = #{LOCARA_TNO}
               AND AB.EXNO_ENCR = #{EXNO}
               AND AB.IDV_TNO = #{IDV_TNO}
               )

  ) GROUP BY CST_NO

SELECT CASE WHEN (SELECT COUNT(FRE_EXPN_ID) FROM TB_SSOP_FRE_EXPN_BAS WHERE PSPC_CST_ID = PS.PSPC_CST_ID AND DTA_DL_YN = 'N') > 0 THEN '3' ELSE '2' END AS CST_DV , PS.CST_NO , (SELECT ITG_CST_NO FROM TB_CUBS_CST_BAS WHERE CST_NO = PS.CST_NO AND DTA_DL_YN = 'N') AS ITG_CST_NO , PS.PSPC_CST_KNM AS CST_KNM , PS.CRAL_LOCARA_TNO , PS.MEXNO_ENCR AS MEXNO , PS.CRAL_IDV_TNO , PS.LOCARA_TNO , PS.EXNO_ENCR AS EXNO , PS.IDV_TNO , PS.EMADR , PS.BRYY_MMDD , PS.SEX_DV_CD FROM TB_SSOP_PSPC_CST_BAS PS WHERE PS.DTA_DL_YN = 'N' AND PS.CST_NO = #{CST_NO} AND PS.PSPC_CST_KNM = #{CST_NM}
AND ( 1=1 AND PS.CRAL_LOCARA_TNO = #{CRAL_LOCARA_TNO} AND PS.MEXNO_ENCR = #{MEXNO} AND PS.CRAL_IDV_TNO = #{CRAL_IDV_TNO} )
AND ( 1=1 AND PS.LOCARA_TNO = #{LOCARA_TNO} AND PS.EXNO_ENCR = #{EXNO} AND PS.IDV_TNO = #{IDV_TNO} )
