SELECT COUNT(1) FROM TB_CUBS_CST_BAS T1 LEFT OUTER JOIN TB_OGBS_EMP_BAS T2 -- 임직원의 경우 ON T2.BRYY_MMDD = T1.BRYY_MMDD AND T2.CRAL_LOCARA_TNO = T1.CRAL_LOCARA_TNO AND T2.MEXNO_ENCR = T1.MEXNO_ENCR AND T2.CRAL_IDV_TNO = T1.CRAL_IDV_TNO AND T2.FNM = T1.CST_KNM LEFT OUTER JOIN TB_CUBS_CST_CTPLC_BAS T3 ON T3.DTA_DL_YN = 'N' AND T3.CST_CTPLC_OJ_DV_CD = '03' AND T3.CST_CTPLC_OJ_REFK_VAL = T1.CST_NO AND T3.CTPLC_TP_CD = '05' AND T3.HIST_END_DTM = '99991231235959' LEFT OUTER JOIN TB_CUBS_CST_CTPLC_BAS T4 ON T4.CST_CTPLC_OJ_REFK_VAL = T1.CST_NO AND T4.CTPLC_TP_CD = DECODE(T1.COPN_DV_CD, '1', '04', '2', '05') AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN T4.HIST_STRT_DTM AND T4.HIST_END_DTM AND T4.DTA_DL_YN = 'N' AND T4.FST_RGST_DTM = (SELECT MAX(FST_RGST_DTM) FROM TB_CUBS_CST_CTPLC_BAS WHERE CST_CTPLC_OJ_REFK_VAL = T4.CST_CTPLC_OJ_REFK_VAL AND CTPLC_TP_CD = DECODE(T1.COPN_DV_CD, '1', '04', '2', '05') AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN HIST_STRT_DTM AND HIST_END_DTM AND DTA_DL_YN = 'N') WHERE 1 = 1
AND T1.COPN_DV_CD = '2' AND T1.BZRNO = #{bzrno}
AND T1.COPN_DV_CD = '1'
AND T1.CRAL_LOCARA_TNO = #{cralLocaraTno}
AND T1.MEXNO_ENCR = #{mexnoEncr}
AND T1.CRAL_IDV_TNO = #{cralIdvTno}
AND T1.CST_KNM = #{cstKnm} AND T1.DTA_DL_YN = 'N'

SELECT T1.EOT_DLQ_AMT FROM TB_CBCL_DLQ_BAS T1 INNER JOIN TB_SSCT_CNTR_DTL T2 ON T2.CNTR_NO = T1.CNTR_NO AND T2.CNTR_SN = T1.CNTR_SN AND T2.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_BAS T3 ON T3.CNTR_NO = T2.CNTR_NO AND T3.CNTR_CST_NO IN
#{cstNo}
AND T3.DTA_DL_YN = 'N' WHERE 1 = 1 AND T1.DLQ_MCN > 2 AND T1.DTA_DL_YN = 'N'

SELECT DISTINCT(CST_NO) FROM TB_SSCT_CNTR_CST_REL WHERE 1=1 AND VL_END_DTM = '99991231235959' AND CNTR_CST_REL_TP_CD = '10' AND (DTL_CNTR_NO, DTL_CNTR_SN) IN ( SELECT DTL_CNTR_NO, DTL_CNTR_SN FROM TB_SSCT_CNTR_CST_REL WHERE 1=1 AND VL_END_DTM = '99991231235959' AND CNTR_CST_REL_TP_CD = '20' AND DTA_DL_YN = 'N' AND CST_NO IN
#{cstNo}
)

