SELECT CONCAT(CONCAT(A1.CNTR_NO, '-'), A1.CNTR_SN) AS CNTR_DTL_NO /* 계약번호 - 계약일련번호 [계약상세번호] */ , A1.CNTR_NO /* 계약번호 [계약번호] */ , A1.CNTR_SN /* 계약일련번호 [계약번호] */ , C1.CST_KNM /* 고객한글명 [계약자명] */ , C1.COPN_DV_CD /* 법인격구분코드(계약자 고객구분 ) 1:개인, 2:법인 */ , F_CMZ_CD_NM ('TNT_WELLS', 'COPN_DV_CD', C1.COPN_DV_CD) AS COPN_DV_NM /* 고객구분명 [고객구분]*/ /* 개인인경우 생년월일, 사업자인경우 사업자 등록번호로 조회 하려 했으나 실명구분코드와 실명번호 암호화로 대체 처리하여도 되는지 데이터 확인 후 진행 */ , C1.BZRNO /* 사업자등록번호(계약자) */ , C1.BRYY_MMDD /* 생년월일(계약자) */ , C1.SEX_DV_CD /* 성별구분코드(계약자) */ , CASE WHEN C1.COPN_DV_CD = '1' /* 계약자 고객구분 1(개인) 인 경우 */ THEN C1.BRYY_MMDD WHEN C1.COPN_DV_CD = '2' /* 계약자 고객구분 2(법인) 인 경우 */ THEN C1.BZRNO ELSE '' END AS RNMNO /* [주민/사업자번호] */ , C1.RNMNO_DV_CD /* 실명번호구분코드 01:주민등록번호, 02:외국인등록번호, 03:법인등록번호, 04:사업자등록번호, 05:단체고유번호 */ , C1.RNMNO_ENCR /* 실명번호암호화 */ , A1.BASE_PD_CD /* 기준상품코드[상품코드] */ /* 기준상품만을 조건으로 조회하려 했으나 복합상품, 기준상품, 서비스, 교재 제품등 해당 상품 유형이 확인 되지 않아 MAX로 조회 */ , ( SELECT MAX(D1.PD_NM) /* 상품기본.상품명 */ FROM TB_PDBS_PD_BAS D1 /* 상품기본 */ WHERE D1.PD_CD = A1.BASE_PD_CD /* 상품기본.상품코드 = 계약상세.기준상품코드 */ AND D1.DTA_DL_YN != 'Y' /* 상품기본.데이터삭제여부가 'Y'가 아닌 건 */ ) AS BASE_PD_NM /* [상품명] */ , A1.SELL_DSC_DV_CD /* 계약상세.할인적용유형코드 [할인구분]*/ , F_CMZ_CD_NM( 'TNT_WELLS' , (SELECT CM1.USER_DFN_02 AS ID FROM T_CMZ_CD_D CM1 WHERE 1 = 1 AND CM1.CD_ID = 'DSC_APY_TP_CD' AND CM1.CD_VLD_VAL = A1.SELL_TP_CD AND CM1.TENANT_ID = 'TNT_WELLS') , A1.SELL_DSC_DV_CD ) AS DSC_APY_TP_NM , A1.SELL_DSCR_CD /* 계약상세.할인적용상세코드 [할인유형] */ /* 할인유형명 계층형으로 되어있음 */ , F_CMZ_CD_NM( 'TNT_WELLS' , (SELECT CM2.USER_DFN_02 FROM T_CMZ_CD_D CM2 WHERE 1 = 1 AND CM2.CD_ID = 'DSC_APY_DTL_CD' AND CM2.PRTS_CD_ID = (SELECT CM1.USER_DFN_02 AS ID FROM T_CMZ_CD_D CM1 WHERE CM1.CD_ID = 'DSC_APY_TP_CD' AND CM1.CD_VLD_VAL = A1.SELL_TP_CD AND CM1.TENANT_ID = 'TNT_WELLS') AND CM2.TENANT_ID = 'TNT_WELLS') , A1.SELL_DSCR_CD) AS DSC_APY_DTL_NM , B1.CNTR_RCP_FSH_DTM /* 계약기본.계약접수완료일시 [접수일] */ , A1.SPP_DUEDT /* 계약상세.배송예정일자 [예정일] */ , (SELECT MAX(E1.SL_RCOG_DT) FROM TB_CBCL_WELLS_SL_MM_CL_IZ E1 /* WELLS매출월마감내역 */ WHERE E1.CNTR_NO = A1.CNTR_NO AND E1.CNTR_SN = A1.CNTR_SN AND E1.SL_CL_YM BETWEEN NVL(A1.CNTR_PD_STRTDT, TO_CHAR(SYSDATE,'YYYYMM')) AND TO_CHAR(SYSDATE,'YYYYMM')) AS SL_DT /* WELLS매출월마감내역.매출일자 - 현재월기준 [매출일] */ , SUBSTR(B1.CNTR_CAN_DTM, 1, 8) AS CNTR_CAN_DT /* 계약기본.계약취소일시 [취소일] */ , F1.CPS_DT /* 계약WELLS상세.보상일자 [보상일] */ /* 매출경과일 항목 조회 기준 확정 이후 처리 - 해당항목 as-is에 없음 * as-is LC3000.LCDEMY/LCDEMM/LCDEMD 예정년월일 + 14일 * to-be 매핑정보 기준 배송예정일자 + 14일 */ , CASE WHEN TRIM(A1.SPP_DUEDT) IS NOT NULL THEN TO_CHAR(TO_DATE(A1.SPP_DUEDT, 'YYYYMMDD')+14, 'YYYYMMDD') ELSE '' END AS SL_PASG_DT /* [매출경과일] */ , CASE WHEN TRIM(B1.CNTR_CNFM_APR_DTM) IS NOT NULL THEN 'Y' ELSE 'N' END SL_CNFM_YN /* [매출확정여부] */ , B1.CNTR_CAN_RSON_CD /* 계약기본.계약취소사유코드 [취소유형] */ , F_CMZ_CD_NM ('TNT_WELLS', 'CNTR_CAN_RSON_CD', B1.CNTR_CAN_RSON_CD) AS CAN_RSON_NM /* [취소유형명] */ , T40.CNTR_CH_RSON_CD AS CNTR_CH_DTL_RSON_CD /* , A1.CNTR_CH_DTL_RSON_CD 계약상세.계약변경상세사유코드 TODO: 계약변경 상세변경상세사유코드 삭제 여부 확인 */ , F_CMZ_CD_NM ('TNT_WELLS', 'CNTR_CH_RSON_CD', T40.CNTR_CH_RSON_CD) AS CNTR_CH_DTL_RSON_NM /* [계약변경명] TODO: 계약변경 상세변경상세사유코드 삭제 여부 확인 */ , A1.FRISU_YN /* 계약상세.무상여부 [무료체험여부] */ , '' AS FRE_EXPN_CNFM_DTM /* TODO : 무료체험 확정일 매핑정보 확인 필요 무료체험기본.무료체험확정일시(TB_SSOP_FRE_EXPN_BAS.FRE_EXPN_CNFM_DTM)가 있으나 JOIN 정보 확인 불가 */ , ( SELECT MAX(G1.CTT_TP_CD) KEEP (DENSE_RANK FIRST ORDER BY G1.CTT_RCP_DTM DESC NULLS LAST, G1.CTT_FSH_DTM DESC NULLS LAST) AS CTT_TP_CD /* 컨택기본.컨택완료일시 */ FROM TB_SSOP_CTT_BAS G1 /* 컨택기본 */ WHERE G1.DTA_DL_YN = 'N' AND G1.CNTR_NO = A1.CNTR_NO /* 컨택기본.계약번호 = 계약상세.계약번호 */ ) AS CTT_TP_CD /* [컨택코드] */ , ( SELECT F_CMZ_CD_NM ('TNT_WELLS', 'CNTR_TP_CD', (MAX(G1.CTT_TP_CD) KEEP (DENSE_RANK FIRST ORDER BY G1.CTT_RCP_DTM DESC NULLS LAST, G1.CTT_FSH_DTM DESC NULLS LAST))) AS CTT_TP_CD /* 컨택기본.컨택완료일시 */ FROM TB_SSOP_CTT_BAS G1 /* 컨택기본 */ WHERE G1.DTA_DL_YN = 'N' AND G1.CNTR_NO = A1.CNTR_NO /* 컨택기본.계약번호 = 계약상세.계약번호 */ ) AS CTT_TP_NM /* [컨택코드명] */ , (SELECT MAX(I1.SPP_MTHD_TP_CD) KEEP (DENSE_RANK FIRST ORDER BY I1.SPP_D