mapper.sql:
SELECT T_CNTR_DTL.SELL_TP_CD , T_CNTR_DTL.CNTR_DTL_STAT_CD , T_CNTR_BAS.COPN_DV_CD , T_CNTR_BAS.CNTR_CST_NO , T_CNTR_DTL.CNTR_NO , T_CNTR_DTL.CNTR_SN , T_CNTR_CST_BAS.CST_KNM , T_CNTR_CST_BAS.SEX_DV_CD , T_CNTR_CST_BAS.BRYY_MMDD , T_CNTR_CST_BAS.CRAL_LOCARA_TNO , T_CNTR_CST_BAS.MEXNO_ENCR , T_CNTR_CST_BAS.CRAL_IDV_TNO , T_BASE_PD_BAS.PD_NM , T_CNTR_DTL.STPL_PTRM , T_WELLS_SL_MM_CL_IZ.RENTAL_TN AS IST_TN , CASE WHEN T_DLQ_BAS.EOT_DLQ_AMT > 0 THEN 'Y' ELSE 'N' END AS DLQ_YN , T_CNTR_WELLS_DTL.REQD_DT /* 철거일자 */ , T_CNTR_DTL.CNTR_PD_ENDDT /* 종료일자(취소,만료 등) */ , T_CNTR_WELLS_DTL.CPS_DT /* 보상일자 */ FROM TB_SSCT_CNTR_DTL T_CNTR_DTL INNER JOIN TB_SSCT_CNTR_BAS T_CNTR_BAS ON T_CNTR_BAS.DTA_DL_YN = 'N' AND T_CNTR_BAS.CNTR_NO = T_CNTR_DTL.CNTR_NO INNER JOIN TB_CUBS_CST_BAS T_CNTR_CST_BAS ON T_CNTR_CST_BAS.DTA_DL_YN = 'N' AND T_CNTR_CST_BAS.CST_NO = T_CNTR_BAS.CNTR_CST_NO INNER JOIN TB_PDBS_PD_BAS T_BASE_PD_BAS ON T_BASE_PD_BAS.DTA_DL_YN = 'N' AND T_BASE_PD_BAS.PD_CD = T_CNTR_DTL.BASE_PD_CD INNER JOIN TB_PDBS_PD_CLSF_BAS T_PD_CLSF_BAS ON T_PD_CLSF_BAS.PD_CLSF_ID = T_BASE_PD_BAS.PD_CLSF_ID INNER JOIN TB_SSCT_CNTR_WELLS_DTL T_CNTR_WELLS_DTL ON T_CNTR_WELLS_DTL.CNTR_NO = T_CNTR_DTL.CNTR_NO AND T_CNTR_WELLS_DTL.CNTR_SN = T_CNTR_DTL.CNTR_SN INNER JOIN TB_SSCT_CNTR_PRTNR_REL T_CNTR_PRTNR_REL ON T_CNTR_PRTNR_REL.CNTR_NO = T_CNTR_DTL.CNTR_NO AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN T_CNTR_PRTNR_REL.VL_STRT_DTM AND T_CNTR_PRTNR_REL.VL_END_DTM LEFT OUTER JOIN TB_CBCL_DLQ_BAS T_DLQ_BAS ON T_DLQ_BAS.CNTR_NO = T_CNTR_DTL.CNTR_NO AND T_DLQ_BAS.CNTR_SN = T_CNTR_DTL.CNTR_SN AND T_DLQ_BAS.PERF_YM = TO_CHAR(SYSDATE, 'YYYYMM') - 1 LEFT OUTER JOIN TB_CBCL_WELLS_SL_MM_CL_IZ T_WELLS_SL_MM_CL_IZ ON T_WELLS_SL_MM_CL_IZ.CNTR_NO = T_CNTR_DTL.CNTR_NO AND T_WELLS_SL_MM_CL_IZ.CNTR_SN = T_CNTR_DTL.CNTR_SN AND T_WELLS_SL_MM_CL_IZ.SL_CL_YM = (SELECT MAX(SL_CL_YM) FROM TB_CBCL_WELLS_SL_MM_CL_IZ WHERE CNTR_NO = T_CNTR_DTL.CNTR_NO AND CNTR_SN = T_CNTR_DTL.CNTR_SN) WHERE T_CNTR_DTL.SELL_TP_CD = '2' AND T_CNTR_DTL.CNTR_DTL_STAT_CD = '101' /* 해약/취소건이 아니면서 */ AND T_CNTR_WELLS_DTL.REQD_DT IS NULL /* 철거일자가 없으면서 */ AND T_CNTR_BAS.COPN_DV_CD = #{copnDvCd} AND ( (T_CNTR_BAS.SELL_PRTNR_NO = #{session.employeeIDNumber} AND T_CNTR_BAS.SELL_OG_TP_CD = #{session.ogTpCd} ) OR T_PD_CLSF_BAS.REF_PD_CLSF_VAL = '05001001') /* 자기자신의 건만 보이거나 웰스팜 건 전체 */ AND (T_DLQ_BAS.DLQ_ACU_MCN IS NULL OR T_DLQ_BAS.DLQ_ACU_MCN < 3) /* 3연체가 아닌 건 */ /* 가장 중요한 재약정기준정보 차월 조건 거는 부분이 빠져 있음, 이는 상품에 추가 후에 추가하면 됨(보완필요) */ AND EXISTS (SELECT COUNT(1) FROM TB_PDBS_PD_RSTL_BAS T_PD_RSTL_BAS WHERE 1=1 AND T_PD_RSTL_BAS.PD_CD = T_CNTR_DTL.BASE_PD_CD AND (T_PD_RSTL_BAS.RSTL_SELL_CHNL_DV_CD = SUBSTR(T_CNTR_BAS.SELL_INFLW_CHNL_DTL_CD,0,2) OR T_PD_RSTL_BAS.RSTL_SELL_CHNL_DV_CD = '00') AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN T_PD_RSTL_BAS.VL_STRT_DTM AND T_PD_RSTL_BAS.VL_END_DTM AND T_WELLS_SL_MM_CL_IZ.RENTAL_TN BETWEEN T_PD_RSTL_BAS.RCP_STRT_MCN AND T_PD_RSTL_BAS.RCP_END_MCN AND T_PD_RSTL_BAS.DTA_DL_YN = 'N')
AND T_CNTR_CST_BAS.CST_KNM = #{cstKnm}
AND T_CNTR_CST_BAS.CRAL_LOCARA_TNO = #{cralLocaraTno} AND T_CNTR_CST_BAS.MEXNO_ENCR = #{mexnoEncr} AND T_CNTR_CST_BAS.CRAL_IDV_TNO = #{cralIdvTno}
