mapper.update:
        MERGE INTO TB_SSCT_TXINV_RCP_BASE_IZ
        USING DUAL
        ON (
             CNTR_NO = #{cntrNo}
		 AND CNTR_SN = #{cntrSn}
        )
        WHEN MATCHED THEN
        UPDATE
		   SET TXINV_PD_DV_CD = #{txinvPdDvCd}
		     , TXINV_PBL_DV_CD = #{txinvPblDvCd}
		     , APLC_PSIC_ID = #{aplcPsicId}
		     , RCPDT = #{rcpdt}
		     , BZRNO = #{bzrno}
		     , CNTR_CST_NO = #{cntrCstNo}
		     , TXINV_PBL_YN = #{txinvPblYn}
		     , TXINV_PBL_D = #{txinvPblD}
		     , TXINV_BNDL_SN = #{txinvBndlSn}
		     , DLPNR_PSIC_NM = #{dlpnrPsicNm}
		     , EMADR = #{emadr}
		     , LOCARA_TNO = #{locaraTno}
		     , EXNO_ENCR = #{exnoEncr}
		     , IDV_TNO = #{idvTno}
		     , CRAL_LOCARA_TNO = #{cralLocaraTno}
		     , MEXNO_ENCR = #{mexnoEncr}
		     , CRAL_IDV_TNO = #{cralIdvTno}
		     , RMK_CN = #{rmkCn}
		     , MVS_DSTC_RCVRY_BASE_DTM = #{mvsDstcRcvryBaseDtm}
		     , MVS_DSTC_RCVRY_DV_CD = #{mvsDstcRcvryDvCd}
		     , MVS_DSTC_RCVRY_DTM = #{mvsDstcRcvryDtm}
		     , DTA_DL_YN = #{dtaDlYn}

        WHEN NOT MATCHED THEN
        INSERT (
              CNTR_NO                 /* 계약번호 */
            , CNTR_SN                 /* 계약일련번호 */
            , TXINV_PD_DV_CD          /* 세금계산서상품구분코드 */
            , TXINV_PBL_DV_CD         /* 세금계산서발행구분코드 */
            , APLC_PSIC_ID            /* 신청담당자ID */
            , RCPDT                   /* 접수일자 */
            , BZRNO                   /* 사업자등록번호 */
            , CNTR_CST_NO             /* 계약고객번호 */
            , TXINV_PBL_YN            /* 세금계산서발행여부 */
            , TXINV_PBL_D             /* 세금계산서발행일 */
            , TXINV_BNDL_SN           /* 세금계산서묶음일련번호 */
            , DLPNR_PSIC_NM           /* 거래처담당자명 */
            , EMADR                   /* 이메일주소 */
            , LOCARA_TNO              /* 지역전화번호 */
            , EXNO_ENCR               /* 전화국번호암호화 */
            , IDV_TNO                 /* 개별전화번호 */
            , CRAL_LOCARA_TNO         /* 휴대지역전화번호 */
            , MEXNO_ENCR              /* 휴대전화국번호암호화 */
            , CRAL_IDV_TNO            /* 휴대개별전화번호 */
            , RMK_CN                  /* 비고내용 */
            , MVS_DSTC_RCVRY_BASE_DTM /* 소산파기복구기준일시 */
            , MVS_DSTC_RCVRY_DV_CD    /* 소산파기복구구분코드 */
            , MVS_DSTC_RCVRY_DTM      /* 소산파기복구일시 */
            , DTA_DL_YN               /* 데이터삭제여부 */

        ) VALUES (
              #{cntrNo}
            , #{cntrSn}
            , #{txinvPdDvCd}
            , #{txinvPblDvCd}
            , #{aplcPsicId}
            , #{rcpdt}
            , #{bzrno}
            , #{cntrCstNo}
            , #{txinvPblYn}
            , #{txinvPblD}
            , #{txinvBndlSn}
            , #{dlpnrPsicNm}
            , #{emadr}
            , #{locaraTno}
            , #{exnoEncr}
            , #{idvTno}
            , #{cralLocaraTno}
            , #{mexnoEncr}
            , #{cralIdvTno}
            , #{rmkCn}
            , #{mvsDstcRcvryBaseDtm}
            , #{mvsDstcRcvryDvCd}
            , #{mvsDstcRcvryDtm}
            , #{dtaDlYn}

        )

mapper.insert:
INSERT INTO TB_SSCT_TXINV_RCP_BASE_CH_HIST (  /* 세금계산서접수기준변경이력 */
      CNTR_NO                 /* 계약번호 */
    , CNTR_SN                 /* 계약일련번호 */
    , CH_SN                   /* 변경일련번호 */
    , TXINV_PD_DV_CD          /* 세금계산서상품구분코드 */
    , TXINV_PBL_DV_CD         /* 세금계산서발행구분코드 */
    , APLC_PSIC_ID            /* 신청담당자ID */
    , RCPDT                   /* 접수일자 */
    , BZRNO                   /* 사업자등록번호 */
    , CNTR_CST_NO             /* 계약고객번호 */
    , TXINV_PBL_YN            /* 세금계산서발행여부 */
    , TXINV_PBL_D             /* 세금계산서발행일 */
    , TXINV_BNDL_SN           /* 세금계산서묶음일련번호 */
    , DLPNR_PSIC_NM           /* 거래처담당자명 */
    , EMADR                   /* 이메일주소 */
    , LOCARA_TNO              /* 지역전화번호 */
    , EXNO_ENCR               /* 전화국번호암호화 */
    , IDV_TNO                 /* 개별전화번호 */
    , CRAL_LOCARA_TNO         /* 휴대지역전화번호 */
    , MEXNO_ENCR              /* 휴대전화국번호암호화 */
    , CRAL_IDV_TNO            /* 휴대개별전화번호 */
    , RMK_CN                  /* 비고내용 */
    , MVS_DSTC_RCVRY_BASE_DTM /* 소산파기복구기준일시 */
    , MVS_DSTC_RCVRY_DV_CD    /* 소산파기복구구분코드 */
    , MVS_DSTC_RCVRY_DTM      /* 소산파기복구일시 */
    , DTA_DL_YN               /* 데이터삭제여부 */
    )
VALUES (
      #{cntrNo}
    , #{cntrSn}
    , ( SELECT NVL(MAX(H.CH_SN), 0) + 1
          FROM TB_SSCT_TXINV_RCP_BASE_CH_HIST H
         WHERE H.CNTR_NO = #{cntrNo}
           AND H.CNTR_SN = #{cntrSn}
       )
    , #{txinvPdDvCd}
    , #{txinvPblDvCd}
    , #{aplcPsicId}
    , #{rcpdt}
    , #{bzrno}
    , #{cntrCstNo}
    , #{txinvPblYn}
    , #{txinvPblD}
    , #{txinvBndlSn}
    , #{dlpnrPsicNm}
    , #{emadr}
    , #{locaraTno}
    , #{exnoEncr}
    , #{idvTno}
    , #{cralLocaraTno}
    , #{mexnoEncr}
    , #{cralIdvTno}
    , #{rmkCn}
    , #{mvsDstcRcvryBaseDtm}
    , #{mvsDstcRcvryDvCd}
    , #{mvsDstcRcvryDtm}
    , #{dtaDlYn}
     )
