mapper.select:
SELECT * FROM ( SELECT T1.SELL_LM_ID , T1.DTA_DL_YN , T1.DTA_DL_YN AS ORGL_DTA_DL_YN , T2.SELL_TP_CD , T3.CNTR_CST_NO , T1.SELL_LM_RSON_CN , T1.SELL_LM_CNTR_NO , T1.SELL_LM_CNTR_SN , T4.CST_KNM , T3.COPN_DV_CD , T4.BRYY_MMDD , T4.BZRNO , I1.CRAL_LOCARA_TNO AS CNTR_CRAL_LOCARA_TNO , I1.MEXNO_ENCR AS CNTR_MEXNO_ENCR , I1.CRAL_IDV_TNO AS CNTR_CRAL_IDV_TNO , '' AS CNTR_MPNO , I2.LOCARA_TNO AS CNTR_LOCARA_TNO , I2.EXNO_ENCR AS CNTR_EXNO_ENCR , I2.IDV_TNO AS CNTR_IDV_TNO , '' AS CNTR_TNO , CASE WHEN I3.RNADR IS NOT NULL THEN I3.NEW_ADR_ZIP WHEN I3.LTN_ADR IS NOT NULL THEN I3.OLD_ADR_ZIP ELSE '' END AS CNTR_ZIP , CASE WHEN I3.RNADR IS NOT NULL THEN I3.RNADR || ' ' || I3.RDADR WHEN I3.LTN_ADR IS NOT NULL THEN I3.LTN_ADR || ' ' || I3.LTN_DTL_ADR ELSE '' END AS CNTR_ADR , I4.RCGVP_KNM AS ISTLL_KNM , I4.CRAL_LOCARA_TNO AS ISTLL_CRAL_LOCARA_TNO , I4.MEXNO_ENCR AS ISTLL_MEXNO_ENCR , I4.CRAL_IDV_TNO AS ISTLL_CRAL_IDV_TNO , '' AS ISTLL_MPNO , I4.LOCARA_TNO AS ISTLL_LOCARA_TNO , I4.EXNO_ENCR AS ISTLL_EXNO_ENCR , I4.IDV_TNO AS ISTLL_IDV_TNO , '' AS ISTLL_TNO , CASE WHEN I5.RNADR IS NOT NULL THEN I5.NEW_ADR_ZIP WHEN I5.LTN_ADR IS NOT NULL THEN I5.OLD_ADR_ZIP ELSE '' END AS ISTLL_ZIP , CASE WHEN I5.RNADR IS NOT NULL THEN I5.RNADR || ' ' || I5.RDADR WHEN I5.LTN_ADR IS NOT NULL THEN I5.LTN_ADR || ' ' || I5.LTN_DTL_ADR ELSE '' END AS ISTLL_ADR , T5.OG_NM , T5.PRTNR_KNM , T3.SELL_PRTNR_NO AS PRTNR_NO , I6.CRAL_LOCARA_TNO AS PRTNR_CRAL_LOCARA_TNO , I6.MEXNO_ENCR AS PRTNR_MEXNO_ENCR , I6.CRAL_IDV_TNO AS PRTNR_CRAL_IDV_TNO , '' AS PRTNR_MPNO , T1.FST_RGST_DTM , T1.FST_RGST_USR_ID , T1.FNL_MDFC_DTM , T1.FNL_MDFC_USR_ID FROM TB_SSCT_SELL_LM_OJ_IZ T1 INNER JOIN TB_SSCT_CNTR_DTL T2 ON T2.CNTR_NO = T1.SELL_LM_CNTR_NO AND T2.CNTR_SN = T1.SELL_LM_CNTR_SN AND T2.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_BAS T3 ON T3.CNTR_NO = T1.SELL_LM_CNTR_NO AND T3.DTA_DL_YN = 'N' INNER JOIN TB_CUBS_CST_BAS T4 ON T4.CST_NO = T3.CNTR_CST_NO AND T4.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ T5 ON T5.PRTNR_NO = T3.SELL_PRTNR_NO AND T5.OG_TP_CD = T3.SELL_OG_TP_CD AND T5.BASE_YM = SUBSTR(T3.CNTR_CNFM_DTM, 0, 6) AND T5.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_ADR_REL T6 ON T6.DTL_CNTR_NO = T2.CNTR_NO AND T6.DTL_CNTR_SN = T2.CNTR_SN AND T6.DTA_DL_YN = 'N'
LEFT OUTER JOIN TB_CUBS_CST_BAS I1 ON I1.CST_NO = T3.CNTR_CST_NO LEFT OUTER JOIN (SELECT * FROM TB_CUBS_CST_CTPLC_BAS WHERE CST_CTPLC_OJ_DV_CD = 1) I2 ON I2.CST_CTPLC_OJ_REFK_VAL = T3.CNTR_CST_NO AND I2.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_GBCO_ADR_BAS I3 ON I3.ADR_ID = T4.ADR_ID AND I3.DTA_DL_YN = 'N'
LEFT OUTER JOIN TB_SSCT_CNTR_ADRPC_BAS I4 ON I4.CNTR_ADRPC_ID = T6.CNTR_ADRPC_ID AND I4.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_GBCO_ADR_BAS I5 ON I5.ADR_ID= I4.ADR_ID AND I5.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_PRTNR_BAS I6 ON I6.OG_TP_CD = T5.OG_TP_CD AND I6.PRTNR_NO = T5.PRTNR_NO AND I6.DTA_DL_YN = 'N'
WHERE 1 = 1 AND T1.DTA_DL_YN = 'N' AND T1.SELL_LM_OJ_DRM_CD = '2'
AND T1.SELL_LM_CNTR_NO = #{cntrNo}
AND T3.CNTR_CST_NO = #{cstNo}
AND T5.CST_KNM = #{cstKnm}
) WHERE 1 = 1
AND (CNTR_ADR LIKE '%' || #{adr} || '%' OR CNTR_ZIP LIKE '%' || #{adr} || '%')
AND ( I1.CRAL_LOCARA_TNO = #{cralLocaraTno} AND I1.MEXNO_ENCR = #{mexnoEncr} AND I1.CRAL_IDV_TNO = #{cralIdvTno} ) OR ( I2.LOCARA_TNO = #{cralLocaraTno} AND I2.EXNO_ENCR = #{mexnoEncr} AND I2.IDV_TNO = #{cralIdvTno} )
AND (PRTNR_KNM LIKE '%' || #{selrInf} || '%' OR PRTNR_ LIKE '%' || #{selrInf} || '%')

mapper.update:
UPDATE TB_SSCT_SELL_LM_OJ_IZ
   SET SELL_LM_CNTR_NO = #{sellLmCntrNo}
     , SELL_LM_CNTR_SN = #{sellLmCntrSn}
     , SELL_LM_RSON_CN = #{sellLmRsonCn}



     , SELL_LM_OC_DTM = TO_CHAR(SYSDATE, 'YYYYMMDDHHMMSS')


     , SELL_LM_RLS_DTM = TO_CHAR(SYSDATE, 'YYYYMMDDHHMMSS')




 WHERE SELL_LM_ID = #{sellLmId}
