mapper.select:
/* 재지급 대상 생성 전, 확정여부 체크 validation */ SELECT COUNT(1) AS CNT FROM TB_FEAM_WELS_NRCTR_MM_CL M INNER JOIN TB_FEAM_NRCTR_MM_CL S /*순주문계약월마감*/ ON M.BASE_YM = S.BASE_YM AND M.FEE_TCNT_DV_CD = S.FEE_TCNT_DV_CD AND M.CNTR_PERF_CRT_DV_CD = S.CNTR_PERF_CRT_DV_CD WHERE M.DTA_DL_YN = 'N' AND M.BASE_YM = #{baseYm} /*파라미터로 넘어온 반영년월*/ AND S.NTOR_CNFM_STAT_CD = '02' /*01: 임시저장, 02: 확정*/
AND M.CNTR_PERF_CRT_DV_CD = '08' /*08: P추진단 재지급*/
AND M.CNTR_PERF_CRT_DV_CD = '09' /*09: M추진단 재지급*/
AND M.OG_TP_CD = #{ogTpCd}

mapper.delete:
/* 재지급 대상(실적) 생성 전, 임시저장 데이터 삭제 */ DELETE FROM TB_FEAM_WELS_NRCTR_MM_CL M WHERE M.BASE_YM = #{baseYm} /*파라미터로 넘어온 발생년월*/
AND M.CNTR_PERF_CRT_DV_CD = '08' /*08: P추진단 재지급*/
AND M.CNTR_PERF_CRT_DV_CD = '09' /*09: M추진단 재지급*/
AND M.OG_TP_CD = #{ogTpCd}

mapper.update:
UPDATE
/*WELLS*/ TB_FEAM_WELS_NRCTR_MM_CL /* 웰스순주문계약월마감 */
/*EDU*/ TB_FEAM_EDU_NRCTR_MM_CL M /*EDU순주문계약월마감*/
SET NTOR_CNFM_STAT_CD = '02' /* 순주문확정상태코드 */ WHERE 1 = 1 AND BASE_YM = #{baseYm} /* 기준년월 */ AND NTOR_CNFM_STAT_CD = '01' /* 순주문확정상태코드 */
