mapper.select:
/*수당(실적) 되물림 관리_WELLS 총판, B2B*/ SELECT M.BASE_YM /*발생년월*/ , M.PERF_YM AS SL_YM /*매출년월*/ , A.OG_NM /*업체명*/ , A.OG_CD /*소속코드*/ , A.PRTNR_KNM /*판매자명*/ , M.PRTNR_NO /*파트너번호*/ , M.CNTR_NO /*계약번호*/ , M.CNTR_SN /*계약일련번호*/ , M.CNTR_NO || M.CNTR_SN AS CNTR_NO_SN /*계약상세번호*/ , D.CST_KNM /*고객명*/ , M.PD_CD /*제품코드(상품코드)*/ , D.PD_CLSF_NM /*제품명(상품명)*/ , D.MODEL_NO /*모델명*/ , M.SELL_DSC_DV_CD || '-' || M.SELL_DSC_TP_CD AS SELL_DSC_DV_CD /*할인구분(ASIS: D-C)*/ , M.SELL_DSC_DV_CD || '-' || M.SELL_DSC_TP_CD AS SELL_DSC_TP_CD /*할인유형(ASIS: P)*/ , M.PMOT_USWY_DV_CD /*업소*/ , M.BFSVC_PRD_CD /*주기*/ , B1.MCHN_CH_TP_CD AS MCHN_CH1 /*기변영*/ , B2.MCHN_CH_TP_CD AS MCHN_CH2 /*기변센*/ , M.RCPDT /*계약일자*/ , M.SL_DT /*매출일자*/ , M.CAN_DT /*취소일자*/ , NVL(C.REDF_AMT, 0) AS REDF_AMT /*되물림액(총판수수료)*/ FROM TB_FEAM_WELS_NRCTR_MM_CL M /*웰스순주문계약월마감*/ INNER JOIN TB_OGBS_PRTNR_BAS A /*파트너기본*/ ON M.PRTNR_NO = A.PRTNR_NO AND M.OG_TP_CD = A.OG_TP_CD LEFT OUTER JOIN (SELECT BASE_CNTR_NO , BASE_CNTR_SN , MCHN_CH_TP_CD FROM TB_SSCT_MCHN_CH_IZ) B1 /*기기변경내역.상품구분1(ASIS유형1), TODO: 유형1이 신규인지 유형2가 신규인지 확인필요*/ ON M.CNTR_NO = B1.BASE_CNTR_NO AND M.CNTR_SN = B1.BASE_CNTR_SN LEFT OUTER JOIN (SELECT OJ_CNTR_NO , OJ_CNTR_SN , MCHN_CH_TP_CD FROM TB_SSCT_MCHN_CH_IZ) B2 /*기기변경내역.상품구분1(ASIS유형1), TODO: 유형1이 신규인지 유형2가 신규인지 확인필요*/ ON M.CNTR_NO = B2.OJ_CNTR_NO AND M.CNTR_SN = B2.OJ_CNTR_SN LEFT OUTER JOIN ( SELECT D1.CNTR_NO /*계약번호*/ , D1.CNTR_SN /*계약일련번호*/ , D3.CST_NO /*고객번호*/ , D3.CST_KNM /*고객명*/ , D4.MODEL_NO /*모델번호(모델명)*/ , D5.PD_CLSF_NM /*상품분류명*/ FROM TB_FEAM_WELS_NRCTR_MM_CL D1 /*웰스순주문계약월마감*/ INNER JOIN TB_SSCT_CNTR_BAS D2 /*계약기본*/ ON D1.CNTR_NO = D2.CNTR_NO INNER JOIN TB_CUBS_CST_BAS D3 /*고객기본*/ ON D2.CNTR_CST_NO = D3.CST_NO LEFT OUTER JOIN TB_PDBS_PD_BAS D4 /*상품기본*/ ON D1.PD_CD = D4.PD_CD LEFT OUTER JOIN TB_PDBS_PD_CLSF_BAS D5 /*상품분류기본*/ ON D4.PD_MCLSF_ID = D5.PD_CLSF_ID /*상품기본.중분류ID = 상품분류기본.상품분류ID*/ AND D5.PD_TP_CD = 'P' /* 상품유형코드 = 'P'(기준상품) */ WHERE D1.DTA_DL_YN = 'N'
AND D1.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} /*발생년월*/
AND D1.PERF_YM BETWEEN #{slYmFrom} AND #{slYmTo} /*실적년월*/
AND D1.OG_TP_CD = #{ogTpCd} /*조직유형*/
) D /*고객 및 상품 정보*/ ON M.CNTR_NO = D.CNTR_NO AND M.CNTR_SN = D.CNTR_SN LEFT OUTER JOIN ( /*TODO: 02: 계약실적생성구분코드 되물림생성이 아직 데이터가 만들어지지않아 아우터조인*/ SELECT DISTINCT C1.PRTNR_NO /*조인용.파트너번호*/ , C1.OG_TP_CD /*조인용.조직유형*/ , C1.BASE_YM /*조인용.기준년월*/ , C1.PERF_YM /*조인용.실적년월*/ , C1.CO_CD /*조인용.회사코드*/ , C1.FEE_TCNT_DV_CD /*조인용.수수료차수구분코드*/ , C1.CNTR_NO , C1.CNTR_SN , C1.CNTR_PERF_CRT_DV_CD /*조인용.계약실적생성구분코드( 02: 되물림 고정 )*/
, NVL((CASE WHEN C1.PERF_ATC_CD = 'W05P00001' THEN C1.PERF_VAL END), 0) AS REDF_AMT /*되물림액(총판수수료)*/
, NVL((CASE WHEN C1.PERF_ATC_CD = 'W04P00001' THEN C1.PERF_VAL END), 0) AS REDF_AMT /*되물림액(B2B수수료)*/
FROM TB_FEAM_NTORP_CNTR_MM_CL C1 /*순주문파트너계약월마감*/ WHERE C1.DTA_DL_YN = 'N'
AND C1.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo}
AND C1.PERF_YM BETWEEN #{slYmFrom} AND #{slYmTo}
-- AND C1.CNTR_PERF_CRT_DV_CD = '02' /*계약실적생성구분코드 02: 되물림생성, TODO: 현재는 되물림생성 데이터가 없어서 주석처리, 추후 이행시 주석해제*/ ) C ON M.PRTNR_NO = C.PRTNR_NO AND M.OG_TP_CD = C.OG_TP_CD AND M.CNTR_NO = C.CNTR_NO AND M.CNTR_SN = C.CNTR_SN AND M.BASE_YM = C.BASE_YM AND M.PERF_YM = C.PERF_YM AND M.CO_CD = C.CO_CD AND M.FEE_TCNT_DV_CD = C.FEE_TCNT_DV_CD AND M.CNTR_PERF_CRT_DV_CD = C.CNTR_PERF_CRT_DV_CD WHERE M.DTA_DL_YN = 'N'
AND M.OG_TP_CD = #{ogTpCd}
AND M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo}
AND M.PERF_YM BETWEEN #{slYmFrom} AND #{slYmTo}
AND A.OG_CD BETWEEN #{ogCdFrom} AND #{ogCdTo}
AND M.PERF_TP_CD IN ('1', '3') /*화면값이 취소(0202)면 IN 조건 1:취소, 3:매변*/
AND M.PERF_TP_CD = '10' /*화면값이 연체(0203)면 10: 연체(전집)*/
