mapper.select:
/*수당(실적) 되물림 관리_WELLS 총판, B2B*/ SELECT M.BASE_YM /*발생년월*/ , M.PERF_YM AS SL_YM /*매출년월*/ , (SELECT X.PRTNR_KNM FROM TB_OGBS_PRTNR_BAS X WHERE X.PRTNR_NO = M1.HOO_PRTNR_NO AND X.OG_TP_CD = M1.OG_TP_CD) AS OG_NM , A.OG_CD /*소속코드*/ , A.PRTNR_KNM /*판매자명*/ , M.PRTNR_NO /*파트너번호*/ , M.CNTR_NO /*계약번호*/ , M.CNTR_SN /*계약일련번호*/ , M.CNTR_NO || M.CNTR_SN AS CNTR_NO_SN /*계약상세번호*/ , D.CST_KNM /*고객명*/ , M.PD_CD /*제품코드(상품코드)*/ , D.PD_CLSF_NM /*제품명(상품명)*/ , D.MODEL_NO /*모델명*/ , (CASE WHEN D.SELL_DSC_DV_CD IS NOT NULL THEN D.SELL_DSC_DV_CD || '-' || DECODE(D.STPL_PTRM, '12', '1', '24', '2', '36', '3', '48', '4', '60', '5', '72', '6') ELSE '0' END) AS SELL_DSC_DV_CD /*할인구분(ASIS: D-C)*/ , (CASE WHEN D.SELL_DSC_DV_CD IS NOT NULL THEN D.SELL_DSC_DV_CD || '-' || DECODE(D.STPL_PTRM, '12', '1', '24', '2', '36', '3', '48', '4', '60', '5', '72', '6') ELSE '0' END) AS SELL_DSC_TP_CD /*할인유형(ASIS: P)*/ , D.SV_PD_TP_CD AS PMOT_USWY_DV_CD /*업소*/ , M.BFSVC_PRD_CD /*주기*/ , B1.MCHN_CH_TP_CD AS MCHN_CH1 /*기변영*/ , B2.MCHN_CH_TP_CD AS MCHN_CH2 /*기변센*/ , M.RCPDT /*계약일자*/ , M.SL_DT /*매출일자*/ , M.CAN_DT /*취소일자*/ , (CASE WHEN P1.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN NVL(P1.REDF_ADSB_OC_AMT, 0) WHEN P1.REDF_ADSB_CTR_CNFM_AMT IS NOT NULL THEN NVL(P1.REDF_ADSB_CTR_CNFM_AMT, 0) ELSE 0 END) AS REDF_AMT /*되물림 금액*/ FROM TB_FEAM_WELS_NRCTR_MM_CL M /*웰스순주문계약월마감*/ LEFT OUTER JOIN TB_FEAM_MM_PRTNR_HOO_IZ M1 ON M.PRTNR_NO = M1.PRTNR_NO AND M.PERF_YM = M1.BASE_YM AND M.OG_TP_CD = M1.OG_TP_CD AND M1.PERF_DV_CD = '2' /*총판의 경우, 15직급과 7직급(PERF_DV_CD = '2')만 존재, 판매자는 15, 업체는 7*/ INNER JOIN TB_OGBS_PRTNR_BAS A /*파트너기본*/ ON M.PRTNR_NO = A.PRTNR_NO AND M.OG_TP_CD = A.OG_TP_CD LEFT OUTER JOIN (SELECT BASE_CNTR_NO , BASE_CNTR_SN , MCHN_CH_TP_CD FROM TB_SSCT_MCHN_CH_IZ) B1 /*기기변경내역.상품구분1(ASIS유형1), TODO: 유형1이 신규인지 유형2가 신규인지 확인필요*/ ON M.CNTR_NO = B1.BASE_CNTR_NO AND M.CNTR_SN = B1.BASE_CNTR_SN LEFT OUTER JOIN (SELECT OJ_CNTR_NO , OJ_CNTR_SN , MCHN_CH_TP_CD FROM TB_SSCT_MCHN_CH_IZ) B2 /*기기변경내역.상품구분1(ASIS유형1), TODO: 유형1이 신규인지 유형2가 신규인지 확인필요*/ ON M.CNTR_NO = B2.OJ_CNTR_NO AND M.CNTR_SN = B2.OJ_CNTR_SN LEFT OUTER JOIN ( SELECT D1.CNTR_NO /*계약번호*/ , D1.CNTR_SN /*계약일련번호*/ , D3.CST_NO /*고객번호*/ , D3.CST_KNM /*고객명*/ , D4.MODEL_NO /*모델번호(모델명)*/ , D4.CST_BASE_PD_ABBR_NM AS PD_CLSF_NM /*제품명*/ , D6.SELL_DSC_DV_CD , D6.SELL_DSC_TP_CD , D6.STPL_PTRM , P.SV_PD_TP_CD FROM TB_FEAM_WELS_NRCTR_MM_CL D1 /*웰스순주문계약월마감*/ LEFT OUTER JOIN TB_SVPD_CST_SV_EXCN_IZ S /* 고객서비스수행내역 */ ON D1.CNTR_NO = S.CNTR_NO AND D1.CNTR_SN = S.CNTR_SN LEFT OUTER JOIN TB_PDBS_PD_BAS P /* 상품기본 - 서비스상품 */ ON S.SV_PD_CD = P.PD_CD INNER JOIN TB_SSCT_CNTR_BAS D2 /*계약기본*/ ON D1.CNTR_NO = D2.CNTR_NO INNER JOIN TB_SSCT_CNTR_DTL D6 ON D1.CNTR_NO = D6.CNTR_NO AND D1.CNTR_SN = D6.CNTR_SN INNER JOIN TB_CUBS_CST_BAS D3 /*고객기본*/ ON D2.CNTR_CST_NO = D3.CST_NO LEFT OUTER JOIN TB_PDBS_PD_BAS D4 /*상품기본*/ ON D6.BASE_PD_CD = D4.PD_CD LEFT OUTER JOIN TB_PDBS_PD_CLSF_BAS D5 /*상품분류기본*/ ON D4.PD_MCLSF_ID = D5.PD_CLSF_ID /*상품기본.중분류ID = 상품분류기본.상품분류ID*/ AND D5.PD_TP_CD = 'P' /* 상품유형코드 = 'P'(기준상품) */ WHERE D1.DTA_DL_YN = 'N'
AND D1.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} /*발생년월*/
AND D1.PERF_YM BETWEEN #{slYmFrom} AND #{slYmTo} /*실적년월*/
AND D1.OG_TP_CD = #{ogTpCd} /*조직유형*/
) D /*고객 및 상품 정보*/ ON M.CNTR_NO = D.CNTR_NO AND M.CNTR_SN = D.CNTR_SN LEFT OUTER JOIN TB_FEDD_FEE_REDF_ADSB_DTL P1 ON M.BASE_YM = P1.REDF_ADSB_OC_YM AND M.PERF_YM = P1.PERF_YM AND M.PRTNR_NO = P1.PRTNR_NO AND M.OG_TP_CD = P1.OG_TP_CD AND M.CNTR_NO = P1.CNTR_NO AND M.CNTR_SN = P1.CNTR_SN WHERE M.DTA_DL_YN = 'N'
AND M.OG_TP_CD = #{ogTpCd} AND (CASE WHEN #{ogTpCd} = 'W01' THEN '02' WHEN #{ogTpCd} = 'W02' THEN '03' WHEN #{ogTpCd} = 'W03' THEN '04' WHEN #{ogTpCd} = 'W05' THEN '05' WHEN #{ogTpCd} = 'W04' THEN '06' WHEN #{ogTpCd} = 'Z02' THEN '07' END) = M.CNTR_PERF_CRT_DV_CD /*계약실적생성구분코드: 되물림*/
AND M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo}
AND M.PERF_YM BETWEEN #{slYmFrom} AND #{slYmTo}
AND A.OG_CD BETWEEN #{ogCdFrom} AND #{ogCdTo}
AND M.PERF_TP_CD IN ('1', '3') /*화면값이 취소(0202)면 IN 조건 1:취소, 3:매변*/
AND M.PERF_TP_CD = '10' /*화면값이 연체(0203)면 10: 연체(전집)*/

/*수당(실적) 되물림 관리 - 파트너 되물림 대상 집계 현황 조회 (통합) 목록 조회 - 영업부 되물림 생성 조회*/
/*============================ P조직일때, 쿼리 튜닝 P조직,M조직 따로 하기위해 분리 =============================*/ SELECT /*+ PUSH_PRED(M) use_nl(D M) */ D.PERF_YM /*실적년월*/ , D.REDF_ADSB_OC_YM AS BASE_YM /*발생년월*/ , AK05.OG_CD /*소속코드*/ , AK05.PRTNR_KNM /*성명*/ , AK05.PRTNR_NO /*파트너번호*/ , AK05.PSTN_DV_CD /*직급*/ , AK05.OG_TP_CD /*조직유형*/ , F_CMZ_CD_NM(#{session.tenantId}, 'OG_TP_CD', AK05.OG_TP_CD, #{session.langId}) AS OG_TP_NM /*조직유형*/ , F_CMZ_CD_NM(#{session.tenantId}, (CASE WHEN AK05.OG_TP_CD = 'W01' THEN 'PQLF_DV_CD' ELSE 'QLF_DV_CD' END), AK05.QLF_DV_CD, #{session.langId}) AS QLF_DV_NM /*자격*/ , (CASE WHEN AK55.PSTN_DV_CD = '7' THEN '지점장' WHEN AK55.BZ_STAT_CD = '2' THEN '해약' ELSE F_CMZ_CD_NM(#{session.tenantId}, 'QLF_DV_CD', AK55.QLF_DV_CD, #{session.langId}) END) AS QLF_DV_NM_1 /*자격(M+1)*/ , (CASE WHEN AK05.PSTN_DV_CD = '10' AND M.PERF_YM >= '201801' /*2018 개편기준(ASIS기준)*/ THEN (SELECT X.QLF_DV_CD FROM TB_OGBS_MM_PRTNR_IZ X WHERE X.OG_TP_CD = 'W02' AND X.BASE_YM = M.PERF_YM AND X.PRTNR_NO = M.PRTNR_NO) ELSE '' END) AS DSTRC_GD /*지구등급*/ , M.ATDC_DC AS ATDC_DC /*미팅(해당 실적년월의 출근일수)*/
, F_CMZ_CD_NM(#{session.tenantId}, 'PERF_DV_CD', D.PERF_DV_CD_CHK, #{session.langId}) AS PERF_DV_CD /*실적구분*/ , '' AS BRCH_DEC /*지점순감, TODO: AB7750P.지역단장 성과관리 매핑 테이블을 찾지못했음*/ , (SELECT CASE WHEN X.PSTN_DV_CD = '10' THEN 'Y' ELSE 'N' END FROM TB_OGBS_MM_PRTNR_IZ X /*월파트너내역*/ WHERE X.DTA_DL_YN = 'N' AND X.BASE_YM = '201903' AND X.OG_TP_CD = M.PRTNR_NO AND X.PRTNR_NO = M.PRTNR_NO) AS MARCH_DSTRC_YN /*3월지구장*/ , AK06.MNGT_YM AS CH_YM /*수석승급기준월(관리년월)*/ , AK06.UPGR_YM AS WP_CH_YM /*승급년월(WELLS-P)*/ , (SELECT CASE WHEN X.EDUC_CPC_ACKMT_YN = 'Y' THEN SUBSTR(X.EDUC_CPC_ACKMT_DT, 1, 6) ELSE '' END FROM TB_PSCA_MCPTN_EDUC_IZ X /*월별파트너교육내역*/ WHERE X.PRTNR_NO = D.PRTNR_NO AND X.OG_TP_CD = D.OG_TP_CD AND X.EDUC_CRSE_NO IN ('100', '106') /*ASIS 기준, '100', '106' 고정*/
AND X.OG_TP_CD = #{ogTpCd}
ORDER BY X.EDUC_CPC_ACKMT_DT DESC FETCH FIRST 1 ROW ONLY ) AS EDUC_CPC_MM /*수석정착 수료월*/ , CASE WHEN NVL(D.SETTLE_REDF, 0) > 0 THEN 'Y' ELSE '' END AS AKSD48_YN /*정착 지급여부*/ , CASE WHEN (SELECT X.EDUC_CPC_ACKMT_YN FROM TB_PSCA_MCPTN_EDUC_IZ X /*월별파트너교육내역*/ WHERE X.PRTNR_NO = D.PRTNR_NO AND X.OG_TP_CD = D.OG_TP_CD
AND X.OG_TP_CD = #{ogTpCd}
ORDER BY X.EDUC_CPC_ACKMT_DT DESC FETCH FIRST 1 ROW ONLY /*TODO: 동일한 날짜(EDUC_CRSE_CRT_BASE_YM)에 EDUC_CRSE_NO가 다른 데이터가 있어서 여러 건 출력되어서 EDUC_CPC_ACKMT_DT기준으로 최신 데이터 뽑음. 맞는지 확인 필요*/ ) = 'Y' THEN 'Y' ELSE '' END AS EDU17_YN /*플래너실전 수료여부*/ , NVL(C.SELL_AMT, 0) AS SELL_AMT /*귀속월매출*/ , NVL(C.ACKMT_PERF_RT, 0) || '%' AS ACKMT_PERF_RT /*지급율*/ , (CASE WHEN AK05.PSTN_DV_CD = '10' THEN (SELECT CASE WHEN COUNT(1) > 1 THEN '' ELSE 'Y' END FROM TB_OGBS_OG_BAS A WHERE A.OG_CD = AK05.OG_CD AND A.OG_TP_CD = AK05.OG_TP_CD) END) AS ONETEAM_YN /*단독지구*/ , NVL(M.HOME_CARE, 0) AS HOME_CARE /*홈케어실적*/ , NVL(M.MC_PERF, 0) AS MC_PERF /*기변실적*/ /*환경가전*/ , NVL(M.TB_ENVR_ELHM, 0) AS TB_ENVR_ELHM /*환경가전.귀속전월실적*/ , NVL(M.T_ENVR_ELHM, 0) AS T_ENVR_ELHM /*환경가전.귀속월실적*/ -- 되물림전실적 : 되물림후실적 - 되물림실적 , NVL(M.A_ENVR_ELHM, 0) + NVL(M.ENVR_ELHM, 0) AS B_ENVR_ELHM /*환경가전.되물림전실적*/ , NVL(M.ENVR_ELHM, 0) AS ENVR_ELHM -- 되물림후실적 = 되물림후실적이 0이거나 없으면, 최초실적에서 되물림이 일어나지 않았다는것이므로, 되물림전실적을 보여준다. 아니면 되물림 후실적 보여줌 , NVL(M.A_ENVR_ELHM, 0) AS A_ENVR_ELHM /*환경가전(건수)*/ , NVL(M.TB_ENVR_ELHM_CNT, 0) AS TB_ENVR_ELHM_CNT /*환경가전(건수).귀속전월실적*/ , NVL(M.T_ENVR_ELHM_CNT, 0) AS T_ENVR_ELHM_CNT /*환경가전(건수).귀속월실적*/ , NVL(M.A_ENVR_ELHM_CNT, 0) + NVL(M.ENVR_ELHM_CNT, 0) AS B_ENVR_ELHM_CNT /*환경가전.되물림전실적*/ , NVL(M.ENVR_ELHM_CNT, 0) AS ENVR_ELHM_CNT /*환경가전(건수).되물림실적*/ , NVL(M.A_ENVR_ELHM_CNT, 0) AS A_ENVR_ELHM_CNT /*환경가전(건수).되물림후실적*/ /*환경가전(개인)*/ , 0 AS TB_ENVR_ELHM_CNT_PR /*환경가전(건수).귀속전월실적*/ , 0 AS T_ENVR_ELHM_CNT_PR /*환경가전(건수).귀속월실적*/ , 0 AS B_ENVR_ELHM_CNT_PR /*환경가전.되물림전실적*/ , 0 AS ENVR_ELHM_CNT_PR /*환경가전(건수).되물림실적*/ , 0 AS A_ENVR_ELHM_CNT_PR /*환경가전(건수).되물림후실적*/ /*환경가전(조직)*/ , 0 AS TB_ENVR_ELHM_CNT_JO /*환경가전(건수).귀속전월실적*/ , 0 AS T_ENVR_ELHM_CNT_JO /*환경가전(건수).귀속월실적*/ , 0 AS B_ENVR_ELHM_CNT_JO /*환경가전.되물림전실적*/ , 0 AS ENVR_ELHM_CNT_JO /*환경가전(건수).되물림실적*/ , 0 AS A_ENVR_ELHM_CNT_JO /*환경가전(건수).되물림후실적*/
/*환경가전(기준가격)*/ , NVL(M.T_ENVR_ELHM_BASE_AMT, 0) AS T_ENVR_ELHM_BASE_AMT /*환경가전(기준가격).귀속월실적*/ , NVL(M.A_ENVR_ELHM_BASE_AMT, 0) + NVL(M.ENVR_ELHM_BASE_AMT, 0) AS B_ENVR_ELHM_BASE_AMT /*환경가전(기준가격).되물림전실적*/ , NVL(M.ENVR_ELHM_BASE_AMT, 0) AS ENVR_ELHM_BASE_AMT /*환경가전(기준가격).되물림실적*/ , NVL(M.A_ENVR_ELHM_BASE_AMT, 0) AS A_ENVR_ELHM_BASE_AMT /*환경가전(기준가격).되물림후실적*/ /*환경가전 렌탈*/ , NVL(M.TB_ENVR_ELHM_RENTAL, 0) AS TB_ENVR_ELHM_RENTAL /*환경가전 렌탈.귀속전월실적*/ , NVL(M.T_ENVR_ELHM_RENTAL, 0) AS T_ENVR_ELHM_RENTAL /*환경가전 렌탈.귀속월실적*/ , NVL(M.A_ENVR_ELHM_RENTAL, 0) + NVL(M.ENVR_ELHM_RENTAL, 0) AS B_ENVR_ELHM_RENTAL /*환경가전 렌탈.되물림전실적*/ , NVL(M.ENVR_ELHM_RENTAL, 0) AS ENVR_ELHM_RENTAL /*환경가전 렌탈.되물림실적*/ , NVL(M.A_ENVR_ELHM_RENTAL, 0) AS A_ENVR_ELHM_RENTAL /*환경가전 렌탈.되물림후실적*/ /*환경가전 렌탈(조직)*/ , NVL(CASE WHEN M.PERF_DV_CD = 'J' THEN NVL(M.TB_ENVR_ELHM_RENTAL, 0) END, 0) AS TB_ENVR_ELHM_RENTAL_JO /*환경가전 렌탈(조직).귀속전월실적*/ , NVL(CASE WHEN M.PERF_DV_CD = 'J' THEN NVL(M.T_ENVR_ELHM_RENTAL, 0) END, 0) AS T_ENVR_ELHM_RENTAL_JO /*환경가전 렌탈(조직).귀속월실적*/ , NVL(CASE WHEN M.PERF_DV_CD = 'J' THEN NVL(M.A_ENVR_ELHM_RENTAL, 0) + NVL(M.ENVR_ELHM_RENTAL, 0) END, 0) AS B_ENVR_ELHM_RENTAL_JO /*환경가전 렌탈(조직).되물림전실적*/ , NVL(CASE WHEN M.PERF_DV_CD = 'J' THEN NVL(M.ENVR_ELHM_RENTAL, 0) END, 0) AS ENVR_ELHM_RENTAL_JO /*환경가전 렌탈(조직).되물림실적*/ , NVL(CASE WHEN M.PERF_DV_CD = 'J' THEN NVL(M.A_ENVR_ELHM_RENTAL, 0) END, 0) AS A_ENVR_ELHM_RENTAL_JO /*환경가전 렌탈(조직).되물림후실적*/ /*환경가전 렌탈(개인)*/ , NVL(CASE WHEN M.PERF_DV_CD = 'P' THEN NVL(M.TB_ENVR_ELHM_RENTAL, 0) END, 0) AS TB_ENVR_ELHM_RENTAL_PR /*환경가전 렌탈(개인).귀속전월실적*/ , NVL(CASE WHEN M.PERF_DV_CD = 'P' THEN NVL(M.T_ENVR_ELHM_RENTAL, 0) END, 0) AS T_ENVR_ELHM_RENTAL_PR /*환경가전 렌탈(개인).귀속월실적*/ , NVL(CASE WHEN M.PERF_DV_CD = 'P' THEN NVL(M.A_ENVR_ELHM_RENTAL, 0) + NVL(M.ENVR_ELHM_RENTAL, 0) END, 0) AS B_ENVR_ELHM_RENTAL_PR /*환경가전 렌탈(개인).되물림전실적*/ , NVL(CASE WHEN M.PERF_DV_CD = 'P' THEN NVL(M.ENVR_ELHM_RENTAL, 0) END, 0) AS ENVR_ELHM_RENTAL_PR /*환경가전 렌탈(개인).되물림실적*/ , NVL(CASE WHEN M.PERF_DV_CD = 'P' THEN 