) >= 1 THEN 'Y' ELSE '' END) AS CPSN_REDF_YN /*강제되물림포함여부*/ FROM ( SELECT M.REDF_ADSB_OC_YM , M.PERF_YM , M.OG_TP_CD , M.PRTNR_NO , M.REDF_ADSB_TP_CD , M.PERF_DV_CD /*분기처리(M조직, P조직)*/ /*M조직(지구장 이하): 기변수당, 기기변경,수석플래너,홈케어,환경가전 목표달성,환경가전영업,환경가전 미팅,환경가전 비례,환경가전외 영업,환경가전외 미팅,환경가전외 비례,판매장려,교육,정착,판매장려 소급,환경가전 조직,환경가전 조직미팅 * 환경가전외 조직비례,환경가전 조직외미팅*/ /*M조직(지점장 이상): 기변수당,수석플래너,홈케어,기기변경,판매장려,교육,정착,환경가전 목표달성,환경가전영업,환경가전외 영업,환경가전 비례,환경가전외 비례,환경가전 미팅,환경가전외 미팅,환경가전 조직,환경가전외 조직,환경가전외 조직비례 * 환경가전 조직미팅, 환경가전외 조직미팅,조직판매 장려, 신규판매, 조직관리*/ /*P조직(지점장 이상): 가전 개인비례, 가전 외 개인비례, 개인 판매장려, 가전 조직비례, 가전 외 조직비례, 조직 판매장려, 정착, 정액*/
/*M조직(지구장 이하)*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W020005' AND M.PERF_YM <= '201903' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS MCHN_CH_REDF201903 /*기변수당(TODO: ASIS기준 2019년 03월 이전엔 기변수당, 코드는 같음)*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W020005' AND M.PERF_YM >= '201904' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS MCHN_CH_REDF201904 /*기기변경(TODO: ASIS기준 2019년 04월 이후엔 기기변경, 코드는 같음)*/ , SUM(NVL((CASE WHEN M.FEE_CD = '@@@' /* TODO: 수석플래너 라는 수수료코드는 존재치 않는데 어떤 코드인지 찾아야함*/ THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS SP_REDF /*수석플래너*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020078', 'W020079') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS HOME_CARE_REDF /*홈케어*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W020051' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_TRG_REDF /*환경가전 목표달성*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020001', 'W020002') AND M.PERF_YM <= '201903' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_BZNS_REDF201903 /*환경가전영업 TODO: 201903까지 환경가전영업, 201904부터 환경가전 비례*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020001', 'W020002') AND M.PERF_YM >= '201904' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_BZNS_REDF201904 /*환경가전 비례 TODO: 201903까지 환경가전영업, 201904부터 환경가전 비례*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W020062' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_METG_REDF /*환경가전 미팅*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020003', 'W020004') AND M.PERF_YM <= '201903' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_EXCP_BZNS_REDF201903 /*환경가전외 영업 TODO: 201903까지 환경가전외 영업, 201904부터 환경가전외 비례*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020003', 'W020004') AND M.PERF_YM >= '201904' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_EXCP_BZNS_REDF201904 /*환경가전외 비례 TODO: 201903까지 환경가전외 영업, 201904부터 환경가전외 비례*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W020063' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_EXCP_METG_REDF /*환경가전외 미팅*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020012', 'W020008', 'W020009', 'W020010', 'W020011', 'W020115' ) THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS SELL_ENCRG_REDF /*판매장려*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020077', 'W020076', 'W020075', 'W020074', 'W020116') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS EDU_REDF /*교육*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020082', 'W020081', 'W020080') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS SETTLE_REDF /*정착*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W020083' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS SELL_ENCRG_RETT_REDF /*판매장려 소급*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020019', 'W020020', 'W020021' ) THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_OG_REDF /*환경가전 조직*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W020065' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_OG_METG /*환경가전 조직미팅*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W020024' AND M.PERF_YM <= '201903' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_EXCP_OG_REDF201903 /*환경가전외 조직비례(~2019.03)*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020022', 'W020023') AND M.PERF_YM >= '201904' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_EXCP_OG_REDF201904 /*환경가전외 조직(2019.04~)*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W020066' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS ENVR_EXCP_OG_METG_REDF /*환경가전외 조직미팅*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020017', 'W020046', 'W020044', 'W020045', 'W020043') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS OG_SELL_ENCRG_REDF /*조직판매 장려*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020039', 'W020013', 'W020040') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS NW_SELL_REDF /*신규판매*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W020018', 'W020047', 'W020048') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS OG_MGT_REDF /*조직관리*/
/*P조직(지구장 이하)*/ /*P조직(지구장 이하): 가전,가전외비례,판매장려,미팅,정착,정액*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W010001', 'W010101') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_ENVR_PR_REDF /*가전개인비례*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W010002', 'W010102') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_ENVR_EXCP_PR_REDF /*가전 외 개인비례*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W010003', 'W010103') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_PR_SELL_ENCRG_REDF /*개인판매장려*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W010004' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_METG_REDF /*미팅수수료*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W010005', 'W010105') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_SETTLE_REDF_DSTRC /*정착수수료(지구장이하)*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W010023', 'W010123') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_FXAM_REDF_DSTRC /*정액(지구장이하)*/
, SUM(NVL((CASE WHEN M.FEE_CD IN ('W010023', 'W010123') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_FXAM_REDF_BRCH /*정액(지점장이상)*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W010011' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_ENVR_OG_REDF /*가전 조직비례*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W010012' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_ENVR_EXCP_OG_REDF /*가전외 조직비례*/ , SUM(NVL((CASE WHEN M.FEE_CD = 'W010013' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_OG_SELL_ENCRG_REDF /*조직 판매장려*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W010005', 'W010105') THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_SETTLE_REDF_BRCH /*정착수수료(지점장이상)*/ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W010021', 'W010121', 'W010022') AND M.REDF_ADSB_TP_CD = '0202' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_LIF_REDF_AMT /*상조 취소 되물림(개인,조직 모두 표현(화면상)) 상조취소 */ , SUM(NVL((CASE WHEN M.FEE_CD IN ('W010021', 'W010121', 'W010022') AND M.REDF_ADSB_TP_CD = '0203' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS WP_LIF_0203_REDF_AMT /*상조 연체 되물림(개인,조직 모두 표현(화면상)) 상조연체 */ , SUM(CASE WHEN M.REDF_ADSB_TP_CD = '0202' THEN NVL2(M.REDF_ADSB_CTR_CNFM_AMT, M.REDF_ADSB_CTR_CNFM_AMT, M.REDF_ADSB_OC_AMT) ELSE 0 END) AS CAN_REDF_AMT /*취소되물림(전체 SUM)*/ , SUM(NVL((CASE WHEN M.REDF_ADSB_TP_CD = '0203' THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS DLQ_REDF_AMT /*연체되물림*/ , SUM(NVL((CASE WHEN M.REDF_ADSB_TP_CD = '0203' AND S.SELL_TP_CD_2 IS NOT NULL THEN S.FEE_ACKMT_BASE_AMT END), 0)) AS REDF_PERF_RENTAL /*되물림구분이 연체일 때, 되물림실적.렌탈*/ , SUM(NVL((CASE WHEN M.REDF_ADSB_TP_CD = '0203' AND S.SELL_TP_CD_2 IS NOT NULL THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS DLQ_REDF_PERF_RENTAL /*되물림구분이 연체일 때, 연체되물림실적.렌탈*/ , SUM(NVL((CASE WHEN M.REDF_ADSB_TP_CD = '0203' AND S.SELL_TP_CD_1 IS NOT NULL THEN S.FEE_ACKMT_BASE_AMT END), 0)) AS REDF_PERF_EXCP /*되물림구분이 연체일 때, 되물림실적.가전외일시불*/ , SUM(NVL((CASE WHEN M.REDF_ADSB_TP_CD = '0203' AND S.SELL_TP_CD_1 IS NOT NULL THEN (CASE WHEN M.REDF_ADSB_CTR_CNFM_AMT IS NULL THEN M.REDF_ADSB_OC_AMT ELSE M.REDF_ADSB_CTR_CNFM_AMT END) END), 0)) AS DLQ_REDF_PERF_EXCP /*되물림구분이 연체일 때, 되물림실적.가전외일시불*/ FROM TB_FEDD_FEE_REDF_ADSB_BAS M /*수수료되물림재지급기본*/ LEFT OUTER JOIN ( SELECT S1.FEE_REDF_ADSB_ID , MAX(CASE WHEN S2.SELL_TP_CD = '1' THEN '1' END) AS SELL_TP_CD_1 , MAX(CASE WHEN S2.SELL_TP_CD = '2' THEN '2' END) AS SELL_TP_CD_2
, SUM(S2.FEE_ACKMT_BASE_AMT) AS FEE_ACKMT_BASE_AMT /*수수료인정기준금액, = 되물림실적*/ , SUM(S1.REDF_ADSB_OC_AMT) AS REDF_ADSB_OC_AMT , SUM(S1.REDF_ADSB_CTR_CNFM_AMT) AS REDF_ADSB_CTR_CNFM_AMT FROM TB_FEDD_FEE_REDF_ADSB_DTL S1 /*수수료되물림재지급상세*/ LEFT OUTER JOIN TB_SSCT_CNTR_DTL S2 /*계약상세*/ ON S1.CNTR_NO = S2.CNTR_NO AND S1.CNTR_SN = S2.CNTR_SN WHERE 1 = 1
AND S1.PERF_YM BETWEEN #{perfYmFrom} AND #{perfYmTo} /*PARAM: 실적년월*/
AND S1.REDF_ADSB_OC_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} /*PARAM: 발생년월*/
GROUP BY S1.FEE_REDF_ADSB_ID
) S ON M.FEE_REDF_ADSB_ID = S.FEE_REDF_ADSB_ID WHERE M.DTA_DL_YN = 'N' AND M.OG_TP_CD = #{ogTpCd}
AND M.PRTNR_NO = #{prtnrNo}
AND M.REDF_ADSB_TP_CD IN( '0202', '0203') /*P조직일 경우에는 취소연체 둘다*/
AND M.REDF_ADSB_TP_CD = #{redfAdsbTpCd}
AND M.PERF_DV_CD = '0'
/*수수료체크리스트 화면 기준으로는 지점장 이상일 때, 개인판매도 포함하여 보여주기 때문에 0, 2로 변경*/ AND M.PERF_DV_CD IN ('0','2')
AND M.PERF_YM BETWEEN #{perfYmFrom} AND #{perfYmTo}
AND M.REDF_ADSB_OC_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo}
GROUP BY M.REDF_ADSB_OC_YM , M.PERF_YM , M.OG_TP_CD , M.PRTNR_NO , M.REDF_ADSB_TP_CD , M.PERF_DV_CD ) D /*되물림 수수료*/ LEFT OUTER JOIN LATERAL ( SELECT MAX(B.BASE_YM) AS BASE_YM , A.PERF_YM , TO_CHAR(ADD_MONTHS(TO_DATE(A.PERF_YM || '01', 'YYYYMMDD'), 1), 'YYYYMM') AS PERF_YM_1 , A.OG_TP_CD , A.PRTNR_NO
, NVL(B.HOME_CARE, 0) AS HOME_CARE /*홈케어실적*/ , NVL(B.MC_PERF, 0) AS MC_PERF /*기변실적*/ , NVL(B.ATDC_DC, 0) AS ATDC_DC /*미팅(출근일수)*/ , NVL(A.T_ENVR_ELHM, 0) AS T_ENVR_ELHM /*환경가전.귀속월실적*/ , NVL(A.T_ENVR_ELHM_CNT, 0) AS T_ENVR_ELHM_CNT /*환경가전(건수).귀속월실적*/ , NVL(A.T_ENVR_ELHM_CNT_PR, 0) AS T_ENVR_ELHM_CNT_PR /*환경가전 건수(개인).귀속월실적*/ , NVL(A.T_ENVR_ELHM_CNT_JO, 0) AS T_ENVR_ELHM_CNT_JO /*환경가전 건수(조직).귀속월실적*/ , NVL(A.T_ENVR_ELHM_BASE_AMT, 0) AS T_ENVR_ELHM_BASE_AMT /*환경가전(기준금액).귀속월실적*/ , NVL(A.T_ENVR_ELHM_RENTAL, 0) AS T_ENVR_ELHM_RENTAL /*환경가전 렌탈.귀속월실적*/ , NVL(A.T_ENVR_ELHM_RENTAL_PR, 0) AS T_ENVR_ELHM_RENTAL_PR /*환경가전 렌탈(개인).귀속월실적*/ , NVL(A.T_ENVR_ELHM_RENTAL_JO, 0) AS T_ENVR_ELHM_RENTAL_JO /*환경가전 렌탈(조직).귀속월실적*/ , NVL(A.T_ENVR_ELHM_SPAY, 0) AS T_ENVR_ELHM_SPAY /*환경가전 일시불.귀속월실적*/ , NVL(A.T_ENVR_ELHM_SPAY_PR, 0) AS T_ENVR_ELHM_SPAY_PR /*환경가전 일시불(개인).귀속월실적*/ , NVL(A.T_ENVR_ELHM_SPAY_JO, 0) AS T_ENVR_ELHM_SPAY_JO /*환경가전 일시불(조직).귀속월실적*/ , NVL(A.T_ENVR_ELHM_EXCP, 0) AS T_ENVR_ELHM_EXCP /*환경가전외.귀속월실적*/ , NVL(A.T_ENVR_ELHM_EXCP_PR, 0) AS T_ENVR_ELHM_EXCP_PR /*환경가전외 일시불(개인).귀속월실적*/ , NVL(A.T_ENVR_ELHM_EXCP_JO, 0) AS T_ENVR_ELHM_EXCP_JO /*환경가전외 일시불(조직).귀속월실적*/ , NVL(A.T_ENVR_ELHM_EXCP_FXAM, 0) AS T_ENVR_ELHM_EXCP_FXAM /*환경가전외 정액.귀속월실적*/ , NVL(A.T_ENVR_ELHM_FXAM_PR, 0) AS T_ENVR_ELHM_FXAM_PR /*환경가전 정액(개인).귀속월실적*/ , NVL(A.T_ENVR_ELHM_EXCP_FXAM_PR, 0) AS T_ENVR_ELHM_EXCP_FXAM_PR /*환경가전외 정액(개인).귀속월실적*/ , NVL(A.T_NW_SELL_CNT_PR, 0) AS T_NW_SELL_CNT_PR /*신규판매건수(개인).귀속월실적*/ , NVL(A.T_NW_SELL_CNT_JO, 0) AS T_NW_SELL_CNT_JO /*신규판매건수(개인).귀속월실적*/ , NVL(A.T_OG_MGT_PPL, 0) AS T_OG_MGT_PPL /*조직관리 실활동(명).귀속월실적*/ , NVL(A.T_MC, 0) AS T_MC /*기변(WELLS-P).귀속월실적*/ , NVL(A.T_FXAM, 0) AS T_FXAM /*정액(WELLS-P).귀속월실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM, 0) END) AS A_ENVR_ELHM /*환경가전.되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_CNT, 0) END) AS A_ENVR_ELHM_CNT /*환경가전(건수).되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_CNT_PR, 0) END) AS A_ENVR_ELHM_CNT_PR /*환경가전 건수(개인).되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_CNT_JO, 0) END) AS A_ENVR_ELHM_CNT_JO /*환경가전 건수(조직).되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_BASE_AMT, 0) END) AS A_ENVR_ELHM_BASE_AMT /*환경가전(기준금액).되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_RENTAL, 0) END) AS A_ENVR_ELHM_RENTAL /*환경가전 렌탈.되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_RENTAL_PR, 0) END) AS A_ENVR_ELHM_RENTAL_PR /*환경가전 렌탈(개인).되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_RENTAL_JO, 0) END) AS A_ENVR_ELHM_RENTAL_JO /*환경가전 렌탈(조직).되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_SPAY, 0) END) AS A_ENVR_ELHM_SPAY /*환경가전 일시불.되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_SPAY_PR, 0) END) AS A_ENVR_ELHM_SPAY_PR /*환경가전 일시불(개인).되물림전실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_SPAY_JO, 0) END) AS A_ENVR_ELHM_SPAY_JO /*환경가전 일시불(조직).되물림전실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_EXCP, 0) END) AS A_ENVR_ELHM_EXCP /*환경가전외.되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_EXCP_PR, 0) END) AS A_ENVR_ELHM_EXCP_PR /*환경가전외 일시불(개인).되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_EXCP_JO, 0) END) AS A_ENVR_ELHM_EXCP_JO /*환경가전외 일시불(조직).되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_FXAM_PR, 0) END) AS A_ENVR_ELHM_FXAM_PR /*환경가전 정액(개인).되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_EXCP_FXAM_PR, 0) END) AS A_ENVR_ELHM_EXCP_FXAM_PR /*환경가전외 정액(개인).되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_ENVR_ELHM_EXCP_FXAM, 0) END) AS A_ENVR_ELHM_EXCP_FXAM /*환경가전외 정액.되물림후실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_NW_SELL_CNT_PR, 0) END) AS A_NW_SELL_CNT_PR /*신규판매건수(개인).되물림전실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_NW_SELL_CNT_JO, 0) END) AS A_NW_SELL_CNT_JO /*신규판매건수(조직).되물림전실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_OG_MGT_PPL, 0) END) AS A_OG_MGT_PPL /*조직관리 실활동(명).되물림전실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_MC, 0) END) AS A_MC /*기변(WELLS-P).되물림전실적*/ , MAX(CASE WHEN B.RN = 1 THEN NVL(B.A_FXAM, 0) END) AS A_FXAM /*정액(WELLS-P).되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM, 0) END) AS B_ENVR_ELHM /*환경가전.되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_CNT_PR, 0) END) AS B_ENVR_ELHM_CNT_PR /*환경가전 건수(개인).되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_CNT_JO, 0) END) AS B_ENVR_ELHM_CNT_JO /*환경가전 건수(조직).되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_CNT, 0) END) AS B_ENVR_ELHM_CNT /*환경가전(건수).되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_BASE_AMT, 0) END) AS B_ENVR_ELHM_BASE_AMT /*환경가전(기준금액).되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_RENTAL, 0) END) AS B_ENVR_ELHM_RENTAL /*환경가전 렌탈.되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_RENTAL_PR, 0) END) AS B_ENVR_ELHM_RENTAL_PR /*환경가전 렌탈(개인).되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_RENTAL_JO, 0) END) AS B_ENVR_ELHM_RENTAL_JO /*환경가전 렌탈(조직).되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_SPAY, 0) END) AS B_ENVR_ELHM_SPAY /*환경가전 일시불.되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_SPAY_PR, 0) END) AS B_ENVR_ELHM_SPAY_PR /*환경가전 일시불(개인).되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_SPAY_JO, 0) END) AS B_ENVR_ELHM_SPAY_JO /*환경가전 일시불(조직).되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_EXCP, 0) END) AS B_ENVR_ELHM_EXCP /*환경가전외.되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_EXCP_PR, 0) END) AS B_ENVR_ELHM_EXCP_PR /*환경가전외 일시불(개인).되물림후실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_EXCP_JO, 0) END) AS B_ENVR_ELHM_EXCP_JO /*환경가전외 일시불(조직).되물림후실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_FXAM_PR, 0) END) AS B_ENVR_ELHM_FXAM_PR /*환경가전 정액(개인).되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_EXCP_FXAM_PR, 0) END) AS B_ENVR_ELHM_EXCP_FXAM_PR /*환경가전외 정액.되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_ENVR_ELHM_EXCP_FXAM, 0) END) AS B_ENVR_ELHM_EXCP_FXAM /*환경가전외 정액.되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN NVL(B.A_NW_SELL_CNT_PR, 0) END) AS B_NW_SELL_CNT_PR /*신규판매건수(개인).되물림전실적*/ , MAX(CASE WHEN B.RN = 2 THEN 