GROUP BY BASE_YM , PERF_YM, OG_TP_CD, PRTNR_NO ) A /*귀속월실적(기준년월 실적년월이 같은 최초 실적)*/ LEFT OUTER JOIN ( SELECT A.RN , A.BASE_YM , A.PERF_YM , A.OG_TP_CD , A.PRTNR_NO
, NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00016', 'W00P00035') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS HOME_CARE /* 홈케어실적 */ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W00P00028', 'W00P00008') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS MC_PERF /*기변실적*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00012', 'W02P00093') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS ATDC_DC /*미팅(출근일수)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W02P00002') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM /*되물림전후실적(AFTER A) 환경가전*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W00P00028') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_MC /*되물림전후실적 기변*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W00P00029') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_FXAM /*되물림전후실적 정액*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W00P00010') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_NW_SELL_CNT_PR /*되물림전후실적 신규판매 건수(개인)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W00P00010') AND A.PERF_DV_CD = '2' THEN A.PERF_VAL END)), 0) AS A_NW_SELL_CNT_JO /*되물림전후실적 신규판매 건수(조직)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00030', 'W02P00002') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_CNT /*되물림전후실적 환경가전(건수)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00030', 'W02P00002') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_CNT_PR /*되물림전후실적 환경가전 건수(개인)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00030', 'W02P00002') AND A.PERF_DV_CD = '2' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_CNT_JO /*되물림전후실적 환경가전 건수(조직)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00001', 'W00P00003', 'W00P00004', 'W00P00005') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_BASE_AMT /*되물림전후실적 환경가전(기준금액)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00001', 'W00P00003') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_RENTAL /*되물림전후실적 환경가전 렌탈*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00001', 'W00P00003') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_RENTAL_PR /*되물림전후실적 환경가전 렌탈(개인)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00001', 'W00P00003') AND A.PERF_DV_CD = '2' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_RENTAL_JO /*되물림전후실적 환경가전 렌탈(조직)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00001', 'W00P00004') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_SPAY /*되물림전후실적 환경가전 일시불*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00001', 'W00P00004') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_SPAY_PR /*되물림전후실적 환경가전 일시불(개인)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00001', 'W00P00004') AND A.PERF_DV_CD = '2' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_SPAY_JO /*되물림전후실적 환경가전 일시불(조직)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W00P00029', 'W00P00005') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_FXAM_PR /*되물림전후실적 환경가전 정액(개인)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00004', 'W00P00006', 'W00P00007') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_EXCP /*되물림전후실적 환경가전외*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00004', 'W00P00006') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_EXCP_PR /*되물림전후실적 환경가전외 일시불(개인)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00004', 'W00P00006') AND A.PERF_DV_CD = '2' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_EXCP_JO /*되물림전후실적 환경가전외 일시불(조직)*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W00P00030', 'W00P00007') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_EXCP_FXAM /*되물림전후실적 환경가전외 정액*/ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W00P00030', 'W00P00007') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_ENVR_ELHM_EXCP_FXAM_PR /*되물림전후실적 환경가전외 정액(개인)*/ ㅇ , NVL(SUM((CASE WHEN A.PERF_ATC_CD IN ('W01P00020', 'W02P00111') AND A.PERF_DV_CD = '0' THEN A.PERF_VAL END)), 0) AS A_OG_MGT_PPL /*되물림전후실적 조직관리 실활동(명)*/ FROM ( SELECT --/*+ INDEX(TB_FEAM_NTORP_PERF_MM_CL (PERF_AGRG_CRT_DV_CD, OG_TP_CD, PERF_YM, BASE_YM)) */ --힌트 제거 BASE_YM , PERF_YM , OG_TP_CD , PRTNR_NO , PERF_DV_CD , PERF_ATC_CD , PERF_VAL , PERF_AGRG_CRT_DV_CD , ROW_NUMBER() OVER(PARTITION BY PRTNR_NO, PERF_ATC_CD, PERF_DV_CD ORDER BY BASE_YM DESC, PERF_YM DESC) AS RN FROM TB_FEAM_NTORP_PERF_MM_CL /* 순주문파트너실적월마감(ASIS: AK1700P) */ WHERE DTA_DL_YN = 'N' AND FEE_TCNT_DV_CD = '02'
AND PERF_YM = D.PERF_YM AND OG_TP_CD = D.OG_TP_CD AND PRTNR_NO = D.PRTNR_NO AND PERF_AGRG_CRT_DV_CD = '202' /*되물림후실적(순주문집계)_M */ AND OG_TP_CD = #{ogTpCd}
AND PERF_YM BETWEEN #{perfYmFrom} AND #{perfYmTo}
AND BASE_YM <= #{redfAdsbOcYmTo}
AND PRTNR_NO = #{prtnrNo}
) A WHERE A.RN IN ('1','2') GROUP BY A.BASE_YM , A.PERF_YM , A.OG_TP_CD , A.PRTNR_NO , A.RN ) B /* 되물림전월실적과 되물림후실적 */
ON A.PERF_YM = B.PERF_YM AND A.OG_TP_CD = B.OG_TP_CD AND A.PRTNR_NO = B.PRTNR_NO AND B.RN IN ('1', '2') LEFT OUTER JOIN ( SELECT BASE_YM , PERF_YM , OG_TP_CD , PRTNR_NO , ROW_NUMBER() OVER(PARTITION BY PRTNR_NO ORDER BY PERF_YM DESC) AS RN , NVL((CASE WHEN PERF_ATC_CD = 'W02P00002' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM /*귀속전월실적(BEFORE B) 환경가전*/ , NVL((CASE WHEN PERF_ATC_CD = 'W01P00002' THEN PERF_VAL END), 0) AS TB_MC /*되물림전후실적 기변*/ , NVL((CASE WHEN PERF_ATC_CD = 'W01P00014' THEN PERF_VAL END), 0) AS TB_FXAM /*되물림전후실적 정액*/ , NVL((CASE WHEN PERF_ATC_CD = 'W02P00002' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_CNT /*귀속전월실적 환경가전(건수)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W02P00002' AND PERF_DV_CD = '0' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_CNT_PR /*귀속전월실적 환경가전 건수(개인)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W02P00002' AND PERF_DV_CD = '2' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_CNT_JO /*귀속전월실적 환경가전 건수(조직)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00003' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_RENTAL /*귀속전월실적 환경가전 렌탈*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00003' AND PERF_DV_CD = '0' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_RENTAL_PR /*귀속전월실적 환경가전 렌탈(개인)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00003' AND PERF_DV_CD = '2' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_RENTAL_JO /*귀속전월실적 환경가전 렌탈(조직)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00010' AND PERF_DV_CD = '0' THEN PERF_VAL END), 0) AS TB_NW_SELL_CNT_PR /*귀속전월실적 신규판매 건수(개인)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00010' AND PERF_DV_CD = '2' THEN PERF_VAL END), 0) AS TB_NW_SELL_CNT_JO /*귀속전월실적 신규판매 건수(조직)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00004' AND PERF_DV_CD = '0' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_SPAY_PR /*귀속전월실적 환경가전 일시불(개인)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00004' AND PERF_DV_CD = '2' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_SPAY_JO /*귀속전월실적 환경가전 일시불(조직)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00006' AND PERF_DV_CD = '0' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_EXCP_PR /*귀속전월실적 환경가전외 일시불(개인)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00006' AND PERF_DV_CD = '2' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_EXCP_JO /*귀속전월실적 환경가전외 일시불(조직)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00005' AND PERF_DV_CD = '0' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_FXAM_PR /*귀속전월실적 환경가전 정액(개인)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00007' AND PERF_DV_CD = '0' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_EXCP_FXAM /*귀속전월실적 환경가전외 정액*/ , NVL((CASE WHEN PERF_ATC_CD = 'W00P00007' AND PERF_DV_CD = '0' THEN PERF_VAL END), 0) AS TB_ENVR_ELHM_EXCP_FXAM_PR /*귀속전월실적 환경가전외 정액(개인)*/ , NVL((CASE WHEN PERF_ATC_CD = 'W02P00111' THEN PERF_VAL END), 0) AS TB_OG_MGT_PPL /*귀속전월실적 조직관리 실활동(명)*/ FROM TB_FEAM_NTORP_PERF_MM_CL /* 순주문파트너실적월마감(ASIS: AK1700P) */ WHERE DTA_DL_YN = 'N' AND FEE_TCNT_DV_CD = '02' AND PERF_AGRG_CRT_DV_CD = '201' /*귀속전월실적(순주문집계)_M */ AND OG_TP_CD = #{ogTpCd} /* M조직 집계 */
AND PRTNR_NO = #{prtnrNo}
AND PERF_YM = TO_CHAR(ADD_MONTHS(TO_DATE(PERF_YM||'01', 'YYYYMMDD'), -1), 'YYYYMM') ) C /*귀속전월실적*/ ON A.BASE_YM = C.BASE_YM AND A.PERF_YM = C.PERF_YM AND A.OG_TP_CD = C.OG_TP_CD AND A.PRTNR_NO = C.PRTNR_NO WHERE 1=1
GROUP BY A.PERF_YM, A.OG_TP_CD, A.PRTNR_NO, B.HOME_CARE, B.ATDC_DC, B.MC_PERF , A.T_ENVR_ELHM, A.T_ENVR_ELHM_CNT, A.T_ENVR_ELHM_BASE_AMT, A.T_ENVR_ELHM_RENTAL, A.T_ENVR_ELHM_SPAY , A.T_ENVR_ELHM_EXCP, A.T_ENVR_ELHM_EXCP_FXAM, C.TB_ENVR_ELHM, C.TB_ENVR_ELHM_CNT, C.TB_ENVR_ELHM_RENTAL , A.T_ENVR_ELHM_CNT_PR, A.T_ENVR_ELHM_CNT_JO, A.T_ENVR_ELHM_RENTAL_PR, A.T_ENVR_ELHM_RENTAL_JO, A.T_ENVR_ELHM_SPAY_PR , A.T_ENVR_ELHM_SPAY_JO, A.T_ENVR_ELHM_EXCP_PR, A.T_ENVR_ELHM_EXCP_JO, A.T_ENVR_ELHM_EXCP_FXAM_PR, C.TB_ENVR_ELHM_CNT_PR , C.TB_ENVR_ELHM_CNT_JO, C.TB_ENVR_ELHM_RENTAL, C.TB_ENVR_ELHM_RENTAL_PR, C.TB_ENVR_ELHM_RENTAL_JO, C.TB_NW_SELL_CNT_PR , C.TB_NW_SELL_CNT_JO, C.TB_ENVR_ELHM_SPAY_PR, C.TB_ENVR_ELHM_SPAY_JO, C.TB_ENVR_ELHM_EXCP_PR, C.TB_ENVR_ELHM_EXCP_JO , C.TB_ENVR_ELHM_EXCP_FXAM, A.T_NW_SELL_CNT_PR, A.T_NW_SELL_CNT_JO, A.T_ENVR_ELHM_EXCP_FXAM_PR, A.T_ENVR_ELHM_FXAM_PR , A.T_OG_MGT_PPL, C.TB_OG_MGT_PPL, A.T_MC, C.TB_MC, A.T_FXAM, C.TB_FXAM, C.TB_ENVR_ELHM_FXAM_PR ) M ON M.PERF_YM = D.PERF_YM AND M.OG_TP_CD = D.OG_TP_CD AND M.PRTNR_NO = D.PRTNR_NO LEFT OUTER JOIN TB_OGBS_PRTNR_DTL AK04 /*현재 직급으로 조회*/ ON D.OG_TP_CD = AK04.OG_TP_CD AND D.PRTNR_NO = AK04.PRTNR_NO LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ AK05 /*월파트너내역*/ ON D.PERF_YM = AK05.BASE_YM AND D.OG_TP_CD = AK05.OG_TP_CD AND D.PRTNR_NO = AK05.PRTNR_NO LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ AK55 /*월파트너내역(실적년월+1)*/ ON M.PERF_YM_1 = AK55.BASE_YM AND M.OG_TP_CD = AK55.OG_TP_CD AND M.PRTNR_NO = AK55.PRTNR_NO LEFT OUTER JOIN TB_OGPS_TOPMR_PLAR_APLC_IZ AK06 /*수석플래너신청내역*/ ON M.PERF_YM = AK06.MNGT_YM AND M.OG_TP_CD = AK06.OG_TP_CD AND M.PRTNR_NO = AK06.PRTNR_NO AND AK06.OG_TP_CD = 'W01' /* 수석플래너는 P조직만 있음 */ LEFT JOIN LATERAL ( SELECT /*+ no_unnest */ B.PSTN_DV_CD FROM TB_OGBS_MM_PRTNR_IZ B WHERE B.PERF_EXCD_OJ_YN = 'N' AND D.REDF_ADSB_OC_YM >= B.BASE_YM /*되물림발생년월 이거나 없으면 이하, 가장최신*/ AND D.OG_TP_CD = B.OG_TP_CD AND D.PRTNR_NO = B.PRTNR_NO ORDER BY B.BASE_YM DESC FETCH FIRST 1 ROWS ONLY ) AK07 ON 1 = 1 LEFT OUTER JOIN LATERAL ( SELECT C1.BASE_YM , C1.PERF_YM , C1.PRTNR_NO , C1.OG_TP_CD , NVL(SUM(SL_AMT), 0) AS SELL_AMT /*매출금액*/ , AVG(NVL(C1.ACKMT_PERF_RT,0)) AS ACKMT_PERF_RT /*인정실적율*/ FROM TB_FEAM_WELS_NRCTR_MM_CL C1 /*웰스순주문계약월마감*/ WHERE C1.DTA_DL_YN = 'N' AND C1.FEE_TCNT_DV_CD = '02' AND M.BASE_YM = C1.BASE_YM AND M.PERF_YM = C1.PERF_YM AND M.PRTNR_NO = C1.PRTNR_NO AND M.OG_TP_CD = C1.OG_TP_CD GROUP BY C1.BASE_YM, C1.PERF_YM, C1.PRTNR_NO, C1.OG_TP_CD ) C ON 1 = 1 WHERE 1 = 1
AND AK05.OG_CD = #{ogCd} /*소속코드*/
/* 직급의 경우, M조직 연체는 실적당시 직급이 아닌 현재 직급으로 조회하는것으로 확인됨 (ASIS기준) */ /* 취소는 실적(AK1700P)의 직급을 보기 때문에, 실적 당시의 직급을 봄 (ASIS기준) */
/*취소인 경우, 실적년월 당시의 직급*/ AND TO_NUMBER(AK05.PSTN_DV_CD) > 7 /*asis 수수료체크리스트 화면 기준으로는 지점장 미만일 때, 7직급은 제외임 반대로 수당되물림일땐 7직급 포함임, 요청으로는 수수료체크리스트 기준으로 조회해야 하기 때문에 변경 2024-01-23 by.김대원*/
/*asis는 연체는 현재 직급, (다시 재수정) 이규병 파트장님 요청, 현직급 아닌 실적 당시 직급으로 변경요청 2023-12-13 by.김대원*/ /*지구장이하 연체일 경우. 실적 시점 직급이 아닌 발생 시점 직급으로 조회 필요!!!!(수당(실적) 되물림 관리 - W-DE-U-0003M02)황부장님 */ AND TO_NUMBER(AK07.PSTN_DV_CD) >= 7 /*지구장 이하, 직급은 지국장도 포함*/
/*취소인 경우, 실적년월 당시의 직급*/ AND TO_NUMBER(AK05.PSTN_DV_CD) <= 7 /*지점장 이상*/
/*연체는 현재 직급, (다시 재수정) 이규병 파트장님 요청, 현직급 아닌 실적 당시 직급으로 변경요청 2023-12-13 by.김대원*/ AND TO_NUMBER(AK07.PSTN_DV_CD) <= 7 /*지점장 이상*/
ORDER BY D.PERF_YM, AK05.OG_CD, AK05.PRTNR_NO
