mapper.select:
/*총판/B2B 되물림 생성 1번탭 조회기준 판매자별,총판 1번그리드*/ SELECT MAIN.PERF_YM /*실적년월*/ , MAIN.OG_CD /*소속코드*/ , MAIN.OG_NM /*업체명(지점명), TODO: 현재 파트너기본에 조직코드, 조직ID가 같은데 조직명은 모두 온라인총판으로 되어있음*/ , MAIN.PRTNR_NO /*판매자번호*/ , MAIN.TOT_OC_MM_CNT /*총실적.발생월*/ , MAIN.TOT_OC_MM_CNT - MAIN.REDF_TOT_CNT AS TOT_REDF_BF_CNT /*총실적.되물림전(총실적.발생월 - 되물림.총실적)*/ , MAIN.TOT_HCR_CNT /*총실적.홈케어*/ , MAIN.SAM_OC_MM_CNT /*삼성제품.발생월*/ , MAIN.SAM_OC_MM_CNT - MAIN.REDF_SAM_CNT AS SAM_REDF_BF_CNT /*삼성제품.되물림전(삼성제품.발생월 - 되물림.삼성실적)*/ , MAIN.WEL_OC_MM_CNT /*자사제품.발생월*/ , MAIN.WEL_OC_MM_CNT - MAIN.REDF_WEL_CNT AS WEL_REDF_BF_CNT /*자사제품.되물림전(자사제품.발생월 - 되물림.자사제품 실적)*/ , MAIN.REDF_TOT_CNT /*되물림.총실적(웰스순주문계약월마감 우리꺼 건수)*/ , MAIN.REDF_SAM_CNT /*되물림.삼성실적(웰스순주문계약월마감 우리꺼 건수)*/ , MAIN.REDF_WEL_CNT /*되물림.자사제품 실적(웰스순주문계약월마감 우리꺼 건수)*/ , (MAIN.TOT_OC_MM_CNT - MAIN.REDF_TOT_CNT) - MAIN.REDF_TOT_CNT AS TOT_REDF_AF_CNT /*되물림 후.총실적(총실적.되물림전 - 되물림.총실적)*/ , (MAIN.SAM_OC_MM_CNT - MAIN.REDF_SAM_CNT) - MAIN.REDF_SAM_CNT AS SAM_REDF_AF_CNT /*되물림 후.삼성실적(삼성제품.되물림전 - 되물림.삼성실적)*/ , (MAIN.WEL_OC_MM_CNT - MAIN.REDF_WEL_CNT) - MAIN.REDF_WEL_CNT AS WEL_REDF_AF_CNT /*발생월 - 되물림전 = 되물림후.자사제품 실적*/ , MAIN.FEE_BASE /*수수료(원).기본*/ , MAIN.FEE_ENCRG /*수수료(원).장려*/ , MAIN.FEE_BASE + MAIN.FEE_ENCRG AS FEE_SUM /*수수료(원).합계*/ FROM ( SELECT M.PERF_YM /*실적년월*/ , B.OG_CD /*소속코드*/ , B.OG_NM /*업체명(지점명), TODO: 현재 파트너기본에 조직코드, 조직ID가 같은데 조직명은 모두 온라인총판으로 되어있음*/ , M.PRTNR_NO /*판매자번호*/ , NVL((CASE WHEN M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND M.PERF_ATC_CD IN ('W05P00010') THEN SUM(M.PERF_VAL) END), 0) AS TOT_OC_MM_CNT /*총실적.발생월(총건수, 실적항목코드 10번)*/ , NVL((CASE WHEN C.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND (C.PERF_TP_CD = '1' OR C.PERF_TP_CD = '10') /*ASIS 기준 연체+취소일때 '되물림전'*/ THEN SUM(C.ACKMT_PERF_CT) END), 0) AS TOT_REDF_BF_CNT /*총실적.되물림전*/ , NVL((CASE WHEN M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND M.PERF_ATC_CD = 'W05P00008' /*ASIS LC5910.LCCN51(홈케어일시수)*/ THEN SUM(M.PERF_VAL) END), 0) AS TOT_HCR_CNT /*총실적.홈케어*/ , NVL((CASE WHEN M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND M.PERF_ATC_CD = 'W05P00002' /*ASIS AB9500P.LCCN34, LCCN36 */ THEN SUM(M.PERF_VAL) END), 0) AS SAM_OC_MM_CNT /*삼성제품.발생월*/ , NVL((CASE WHEN M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND M.PERF_ATC_CD IN ('W05P00003', 'W05P00004', 'W05P00005', 'W05P00006', 'W05P00007') /*ASIS AB9400P AKCN31 + AKCN32 + AKCN33 + AKCN34 + AKCN35 */ THEN SUM(M.PERF_VAL) END), 0) AS WEL_OC_MM_CNT /*자사제품.발생월*/ , NVL((CASE WHEN C.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND (C.PERF_TP_CD = '1' OR C.PERF_TP_CD = '10') /*ASIS 기준 연체+취소일때 '되물림전'*/ THEN SUM(C.ACKMT_PERF_CT) END), 0) AS REDF_TOT_CNT /*되물림.총실적(되물림전)*/ , NVL((CASE WHEN C.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND (C.PERF_TP_CD = '1' OR C.PERF_TP_CD = '10') /*ASIS 기준 연체+취소일때 '되물림전'*/ AND M.PERF_ATC_CD = 'W05P00002' /*ASIS AB9500P.LCCN34, LCCN36 */ THEN SUM(C.ACKMT_PERF_CT) END), 0) AS REDF_SAM_CNT /*되물림.삼성실적(되물림전)*/ , NVL((CASE WHEN C.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND (C.PERF_TP_CD = '1' OR C.PERF_TP_CD = '10') /*ASIS 기준 연체+취소일때 '되물림전'*/ AND M.PERF_ATC_CD IN ('W05P00003', 'W05P00004', 'W05P00005', 'W05P00006', 'W05P00007') /*ASIS AB9400P AKCN31 + AKCN32 + AKCN33 + AKCN34 + AKCN35 */ THEN SUM(C.ACKMT_PERF_CT) END), 0) AS REDF_WEL_CNT /*되물림.자사제품 실적(되물림전)*/ , NVL((CASE WHEN M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND E.FEE_CD = ('W050001') /*ASIS AB9500P.AKAM52, AKAM53, TOBE*/ THEN SUM(M.PERF_VAL) END), 0) AS FEE_BASE /*수수료(원).기본*/ , NVL((CASE WHEN M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND E.FEE_CD = ('W050002') /*ASIS AB9500P.AKAM52, AKAM53, TOBE*/ THEN SUM(M.PERF_VAL) END), 0) AS FEE_ENCRG /*수수료(원).장려*/ FROM TB_FEAM_NTORP_PERF_MM_CL M /*순주문파트너실적월마감*/ INNER JOIN TB_OGBS_PRTNR_BAS B /*파트너기본*/ ON M.PRTNR_NO = B.PRTNR_NO AND M.OG_TP_CD = B.OG_TP_CD LEFT OUTER JOIN TB_FEDD_FEE_REDF_ADSB_BAS E /*수수료되물림재지급기본, 데이터 없어서 OUTER 조인, 추후 INNER로 변경 가능성있음*/ ON M.PRTNR_NO = E.PRTNR_NO AND M.OG_TP_CD = E.OG_TP_CD INNER JOIN TB_FEAM_WELS_NRCTR_MM_CL C /*웰스순주문계약월마감, 현재 0 개인판매 데이터만 있어서 OUTER 조인, 추후 INNER로 변경가능성있음*/ ON M.BASE_YM = C.BASE_YM AND M.PERF_YM = C.PERF_YM AND M.PRTNR_NO = C.PRTNR_NO AND M.OG_TP_CD = C.OG_TP_CD LEFT OUTER JOIN TB_PDBS_PD_ECOM_PRP_DTL D /*상품각사속성상세*/ ON C.PD_CD = D.PD_CD AND D.PD_EXTS_PRP_GRP_CD = 'SPP' WHERE M.DTA_DL_YN = 'N' AND M.OG_TP_CD = 'W05'/*총판 (고정값)*/ AND M.PERF_AGRG_CRT_DV_CD = '501' /*총판수수료실적생성(고정값)*/
AND M.CO_CD = '2000' /*웰스 2000(고정값)*/
AND M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo}
AND M.PERF_YM BETWEEN #{perfYmFrom} AND #{perfYmTo}
AND B.OG_CD BETWEEN #{ogCdFrom} AND #{ogCdTo}
GROUP BY M.PERF_YM, M.BASE_YM, M.PERF_ATC_CD, M.PRTNR_NO , C.PERF_TP_CD, B.OG_CD, B.OG_NM, E.FEE_CD, C.BASE_YM ) MAIN

/*총판/B2B 되물림 생성 1번탭 조회기준 판매자별,조직유형 B2B 그리드 3번*/ SELECT MAIN.PERF_YM , MAIN.OG_CD , MAIN.OG_NM , MAIN.PRTNR_NO , MAIN.OC_PERF_CN , MAIN.OC_PERF_CN - ACKMT_PERF_CT AS REDF_BF_CN /*되물림 전*/ , MAIN.ACKMT_PERF_CT , (MAIN.OC_PERF_CN - ACKMT_PERF_CT) - MAIN.ACKMT_PERF_CT AS REDF_AF_CN /*되물림 후*/ FROM ( SELECT M.PERF_YM /*실적년월*/ , A.OG_CD /*소속코드*/ , A.OG_NM /*업체명*/ , M.PRTNR_NO /*판매자번호(파트너번호)*/ , NVL((CASE WHEN M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND M.PERF_ATC_CD = 'W04P00010' THEN SUM(M.PERF_VAL) END), 0) AS OC_PERF_CN /*발생월(발생 실적 총건수)*/ , NVL(SUM(B.ACKMT_PERF_CT), 0) AS ACKMT_PERF_CT /*당월 되물림(되물림 발생된 인정실적건수)*/ , NVL((CASE WHEN M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND M.PERF_ATC_CD = 'W04P00001' THEN SUM(M.PERF_VAL) END), 0) AS FEE_BASE /*수수료(원)*/ FROM TB_FEAM_NTORP_PERF_MM_CL M /*순주문파트너실적월마감*/ INNER JOIN TB_OGBS_PRTNR_BAS A /*파트너기본*/ ON M.PRTNR_NO = A.PRTNR_NO AND M.OG_TP_CD = A.OG_TP_CD LEFT OUTER JOIN TB_FEAM_WELS_NRCTR_MM_CL B ON M.BASE_YM = B.BASE_YM AND M.PERF_YM = B.PERF_YM AND M.PRTNR_NO = B.PRTNR_NO AND M.OG_TP_CD = B.OG_TP_CD WHERE M.DTA_DL_YN = 'N' AND M.OG_TP_CD = 'W04'/*B2B (고정값)*/ AND M.PERF_AGRG_CRT_DV_CD = '401' /*총판수수료실적생성(고정값)*/
AND M.CO_CD = '2000' /*웰스 2000(고정값)*/
AND M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo}
AND M.PERF_YM BETWEEN #{perfYmFrom} AND #{perfYmFrom} /*실적년월*/
GROUP BY M.PERF_YM, M.BASE_YM, M.PERF_ATC_CD, M.PRTNR_NO , A.OG_CD, A.OG_NM ) MAIN

/*총판/B2B 되물림 생성 2번탭*/ SELECT A.OG_NM /*업체명(지점명)*/ , A.OG_CD /*소속코드(지점코드)*/ , M.REDF_ADSB_OC_YM /*발생년월 KEY*/ , M.OG_TP_CD /*조직유형 KEY*/ , M.PRTNR_NO /*판매자번호*/ , NVL(SUM(M.REDF_DDTN_BLAM_CRDOVR_AMT), 0) AS REDF_DDTN_BLAM_CRDOVR_AMT /*전월잔액(ASIS: AB9420P.ABIWAM 이월잔액)*/ , NVL(SUM(M.REDF_OC_AMT), 0) AS REDF_OC_AMT /*당월발생(ASIS: AB9420.ABDAAM 당월발생), 참조: ASIS는 관리년월이 단일이였고 TOBE는 FROM~TO로 변경됨*/ , NVL(SUM(M.REDF_DDTN_BLAM_CRDOVR_AMT), 0) + NVL(SUM(M.REDF_OC_AMT), 0) AS REDF_OC_SUM /*당월합계, 이월잔액+당월발생*/ , NVL(SUM(M.REDF_DDCTAM), 0) AS REDF_DDCTAM /*당월공제(ASIS: AB9420.ABDGAM 당월공제)*/ , NVL(SUM(M.REDF_DDTN_BLAM), 0) AS REDF_DDTN_BLAM /*당월잔액(ASIS: AB9420.ABJAAM 당월잔액)*/ FROM TB_FEDD_SODBT_FEE_MM_REDF_IZ M /*총판수수료월되물림내역*/ LEFT OUTER JOIN TB_OGBS_PRTNR_BAS A /*파트너기본*/ ON M.PRTNR_NO = A.PRTNR_NO AND M.OG_TP_CD = A.OG_TP_CD WHERE M.DTA_DL_YN = 'N'
AND M.REDF_ADSB_OC_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} /*관리년월(발생년월)*/
AND M.OG_TP_CD = #{ogTpCd} GROUP BY A.OG_NM, A.OG_CD, M.PRTNR_NO, M.REDF_ADSB_OC_YM, M.OG_TP_CD

mapper.update:
UPDATE TB_FEDD_SODBT_FEE_MM_REDF_IZ /*총판수수료월되물림내역*/
   SET REDF_DDCTAM = #{redfDdctam}
     , REDF_DDTN_BLAM = (${redfOcAmt} + ${redfDdtnBlamCrdovrAmt}) - ${redfDdctam}
 WHERE REDF_ADSB_OC_YM = #{redfAdsbOcYm}
   AND OG_TP_CD = #{ogTpCd}
   AND PRTNR_NO = #{prtnrNo}
