mapper.select:
/*총판/B2B 되물림 생성 1번탭 조회기준 판매자별,조직유형 B2B 그리드 3번*/ SELECT MAIN.PERF_YM , MAIN.OG_CD , MAIN.OG_NM , MAIN.PRTNR_NO , MAIN.OC_PERF_CN , MAIN.OC_PERF_CN - ACKMT_PERF_CT AS REDF_BF_CN /*되물림 전*/ , MAIN.ACKMT_PERF_CT , (MAIN.OC_PERF_CN - ACKMT_PERF_CT) - MAIN.ACKMT_PERF_CT AS REDF_AF_CN /*되물림 후*/ , MAIN.FEE_BASE /*수수료(원)*/ FROM ( SELECT M.PERF_YM /*실적년월*/ , A.OG_CD /*소속코드*/ , A.OG_NM /*업체명*/ , M.PRTNR_NO /*판매자번호(파트너번호)*/ , NVL((CASE WHEN M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND M.PERF_ATC_CD = 'W04P00010' THEN SUM(M.PERF_VAL) END), 0) AS OC_PERF_CN /*발생월(발생 실적 총건수)*/ , NVL(SUM(B.ACKMT_PERF_CT), 0) AS ACKMT_PERF_CT /*당월 되물림(되물림 발생된 인정실적건수)*/ , NVL((CASE WHEN M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo} AND M.PERF_ATC_CD = 'W04P00001' THEN SUM(M.PERF_VAL) END), 0) AS FEE_BASE /*수수료(원)*/ FROM TB_FEAM_NTORP_PERF_MM_CL M /*순주문파트너실적월마감*/ INNER JOIN TB_OGBS_PRTNR_BAS A /*파트너기본*/ ON M.PRTNR_NO = A.PRTNR_NO AND M.OG_TP_CD = A.OG_TP_CD LEFT OUTER JOIN TB_FEAM_WELS_NRCTR_MM_CL B ON M.BASE_YM = B.BASE_YM AND M.PERF_YM = B.PERF_YM AND M.PRTNR_NO = B.PRTNR_NO AND M.OG_TP_CD = B.OG_TP_CD WHERE M.DTA_DL_YN = 'N' AND M.OG_TP_CD = 'W04'/*B2B (고정값)*/ AND M.PERF_AGRG_CRT_DV_CD = '401' /*총판수수료실적생성(고정값)*/
AND M.CO_CD = '2000' /*웰스 2000(고정값)*/
AND M.BASE_YM BETWEEN #{redfAdsbOcYmFrom} AND #{redfAdsbOcYmTo}
AND M.PERF_YM BETWEEN #{perfYmFrom} AND #{perfYmFrom} /*실적년월*/
GROUP BY M.PERF_YM, M.BASE_YM, M.PERF_ATC_CD, M.PRTNR_NO , A.OG_CD, A.OG_NM ) MAIN

/*총판/B2B 되물림 생성 2번탭*/ SELECT A.PRTNR_KNM AS OG_NM /*업체명(지점명)*/ , A.OG_CD /*소속코드(지점코드)*/ , M.REDF_ADSB_OC_YM /*발생년월 KEY*/ , M.OG_TP_CD /*조직유형 KEY*/ , M.PRTNR_NO /*판매자번호*/ , NVL(SUM(M.REDF_DDTN_BLAM_CRDOVR_AMT), 0) AS REDF_DDTN_BLAM_CRDOVR_AMT /*전월잔액(ASIS: AB9420P.ABIWAM 이월잔액)*/ , NVL(SUM(M.REDF_OC_AMT), 0) AS REDF_OC_AMT /*당월발생(ASIS: AB9420.ABDAAM 당월발생), 참조: ASIS는 관리년월이 단일이였고 TOBE는 FROM~TO로 변경됨*/ , NVL(SUM(M.REDF_DDTN_BLAM_CRDOVR_AMT), 0) + NVL(SUM(M.REDF_OC_AMT), 0) AS REDF_OC_SUM /*당월합계, 이월잔액+당월발생*/ , NVL(SUM(M.REDF_DDCTAM), 0) AS REDF_DDCTAM /*당월공제(ASIS: AB9420.ABDGAM 당월공제)*/ , NVL(SUM(M.REDF_DDTN_BLAM), 0) AS REDF_DDTN_BLAM /*당월잔액(ASIS: AB9420.ABJAAM 당월잔액)*/ FROM TB_FEDD_SODBT_FEE_MM_REDF_IZ M /*총판수수료월되물림내역*/ LEFT OUTER JOIN TB_OGBS_PRTNR_BAS A /*파트너기본*/ ON M.PRTNR_NO = A.PRTNR_NO AND M.OG_TP_CD = A.OG_TP_CD WHERE M.DTA_DL_YN = 'N'
AND M.REDF_ADSB_OC_YM = #{redfAdsbOcYm} /*관리년월(발생년월)*/
AND M.OG_TP_CD = #{ogTpCd} GROUP BY A.OG_NM, A.OG_CD, M.PRTNR_NO, M.REDF_ADSB_OC_YM, M.OG_TP_CD, A.PRTNR_KNM

mapper.update:
UPDATE TB_FEDD_SODBT_FEE_MM_REDF_IZ /*총판수수료월되물림내역*/
   SET REDF_DDCTAM = #{redfDdctam}
     , REDF_DDTN_BLAM = (${redfOcAmt} + ${redfDdtnBlamCrdovrAmt}) - ${redfDdctam}
 WHERE REDF_ADSB_OC_YM = #{redfAdsbOcYm}
   AND OG_TP_CD = #{ogTpCd}
   AND PRTNR_NO = #{prtnrNo}

mapper.insert:
/*되물림 금액 생성 된 것, 총판가지급 테이블에 MERGE*/ MERGE INTO TB_FEDD_SODBT_FEE_MM_REDF_IZ A /*총판수수료월되물림내역*/ USING ( SELECT S.REDF_ADSB_OC_YM /*되물림재지급발생년월*/ , S.OG_TP_CD /*조직유형*/ , S.PRTNR_NO /*파트너번호*/ , NVL(S2.REDF_DDTN_BLAM_CRDOVR_AMT, 0) AS REDF_DDTN_BLAM_CRDOVR_AMT /*되물림공제잔액이월금액*/ , NVL(S1.REDF_ADSB_OC_AMT, 0) AS REDF_OC_AMT /*되물림발생금액(당월발생)*/ , 0 AS REDF_DDCTAM /*되물림공제금액(당월공제)*/ , NVL(S2.REDF_DDTN_BLAM, 0) AS REDF_DDTN_BLAM /*되물림공제잔액*/ FROM ( SELECT #{redfAdsbOcYm} AS REDF_ADSB_OC_YM /*PARAM: 발생년월*/ , B.OG_TP_CD /*총판 고정*/ , B.PRTNR_NO FROM ( SELECT B1.REDF_ADSB_OC_YM , B1.OG_TP_CD , B2.HOO_PRTNR_NO AS PRTNR_NO , NVL(SUM(B1.REDF_ADSB_CTR_CNFM_AMT), 0) AS REDF_ADSB_OC_AMT FROM TB_FEDD_FEE_REDF_ADSB_BAS B1 INNER JOIN TB_FEAM_MM_PRTNR_HOO_IZ B2 ON B1.PRTNR_NO = B2.PRTNR_NO AND B1.OG_TP_CD = B2.OG_TP_CD AND B1.PERF_YM = B2.BASE_YM AND B1.PERF_DV_CD = B2.PERF_DV_CD WHERE B1.REDF_ADSB_DV_CD = '02' /*되물림 고정*/ AND B1.REDF_ADSB_OC_YM = #{redfAdsbOcYm} /*PARAM: 되물림 금액 발생년월*/ AND B1.OG_TP_CD = 'W05' /*총판 고정*/ GROUP BY B1.REDF_ADSB_OC_YM, B1.OG_TP_CD, B1.PRTNR_NO, B2.HOO_PRTNR_NO ) B UNION SELECT #{redfAdsbOcYm} AS REDF_ADSB_OC_YM /*PARAM: 발생년월*/ , A.OG_TP_CD /*총판 고정*/ , A.PRTNR_NO FROM TB_FEDD_SODBT_FEE_MM_REDF_IZ A WHERE A.REDF_ADSB_OC_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{redfAdsbOcYm}, 'YYYYMM'), -1), 'YYYYMM') /*PARAM: 발생년월 전월 기준*/ AND A.REDF_DDTN_BLAM > 0 ) S LEFT OUTER JOIN ( SELECT B1.REDF_ADSB_OC_YM , B1.OG_TP_CD , B2.HOO_PRTNR_NO AS PRTNR_NO , NVL(SUM(B1.REDF_ADSB_CTR_CNFM_AMT), 0) AS REDF_ADSB_OC_AMT FROM TB_FEDD_FEE_REDF_ADSB_BAS B1 INNER JOIN TB_FEAM_MM_PRTNR_HOO_IZ B2 ON B1.PRTNR_NO = B2.PRTNR_NO AND B1.OG_TP_CD = B2.OG_TP_CD AND B1.PERF_YM = B2.BASE_YM AND B1.PERF_DV_CD = B2.PERF_DV_CD WHERE B1.REDF_ADSB_DV_CD = '02' /*되물림 고정*/ AND B1.REDF_ADSB_OC_YM = #{redfAdsbOcYm} /*PARAM: 되물림 금액 발생년월*/ AND B1.OG_TP_CD = 'W05' /*총판 고정*/ GROUP BY B1.REDF_ADSB_OC_YM, B1.OG_TP_CD, B1.PRTNR_NO, B2.HOO_PRTNR_NO ) S1 ON S.REDF_ADSB_OC_YM = S1.REDF_ADSB_OC_YM AND S.PRTNR_NO = S1.PRTNR_NO AND S.OG_TP_CD = S1.OG_TP_CD LEFT OUTER JOIN TB_FEDD_SODBT_FEE_MM_REDF_IZ S2 ON 1 = 1 AND S.PRTNR_NO = S2.PRTNR_NO AND S.OG_TP_CD = S2.OG_TP_CD AND S2.REDF_ADSB_OC_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{redfAdsbOcYm}, 'YYYYMM'), -1), 'YYYYMM') /*PARAM: 발생년월 전월 기준*/ AND S2.REDF_DDTN_BLAM > 0 ) B ON ( A.REDF_ADSB_OC_YM = B.REDF_ADSB_OC_YM AND A.PRTNR_NO = B.PRTNR_NO AND A.OG_TP_CD = B.OG_TP_CD ) /*해당 발생년월이 이미 만들어져있으면, UPDATE 아니면 INSERT*/ WHEN MATCHED THEN UPDATE SET A.REDF_DDTN_BLAM_CRDOVR_AMT = B.REDF_DDTN_BLAM_CRDOVR_AMT , A.REDF_OC_AMT = B.REDF_OC_AMT , A.REDF_DDCTAM = B.REDF_DDCTAM , A.REDF_DDTN_BLAM = A.REDF_DDTN_BLAM
WHEN NOT MATCHED THEN INSERT ( A.REDF_ADSB_OC_YM , A.OG_TP_CD , A.PRTNR_NO , A.REDF_DDTN_BLAM_CRDOVR_AMT , A.REDF_OC_AMT , A.REDF_DDCTAM , A.REDF_DDTN_BLAM , A.DTA_DL_YN
) VALUES ( B.REDF_ADSB_OC_YM , B.OG_TP_CD , B.PRTNR_NO , B.REDF_DDTN_BLAM_CRDOVR_AMT , B.REDF_OC_AMT , B.REDF_DDCTAM , B.REDF_DDTN_BLAM , 'N'
)
